INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (841, 1, 3, 1, 0, "夜はこれからよ！\n朝まで\nゲームするからね！", 102202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (842, 1, 0, 1, 0, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 102202, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (843, 1, 0, 1, 0, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 102202, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (844, 1, 0, 1, 0, "可愛い…けど、\n私には似合わないかな", 102202, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (845, 1, 0, 1, 0, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 102202, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (846, 1, 0, 1, 0, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 102202, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (847, 1, 0, 1, 0, "最近ゲームばかりだし\nたまにはいいかな？", 102202, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (848, 1, 0, 1, 0, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 102202, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (849, 1, 0, 1, 0, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 102202, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (851, 1, 0, 1, 0, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 102301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (852, 1, 0, 1, 0, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 102301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (853, 1, 0, 1, 0, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 102301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (854, 1, 0, 1, 0, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 102301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (855, 1, 0, 1, 0, "この部屋、ぷうきちを\n置く場所はないのかな", 102301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (856, 4, 0, 1, 0, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 102301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (857, 8, 0, 1, 0, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 102301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (858, 1, 0, 1, 0, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 102301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (859, 1, 0, 1, 0, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 102301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (860, 1, 0, 1, 0, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 102301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (861, 1, 0, 1, 0, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 102301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (862, 4, 0, 1, 0, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 102301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (863, 8, 0, 1, 0, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 102301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (864, 1, 1, 1, 0, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 102301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (865, 1, 2, 1, 0, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 102301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (866, 1, 3, 1, 0, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 102301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (868, 1, 0, 1, 0, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 102301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (869, 1, 0, 1, 0, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 102301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (870, 1, 0, 1, 0, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 102301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (871, 1, 0, 1, 0, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 102301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (873, 1, 0, 1, 0, "おいしい～\nぷうきちも\n食べてみる？", 102301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (874, 1, 0, 1, 0, "ぷうきち！\nこの本、読んで～！", 102301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (901, 1, 0, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (902, 1, 0, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (903, 1, 0, 1, 0, "どうしたら転ばなく\nなるんでしょうか？", 102501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (904, 1, 0, 1, 0, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 102501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (905, 1, 0, 1, 0, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 102501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (906, 4, 0, 1, 0, "おいしい料理で\nお出迎えしなくちゃ！", 102501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (907, 8, 0, 1, 0, "一生懸命\nご奉仕しますよ～♪", 102501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (908, 1, 0, 1, 0, "耳かきしましょうか？\nえ？　こわい？", 102501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (909, 1, 0, 1, 0, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 102501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (910, 1, 0, 1, 0, "おかえりなさいませ\nご主人さま♪", 102501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (911, 1, 0, 1, 0, "すみません\nすみません\nすみません～～", 102501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (912, 4, 0, 1, 0, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 102501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (913, 8, 0, 1, 0, "私のこと、\n専属メイドと思って\n頼ってくださいね", 102501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (914, 1, 1, 1, 0, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 102501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (915, 1, 2, 1, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (916, 1, 3, 1, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (917, 1, 0, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102501, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (918, 1, 0, 1, 0, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 102501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (919, 1, 0, 1, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 102501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (920, 1, 0, 1, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (921, 1, 0, 1, 0, "ここでお洗濯ものを\n乾かしましょうか", 102501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (922, 1, 0, 1, 0, "あわわわっ…\nこ、転びそうです～！", 102501, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (923, 1, 0, 1, 0, "これくらい\nおいしいものを\n作ってみたいです～", 102501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (924, 1, 0, 1, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (926, 1, 0, 1, 0, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 102502, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (927, 1, 0, 1, 0, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 102502, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (928, 1, 0, 1, 0, "どうやったら、立派な\nメイドになれるのかな", 102502, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (929, 1, 0, 1, 0, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 102502, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (930, 1, 0, 1, 0, "ご用があれば\nなんでも言って\nくださいね", 102502, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (931, 8, 0, 1, 0, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 102502, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (932, 8, 0, 1, 0, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 102502, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (933, 1, 0, 1, 0, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 102502, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (934, 1, 0, 1, 0, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 102502, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (935, 1, 0, 1, 0, "私、またなにか\nやっちゃいましたか？", 102502, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (936, 1, 0, 1, 0, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 102502, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (937, 8, 0, 1, 0, "転んだふりであの人に\n…って！\nなんでもないですよ！", 102502, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (938, 8, 0, 1, 0, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 102502, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (939, 1, 1, 1, 0, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 102502, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (940, 1, 2, 1, 0, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 102502, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (941, 1, 3, 1, 0, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 102502, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (942, 1, 0, 1, 0, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 102502, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (943, 1, 0, 1, 0, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 102502, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (944, 1, 0, 1, 0, "かわいい♪\nあとでお洗濯\nしてあげますね", 102502, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (945, 1, 0, 1, 0, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 102502, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (946, 1, 0, 1, 0, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 102502, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (947, 1, 0, 1, 0, "運動しようとすると\nいつも怪我するん\nですよね…", 102502, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (948, 1, 0, 1, 0, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 102502, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (949, 1, 0, 1, 0, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 102502, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (951, 1, 0, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 102701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (952, 1, 0, 1, 0, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 102701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (953, 1, 0, 1, 0, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 102701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (954, 1, 0, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (955, 1, 0, 1, 0, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 102701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (956, 4, 0, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (957, 8, 0, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (958, 1, 0, 1, 0, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 102701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (959, 1, 0, 1, 0, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 102701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (960, 1, 0, 1, 0, "お願いがあるのです。\n新しい研究の実験台に\n…", 102701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (961, 1, 0, 1, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (962, 4, 0, 1, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (963, 8, 0, 1, 0, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 102701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (964, 1, 1, 1, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (965, 1, 2, 1, 0, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 102701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (966, 1, 3, 1, 0, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 102701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (967, 1, 0, 1, 0, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 102701, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (968, 1, 0, 1, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 102701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (969, 1, 0, 1, 0, "あら、キメラの素体に\nぴったりですわ", 102701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (970, 1, 0, 1, 0, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 102701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (971, 1, 0, 1, 0, "あたたかい…\n加熱調合にも\n使えそうですわね", 102701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (972, 1, 0, 1, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102701, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (973, 1, 0, 1, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (974, 1, 0, 1, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (976, 1, 0, 1, 0, "あの方と私だけの\n部屋にしたいですわ", 102702, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (977, 1, 0, 1, 0, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 102702, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (978, 1, 0, 1, 0, "新しい薬\nどうやってあの方に\n飲ませようかしら", 102702, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (979, 1, 0, 1, 0, "部屋からあの方の\n香りが…\nああ、至福ですわ", 102702, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (980, 1, 0, 1, 0, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 102702, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (981, 8, 0, 1, 0, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 102702, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (982, 8, 0, 1, 0, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 102702, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (983, 1, 0, 1, 0, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 102702, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (984, 1, 0, 1, 0, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 102702, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (985, 1, 0, 1, 0, "お願いがあるのです\n新しい研究の\n実験台に…", 102702, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (986, 1, 0, 1, 0, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 102702, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (987, 8, 0, 1, 0, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 102702, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (988, 8, 0, 1, 0, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 102702, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (989, 1, 1, 1, 0, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 102702, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (990, 1, 2, 1, 0, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 102702, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (991, 1, 3, 1, 0, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 102702, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (992, 1, 0, 1, 0, "私ももう少し\n外見に気を配る\nべきでしょうか…", 102702, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (993, 1, 0, 1, 0, "はっ！\nリラックスしたら\n閃きましたわ！", 102702, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (994, 1, 0, 1, 0, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 102702, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (995, 1, 0, 1, 0, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 102702, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (996, 1, 0, 1, 0, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 102702, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (997, 1, 0, 1, 0, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 102702, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (998, 1, 0, 1, 0, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 102702, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (999, 1, 0, 1, 0, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 102702, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1001, 1, 0, 1, 0, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 102801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1002, 1, 0, 1, 0, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 102801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1003, 1, 0, 1, 0, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 102801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1004, 1, 0, 1, 0, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 102801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1005, 1, 0, 1, 0, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 102801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1006, 4, 0, 1, 0, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 102801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1007, 8, 0, 1, 0, "焼きそばパンが\n食べたいな～\nあいつと…", 102801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1008, 1, 0, 1, 0, "ねぇあんた、どこか\n安いお店知らない？", 102801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1009, 1, 0, 1, 0, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 102801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1010, 1, 0, 1, 0, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 102801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1011, 1, 0, 1, 0, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 102801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1012, 4, 0, 1, 0, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 102801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1013, 8, 0, 1, 0, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 102801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1014, 1, 1, 1, 0, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 102801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1015, 1, 2, 1, 0, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 102801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1016, 1, 3, 1, 0, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 102801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1017, 1, 0, 1, 0, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 102801, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1018, 1, 0, 1, 0, "これで仕事は一段落ね\nちょっと休憩…", 102801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1019, 1, 0, 1, 0, "うふふ、\nかわいいものを\n見るのは幸せね～", 102801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1020, 1, 0, 1, 0, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 102801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1021, 1, 0, 1, 0, "風邪を引かないように\n身体を温めなくちゃ", 102801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1022, 1, 0, 1, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102801, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1023, 1, 0, 1, 0, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 102801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1024, 1, 0, 1, 0, "知識から過去を学んで\n未来に活かすわよ", 102801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1026, 1, 0, 1, 0, "安売りのお店の情報\n整理しないと…", 102802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1027, 1, 0, 1, 0, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 102802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1028, 1, 0, 1, 0, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 102802, 3, 1);
