INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1033, 1, 0, 1, 0, "今度買い物に\n付き合いなさい\nセールがあるのよ", 102802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1034, 1, 0, 1, 0, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 102802, 2, 2);
