INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1037, 8, 0, 1, 0, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 102802, 101, 2);
