INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1040, 1, 2, 1, 0, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 102802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1041, 1, 3, 1, 0, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 102802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1042, 1, 0, 1, 0, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 102802, 1, 3);
