INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1047, 1, 0, 1, 0, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 102802, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1048, 1, 0, 1, 0, "しっかり食べて\n栄養補給、栄養補給！", 102802, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1049, 1, 0, 1, 0, "知識から過去を学んで\n未来に活かすわよ", 102802, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1051, 1, 0, 1, 0, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 102901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1052, 1, 0, 1, 0, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 102901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1053, 1, 0, 1, 0, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 102901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1054, 1, 0, 1, 0, "彼とデュエット\nしてみたいな～♪", 102901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1055, 1, 0, 1, 0, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 102901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1056, 4, 0, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1057, 8, 0, 1, 0, "写真集見なくても、\n本物がここに\nいるのにな～", 102901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1058, 1, 0, 1, 0, "握手会の練習、\n付き合ってくれない？", 102901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1059, 1, 0, 1, 0, "写真集のポーズ\n練習中～！\nど、どうかな？", 102901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1060, 1, 0, 1, 0, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 102901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1061, 1, 0, 1, 0, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 102901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1062, 4, 0, 1, 0, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 102901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1063, 8, 0, 1, 0, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 102901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1064, 1, 1, 1, 0, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 102901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1065, 1, 2, 1, 0, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 102901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1066, 1, 3, 1, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 102901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1067, 1, 0, 1, 0, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 102901, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1068, 1, 0, 1, 0, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 102901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1069, 1, 0, 1, 0, "かわいい～♪\nこれは強力な\nライバル登場かな！", 102901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1070, 1, 0, 1, 0, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 102901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1071, 1, 0, 1, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1072, 1, 0, 1, 0, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 102901, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1073, 1, 0, 1, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102901, 7, 3);
