INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1076, 1, 0, 1, 0, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 102902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1077, 1, 0, 1, 0, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 102902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1078, 1, 0, 1, 0, "みんながくれた\nファンレター…\n私の大事な宝物…", 102902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1079, 1, 0, 1, 0, "彼とデュエット\nしてみたいな～♪", 102902, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1080, 1, 0, 1, 0, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 102902, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1081, 8, 0, 1, 0, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 102902, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1082, 8, 0, 1, 0, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 102902, 102, 1);
