INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1085, 1, 0, 1, 0, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 102902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1086, 1, 0, 1, 0, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 102902, 4, 2);
