INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1091, 1, 3, 1, 0, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 102902, 3, 2);
