INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1096, 1, 0, 1, 0, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 102902, 5, 3);
