INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1098, 1, 0, 1, 0, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 102902, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1099, 1, 0, 1, 0, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 102902, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1101, 1, 0, 1, 0, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 103001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1102, 1, 0, 1, 0, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 103001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1103, 1, 0, 1, 0, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 103001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1104, 1, 0, 1, 0, "むむ、アクダイカンの\n気配がするデース", 103001, 4, 1);
