INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1106, 4, 0, 1, 0, "ショーグンと\n出会えて、\nワタシは果報者デス！", 103001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1107, 8, 0, 1, 0, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 103001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1108, 1, 0, 1, 0, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 103001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1109, 1, 0, 1, 0, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 103001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1110, 1, 0, 1, 0, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 103001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1111, 1, 0, 1, 0, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 103001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1112, 4, 0, 1, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1113, 8, 0, 1, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1114, 1, 1, 1, 0, "おはようデス！\n今日も一日\n忍法日和デス！", 103001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1115, 1, 2, 1, 0, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 103001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1116, 1, 3, 1, 0, "明日もきっと\nいい日デスよ、\nショーグン！", 103001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1117, 1, 0, 1, 0, "ちょんまげに\nしてみるのも\n面白いかもデス！", 103001, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1118, 1, 0, 1, 0, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 103001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1119, 1, 0, 1, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1120, 1, 0, 1, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1121, 1, 0, 1, 0, "身体を暖めて\nイクサに向かうデス！", 103001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1122, 1, 0, 1, 0, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 103001, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1123, 1, 0, 1, 0, "うーん、\n美味しいデス…\n幸せデス～", 103001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1124, 1, 0, 1, 0, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 103001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1126, 1, 0, 1, 0, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 103002, 1, 1);
