INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1132, 8, 0, 1, 0, "ショーグンの身は\n何があってもワタシが\n守るデス！", 103002, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1133, 1, 0, 1, 0, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 103002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1134, 1, 0, 1, 0, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 103002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1135, 1, 0, 1, 0, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 103002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1136, 1, 0, 1, 0, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 103002, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1137, 8, 0, 1, 0, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 103002, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1138, 8, 0, 1, 0, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 103002, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1139, 1, 1, 1, 0, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 103002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1140, 1, 2, 1, 0, "もうお昼デス\nお昼寝するデスか？", 103002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1141, 1, 3, 1, 0, "そろそろおネム\nデース…\n一緒に寝るデス？", 103002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1142, 1, 0, 1, 0, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 103002, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1143, 1, 0, 1, 0, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 103002, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1144, 1, 0, 1, 0, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 103002, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1145, 1, 0, 1, 0, "ワビサビというもの\nデスね！\n素晴らしいデス！", 103002, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1146, 1, 0, 1, 0, "身体を暖めてイクサに\n向かうデス！", 103002, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1147, 1, 0, 1, 0, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 103002, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1148, 1, 0, 1, 0, "美味しいデス…\n酢昆布を和えて\nみたいデス", 103002, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1149, 1, 0, 1, 0, "物事を知って\n来たるべきイクサに\n活かすデス", 103002, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1151, 1, 0, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1152, 1, 0, 1, 0, "父は、いつも男性に\nきびしいですね", 103101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1153, 1, 0, 1, 0, "占ってほしい人は、\nいつでも言って\nください", 103101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1154, 1, 0, 1, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1155, 1, 0, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 103101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1156, 4, 0, 1, 0, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 103101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1157, 8, 0, 1, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1158, 1, 0, 1, 0, "女難の相が\n出ていますね…\n気をつけてください", 103101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1159, 1, 0, 1, 0, "今日のあなたの運勢は\n大きょ…\n大吉です", 103101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1160, 1, 0, 1, 0, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 103101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1161, 1, 0, 1, 0, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 103101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1162, 4, 0, 1, 0, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 103101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1163, 8, 0, 1, 0, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 103101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1164, 1, 1, 1, 0, "おはようございます\n今日の運勢を\n占いましょうか？", 103101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1165, 1, 2, 1, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1166, 1, 3, 1, 0, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 103101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1167, 1, 0, 1, 0, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 103101, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1168, 1, 0, 1, 0, "一休みしましょう。\n憑かれて疲れました", 103101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1169, 1, 0, 1, 0, "かわいい…\n付喪神が宿らないかな", 103101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1170, 1, 0, 1, 0, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 103101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1171, 1, 0, 1, 0, "気持ちいいです…\n極楽浄土みたい…", 103101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1172, 1, 0, 1, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103101, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1173, 1, 0, 1, 0, "お父さんも\n食べたいの？\n死んでるのに？", 103101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1174, 1, 0, 1, 0, "この本…\nもしかして予言書？", 103101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1176, 1, 0, 1, 0, "ここにいる幽霊は\nみんな幸せそうです", 103102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1177, 1, 0, 1, 0, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 103102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1178, 1, 0, 1, 0, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 103102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1179, 1, 0, 1, 0, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 103102, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1180, 1, 0, 1, 0, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 103102, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1181, 8, 0, 1, 0, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 103102, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1182, 8, 0, 1, 0, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 103102, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1183, 1, 0, 1, 0, "この部屋、風水的にも\n運気が上がりそうです", 103102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1184, 1, 0, 1, 0, "シノブには指一本\n触れさせん！\nガブッ！！", 103102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1185, 1, 0, 1, 0, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 103102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1186, 1, 0, 1, 0, "ラップ音？\nいえ、父のいびきです", 103102, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1187, 8, 0, 1, 0, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 103102, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1188, 8, 0, 1, 0, "前世って信じますか？\n私とあなたは\nもしかすると…", 103102, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1189, 1, 1, 1, 0, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 103102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1190, 1, 2, 1, 0, "明るい時間は\nあまり得意では\nないんです…", 103102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1191, 1, 3, 1, 0, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 103102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1192, 1, 0, 1, 0, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 103102, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1193, 1, 0, 1, 0, "うとうと…\nあれ…\n走馬灯が見える…", 103102, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1194, 1, 0, 1, 0, "かわいい…\nお父さんみたい…", 103102, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1195, 1, 0, 1, 0, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 103102, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1196, 1, 0, 1, 0, "快適です…\n幽霊もどんどん\n集まってきますね…", 103102, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1197, 1, 0, 1, 0, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 103102, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1198, 1, 0, 1, 0, "お父さんの分も、\n私が食べるね", 103102, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1199, 1, 0, 1, 0, "この本の占い\n私の占いと違う…", 103102, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1201, 1, 0, 1, 0, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 103201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1202, 1, 0, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1203, 1, 0, 1, 0, "一日も早く、\n一人前の商人に\nなりたいものです", 103201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1204, 1, 0, 1, 0, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 103201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1205, 1, 0, 1, 0, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 103201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1206, 4, 0, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1207, 8, 0, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1208, 1, 0, 1, 0, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 103201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1209, 1, 0, 1, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 103201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1210, 1, 0, 1, 0, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 103201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1211, 1, 0, 1, 0, "お金で解決するのは\n悪いことでは\nありませんわよね？", 103201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1212, 4, 0, 1, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 103201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1213, 8, 0, 1, 0, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 103201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1214, 1, 1, 1, 0, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 103201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1215, 1, 2, 1, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1216, 1, 3, 1, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1217, 1, 0, 1, 0, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 103201, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1218, 1, 0, 1, 0, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 103201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1219, 1, 0, 1, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1220, 1, 0, 1, 0, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 103201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1221, 1, 0, 1, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1222, 1, 0, 1, 0, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 103201, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1223, 1, 0, 1, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 103201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1224, 1, 0, 1, 0, "経済の仕組みを\nしっかりと\n学ばなくては…", 103201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1226, 1, 0, 1, 0, "そろそろ新しい\n案件を始めないと\nいけませんわね", 103202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1227, 1, 0, 1, 0, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 103202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1228, 1, 0, 1, 0, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 103202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1229, 1, 0, 1, 0, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 103202, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1230, 1, 0, 1, 0, "この狭さも\n慣れれば\n快適ですわ♪", 103202, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1231, 8, 0, 1, 0, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 103202, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1232, 8, 0, 1, 0, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 103202, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1233, 1, 0, 1, 0, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 103202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1234, 1, 0, 1, 0, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 103202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1235, 1, 0, 1, 0, "資産運用？\nお幾らほど\n増やしたいのです？", 103202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1236, 1, 0, 1, 0, "今度一緒に…\nパーティにでも\n行きませんこと？", 103202, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1237, 8, 0, 1, 0, "その、よろしければ\n私とお散歩など\nいかがです？", 103202, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1238, 8, 0, 1, 0, "あなたさまの優しさは\nお金には\n代えられませんわね", 103202, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1239, 1, 1, 1, 0, "なぜだか\n眠り足りないような\n感じがしますわ…", 103202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1240, 1, 2, 1, 0, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 103202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1241, 1, 3, 1, 0, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 103202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1242, 1, 0, 1, 0, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 103202, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1243, 1, 0, 1, 0, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 103202, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1244, 1, 0, 1, 0, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 103202, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1245, 1, 0, 1, 0, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 103202, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1246, 1, 0, 1, 0, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 103202, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1247, 1, 0, 1, 0, "目標は遠くに\n置いた方が励みに\nなりますわ！", 103202, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1248, 1, 0, 1, 0, "なかなか\n美味ですわ\nシェフはどちらに？", 103202, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1249, 1, 0, 1, 0, "商人って、物語では\n悪役にされやすいの\nですね…", 103202, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1251, 1, 0, 1, 0, "家の中のものを使って\n物ボケ、考えるべ！", 103301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1252, 1, 0, 1, 0, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 103301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1253, 1, 0, 1, 0, "うちに塀ができたら、\nへー、って言いたい！", 103301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1254, 1, 0, 1, 0, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 103301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1255, 1, 0, 1, 0, "エリザベスも\n連れてきてぇ～", 103301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1256, 4, 0, 1, 0, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 103301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1257, 8, 0, 1, 0, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 103301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1258, 1, 0, 1, 0, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 103301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1259, 1, 0, 1, 0, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 103301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1260, 1, 0, 1, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1261, 1, 0, 1, 0, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 103301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1262, 4, 0, 1, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1263, 8, 0, 1, 0, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 103301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1264, 1, 1, 1, 0, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 103301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1265, 1, 2, 1, 0, "お昼だ～腹が減っては\n笑いはできぬ～！", 103301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1266, 1, 3, 1, 0, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 103301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1267, 1, 0, 1, 0, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 103301, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1268, 1, 0, 1, 0, "ナイスなイスに\n座りたい、\nなんちゃって！", 103301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1269, 1, 0, 1, 0, "めんこいのは、\nスベっても許される\nからずるいべな～？", 103301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1270, 1, 0, 1, 0, "必要以上に\n癒されちまうべや～", 103301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1271, 1, 0, 1, 0, "ぽかぽかだ…\nウケた会場みてぇだ", 103301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1272, 1, 0, 1, 0, "お笑い芸人は、\n体が資本だべさ～！", 103301, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1273, 1, 0, 1, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1274, 1, 0, 1, 0, "ネタ作りのヒントが\n載ってねぇか探すべ", 103301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1276, 1, 0, 1, 0, "さぁ今日も張り切って\n漫才の練習するべさ！", 103302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1277, 1, 0, 1, 0, "この部屋でも、商品\n売っていいんだべか？", 103302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1278, 1, 0, 1, 0, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 103302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1279, 1, 0, 1, 0, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 103302, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1280, 1, 0, 1, 0, "エリザベスも\n連れてきてぇ～", 103302, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1281, 8, 0, 1, 0, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 103302, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1282, 8, 0, 1, 0, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 103302, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1283, 1, 0, 1, 0, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 103302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1284, 1, 0, 1, 0, "あんちゃん、百連\nツッコミ特訓だ～！", 103302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1285, 1, 0, 1, 0, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 103302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1286, 1, 0, 1, 0, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 103302, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1287, 8, 0, 1, 0, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 103302, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1288, 8, 0, 1, 0, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 103302, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1289, 1, 1, 1, 0, "おはよう！\n今日もいい\nネタ作り日和だ！", 103302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1290, 1, 2, 1, 0, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 103302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1291, 1, 3, 1, 0, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 103302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1292, 1, 0, 1, 0, "うーん…衣装、\nずれてねぇべか？", 103302, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1293, 1, 0, 1, 0, "ナイスなイスに\n座りたい\nなんちゃって！", 103302, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1294, 1, 0, 1, 0, "はう～\nなまらめんこいべや～", 103302, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1295, 1, 0, 1, 0, "必要以上に\n癒されちまうべや～", 103302, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1296, 1, 0, 1, 0, "笑いをとることを\n忘れるほど\nあったけぇべ～", 103302, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1297, 1, 0, 1, 0, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 103302, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1298, 1, 0, 1, 0, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 103302, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1299, 1, 0, 1, 0, "幅広く教養を\n身につけて\nいいネタ作るべ！", 103302, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1301, 1, 0, 1, 0, "通りにいる\nカップル…爆発\nすればいいのに…", 103401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1302, 1, 0, 1, 0, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 103401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1303, 1, 0, 1, 0, "今日は一日、\n外に出ない！\nもう決めたわ！", 103401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1304, 1, 0, 1, 0, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 103401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1305, 1, 0, 1, 0, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 103401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1306, 4, 0, 1, 0, "デートとか…\n誘っても嫌がられ\nないかな…？", 103401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1307, 8, 0, 1, 0, "もしかして運命の\n相手だったり…\nするのかな", 103401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1308, 1, 0, 1, 0, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 103401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1309, 1, 0, 1, 0, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 103401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1310, 1, 0, 1, 0, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 103401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1311, 1, 0, 1, 0, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 103401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1312, 4, 0, 1, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1313, 8, 0, 1, 0, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 103401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1314, 1, 1, 1, 0, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 103401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1315, 1, 2, 1, 0, "おっひるー♪\nなに食べよっかな～♪", 103401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1316, 1, 3, 1, 0, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 103401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1317, 1, 0, 1, 0, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 103401, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1318, 1, 0, 1, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1319, 1, 0, 1, 0, "こういうの男の子に\nプレゼントして\nもらいたいな…", 103401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1320, 1, 0, 1, 0, "うわあ、\nこれ、私の好きな\n香りだー！", 103401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1321, 1, 0, 1, 0, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 103401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1322, 1, 0, 1, 0, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 103401, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1323, 1, 0, 1, 0, "もぐもぐ…\nおーいしー！\n…太らないよね？", 103401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1324, 1, 0, 1, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1326, 1, 0, 1, 0, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 103402, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1327, 1, 0, 1, 0, "今日は\nのんびり\n過ごそうかな～", 103402, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1328, 1, 0, 1, 0, "あ…\nお皿、洗って\nなかった…", 103402, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1329, 1, 0, 1, 0, "もう少し、大人の\n魅力が出せるように\nなりたいな", 103402, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1330, 1, 0, 1, 0, "素敵な彼氏…\n欲しいなぁ…", 103402, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1331, 8, 0, 1, 0, "彼と私\n釣り合うかな…\n大丈夫よね？", 103402, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1332, 8, 0, 1, 0, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 103402, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1333, 1, 0, 1, 0, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 103402, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1334, 1, 0, 1, 0, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 103402, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1335, 1, 0, 1, 0, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 103402, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1336, 1, 0, 1, 0, "お姉さんが～\n可愛がって\nあげようか～？", 103402, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1337, 8, 0, 1, 0, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 103402, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1338, 8, 0, 1, 0, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 103402, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1339, 1, 1, 1, 0, "さーて、今日も\nいっちょやるぞー！", 103402, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1340, 1, 2, 1, 0, "後でのんびり\nストレッチでも\nしようっと♪", 103402, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1341, 1, 3, 1, 0, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 103402, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1342, 1, 0, 1, 0, "仕事の前に、髪とか\n整えないと…", 103402, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1343, 1, 0, 1, 0, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 103402, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1344, 1, 0, 1, 0, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 103402, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1345, 1, 0, 1, 0, "うわあ\nこれ、私の好きな\n香りだー！", 103402, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1346, 1, 0, 1, 0, "あったかーい…\nだ、だらけ\nちゃいそう…", 103402, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1347, 1, 0, 1, 0, "地道な努力が\nリア充への\n第一歩！", 103402, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1348, 1, 0, 1, 0, "おーいしっ♪\nこのために\n生きてるわねえ", 103402, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1349, 1, 0, 1, 0, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 103402, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1351, 1, 0, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1352, 1, 0, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 103801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1353, 1, 0, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1354, 1, 0, 1, 0, "ちょっと、休憩…\n本でも読もうかな", 103801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1355, 1, 0, 1, 0, "もっとたくさんの本に\n囲まれて生活したいな", 103801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1356, 4, 0, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 103801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1357, 8, 0, 1, 0, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 103801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1358, 1, 0, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1359, 1, 0, 1, 0, "あ…お姉ちゃんと\n約束があったんだ…", 103801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1360, 1, 0, 1, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1361, 1, 0, 1, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1362, 4, 0, 1, 0, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 103801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1363, 8, 0, 1, 0, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1364, 1, 1, 1, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1365, 1, 2, 1, 0, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 103801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1366, 1, 3, 1, 0, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 103801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1367, 1, 0, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？", 103801, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1368, 1, 0, 1, 0, "少し休もう\n…やっぱり\nたくさん休もう", 103801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1369, 1, 0, 1, 0, "かわいいものが\n枕元にあると\n安心できるんですよ", 103801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1370, 1, 0, 1, 0, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 103801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1371, 1, 0, 1, 0, "こうして温まって\nいると、病気も\n治る気がします", 103801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1372, 1, 0, 1, 0, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 103801, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1373, 1, 0, 1, 0, "少食ですけど\nこれならたくさん\n食べられそうです", 103801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1374, 1, 0, 1, 0, "続きが気になって\n読むのを止める\nタイミングが…", 103801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1376, 1, 0, 1, 0, "しっかり休まないと\n早く回復するように", 103802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1377, 1, 0, 1, 0, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 103802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1378, 1, 0, 1, 0, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 103802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1379, 1, 0, 1, 0, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 103802, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1380, 1, 0, 1, 0, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 103802, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1381, 8, 0, 1, 0, "むかしむかしある所に\nシオリと\nあの人が…", 103802, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1382, 8, 0, 1, 0, "あの人の\nためにも早く\n病気を治さないと…", 103802, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1383, 1, 0, 1, 0, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 103802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1384, 1, 0, 1, 0, "新記録です！\n一週間連続、\n平熱を保ちました！", 103802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1385, 1, 0, 1, 0, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 103802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1386, 1, 0, 1, 0, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 103802, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1387, 8, 0, 1, 0, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 103802, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1388, 8, 0, 1, 0, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 103802, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1389, 1, 1, 1, 0, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 103802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1390, 1, 2, 1, 0, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 103802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1391, 1, 3, 1, 0, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 103802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1392, 1, 0, 1, 0, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 103802, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1393, 1, 0, 1, 0, "少し休もう\n…やっぱり\nたくさん休もう", 103802, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1394, 1, 0, 1, 0, "この子がいれば\n病室に一人でも\n寂しくないですね", 103802, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1395, 1, 0, 1, 0, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 103802, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1396, 1, 0, 1, 0, "こうして温まって\nいると、病気も\n治る気がします", 103802, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1397, 1, 0, 1, 0, "私も、少しは\n体力をつけないと\nいけませんよね　", 103802, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1398, 1, 0, 1, 0, "いっぱい食べて\n健康な体にならないと", 103802, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1399, 1, 0, 1, 0, "続きが気になって\n読むのを止める\nタイミングが…", 103802, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1401, 1, 0, 1, 0, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 104001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1402, 1, 0, 1, 0, "あ、忘れずに\nお花に水を\nあげなきゃ…", 104001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1403, 1, 0, 1, 0, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 104001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1404, 1, 0, 1, 0, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 104001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1405, 1, 0, 1, 0, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 104001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1406, 4, 0, 1, 0, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 104001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1407, 8, 0, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1408, 1, 0, 1, 0, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 104001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1409, 1, 0, 1, 0, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 104001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1410, 1, 0, 1, 0, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 104001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1411, 1, 0, 1, 0, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 104001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1412, 4, 0, 1, 0, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 104001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1413, 8, 0, 1, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1414, 1, 1, 1, 0, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 104001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1415, 1, 2, 1, 0, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 104001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1416, 1, 3, 1, 0, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 104001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1417, 1, 0, 1, 0, "私がもっと\n可愛ければ、\n友達できるかなあ？", 104001, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1418, 1, 0, 1, 0, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 104001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1419, 1, 0, 1, 0, "かわいいなあ。\nこの子とも\n友達になりたい…", 104001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1420, 1, 0, 1, 0, "いい香りです…\n精神統一して\n友達のイメトレ…", 104001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1421, 1, 0, 1, 0, "灯りを見てると、\n心がなごみます～", 104001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1422, 1, 0, 1, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104001, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1423, 1, 0, 1, 0, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 104001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1424, 1, 0, 1, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1426, 1, 0, 1, 0, "友達作りは、焦らず\n慌てず、じっくりと…", 104002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1427, 1, 0, 1, 0, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 104002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1428, 1, 0, 1, 0, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 104002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1429, 1, 0, 1, 0, "今度、みなさんと\nピクニックとか…\nいってみたいな", 104002, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1430, 1, 0, 1, 0, "ぼっちでもいい\nたくましく育てば\n…なんて", 104002, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1431, 8, 0, 1, 0, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 104002, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1432, 8, 0, 1, 0, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 104002, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1433, 1, 0, 1, 0, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 104002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1434, 1, 0, 1, 0, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 104002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1435, 1, 0, 1, 0, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 104002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1436, 1, 0, 1, 0, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 104002, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1437, 8, 0, 1, 0, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 104002, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1438, 8, 0, 1, 0, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 104002, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1439, 1, 1, 1, 0, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 104002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1440, 1, 2, 1, 0, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 104002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1441, 1, 3, 1, 0, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 104002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1442, 1, 0, 1, 0, "私…見た目\nそこまで変じゃ\nないですよね…？", 104002, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1443, 1, 0, 1, 0, "ほわあ～…\nふかふかって\nいいものですね～", 104002, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1444, 1, 0, 1, 0, "この子みたいな\nあいくるしさを\n目指したいです！", 104002, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1445, 1, 0, 1, 0, "灯りを見てると\n心がなごみます～", 104002, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1446, 1, 0, 1, 0, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 104002, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1447, 1, 0, 1, 0, "身体を鍛えて\nみなさんを\n守ります！", 104002, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1448, 1, 0, 1, 0, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 104002, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1449, 1, 0, 1, 0, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 104002, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1451, 1, 0, 1, 0, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 104201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1452, 1, 0, 1, 0, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 104201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1453, 1, 0, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1454, 1, 0, 1, 0, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 104201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1455, 1, 0, 1, 0, "あとで曲を復習して\nおかないと…", 104201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1456, 4, 0, 1, 0, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 104201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1457, 8, 0, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1458, 1, 0, 1, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1459, 1, 0, 1, 0, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 104201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1460, 1, 0, 1, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1461, 1, 0, 1, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1462, 4, 0, 1, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1463, 8, 0, 1, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1464, 1, 1, 1, 0, "おはようございます♪\n少し歌の練習を\nしてきますね", 104201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1465, 1, 2, 1, 0, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 104201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1466, 1, 3, 1, 0, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 104201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1467, 1, 0, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104201, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1468, 1, 0, 1, 0, "歌の調子をよくする\nためには休息も\n必要ですよね…", 104201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1469, 1, 0, 1, 0, "この子たちを\n相手に練習して\nみようかな…♪", 104201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1470, 1, 0, 1, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1471, 1, 0, 1, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1472, 1, 0, 1, 0, "歌うためには\n体力づくりは\n欠かせません", 104201, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1473, 1, 0, 1, 0, "歌った後は\nお腹が空いて\nしまうんです", 104201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1474, 1, 0, 1, 0, "音楽について\n理論も勉強\nしないと…", 104201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1476, 1, 0, 1, 0, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 104202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1477, 1, 0, 1, 0, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 104202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1478, 1, 0, 1, 0, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 104202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1479, 1, 0, 1, 0, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 104202, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1480, 1, 0, 1, 0, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 104202, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1481, 8, 0, 1, 0, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 104202, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1482, 8, 0, 1, 0, "私の中にある\n私だけの歌…\n作ってみようかな？", 104202, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1483, 1, 0, 1, 0, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 104202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1484, 1, 0, 1, 0, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 104202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1485, 1, 0, 1, 0, "魔物退治ですか！？\n…違います？\n良かった…", 104202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1486, 1, 0, 1, 0, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 104202, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1487, 8, 0, 1, 0, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 104202, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1488, 8, 0, 1, 0, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 104202, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1489, 1, 1, 1, 0, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 104202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1490, 1, 2, 1, 0, "少し休んだら\n午後の練習に\n行ってきますね", 104202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1491, 1, 3, 1, 0, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 104202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1492, 1, 0, 1, 0, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 104202, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1493, 1, 0, 1, 0, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 104202, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1494, 1, 0, 1, 0, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 104202, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1495, 1, 0, 1, 0, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 104202, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1496, 1, 0, 1, 0, "ここにいると\n穏やかな旋律が\n浮かんできます…", 104202, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1497, 1, 0, 1, 0, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 104202, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1498, 1, 0, 1, 0, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 104202, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1499, 1, 0, 1, 0, "読書は歌に\n新しい風を\n吹き込んでくれます", 104202, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1501, 1, 0, 1, 0, "店の新メニューでも\n考えるかな", 104301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1502, 1, 0, 1, 0, "あたしの店から、\n出前を届けようか？", 104301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1503, 1, 0, 1, 0, "パトロール、\nパトロール…\nここは異常なしかな？", 104301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1504, 1, 0, 1, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1505, 1, 0, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 104301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1506, 4, 0, 1, 0, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 104301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1507, 8, 0, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1508, 1, 0, 1, 0, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 104301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1509, 1, 0, 1, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 104301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1510, 1, 0, 1, 0, "腹減ったか？　なんか\n作ってやろうか？", 104301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1511, 1, 0, 1, 0, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 104301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1512, 4, 0, 1, 0, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 104301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1513, 8, 0, 1, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1514, 1, 1, 1, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 104301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1515, 1, 2, 1, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1516, 1, 3, 1, 0, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 104301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1517, 1, 0, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 104301, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1518, 1, 0, 1, 0, "ふわぁ、\n一休みすっかなぁ～", 104301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1519, 1, 0, 1, 0, "うっ！　か、かわいい\nじゃねぇか", 104301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1520, 1, 0, 1, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1521, 1, 0, 1, 0, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 104301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1522, 1, 0, 1, 0, "オラオラオラーーー！\n燃えるぜーーー！", 104301, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1523, 1, 0, 1, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1524, 1, 0, 1, 0, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 104301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1526, 1, 0, 1, 0, "パトロール終了～\n本日も異常なしっと！", 104302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1527, 1, 0, 1, 0, "あたしの店から\n出前を届けようか？", 104302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1528, 1, 0, 1, 0, "あいつ、早く\n帰ってこないかな", 104302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1529, 1, 0, 1, 0, "あいつ\n腹すかせてないと\nいいんだけど…", 104302, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1530, 1, 0, 1, 0, "のんびりし過ぎるのも\n…性に合わねぇな", 104302, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1531, 8, 0, 1, 0, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 104302, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1532, 8, 0, 1, 0, "今日の献立は…\nあいつの好きなものに\nしてやるか", 104302, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1533, 1, 0, 1, 0, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 104302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1534, 1, 0, 1, 0, "天気がいい日は\n外で運動したく\nならねぇか？", 104302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1535, 1, 0, 1, 0, "腹減ったか？　なんか\n作ってやろうか？", 104302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1536, 1, 0, 1, 0, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 104302, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1537, 8, 0, 1, 0, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 104302, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1538, 8, 0, 1, 0, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 104302, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1539, 1, 1, 1, 0, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 104302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1540, 1, 2, 1, 0, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 104302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1541, 1, 3, 1, 0, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 104302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1542, 1, 0, 1, 0, "た、たまにはその…\nかわいい格好とか\nしても…", 104302, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1543, 1, 0, 1, 0, "ふぅ～♪\n疲れがとれるな～", 104302, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1544, 1, 0, 1, 0, "うっ！　か、かわいい\nじゃねぇか", 104302, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1545, 1, 0, 1, 0, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 104302, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1546, 1, 0, 1, 0, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 104302, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1547, 1, 0, 1, 0, "オラオラオラーーー！\n燃えるぜーーー！", 104302, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1548, 1, 0, 1, 0, "いい味してるぜ！\nうちの店でも\n出してみたいな", 104302, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1549, 1, 0, 1, 0, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 104302, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1551, 1, 0, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 104501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1552, 1, 0, 1, 0, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 104501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1553, 1, 0, 1, 0, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 104501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1554, 1, 0, 1, 0, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 104501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1555, 1, 0, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1556, 4, 0, 1, 0, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 104501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1557, 8, 0, 1, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1558, 1, 0, 1, 0, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 104501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1559, 1, 0, 1, 0, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 104501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1560, 1, 0, 1, 0, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 104501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1561, 1, 0, 1, 0, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 104501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1562, 4, 0, 1, 0, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 104501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1563, 8, 0, 1, 0, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 104501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1564, 1, 1, 1, 0, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 104501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1565, 1, 2, 1, 0, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 104501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1566, 1, 3, 1, 0, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 104501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1567, 1, 0, 1, 0, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 104501, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1568, 1, 0, 1, 0, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 104501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1569, 1, 0, 1, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1570, 1, 0, 1, 0, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 104501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1571, 1, 0, 1, 0, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 104501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1572, 1, 0, 1, 0, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 104501, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1573, 1, 0, 1, 0, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 104501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1574, 1, 0, 1, 0, "この本の下に、\nそういう本を\nカムフラージュ？", 104501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1576, 1, 0, 1, 0, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 104502, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1577, 1, 0, 1, 0, "最近なにも\nしてくれません…\n焦らしてるんですね", 104502, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1578, 1, 0, 1, 0, "も、妄想が\n足りません…\n禁断症状がっ…！", 104502, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1579, 1, 0, 1, 0, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 104502, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1580, 1, 0, 1, 0, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 104502, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1581, 8, 0, 1, 0, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 104502, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1582, 8, 0, 1, 0, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 104502, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1583, 1, 0, 1, 0, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 104502, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1584, 1, 0, 1, 0, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 104502, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1585, 1, 0, 1, 0, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 104502, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1586, 1, 0, 1, 0, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 104502, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1587, 8, 0, 1, 0, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 104502, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1588, 8, 0, 1, 0, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 104502, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1589, 1, 1, 1, 0, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 104502, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1590, 1, 2, 1, 0, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 104502, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1591, 1, 3, 1, 0, "今夜は一晩中…\nなんですね？\nぐふふ…", 104502, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1592, 1, 0, 1, 0, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 104502, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1593, 1, 0, 1, 0, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 104502, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1594, 1, 0, 1, 0, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 104502, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1595, 1, 0, 1, 0, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 104502, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1596, 1, 0, 1, 0, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 104502, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1597, 1, 0, 1, 0, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 104502, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1598, 1, 0, 1, 0, "こんなにいっぱい\n入りません…\nお腹にっ！", 104502, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1599, 1, 0, 1, 0, "どんな本も\nよく見ると\nいやらしいです…！", 104502, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1601, 1, 0, 1, 0, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 104601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1602, 1, 0, 1, 0, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 104601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1603, 1, 0, 1, 0, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 104601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1604, 1, 0, 1, 0, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 104601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1605, 1, 0, 1, 0, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 104601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1606, 4, 0, 1, 0, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 104601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1607, 8, 0, 1, 0, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 104601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1608, 1, 0, 1, 0, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 104601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1609, 1, 0, 1, 0, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 104601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1610, 1, 0, 1, 0, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 104601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1611, 1, 0, 1, 0, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 104601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1612, 4, 0, 1, 0, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 104601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1613, 8, 0, 1, 0, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 104601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1614, 1, 1, 1, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1615, 1, 2, 1, 0, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 104601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1616, 1, 3, 1, 0, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 104601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1617, 1, 0, 1, 0, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 104601, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1618, 1, 0, 1, 0, "うにゃ～！\nお昼寝に\nぴったりにゃ", 104601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1619, 1, 0, 1, 0, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 104601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1620, 1, 0, 1, 0, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 104601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1621, 1, 0, 1, 0, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 104601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1622, 1, 0, 1, 0, "本当は外で\n運動したいん\nだけどにゃ", 104601, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1623, 1, 0, 1, 0, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 104601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1624, 1, 0, 1, 0, "ふむふむ…猫の\nしつけ方は、と…", 104601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1626, 1, 0, 1, 0, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 104602, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1627, 1, 0, 1, 0, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 104602, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1628, 1, 0, 1, 0, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 104602, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1629, 1, 0, 1, 0, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 104602, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1630, 1, 0, 1, 0, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 104602, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1631, 8, 0, 1, 0, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 104602, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1632, 8, 0, 1, 0, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 104602, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1633, 1, 0, 1, 0, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 104602, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1634, 1, 0, 1, 0, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 104602, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1635, 1, 0, 1, 0, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 104602, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1636, 1, 0, 1, 0, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 104602, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1637, 8, 0, 1, 0, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 104602, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1638, 8, 0, 1, 0, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 104602, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1639, 1, 1, 1, 0, "いい朝にゃ！\n顔を洗ってくるにゃ！", 104602, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1640, 1, 2, 1, 0, "そろそろおっひる～\nにゃーんか\n食べたい…", 104602, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1641, 1, 3, 1, 0, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 104602, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1642, 1, 0, 1, 0, "にゃにゃ…\n髪が乱れてるにゃ", 104602, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1643, 1, 0, 1, 0, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 104602, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1644, 1, 0, 1, 0, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 104602, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1645, 1, 0, 1, 0, "猫でもこわくない\n優しい灯りにゃ！", 104602, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1646, 1, 0, 1, 0, "ぽっかぽか\nしあわせな気分にゃ～", 104602, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1647, 1, 0, 1, 0, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 104602, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1648, 1, 0, 1, 0, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 104602, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1649, 1, 0, 1, 0, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 104602, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1651, 1, 0, 1, 0, "内職手伝える人\nいないかしら？", 104801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1652, 1, 0, 1, 0, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 104801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1653, 1, 0, 1, 0, "次の特売セールに\n向けて、しっかり\n準備しないと！", 104801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1654, 1, 0, 1, 0, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 104801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1655, 1, 0, 1, 0, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 104801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1656, 4, 0, 1, 0, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 104801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1657, 8, 0, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1658, 1, 0, 1, 0, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 104801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1659, 1, 0, 1, 0, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 104801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1660, 1, 0, 1, 0, "背後の気配なら\nすぐ分かるわ\n試してみる？", 104801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1661, 1, 0, 1, 0, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 104801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1662, 4, 0, 1, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1663, 8, 0, 1, 0, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 104801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1664, 1, 1, 1, 0, "おはよう！\n今日も朝から\nバイトよ！", 104801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1665, 1, 2, 1, 0, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 104801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1666, 1, 3, 1, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1667, 1, 0, 1, 0, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 104801, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1668, 1, 0, 1, 0, "はぁ～バイトで\n疲れたから癒される～", 104801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1669, 1, 0, 1, 0, "これを内職で\n作ったら\n儲かるかしら？", 104801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1670, 1, 0, 1, 0, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 104801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1671, 1, 0, 1, 0, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 104801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1672, 1, 0, 1, 0, "働き続けるには\n健康第一！　それには\n運動が一番よ", 104801, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1673, 1, 0, 1, 0, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 104801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1674, 1, 0, 1, 0, "日々絶えず学んで、\n生活に活かすのよ！", 104801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1676, 1, 0, 1, 0, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 104802, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1677, 1, 0, 1, 0, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 104802, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1678, 1, 0, 1, 0, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 104802, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1679, 1, 0, 1, 0, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 104802, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1680, 1, 0, 1, 0, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 104802, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1681, 8, 0, 1, 0, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 104802, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1682, 8, 0, 1, 0, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 104802, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1683, 1, 0, 1, 0, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 104802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1684, 1, 0, 1, 0, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 104802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1685, 1, 0, 1, 0, "今から\n休憩するところなの\n一緒にどう？", 104802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1686, 1, 0, 1, 0, "ん？　どうしたの？\n私のことが\n気になるの？", 104802, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1687, 8, 0, 1, 0, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 104802, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1688, 8, 0, 1, 0, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 104802, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1689, 1, 1, 1, 0, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 104802, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1690, 1, 2, 1, 0, "お昼時ね\n少し休憩したら\nまた働くわよ！", 104802, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1691, 1, 3, 1, 0, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 104802, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1692, 1, 0, 1, 0, "女の子らしく…\nどうも忘れちゃう\nのよね", 104802, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1693, 1, 0, 1, 0, "ここで少し\n休んだら\nまたバイトよ！", 104802, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1694, 1, 0, 1, 0, "これを内職で\n作ったら\n儲かるかしら？", 104802, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1695, 1, 0, 1, 0, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 104802, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1696, 1, 0, 1, 0, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 104802, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1697, 1, 0, 1, 0, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 104802, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1698, 1, 0, 1, 0, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 104802, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1699, 1, 0, 1, 0, "日々絶えず学んで\n生活に活かすのよ！", 104802, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1701, 1, 0, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 104901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1702, 1, 0, 1, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 104901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1703, 1, 0, 1, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 104901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1704, 1, 0, 1, 0, "くんくん、\n弟くんのにおい…\n近くにいるね？", 104901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1705, 1, 0, 1, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 104901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1706, 4, 0, 1, 0, "この部屋を、\n私と弟くんの\n愛の巣に…", 104901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1707, 8, 0, 1, 0, "はぁ～…弟くん\n禁断症状だよ～", 104901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1708, 1, 0, 1, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 104901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1709, 1, 0, 1, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 104901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1710, 1, 0, 1, 0, "姉と弟の外出は\nデートって\nいうのかな？", 104901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1711, 1, 0, 1, 0, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 104901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1712, 4, 0, 1, 0, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 104901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1713, 8, 0, 1, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 104901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1714, 1, 1, 1, 0, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 104901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1715, 1, 2, 1, 0, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 104901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1716, 1, 3, 1, 0, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 104901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1717, 1, 0, 1, 0, "きれいなお姉ちゃん\n好きだよね♪", 104901, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1718, 1, 0, 1, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 104901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1719, 1, 0, 1, 0, "かわいい♪\nお姉ちゃんチョップ！", 104901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1720, 1, 0, 1, 0, "この香り、\n弟くん好きかな？", 104901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1721, 1, 0, 1, 0, "これ、弟くんみたいに\nあったかいね～", 104901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1722, 1, 0, 1, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 104901, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1723, 1, 0, 1, 0, "あ、これ\n弟くんが好きな\n味付けだね！", 104901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1724, 1, 0, 1, 0, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 104901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1726, 1, 0, 1, 0, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 104902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1727, 1, 0, 1, 0, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 104902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1728, 1, 0, 1, 0, "弟くんのお友達は\nみんな私の妹だよ♪", 104902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1729, 1, 0, 1, 0, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 104902, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1730, 1, 0, 1, 0, "はっ！\n弟くんが困ってる\n気がする！", 104902, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1731, 8, 0, 1, 0, "はぁ～…弟くん、\n禁断症状だよ～", 104902, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1732, 8, 0, 1, 0, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 104902, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1733, 1, 0, 1, 0, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 104902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1734, 1, 0, 1, 0, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 104902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1735, 1, 0, 1, 0, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 104902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1736, 1, 0, 1, 0, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 104902, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1737, 8, 0, 1, 0, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 104902, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1738, 8, 0, 1, 0, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 104902, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1739, 1, 1, 1, 0, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 104902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1740, 1, 2, 1, 0, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 104902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1741, 1, 3, 1, 0, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 104902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1742, 1, 0, 1, 0, "弟くんを\n誘惑しちゃうぞ～♪", 104902, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1743, 1, 0, 1, 0, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 104902, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1744, 1, 0, 1, 0, "かわいい♪\nお姉ちゃんチョップ！", 104902, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1745, 1, 0, 1, 0, "お肌に潤いが\nでてきたみたい～♪", 104902, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1746, 1, 0, 1, 0, "これ、弟くんみたいに\nあったかいね～", 104902, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1747, 1, 0, 1, 0, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 104902, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1748, 1, 0, 1, 0, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 104902, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1749, 1, 0, 1, 0, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 104902, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1751, 1, 0, 1, 0, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 105001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1752, 1, 0, 1, 0, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 105001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1753, 1, 0, 1, 0, "アタシの大人コーデで\nあいつも夢中ね♪", 105001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1754, 1, 0, 1, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 105001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1755, 1, 0, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1756, 4, 0, 1, 0, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 105001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1757, 8, 0, 1, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 105001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1758, 1, 0, 1, 0, "わーい、アメだー♪\n食べる食べるー♪", 105001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1759, 1, 0, 1, 0, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 105001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1760, 1, 0, 1, 0, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 105001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1761, 1, 0, 1, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 105001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1762, 4, 0, 1, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1763, 8, 0, 1, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1764, 1, 1, 1, 0, "朝のスキンケアは\n大事なの！", 105001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1765, 1, 2, 1, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1766, 1, 3, 1, 0, "エイジングケアを\nしないとねー", 105001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1767, 1, 0, 1, 0, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 105001, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1768, 1, 0, 1, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1769, 1, 0, 1, 0, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 105001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1770, 1, 0, 1, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 105001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1771, 1, 0, 1, 0, "あったかーい♪\nここで宿題\nしようかしら？", 105001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1772, 1, 0, 1, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 105001, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1773, 1, 0, 1, 0, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 105001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1774, 1, 0, 1, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1776, 1, 0, 1, 0, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 105002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1777, 1, 0, 1, 0, "恋人にするなら\nイケメンか\nただイケよね～", 105002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1778, 1, 0, 1, 0, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 105002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1779, 1, 0, 1, 0, "朝顔の観察したら\n理系女子になれる\nかしら？", 105002, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1780, 1, 0, 1, 0, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 105002, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1781, 8, 0, 1, 0, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 105002, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1782, 8, 0, 1, 0, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 105002, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1783, 1, 0, 1, 0, "なーに？\nアタシに甘えたいの？", 105002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1784, 1, 0, 1, 0, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 105002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1785, 1, 0, 1, 0, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 105002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1786, 1, 0, 1, 0, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 105002, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1787, 8, 0, 1, 0, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 105002, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1788, 8, 0, 1, 0, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 105002, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1789, 1, 1, 1, 0, "レディの朝は\n忙しいのよ", 105002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1790, 1, 2, 1, 0, "お腹すいたー\nダイエット\nやめちゃおうかなー", 105002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1791, 1, 3, 1, 0, "エイジングケアを\nしないとねー", 105002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1792, 1, 0, 1, 0, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 105002, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1793, 1, 0, 1, 0, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 105002, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1794, 1, 0, 1, 0, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 105002, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1795, 1, 0, 1, 0, "オシャレ～♪\n大人なレディって\n感じよね♪", 105002, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1796, 1, 0, 1, 0, "あったかーい♪\nここで宿題\nしようかしら？", 105002, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1797, 1, 0, 1, 0, "運動したら成長\nしないかしら？\n背とか胸とか…", 105002, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1798, 1, 0, 1, 0, "レディは、食べ物にも\n気を使うのよ", 105002, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1799, 1, 0, 1, 0, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 105002, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1801, 1, 0, 1, 0, "魔法と科学\nどっちも極めて\nみせるわ", 105101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1802, 1, 0, 1, 0, "仕事も片付いたし\n部屋で\n実験実験…♪", 105101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1803, 1, 0, 1, 0, "たまった治療代の\n請求書を\nまとめないと…", 105101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1804, 1, 0, 1, 0, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 105101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1805, 1, 0, 1, 0, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 105101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1806, 4, 0, 1, 0, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 105101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1807, 8, 0, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1808, 1, 0, 1, 0, "あら、ヒマなら\n薬草の整理を\n手伝って", 105101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1809, 1, 0, 1, 0, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 105101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1810, 1, 0, 1, 0, "具合が悪い？\nなら診てあげる\nまずは触診から…", 105101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1811, 1, 0, 1, 0, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 105101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1812, 4, 0, 1, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1813, 8, 0, 1, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1814, 1, 1, 1, 0, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 105101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1815, 1, 2, 1, 0, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 105101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1816, 1, 3, 1, 0, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 105101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1817, 1, 0, 1, 0, "医者たるもの\nいつだって\n清潔に…♪", 105101, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1818, 1, 0, 1, 0, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 105101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1819, 1, 0, 1, 0, "ぬいぐるみなら\n動物も…", 105101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1820, 1, 0, 1, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 105101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1821, 1, 0, 1, 0, "ここにいると\nついウトウト\nしちゃうわ…", 105101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1822, 1, 0, 1, 0, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 105101, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1823, 1, 0, 1, 0, "研究していると\nつい食べるのを\n忘れちゃうのよね", 105101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1824, 1, 0, 1, 0, "実験もいいけど\nいつか成果を\n本にしないと…", 105101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1826, 1, 0, 1, 0, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 105102, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1827, 1, 0, 1, 0, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 105102, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1828, 1, 0, 1, 0, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 105102, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1829, 1, 0, 1, 0, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 105102, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1830, 1, 0, 1, 0, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 105102, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1831, 8, 0, 1, 0, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 105102, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1832, 8, 0, 1, 0, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 105102, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1833, 1, 0, 1, 0, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 105102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1834, 1, 0, 1, 0, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 105102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1835, 1, 0, 1, 0, "欲しい薬が\nあったら言ってね\n調合してみるから", 105102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1836, 1, 0, 1, 0, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 105102, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1837, 8, 0, 1, 0, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 105102, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1838, 8, 0, 1, 0, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 105102, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1839, 1, 1, 1, 0, "朝しか取れない\n植物があるの\n採取しに行くわよ", 105102, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1840, 1, 2, 1, 0, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 105102, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1841, 1, 3, 1, 0, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 105102, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1842, 1, 0, 1, 0, "身なりについて\n研究するのも\n楽しそうね", 105102, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1843, 1, 0, 1, 0, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 105102, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1844, 1, 0, 1, 0, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 105102, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1845, 1, 0, 1, 0, "いい香り…\n仕事場にも\n置いてみようかしら", 105102, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1846, 1, 0, 1, 0, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 105102, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1847, 1, 0, 1, 0, "運動しないと\n体型の維持が…", 105102, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1848, 1, 0, 1, 0, "適度な食事も\n大切よね", 105102, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1849, 1, 0, 1, 0, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 105102, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1851, 1, 0, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 105201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1852, 1, 0, 1, 0, "いっけな～い！\nひづめで床に傷が！", 105201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1853, 1, 0, 1, 0, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 105201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1854, 1, 0, 1, 0, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 105201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1855, 1, 0, 1, 0, "みんな、体重\nどれくらいなのかな？", 105201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1856, 4, 0, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1857, 8, 0, 1, 0, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 105201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1858, 1, 0, 1, 0, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 105201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1859, 1, 0, 1, 0, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 105201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1860, 1, 0, 1, 0, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 105201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1861, 1, 0, 1, 0, "力仕事ならまかせて！", 105201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1862, 4, 0, 1, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1863, 8, 0, 1, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1864, 1, 1, 1, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1865, 1, 2, 1, 0, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 105201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1866, 1, 3, 1, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 105201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1867, 1, 0, 1, 0, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 105201, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1868, 1, 0, 1, 0, "ミシッっていった！\n私、重すぎるかも！？", 105201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1869, 1, 0, 1, 0, "あ！　あなたも\n美人さんね～♪", 105201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1870, 1, 0, 1, 0, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 105201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1871, 1, 0, 1, 0, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 105201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1872, 1, 0, 1, 0, "な、なんだか野生に\n帰っちゃいそう！", 105201, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1873, 1, 0, 1, 0, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 105201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1874, 1, 0, 1, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1876, 1, 0, 1, 0, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 105202, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1877, 1, 0, 1, 0, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 105202, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1878, 1, 0, 1, 0, "動物苑…って\n嫌なギルド名ね…", 105202, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1879, 1, 0, 1, 0, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 105202, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1880, 1, 0, 1, 0, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 105202, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1881, 8, 0, 1, 0, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 105202, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1882, 8, 0, 1, 0, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 105202, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1883, 1, 0, 1, 0, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 105202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1884, 1, 0, 1, 0, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 105202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1885, 1, 0, 1, 0, "ぺっぺっ！\n…い、いけないっ！", 105202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1886, 1, 0, 1, 0, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 105202, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1887, 8, 0, 1, 0, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 105202, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1888, 8, 0, 1, 0, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 105202, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1889, 1, 1, 1, 0, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 105202, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1890, 1, 2, 1, 0, "ダイエット中だから\nお昼は抜いてるの～", 105202, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1891, 1, 3, 1, 0, "お肌のケアに\nパックしながら\n寝ようかな～", 105202, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1892, 1, 0, 1, 0, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 105202, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1893, 1, 0, 1, 0, "ミシッっていった！\n私、重すぎるかも！？", 105202, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1894, 1, 0, 1, 0, "あ！　あなたも\n美人さんね～♪", 105202, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1895, 1, 0, 1, 0, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 105202, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1896, 1, 0, 1, 0, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 105202, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1897, 1, 0, 1, 0, "な、なんだか野生に\n帰っちゃいそう！", 105202, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1898, 1, 0, 1, 0, "何を食べるかより\n誰と食べるかが\n大事なのよね", 105202, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1899, 1, 0, 1, 0, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 105202, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1901, 1, 0, 1, 0, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 105301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1902, 1, 0, 1, 0, "私は子供ではない…！\nが、お菓子は\nいただこう！", 105301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1903, 1, 0, 1, 0, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 105301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1904, 1, 0, 1, 0, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 105301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1905, 1, 0, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1906, 4, 0, 1, 0, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 105301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1907, 8, 0, 1, 0, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 105301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1908, 1, 0, 1, 0, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 105301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1909, 1, 0, 1, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1910, 1, 0, 1, 0, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 105301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1911, 1, 0, 1, 0, "私ともっと高く、\n共に飛ぼう！", 105301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1912, 4, 0, 1, 0, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 105301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1913, 8, 0, 1, 0, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 105301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1914, 1, 1, 1, 0, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 105301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1915, 1, 2, 1, 0, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 105301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1916, 1, 3, 1, 0, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 105301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1917, 1, 0, 1, 0, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 105301, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1918, 1, 0, 1, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1919, 1, 0, 1, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1920, 1, 0, 1, 0, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 105301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1921, 1, 0, 1, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1922, 1, 0, 1, 0, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 105301, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1923, 1, 0, 1, 0, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 105301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1924, 1, 0, 1, 0, "これを読んで、新たな\n戦術を編み出すぞっ！", 105301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1926, 1, 0, 1, 0, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 105302, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1927, 1, 0, 1, 0, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 105302, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1928, 1, 0, 1, 0, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 105302, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1929, 1, 0, 1, 0, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 105302, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1930, 1, 0, 1, 0, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 105302, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1931, 8, 0, 1, 0, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 105302, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1932, 8, 0, 1, 0, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 105302, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1933, 1, 0, 1, 0, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 105302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1934, 1, 0, 1, 0, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 105302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1935, 1, 0, 1, 0, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 105302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1936, 1, 0, 1, 0, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 105302, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1937, 8, 0, 1, 0, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 105302, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1938, 8, 0, 1, 0, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 105302, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1939, 1, 1, 1, 0, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 105302, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1940, 1, 2, 1, 0, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 105302, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1941, 1, 3, 1, 0, "寝る前に…あと少し\nだけ、鍛錬を…", 105302, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1942, 1, 0, 1, 0, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 105302, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1943, 1, 0, 1, 0, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 105302, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1944, 1, 0, 1, 0, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 105302, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1945, 1, 0, 1, 0, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 105302, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1946, 1, 0, 1, 0, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 105302, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1947, 1, 0, 1, 0, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 105302, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1948, 1, 0, 1, 0, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 105302, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1949, 1, 0, 1, 0, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 105302, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1951, 1, 0, 1, 0, "私たちの騎空艇\nどんなデザインに\nしよう…？", 105701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1952, 1, 0, 1, 0, "団員募集の\nポスター\n作ろうっと♪", 105701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1953, 1, 0, 1, 0, "父さん…\n今頃どこで\n何してるんだろ？", 105701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1954, 1, 0, 1, 0, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 105701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1955, 1, 0, 1, 0, "父さん以上の\n世界一の\n騎空士になりたい！", 105701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1956, 4, 0, 1, 0, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 105701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1957, 8, 0, 1, 0, "父さん…\n私にもね、大切な\n仲間ができたよ", 105701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1958, 1, 0, 1, 0, "仕事の依頼？\nすぐに支度\nしてくるね！", 105701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1959, 1, 0, 1, 0, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 105701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1960, 1, 0, 1, 0, "なーにー？\nどうしたの？\n今呼んだよね？", 105701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1961, 1, 0, 1, 0, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 105701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1962, 4, 0, 1, 0, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 105701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1963, 8, 0, 1, 0, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 105701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1964, 1, 1, 1, 0, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 105701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1965, 1, 2, 1, 0, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 105701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1966, 1, 3, 1, 0, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 105701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1967, 1, 0, 1, 0, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 105701, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1968, 1, 0, 1, 0, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 105701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1969, 1, 0, 1, 0, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 105701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1970, 1, 0, 1, 0, "いい香り～♪\n気持ちが\n落ち着く～…", 105701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1971, 1, 0, 1, 0, "これって\n騎空艇に\n置けるかな…？", 105701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1972, 1, 0, 1, 0, "どんな仕事でも\nこなせるような\n体力がないとね…！", 105701, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1973, 1, 0, 1, 0, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 105701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1974, 1, 0, 1, 0, "空を冒険するには\n色んなことを\n知っておかないと…", 105701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1976, 1, 0, 1, 0, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 105702, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1977, 1, 0, 1, 0, "暇だよ…\nどこかに強い人は\nいないか？", 105702, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1978, 1, 0, 1, 0, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 105702, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1979, 1, 0, 1, 0, "今のうちに、\n魔物退治の\n仕事に行くね！", 105702, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1980, 1, 0, 1, 0, "ハァ～…\nうん、気の巡りは\n問題ないね！", 105702, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1981, 8, 0, 1, 0, "私の力の源は\n仲間との\n絆だよ！", 105702, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1982, 8, 0, 1, 0, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 105702, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1983, 1, 0, 1, 0, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 105702, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1984, 1, 0, 1, 0, "私はいつも通りに\n喋ってるつもり\nだよ？", 105702, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1985, 1, 0, 1, 0, "どうした？\nもしかして手合せ\nしたいか？", 105702, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (1986, 1, 0, 1, 0, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 105702, 4, 2);
