INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2072, 1, 0, 1, 0, "特訓特訓！\nあたしは\nやればできる！", 106001, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2073, 1, 0, 1, 0, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 106001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2074, 1, 0, 1, 0, "魔法を使うにも、\n知識が必要だものね", 106001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2076, 1, 0, 1, 0, "ま、まずいわ…\nギルド活動\nけっこう楽しい♪", 106002, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2077, 1, 0, 1, 0, "いろいろ美味しいもの\n食べられるし…\nけっこう幸せかも", 106002, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2078, 1, 0, 1, 0, "主さま…\nだ、誰も聞いてない\nわよね！？", 106002, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2079, 1, 0, 1, 0, "監視監視…\nうん！　あたしは\nなまけてないわよね", 106002, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2080, 1, 0, 1, 0, "へ、陛下に\nみつからないように\nしなくっちゃ…", 106002, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2081, 8, 0, 1, 0, "あいつが来たら、\n尾行しなくちゃ♪", 106002, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2082, 8, 0, 1, 0, "あいつと会ってから\n調子くるったな…\n楽しいけどさ", 106002, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2083, 1, 0, 1, 0, "食わず嫌いって\nあるのよね\n食べ物もだけど\n人も…", 106002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2084, 1, 0, 1, 0, "うひゃっ！？\nど、どうしてあんたは\nいつも驚かせるのよ！", 106002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2085, 1, 0, 1, 0, "話し相手になって\nやるわよ…情報\n聞き出せるかもだし", 106002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2086, 1, 0, 1, 0, "この部屋って\n野良猫を拾ってきたら\nだめかな？", 106002, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2087, 8, 0, 1, 0, "遊びにいくわよ！\nあんたはあたしの\n言うとおりにすれば\nいいの♪", 106002, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2088, 8, 0, 1, 0, "あんた、あたしに\n操られてみない？", 106002, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2089, 1, 1, 1, 0, "美食殿としては\n朝食もこだわらないと\nねぇ～", 106002, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2090, 1, 2, 1, 0, "ギルド活動だもん！\n美味しいランチを\n食べましょ♪", 106002, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2091, 1, 3, 1, 0, "ディナーに行くわよ！\nギルドの目的\n食べ歩きだもんね♪", 106002, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2092, 1, 0, 1, 0, "今日もしっぽが\nいい感じ♪", 106002, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2093, 1, 0, 1, 0, "うにゃ～\nどんどん眠く\nなる～", 106002, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2094, 1, 0, 1, 0, "これ、あたしの能力で\n操れないかな？", 106002, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2095, 1, 0, 1, 0, "あたし、みんなや\nあいつと同じ香りを\n身に着けてるんだ…", 106002, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2096, 1, 0, 1, 0, "うっ、のんびり\nしたくなる…\n特訓しなきゃ\nいけないのに～", 106002, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2097, 1, 0, 1, 0, "特訓特訓！\nあたしは\nやればできる！", 106002, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2098, 1, 0, 1, 0, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 106002, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2099, 1, 0, 1, 0, "魔法を使うにも\n知識が必要だものね", 106002, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2101, 1, 0, 1, 0, "この空間は主さまの\n香りがします\n…ふふふ♪", 105901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2102, 1, 0, 1, 0, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 105901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2103, 1, 0, 1, 0, "アメスさまへの、\nお祈りができそうな\n場所は…", 105901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2104, 1, 0, 1, 0, "さすが都会…\nおいしいものが\nたくさんあるのですね", 105901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2105, 1, 0, 1, 0, "主さまが不自由なく\n使える部屋に\nしなくては…", 105901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2106, 4, 0, 1, 0, "できるなら…\n主さまと二人だけの\n部屋の方が…", 105901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2107, 8, 0, 1, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 105901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2108, 1, 0, 1, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 105901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2109, 1, 0, 1, 0, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 105901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2110, 1, 0, 1, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 105901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2111, 1, 0, 1, 0, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 105901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2112, 4, 0, 1, 0, "主さまのおてて\n温かいですね…♪", 105901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2113, 8, 0, 1, 0, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 105901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2114, 1, 1, 1, 0, "おはようございます。\n洗濯をしなければ…", 105901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2115, 1, 2, 1, 0, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 105901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2116, 1, 3, 1, 0, "おやすみになる時は、\nお声をかけてください\nお供致します", 105901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2117, 1, 0, 1, 0, "もう少し…\nおっきくなりたい\nです", 105901, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2118, 1, 0, 1, 0, "このふかふかの優しさ\n主さまのようです", 105901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2119, 1, 0, 1, 0, "かわいい♪\n主さまにそっくり♪", 105901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2120, 1, 0, 1, 0, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 105901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2121, 1, 0, 1, 0, "ここで洗濯物を\n乾かせるでしょうか", 105901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2122, 1, 0, 1, 0, "都会の人々は\nこんなふうに\n運動を…！？", 105901, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2123, 1, 0, 1, 0, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 105901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2124, 1, 0, 1, 0, "よく学んで、\n主さまのお役に\n立たなければ", 105901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2126, 1, 0, 1, 0, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 105902, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2127, 1, 0, 1, 0, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 105902, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2128, 1, 0, 1, 0, "アメスさまへの\nお祈りができそうな\n場所は…", 105902, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2129, 1, 0, 1, 0, "瞑想をすると\n心が安らぎます…", 105902, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2130, 1, 0, 1, 0, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 105902, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2131, 8, 0, 1, 0, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 105902, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2132, 8, 0, 1, 0, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 105902, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2133, 1, 0, 1, 0, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 105902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2134, 1, 0, 1, 0, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 105902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2135, 1, 0, 1, 0, "お出かけですか？\nわたくしもお供\nいたします", 105902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2136, 1, 0, 1, 0, "ひゃん！\n主さま…\nいじわるです", 105902, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2137, 8, 0, 1, 0, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 105902, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2138, 8, 0, 1, 0, "主さまの優しさに\nわたくしはいつも\n癒されています…", 105902, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2139, 1, 1, 1, 0, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 105902, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2140, 1, 2, 1, 0, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 105902, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2141, 1, 3, 1, 0, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 105902, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2142, 1, 0, 1, 0, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 105902, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2143, 1, 0, 1, 0, "このふかふかの優しさ\n主さまのようです", 105902, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2144, 1, 0, 1, 0, "かわいい♪\n主さまにそっくり♪", 105902, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2145, 1, 0, 1, 0, "この心地よさ…\n主さまのようです♪", 105902, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2146, 1, 0, 1, 0, "ここで洗濯物を\n乾かせるでしょうか　", 105902, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2147, 1, 0, 1, 0, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 105902, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2148, 1, 0, 1, 0, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 105902, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2149, 1, 0, 1, 0, "都会のことを\n学ばないと…\nふむふむ", 105902, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2151, 1, 0, 1, 0, "ふぁ～…あっ\n見られちゃい\nましたか？", 190801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2152, 1, 0, 1, 0, "ギルドハウスの\n使い心地は\nどうですか？", 190801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2153, 1, 0, 1, 0, "ご用があれば\nなんでも言って\nくださいね～♪", 190801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2154, 1, 0, 1, 0, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 190801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2155, 1, 1, 1, 0, "おはようございます！\nお掃除の方、\nはじめていきますね", 190801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2156, 1, 2, 1, 0, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 190801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2157, 1, 3, 1, 0, "ここは夜でも\nにぎやかですね", 190801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2158, 1, 0, 1, 0, "…\n少しうとうと\nしてしまった", 104701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2159, 1, 0, 1, 0, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 104701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2160, 1, 0, 1, 0, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 104701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2161, 1, 0, 1, 0, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 104701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2162, 1, 0, 1, 0, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 104701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2163, 4, 0, 1, 0, "少年のことを考えると\n顔がにやけてしまう", 104701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2164, 8, 0, 1, 0, "少年は今頃\n平和に過ごせて\nいるかな…", 104701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2165, 1, 0, 1, 0, "ん？　悪いが\n兜は脱がないぞ", 104701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2166, 1, 0, 1, 0, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 104701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2167, 1, 0, 1, 0, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 104701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2168, 1, 0, 1, 0, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 104701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2169, 4, 0, 1, 0, "君といると、兜の中が\nいつもより暑いんだ", 104701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2170, 8, 0, 1, 0, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 104701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2171, 1, 1, 1, 0, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 104701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2172, 1, 2, 1, 0, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 104701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2173, 1, 3, 1, 0, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 104701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2175, 1, 0, 1, 0, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 104701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2176, 1, 0, 1, 0, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 104701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2177, 1, 0, 1, 0, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 104701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2178, 1, 0, 1, 0, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 104701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2180, 1, 0, 1, 0, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 104701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2181, 1, 0, 1, 0, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 104701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2183, 1, 0, 1, 0, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 102601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2184, 1, 0, 1, 0, "ドングリの木\n近くに生えてない？", 102601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2185, 1, 0, 1, 0, "快適な寝床があれば\nそれだけで幸せだねぇ", 102601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2186, 1, 0, 1, 0, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 102601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2187, 1, 0, 1, 0, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 102601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2188, 4, 0, 1, 0, "あんパン買ったし\nあいつにも\n分けてあげよう", 102601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2189, 8, 0, 1, 0, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 102601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2190, 1, 0, 1, 0, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 102601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2191, 1, 0, 1, 0, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 102601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2192, 1, 0, 1, 0, "ドングリあげるから\nあんパン買ってきて～", 102601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2193, 1, 0, 1, 0, "う～\n疲れちゃったよ～\nおんぶして運んで～", 102601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2194, 4, 0, 1, 0, "あんたといると\nリア充って\n感じがするよ", 102601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2195, 8, 0, 1, 0, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 102601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2196, 1, 1, 1, 0, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 102601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2197, 1, 2, 1, 0, "そろそろお昼寝でも\nしたい気分だねぇ", 102601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2198, 1, 3, 1, 0, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 102601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2200, 1, 0, 1, 0, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 102601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2201, 1, 0, 1, 0, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 102601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2202, 1, 0, 1, 0, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 102601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2203, 1, 0, 1, 0, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 102601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2205, 1, 0, 1, 0, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 102601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2206, 1, 0, 1, 0, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 102601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2208, 1, 0, 1, 0, "ランドソル…\nおっきな街だなぁ…", 106301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2209, 1, 0, 1, 0, "ロザリア…\nきっと、いるよね？", 106301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2210, 1, 0, 1, 0, "最近、弓の練習\nサボっちゃってるな…", 106301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2211, 1, 0, 1, 0, "今日の私は\n森番じゃなくて\n家番だよ！", 106301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2212, 1, 0, 1, 0, "ちょっとだけ\n慣れてきたかも？", 106301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2213, 4, 0, 1, 0, "いつか、\nあの人と一緒に…", 106301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2214, 8, 0, 1, 0, "…なんか最近、\nあの人のこと\nばっかり…", 106301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2215, 1, 0, 1, 0, "もしよかったら\n街に出かけない？", 106301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2216, 1, 0, 1, 0, "この部屋、\n妖精さんも\n気に入るかも！", 106301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2217, 1, 0, 1, 0, "今日は\nなににしよっか？", 106301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2218, 1, 0, 1, 0, "今日はどこ行く？", 106301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2219, 4, 0, 1, 0, "たまには二人で…\nな、なんちゃって！", 106301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2220, 8, 0, 1, 0, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 106301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2221, 1, 1, 1, 0, "おはよう！\n今日も一緒に\n頑張ろうね！", 106301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2222, 1, 2, 1, 0, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 106301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2223, 1, 3, 1, 0, "ランドソルは\n夜でも賑やかだね！", 106301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2225, 1, 0, 1, 0, "わぁっ！\nすっごく\nふわふわだよ！", 106301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2226, 1, 0, 1, 0, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 106301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2227, 1, 0, 1, 0, "くんくん\nなんか…いい匂い？", 106301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2228, 1, 0, 1, 0, "すっごいなー\nあったかいなー！", 106301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2230, 1, 0, 1, 0, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 106301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2231, 1, 0, 1, 0, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 106301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2233, 1, 0, 1, 0, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 103601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2234, 1, 0, 1, 0, "カップケーキが\n食べたいな…", 103601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2235, 1, 0, 1, 0, "予習が終わりました！\nさて、次は復習を\nしないと…", 103601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2236, 1, 0, 1, 0, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 103601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2237, 1, 0, 1, 0, "男の人は、みんな\nへんたいだと\n聞きました…", 103601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2238, 4, 0, 1, 0, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 103601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2239, 8, 0, 1, 0, "早く大人になりたいな\n…あの人と同じくらい", 103601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2240, 1, 0, 1, 0, "ぼ、防犯魔石を\n鳴らしますよ！？", 103601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2241, 1, 0, 1, 0, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 103601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2242, 1, 0, 1, 0, "これ宿題です\nやっておいて\nくださいね", 103601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2243, 1, 0, 1, 0, "あ、遅刻ですよ\n待っていたんですから", 103601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2244, 4, 0, 1, 0, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 103601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2245, 8, 0, 1, 0, "あの…何歳まで\n子供だと思いますか？", 103601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2246, 1, 1, 1, 0, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 103601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2247, 1, 2, 1, 0, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 103601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2248, 1, 3, 1, 0, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 103601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2250, 1, 0, 1, 0, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 103601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2251, 1, 0, 1, 0, "つ、つぶらな瞳には\n弱いんです", 103601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2252, 1, 0, 1, 0, "リラックスできますね\n集中力も\n上がりそうです", 103601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2253, 1, 0, 1, 0, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 103601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2255, 1, 0, 1, 0, "ピーマンもこれくらい\nおいしければ\nいいのに…", 103601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2256, 1, 0, 1, 0, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 103601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2258, 1, 0, 1, 0, "今日はみんなに\nなにを読んで\nあげようかしら", 101501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2259, 1, 0, 1, 0, "子どもたちと同じ\n目線で考えるのが\n大事よね", 101501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2260, 1, 0, 1, 0, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 101501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2261, 1, 0, 1, 0, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 101501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2262, 1, 0, 1, 0, "お遊戯会の配役、\n考え出すと\n大変だわ～", 101501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2263, 4, 0, 1, 0, "彼は優しいから、\nきっと良い\nお父さんになるわね", 101501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2264, 8, 0, 1, 0, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 101501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2265, 1, 0, 1, 0, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 101501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2266, 1, 0, 1, 0, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 101501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2267, 1, 0, 1, 0, "よかったら一緒に\n絵本を作らない？", 101501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2268, 1, 0, 1, 0, "あなたはいつも元気ね\n先生も見習わないと♪", 101501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2269, 4, 0, 1, 0, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 101501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2270, 8, 0, 1, 0, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 101501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2271, 1, 1, 1, 0, "おはよう～\nちゃんと朝ごはんは\n食べた？", 101501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2272, 1, 2, 1, 0, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 101501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2273, 1, 3, 1, 0, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 101501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2275, 1, 0, 1, 0, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 101501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2276, 1, 0, 1, 0, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 101501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2277, 1, 0, 1, 0, "いい香りね～\n保育園にも\n置こうかしら", 101501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2278, 1, 0, 1, 0, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 101501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2280, 1, 0, 1, 0, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 101501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2281, 1, 0, 1, 0, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 101501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2283, 1, 0, 1, 0, "世界の前に、\nこの部屋を征服\nしてやろう♪", 104401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2284, 1, 0, 1, 0, "拠点とはやはり\n斯くあるべきじゃの", 104401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2285, 1, 0, 1, 0, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 104401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2286, 1, 0, 1, 0, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 104401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2287, 1, 0, 1, 0, "うるさいのう～\nまったく、近頃の\n若いもんは…", 104401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2288, 4, 0, 1, 0, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 104401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2289, 8, 0, 1, 0, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 104401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2290, 1, 0, 1, 0, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 104401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2291, 1, 0, 1, 0, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 104401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2292, 1, 0, 1, 0, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 104401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2293, 1, 0, 1, 0, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 104401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2294, 4, 0, 1, 0, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 104401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2295, 8, 0, 1, 0, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 104401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2296, 1, 1, 1, 0, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 104401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2297, 1, 2, 1, 0, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 104401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2298, 1, 3, 1, 0, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 104401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2300, 1, 0, 1, 0, "よい座り心地じゃ\nわらわの玉座としよう", 104401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2301, 1, 0, 1, 0, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 104401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2302, 1, 0, 1, 0, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 104401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2303, 1, 0, 1, 0, "あたたかい…\n身も心も\n浄化されるようじゃ", 104401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2305, 1, 0, 1, 0, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 104401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2306, 1, 0, 1, 0, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 104401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2308, 1, 0, 1, 0, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 105401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2309, 1, 0, 1, 0, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 105401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2310, 1, 0, 1, 0, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 105401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2311, 1, 0, 1, 0, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 105401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2312, 1, 0, 1, 0, "ノゾミさんの\n衣装はああして\nチカさんのは…", 105401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2313, 4, 0, 1, 0, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 105401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2314, 8, 0, 1, 0, "まさか私が\nアイドルになるなんて\n思ってなかったです", 105401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2315, 1, 0, 1, 0, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 105401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2316, 1, 0, 1, 0, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 105401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2317, 1, 0, 1, 0, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 105401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2318, 1, 0, 1, 0, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 105401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2319, 4, 0, 1, 0, "どうしました？\n新しい服が\n欲しくなりましたか？", 105401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2320, 8, 0, 1, 0, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 105401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2321, 1, 1, 1, 0, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 105401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2322, 1, 2, 1, 0, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 105401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2323, 1, 3, 1, 0, "夜の方が\n作業が捗るのは\n私だけですかね？", 105401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2325, 1, 0, 1, 0, "このふかふか具合\nいい素材を\n使ってますね", 105401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2326, 1, 0, 1, 0, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 105401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2327, 1, 0, 1, 0, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 105401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2328, 1, 0, 1, 0, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 105401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2330, 1, 0, 1, 0, "何をするにも\n食事は大切！\n体力が資本です！", 105401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2331, 1, 0, 1, 0, "私もいつか\nデザインを\nまとめて本に…", 105401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2333, 1, 0, 1, 0, "またお魚を\n捕りにいこうかな♪", 107501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2334, 1, 0, 1, 0, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 107501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2335, 1, 0, 1, 0, "ここなら、\n日焼けの心配は\nありませんね！", 107501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2336, 1, 0, 1, 0, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 107501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2337, 1, 0, 1, 0, "泳ぐのも食べるのも\n大得意です！", 107501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2338, 4, 0, 1, 0, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 107501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2339, 8, 0, 1, 0, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 107501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2340, 1, 0, 1, 0, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 107501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2341, 1, 0, 1, 0, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 107501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2342, 1, 0, 1, 0, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 107501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2343, 1, 0, 1, 0, "あなたの水着、\n私が選んであげます！", 107501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2344, 4, 0, 1, 0, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 107501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2345, 8, 0, 1, 0, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 107501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2346, 1, 1, 1, 0, "朝の海も\n幻想的で\nいいですよねぇ～！", 107501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2347, 1, 2, 1, 0, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 107501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2348, 1, 3, 1, 0, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 107501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2350, 1, 0, 1, 0, "泳ぎ疲れたので\nちょっと休憩です～", 107501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2351, 1, 0, 1, 0, "あなたにも、\n可愛い水着を\n着せてあげたいです", 107501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2352, 1, 0, 1, 0, "なんだか、\nリゾート気分です…", 107501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2353, 1, 0, 1, 0, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 107501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2355, 1, 0, 1, 0, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 107501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2356, 1, 0, 1, 0, "南国のフルーツ図鑑\n…じゅるり…", 107501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2358, 1, 0, 1, 0, "わ～♪　おいしそ…\nか、かわいいですね～", 107501, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2359, 1, 0, 1, 0, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 107501, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2360, 1, 0, 1, 0, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 107601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2361, 1, 0, 1, 0, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 107601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2362, 1, 0, 1, 0, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 107601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2363, 1, 0, 1, 0, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 107601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2364, 1, 0, 1, 0, "クロールの練習なら、\n部屋でもできそうです", 107601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2365, 4, 0, 1, 0, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 107601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2366, 8, 0, 1, 0, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 107601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2367, 1, 0, 1, 0, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 107601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2368, 1, 0, 1, 0, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 107601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2369, 1, 0, 1, 0, "海の美しさは、\n森とはちがう\n魅力がありました", 107601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2370, 1, 0, 1, 0, "この髪飾りですか？\n造花ですから\n水の中も平気です", 107601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2371, 4, 0, 1, 0, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 107601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2372, 8, 0, 1, 0, "はじめての海が、\n主さまと一緒で\n幸せです♪", 107601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2373, 1, 1, 1, 0, "おはようございます。\n泳ぎに行く準備は\n万端です", 107601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2374, 1, 2, 1, 0, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 107601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2375, 1, 3, 1, 0, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 107601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2377, 1, 0, 1, 0, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 107601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2378, 1, 0, 1, 0, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 107601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2379, 1, 0, 1, 0, "この香り…\nきっと主さまも\n気に入ります", 107601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2380, 1, 0, 1, 0, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 107601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2382, 1, 0, 1, 0, "泳いだ後の食事は\n一段と美味しく\n感じます", 107601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2383, 1, 0, 1, 0, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 107601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2385, 1, 0, 1, 0, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 107601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2386, 1, 0, 1, 0, "ふかふかですね\n主さまのようです♪", 107601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2387, 1, 0, 1, 0, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 107701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2388, 1, 0, 1, 0, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 107701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2389, 1, 0, 1, 0, "今日はドジを\nしませんように！", 107701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2390, 1, 0, 1, 0, "接客業を極めれば\nメイドとして\n成長できるかも？", 107701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2391, 1, 0, 1, 0, "水着って\n動きやすいんですけど\n人の目が気になります", 107701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2392, 4, 0, 1, 0, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 107701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2393, 8, 0, 1, 0, "水着でご奉仕したら、\nあの人は喜ぶかな？", 107701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2394, 1, 0, 1, 0, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 107701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2395, 1, 0, 1, 0, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 107701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2396, 1, 0, 1, 0, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 107701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2397, 1, 0, 1, 0, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 107701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2398, 4, 0, 1, 0, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 107701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2399, 8, 0, 1, 0, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 107701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2400, 1, 1, 1, 0, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 107701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2401, 1, 2, 1, 0, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 107701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2402, 1, 3, 1, 0, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 107701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2404, 1, 0, 1, 0, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 107701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2405, 1, 0, 1, 0, "きれいにすれば\nかわいさも倍増\nですよ～♪", 107701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2406, 1, 0, 1, 0, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 107701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2407, 1, 0, 1, 0, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 107701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2409, 1, 0, 1, 0, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 107701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2410, 1, 0, 1, 0, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 107701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2412, 1, 0, 1, 0, "えへへ♪\n南の島まで\n乗せてください！", 107701, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2413, 1, 0, 1, 0, "なんだか、\n人魚姫に\nなった気分です♪", 107701, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2414, 1, 0, 1, 0, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 107801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2415, 1, 0, 1, 0, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 107801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2416, 1, 0, 1, 0, "ビーチにいると\n日焼けが心配だわ", 107801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2417, 1, 0, 1, 0, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 107801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2418, 1, 0, 1, 0, "お、泳ぎの練習なんて\nしないからね！", 107801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2419, 4, 0, 1, 0, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 107801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2420, 8, 0, 1, 0, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 107801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2421, 1, 0, 1, 0, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 107801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2422, 1, 0, 1, 0, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 107801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2423, 1, 0, 1, 0, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 107801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2424, 1, 0, 1, 0, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 107801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2425, 4, 0, 1, 0, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 107801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2426, 8, 0, 1, 0, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 107801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2427, 1, 1, 1, 0, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 107801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2428, 1, 2, 1, 0, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 107801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2429, 1, 3, 1, 0, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 107801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2431, 1, 0, 1, 0, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 107801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2432, 1, 0, 1, 0, "こんな魔物がいたら\n操るのも楽しいかも", 107801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2433, 1, 0, 1, 0, "にゃう～♪\nまるで南国にいる\n気分ね♪", 107801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2434, 1, 0, 1, 0, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 107801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2436, 1, 0, 1, 0, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 107801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2437, 1, 0, 1, 0, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 107801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2439, 1, 0, 1, 0, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 107801, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2440, 1, 0, 1, 0, "リゾートリゾートっ\nのんびりしましょ♪", 107801, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2441, 1, 0, 1, 0, "ここなら思う存分\nデスクワークに\n励めるわね！", 108001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2442, 1, 0, 1, 0, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 108001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2443, 1, 0, 1, 0, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 108001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2444, 1, 0, 1, 0, "少し休んだら\n次のバイトに\n行ってくるわね！", 108001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2445, 1, 0, 1, 0, "この格好でここに\nいるのって\n気恥ずかしいわね…", 108001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2446, 4, 0, 1, 0, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 108001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2447, 8, 0, 1, 0, "キミだけね…\n私のことをここまで\n理解してくれるのは", 108001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2448, 1, 0, 1, 0, "普段と違う格好\nだからって\nそんなに見ないでよ…", 108001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2449, 1, 0, 1, 0, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 108001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2450, 1, 0, 1, 0, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 108001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2451, 1, 0, 1, 0, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 108001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2452, 4, 0, 1, 0, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 108001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2453, 8, 0, 1, 0, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 108001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2454, 1, 1, 1, 0, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 108001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2455, 1, 2, 1, 0, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 108001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2456, 1, 3, 1, 0, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 108001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2458, 1, 0, 1, 0, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 108001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2459, 1, 0, 1, 0, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 108001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2460, 1, 0, 1, 0, "いい香りね…\n働く意欲が\n湧いてくるわ", 108001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2461, 1, 0, 1, 0, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 108001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2463, 1, 0, 1, 0, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 108001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2464, 1, 0, 1, 0, "座学も大切ね\nもっと知識を\n身につけないと…", 108001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2466, 1, 0, 1, 0, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 108001, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2467, 1, 0, 1, 0, "落ち着くわ…\n海とひとつになった\n気分ね！", 108001, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2468, 1, 0, 1, 0, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 107901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2469, 1, 0, 1, 0, "猫グッズで\n海の家を飾りたいにゃ", 107901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2470, 1, 0, 1, 0, "海の幸は美味しいけど\n水はイヤだにゃ…", 107901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2471, 1, 0, 1, 0, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 107901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2472, 1, 0, 1, 0, "海と言えば水着にゃ\n似合ってるかにゃ？", 107901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2473, 4, 0, 1, 0, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 107901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2474, 8, 0, 1, 0, "あたしの宝は\nここにあるにゃ～", 107901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2475, 1, 0, 1, 0, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 107901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2476, 1, 0, 1, 0, "今からキミを\nあたしの相棒に\n任命するにゃ！", 107901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2477, 1, 0, 1, 0, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 107901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2478, 1, 0, 1, 0, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 107901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2479, 4, 0, 1, 0, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 107901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2480, 8, 0, 1, 0, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 107901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2481, 1, 1, 1, 0, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 107901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2482, 1, 2, 1, 0, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 107901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2483, 1, 3, 1, 0, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 107901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2485, 1, 0, 1, 0, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 107901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2486, 1, 0, 1, 0, "にゃにゃ～\nなんてかわいさにゃ～", 107901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2487, 1, 0, 1, 0, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 107901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2488, 1, 0, 1, 0, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 107901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2490, 1, 0, 1, 0, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 107901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2491, 1, 0, 1, 0, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 107901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2493, 1, 0, 1, 0, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 107901, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2494, 1, 0, 1, 0, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 107901, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2495, 1, 0, 1, 0, "人の家って、道場とは\n違った香りがするね", 103701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2496, 1, 0, 1, 0, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 103701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2497, 1, 0, 1, 0, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 103701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2498, 1, 0, 1, 0, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 103701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2499, 1, 0, 1, 0, "もっと\n上手く立ち回って\n出世したいな", 103701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2500, 4, 0, 1, 0, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 103701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2501, 8, 0, 1, 0, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 103701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2502, 1, 0, 1, 0, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 103701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2503, 1, 0, 1, 0, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 103701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2504, 1, 0, 1, 0, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 103701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2505, 1, 0, 1, 0, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 103701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2506, 4, 0, 1, 0, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 103701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2507, 8, 0, 1, 0, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 103701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2508, 1, 1, 1, 0, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 103701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2509, 1, 2, 1, 0, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 103701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2510, 1, 3, 1, 0, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 103701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2512, 1, 0, 1, 0, "いざという時のため\nにも、休憩は大事だね", 103701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2513, 1, 0, 1, 0, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 103701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2514, 1, 0, 1, 0, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 103701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2515, 1, 0, 1, 0, "あったかいなぁ。\nたまにはのんびり\nしようか", 103701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2517, 1, 0, 1, 0, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 103701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2518, 1, 0, 1, 0, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 103701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2520, 1, 0, 1, 0, "正義の刃で\n悪を挫く！\n…キマったッス♪", 100501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2521, 1, 0, 1, 0, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 100501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2522, 1, 0, 1, 0, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 100501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2523, 1, 0, 1, 0, "一日一善！\nヒーローだったら\n当たり前ッス♪", 100501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2524, 1, 0, 1, 0, "どうッスか\nこのポーズ！\nイイ感じッスか？", 100501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2525, 4, 0, 1, 0, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 100501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2526, 8, 0, 1, 0, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 100501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2527, 1, 0, 1, 0, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 100501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2528, 1, 0, 1, 0, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 100501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2529, 1, 0, 1, 0, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 100501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2530, 1, 0, 1, 0, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 100501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2531, 4, 0, 1, 0, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 100501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2532, 8, 0, 1, 0, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 100501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2533, 1, 1, 1, 0, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 100501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2534, 1, 2, 1, 0, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 100501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2535, 1, 3, 1, 0, "夜のパトロールで\n一緒に平和を\n守るッス！", 100501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2537, 1, 0, 1, 0, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 100501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2538, 1, 0, 1, 0, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 100501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2539, 1, 0, 1, 0, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 100501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2540, 1, 0, 1, 0, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 100501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2542, 1, 0, 1, 0, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 100501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2543, 1, 0, 1, 0, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 100501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2545, 1, 0, 1, 0, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 108101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2546, 1, 0, 1, 0, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 108101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2547, 1, 0, 1, 0, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 108101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2548, 1, 0, 1, 0, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 108101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2549, 1, 0, 1, 0, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 108101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2550, 4, 0, 1, 0, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 108101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2551, 8, 0, 1, 0, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 108101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2552, 1, 0, 1, 0, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 108101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2553, 1, 0, 1, 0, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 108101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2554, 1, 0, 1, 0, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 108101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2555, 1, 0, 1, 0, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 108101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2556, 4, 0, 1, 0, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 108101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2557, 8, 0, 1, 0, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 108101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2558, 1, 1, 1, 0, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 108101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2559, 1, 2, 1, 0, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 108101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2560, 1, 3, 1, 0, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 108101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2562, 1, 0, 1, 0, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 108101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2563, 1, 0, 1, 0, "つん、つん。ふふっ♪\n癒やされます…", 108101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2564, 1, 0, 1, 0, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 108101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2565, 1, 0, 1, 0, "あったかいね\nお父さん…\nえ？　分からない？", 108101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2567, 1, 0, 1, 0, "霊のみなさんは\n食べられないので…\n私がいただきます", 108101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2568, 1, 0, 1, 0, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 108101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2570, 1, 0, 1, 0, "わぁ、お菓子が沢山…", 108101, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2572, 1, 0, 1, 0, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 108201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2573, 1, 0, 1, 0, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 108201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2574, 1, 0, 1, 0, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 108201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2575, 1, 0, 1, 0, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 108201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2576, 1, 0, 1, 0, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 108201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2577, 4, 0, 1, 0, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 108201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2578, 8, 0, 1, 0, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 108201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2579, 1, 0, 1, 0, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 108201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2580, 1, 0, 1, 0, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 108201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2581, 1, 0, 1, 0, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 108201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2582, 1, 0, 1, 0, "プリンの可能性は\n無限大なの～", 108201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2583, 4, 0, 1, 0, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 108201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2584, 8, 0, 1, 0, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 108201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2585, 1, 1, 1, 0, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 108201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2586, 1, 2, 1, 0, "ランチは豪華に\nプリンアラモードなの", 108201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2587, 1, 3, 1, 0, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 108201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2589, 1, 0, 1, 0, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 108201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2590, 1, 0, 1, 0, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 108201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2591, 1, 0, 1, 0, "癒されるの～\nまるで天国なの～", 108201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2592, 1, 0, 1, 0, "ここでなら、\n焼きプリンが\n作れるの！", 108201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2594, 1, 0, 1, 0, "腹が減っては\nハロウィンは\nできぬなの！", 108201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2595, 1, 0, 1, 0, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 108201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2597, 1, 0, 1, 0, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 108201, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2599, 1, 0, 1, 0, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 108301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2600, 1, 0, 1, 0, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 108301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2601, 1, 0, 1, 0, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 108301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2602, 1, 0, 1, 0, "この格好で学院に\n行ったら、\nみんな驚くかしら", 108301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2603, 1, 0, 1, 0, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 108301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2604, 4, 0, 1, 0, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 108301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2605, 8, 0, 1, 0, "アイツを魅了する\n魔法とかないかしら？", 108301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2606, 1, 0, 1, 0, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 108301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2607, 1, 0, 1, 0, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 108301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2608, 1, 0, 1, 0, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 108301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2609, 1, 0, 1, 0, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 108301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2610, 4, 0, 1, 0, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 108301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2611, 8, 0, 1, 0, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 108301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2612, 1, 1, 1, 0, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 108301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2613, 1, 2, 1, 0, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 108301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2614, 1, 3, 1, 0, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 108301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2616, 1, 0, 1, 0, "あう～、お菓子の\n食べすぎで\n動けな～い", 108301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2617, 1, 0, 1, 0, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 108301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2618, 1, 0, 1, 0, "大人な香りに\n包まれてる～♪", 108301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2619, 1, 0, 1, 0, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 108301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2621, 1, 0, 1, 0, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 108301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2622, 1, 0, 1, 0, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 108301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2624, 1, 0, 1, 0, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 108301, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2626, 1, 0, 1, 0, "次はどんな策で団長を\nからかってみようか…", 107101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2627, 1, 0, 1, 0, "む？\n血の臭いがするな\n祭りの時間か☆", 107101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2628, 1, 0, 1, 0, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 107101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2629, 1, 0, 1, 0, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 107101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2630, 1, 0, 1, 0, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 107101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2631, 4, 0, 1, 0, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 107101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2632, 8, 0, 1, 0, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 107101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2633, 1, 0, 1, 0, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 107101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2634, 1, 0, 1, 0, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 107101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2635, 1, 0, 1, 0, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 107101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2636, 1, 0, 1, 0, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 107101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2637, 4, 0, 1, 0, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 107101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2638, 8, 0, 1, 0, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 107101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2639, 1, 1, 1, 0, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 107101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2640, 1, 2, 1, 0, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 107101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2641, 1, 3, 1, 0, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 107101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2643, 1, 0, 1, 0, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 107101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2644, 1, 0, 1, 0, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 107101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2645, 1, 0, 1, 0, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 107101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2646, 1, 0, 1, 0, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 107101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2648, 1, 0, 1, 0, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 107101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2649, 1, 0, 1, 0, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 107101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2651, 1, 0, 1, 0, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 105601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2652, 1, 0, 1, 0, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 105601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2653, 1, 0, 1, 0, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 105601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2654, 1, 0, 1, 0, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 105601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2655, 1, 0, 1, 0, "ふあぁ～…\n昼寝でもするか", 105601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2656, 4, 0, 1, 0, "ふふっ…\nあの子、見た目より\n根性あるんだね", 105601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2657, 8, 0, 1, 0, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 105601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2658, 1, 0, 1, 0, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 105601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2659, 1, 0, 1, 0, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 105601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2660, 1, 0, 1, 0, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 105601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2661, 1, 0, 1, 0, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 105601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2662, 4, 0, 1, 0, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 105601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2663, 8, 0, 1, 0, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 105601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2664, 1, 1, 1, 0, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 105601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2665, 1, 2, 1, 0, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 105601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2666, 1, 3, 1, 0, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 105601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2668, 1, 0, 1, 0, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 105601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2669, 1, 0, 1, 0, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 105601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2670, 1, 0, 1, 0, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 105601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2671, 1, 0, 1, 0, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 105601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2673, 1, 0, 1, 0, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 105601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2674, 1, 0, 1, 0, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 105601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2676, 1, 0, 1, 0, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 101301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2677, 1, 0, 1, 0, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 101301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2678, 1, 0, 1, 0, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 101301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2679, 1, 0, 1, 0, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 101301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2680, 1, 0, 1, 0, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 101301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2681, 4, 0, 1, 0, "次はどの漫画を\n貸そうかなー♪", 101301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2682, 8, 0, 1, 0, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 101301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2683, 1, 0, 1, 0, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 101301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2684, 1, 0, 1, 0, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 101301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2685, 1, 0, 1, 0, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 101301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2686, 1, 0, 1, 0, "新魔法思いついた！\n私って天才！？", 101301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2687, 4, 0, 1, 0, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 101301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2688, 8, 0, 1, 0, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 101301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2689, 1, 1, 1, 0, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 101301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2690, 1, 2, 1, 0, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 101301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2691, 1, 3, 1, 0, "夜からが、\nオタ活本番でしょ！", 101301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2693, 1, 0, 1, 0, "ふいー！\nくつろいで本を読む！\n最高だねー！", 101301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2694, 1, 0, 1, 0, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 101301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2695, 1, 0, 1, 0, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 101301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2696, 1, 0, 1, 0, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 101301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2698, 1, 0, 1, 0, "ウマ―！\nこれの詳細きぼんぬ！", 101301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2699, 1, 0, 1, 0, "こ、古代語の本！？\n現代語でおk…", 101301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2701, 1, 0, 1, 0, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 190901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2702, 1, 0, 1, 0, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 190901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2703, 1, 0, 1, 0, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 190901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2704, 1, 0, 1, 0, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 190901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2705, 1, 0, 1, 0, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 190901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2706, 1, 0, 1, 0, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 190901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2707, 1, 0, 1, 0, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 190901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2708, 1, 0, 1, 0, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 190901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2709, 1, 0, 1, 0, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 190901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2710, 1, 1, 1, 0, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 190901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2711, 1, 2, 1, 0, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 190901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2712, 1, 3, 1, 0, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 190901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2714, 1, 0, 1, 0, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 190901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2715, 1, 0, 1, 0, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 190901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2716, 1, 0, 1, 0, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 190901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2717, 1, 0, 1, 0, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 190901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2719, 1, 0, 1, 0, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 190901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2720, 1, 0, 1, 0, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 190901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2722, 1, 0, 1, 0, "メリークリスマス！\n歌で幸せを\n届けます…！", 108401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2723, 1, 0, 1, 0, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 108401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2724, 1, 0, 1, 0, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 108401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2725, 1, 0, 1, 0, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 108401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2726, 1, 0, 1, 0, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 108401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2727, 4, 0, 1, 0, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 108401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2728, 8, 0, 1, 0, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 108401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2729, 1, 0, 1, 0, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 108401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2730, 1, 0, 1, 0, "歌を届ける\nサンタさんに\nなりたいな…", 108401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2731, 1, 0, 1, 0, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 108401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2732, 1, 0, 1, 0, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 108401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2733, 4, 0, 1, 0, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 108401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2734, 8, 0, 1, 0, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 108401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2735, 1, 1, 1, 0, "今日も一日\n幸せな歌で世界が\n包まれますように…", 108401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2736, 1, 2, 1, 0, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 108401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2737, 1, 3, 1, 0, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 108401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2739, 1, 0, 1, 0, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 108401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2740, 1, 0, 1, 0, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 108401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2741, 1, 0, 1, 0, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 108401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2742, 1, 0, 1, 0, "この光みたいに\n灯火になるような\n歌を歌いたい…", 108401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2744, 1, 0, 1, 0, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 108401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2745, 1, 0, 1, 0, "物語には\n曲作りのヒントが\n詰まっているんです", 108401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2747, 1, 0, 1, 0, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 108401, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2748, 1, 0, 1, 0, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 108401, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2749, 1, 0, 1, 0, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 108501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2750, 1, 0, 1, 0, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 108501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2751, 1, 0, 1, 0, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 108501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2752, 1, 0, 1, 0, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 108501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2753, 1, 0, 1, 0, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 108501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2754, 4, 0, 1, 0, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 108501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2755, 8, 0, 1, 0, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 108501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2756, 1, 0, 1, 0, "メリー\nクリスマスです、\nお兄ちゃん♪", 108501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2757, 1, 0, 1, 0, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 108501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2758, 1, 0, 1, 0, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 108501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2759, 1, 0, 1, 0, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 108501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2760, 4, 0, 1, 0, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 108501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2761, 8, 0, 1, 0, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 108501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2762, 1, 1, 1, 0, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 108501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2763, 1, 2, 1, 0, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 108501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2764, 1, 3, 1, 0, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 108501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2766, 1, 0, 1, 0, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 108501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2767, 1, 0, 1, 0, "この子と一緒に\nおままごとしたいな♪", 108501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2768, 1, 0, 1, 0, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 108501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2769, 1, 0, 1, 0, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 108501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2771, 1, 0, 1, 0, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 108501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2772, 1, 0, 1, 0, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 108501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2774, 1, 0, 1, 0, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 108501, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2775, 1, 0, 1, 0, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 108501, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2776, 1, 0, 1, 0, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 108601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2777, 1, 0, 1, 0, "毎日が\nクリスマスだったら\nいいのにな～♪", 108601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2778, 1, 0, 1, 0, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 108601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2779, 1, 0, 1, 0, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 108601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2780, 1, 0, 1, 0, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 108601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2781, 4, 0, 1, 0, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 108601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2782, 8, 0, 1, 0, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 108601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2783, 1, 0, 1, 0, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 108601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2784, 1, 0, 1, 0, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 108601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2785, 1, 0, 1, 0, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 108601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2786, 1, 0, 1, 0, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 108601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2787, 4, 0, 1, 0, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 108601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2788, 8, 0, 1, 0, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 108601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2789, 1, 1, 1, 0, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 108601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2790, 1, 2, 1, 0, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 108601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2791, 1, 3, 1, 0, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 108601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2793, 1, 0, 1, 0, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 108601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2794, 1, 0, 1, 0, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 108601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2795, 1, 0, 1, 0, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 108601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2796, 1, 0, 1, 0, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 108601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2798, 1, 0, 1, 0, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 108601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2799, 1, 0, 1, 0, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 108601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2801, 1, 0, 1, 0, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 108601, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2802, 1, 0, 1, 0, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 108601, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2803, 1, 0, 1, 0, "着物も慣れると\n動きやすいものさ", 108901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2804, 1, 0, 1, 0, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 108901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2805, 1, 0, 1, 0, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 108901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2806, 1, 0, 1, 0, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 108901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2807, 1, 0, 1, 0, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 108901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2808, 4, 0, 1, 0, "これからもずっと\n仲間と、彼と\n一緒に…", 108901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2809, 8, 0, 1, 0, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 108901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2810, 1, 0, 1, 0, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 108901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2811, 1, 0, 1, 0, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 108901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2812, 1, 0, 1, 0, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 108901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2813, 1, 0, 1, 0, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 108901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2814, 4, 0, 1, 0, "キミといると\n気持ちが段々と\n安らいでいくよ", 108901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2815, 8, 0, 1, 0, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 108901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2816, 1, 1, 1, 0, "おはよう\n朝の修行を始めるけど\nキミもどう？", 108901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2817, 1, 2, 1, 0, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 108901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2818, 1, 3, 1, 0, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 108901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2820, 1, 0, 1, 0, "…疲れていたのかな\nもう少しで眠る\nところだった…", 108901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2821, 1, 0, 1, 0, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 108901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2822, 1, 0, 1, 0, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 108901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2823, 1, 0, 1, 0, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 108901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2825, 1, 0, 1, 0, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 108901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2826, 1, 0, 1, 0, "外を出歩くのも\nいいけど、中で\n読書もありだな", 108901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2828, 1, 0, 1, 0, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 108801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2829, 1, 0, 1, 0, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 108801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2830, 1, 0, 1, 0, "今年は\nどんな福袋を\n買おうかな…♪", 108801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2831, 1, 0, 1, 0, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 108801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2832, 1, 0, 1, 0, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 108801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2833, 4, 0, 1, 0, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 108801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2834, 8, 0, 1, 0, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 108801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2835, 1, 0, 1, 0, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 108801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2836, 1, 0, 1, 0, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 108801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2837, 1, 0, 1, 0, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 108801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2838, 1, 0, 1, 0, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 108801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2839, 4, 0, 1, 0, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 108801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2840, 8, 0, 1, 0, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 108801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2841, 1, 1, 1, 0, "おはよう\n太陽の光に当たると\n元気が出るよ", 108801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2842, 1, 2, 1, 0, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 108801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2843, 1, 3, 1, 0, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 108801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2845, 1, 0, 1, 0, "ふぅ…\n帯も少し\nゆるめちゃおう…", 108801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2846, 1, 0, 1, 0, "お正月用に\n着物を縫って\nあげたいな", 108801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2847, 1, 0, 1, 0, "これでお肌が\n保湿出来たら\nいいのになぁ", 108801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2848, 1, 0, 1, 0, "ん～…\nぬくぬくしてて\n動きたくないよ～", 108801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2850, 1, 0, 1, 0, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 108801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2851, 1, 0, 1, 0, "年末年始は\n告白のチャンス…\nむ、無理無理！", 108801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2853, 1, 0, 1, 0, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 108701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2854, 1, 0, 1, 0, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 108701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2855, 1, 0, 1, 0, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 108701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2856, 1, 0, 1, 0, "初詣楽しみー！\n出店はどこから\n行こうかな？", 108701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2857, 1, 0, 1, 0, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 108701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2858, 4, 0, 1, 0, "うーん…\n騎士くんはお正月\n何してるんだろ…", 108701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2859, 8, 0, 1, 0, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 108701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2860, 1, 0, 1, 0, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 108701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2861, 1, 0, 1, 0, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 108701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2862, 1, 0, 1, 0, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 108701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2863, 1, 0, 1, 0, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 108701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2864, 4, 0, 1, 0, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 108701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2865, 8, 0, 1, 0, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 108701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2866, 1, 1, 1, 0, "おはよー！\n今日も明るく元気に\nがんばろー！", 108701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2867, 1, 2, 1, 0, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 108701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2868, 1, 3, 1, 0, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 108701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2870, 1, 0, 1, 0, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 108701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2871, 1, 0, 1, 0, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 108701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2872, 1, 0, 1, 0, "はぁー♪\nいい香りー…\n気分スッキリ♪", 108701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2873, 1, 0, 1, 0, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 108701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2875, 1, 0, 1, 0, "ど、どうしよう…！\n手が、止まんないよ！", 108701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2876, 1, 0, 1, 0, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 108701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2878, 1, 0, 1, 0, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 109001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2879, 1, 0, 1, 0, "ここを私のチョコで\n埋め尽くしますわ", 109001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2880, 1, 0, 1, 0, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 109001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2881, 1, 0, 1, 0, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 109001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2882, 1, 0, 1, 0, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 109001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2883, 4, 0, 1, 0, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 109001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2884, 8, 0, 1, 0, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 109001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2885, 1, 0, 1, 0, "あなた様。私に\n隠しているものなど\nありませんよね…？", 109001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2886, 1, 0, 1, 0, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 109001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2887, 1, 0, 1, 0, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 109001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2888, 1, 0, 1, 0, "解毒薬はどこに\nいれたかしら…？", 109001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2889, 4, 0, 1, 0, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 109001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2890, 8, 0, 1, 0, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 109001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2891, 1, 1, 1, 0, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 109001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2892, 1, 2, 1, 0, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 109001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2893, 1, 3, 1, 0, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 109001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2895, 1, 0, 1, 0, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 109001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2896, 1, 0, 1, 0, "この中、何か\n入りそうですわね\n…チョコレート、とか", 109001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2897, 1, 0, 1, 0, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 109001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2898, 1, 0, 1, 0, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 109001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2900, 1, 0, 1, 0, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 109001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2901, 1, 0, 1, 0, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 109001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2903, 1, 0, 1, 0, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 109101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2904, 1, 0, 1, 0, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 109101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2905, 1, 0, 1, 0, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 109101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2906, 1, 0, 1, 0, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 109101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2907, 1, 0, 1, 0, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 109101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2908, 4, 0, 1, 0, "お姉ちゃんのチョコ\n食べさせてあげたい…", 109101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2909, 8, 0, 1, 0, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 109101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2910, 1, 0, 1, 0, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 109101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2911, 1, 0, 1, 0, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 109101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2912, 1, 0, 1, 0, "ナデナデするのは\nお姉ちゃんの仕事！", 109101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2913, 1, 0, 1, 0, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 109101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2914, 4, 0, 1, 0, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 109101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2915, 8, 0, 1, 0, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 109101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2916, 1, 1, 1, 0, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 109101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2917, 1, 2, 1, 0, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 109101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2918, 1, 3, 1, 0, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 109101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2920, 1, 0, 1, 0, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 109101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2921, 1, 0, 1, 0, "このかわいさは\nお姉ちゃんも大満足♪", 109101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2922, 1, 0, 1, 0, "この香り、チョコに\n使えるかな…？", 109101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2923, 1, 0, 1, 0, "弟くんと一緒に\nぬくぬくしたいな…♪", 109101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2925, 1, 0, 1, 0, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 109101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2926, 1, 0, 1, 0, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 109101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2928, 1, 0, 1, 0, "アタシって…\nここにいても\nいいのか…？", 106101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2929, 1, 0, 1, 0, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 106101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2930, 1, 0, 1, 0, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 106101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2931, 1, 0, 1, 0, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 106101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2932, 1, 0, 1, 0, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 106101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2933, 4, 0, 1, 0, "みんなが早く\nアタシのこと\n思い出さないかな", 106101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2934, 8, 0, 1, 0, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 106101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2935, 1, 0, 1, 0, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 106101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2936, 1, 0, 1, 0, "どうした？\n戦うのか！？\n…なんだ、違うのか", 106101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2937, 1, 0, 1, 0, "身長のことは\nあんまり言うな！\n気にしてるからな！", 106101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2938, 1, 0, 1, 0, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 106101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2939, 4, 0, 1, 0, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 106101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2940, 8, 0, 1, 0, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 106101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2941, 1, 1, 1, 0, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 106101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2942, 1, 2, 1, 0, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 106101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2943, 1, 3, 1, 0, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 106101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2945, 1, 0, 1, 0, "ふぃ～…\n極楽極楽…", 106101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2946, 1, 0, 1, 0, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 106101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2947, 1, 0, 1, 0, "くんくん…\nたまにはこういうのも\n悪くないかもな", 106101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2948, 1, 0, 1, 0, "あー…\nもっと前にコイツと\n出会いたかった…", 106101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2950, 1, 0, 1, 0, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 106101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2951, 1, 0, 1, 0, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 106101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2953, 1, 0, 1, 0, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 101401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2954, 1, 0, 1, 0, "事件のオチは\n往々にして\n取るに足らないものさ", 101401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2955, 1, 0, 1, 0, "読書で得る知識が\n探偵の\n血肉になるんだよ", 101401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2956, 1, 0, 1, 0, "この前の\n捜査資料…\nどこにあったかな？", 101401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2957, 1, 0, 1, 0, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 101401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2958, 4, 0, 1, 0, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 101401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2959, 8, 0, 1, 0, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 101401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2960, 1, 0, 1, 0, "珈琲、淹れたてだよ\n一杯どうだい？", 101401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2961, 1, 0, 1, 0, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 101401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2962, 1, 0, 1, 0, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 101401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2963, 1, 0, 1, 0, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 101401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2964, 4, 0, 1, 0, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 101401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2965, 8, 0, 1, 0, "助手くんは\n私にとって\n最高の相棒だよ☆", 101401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2966, 1, 1, 1, 0, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 101401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2967, 1, 2, 1, 0, "一休みしたら\n推理の答え合わせを\nしてみようか？", 101401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2968, 1, 3, 1, 0, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 101401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2970, 1, 0, 1, 0, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 101401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2971, 1, 0, 1, 0, "この愛らしさ…\n要捜査対象だね！", 101401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2972, 1, 0, 1, 0, "思考が活性化される\n素敵な香りだね…", 101401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2973, 1, 0, 1, 0, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 101401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2975, 1, 0, 1, 0, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 101401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2976, 1, 0, 1, 0, "探偵だからね\nミステリには\n一家言あるんだ", 101401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2978, 1, 0, 1, 0, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 105501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2979, 1, 0, 1, 0, "せ、先輩がいた部屋！\n私には刺激が強い…", 105501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2980, 1, 0, 1, 0, "先輩を観察しやすい\n場所はあるかな…", 105501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2981, 1, 0, 1, 0, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 105501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2982, 1, 0, 1, 0, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 105501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2983, 4, 0, 1, 0, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 105501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2984, 8, 0, 1, 0, "せ、先輩になら\n観察されてもいいなぁ", 105501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2985, 1, 0, 1, 0, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 105501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2986, 1, 0, 1, 0, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 105501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2987, 1, 0, 1, 0, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 105501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2988, 1, 0, 1, 0, "ま、また…\n先輩と一緒に\nバイトしたい…です", 105501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2989, 4, 0, 1, 0, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 105501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2990, 8, 0, 1, 0, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 105501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2991, 1, 1, 1, 0, "せ、先輩…！\nおひゃっ！\nおはようございます！", 105501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2992, 1, 2, 1, 0, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 105501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2993, 1, 3, 1, 0, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 105501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2995, 1, 0, 1, 0, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 105501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2996, 1, 0, 1, 0, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 105501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2997, 1, 0, 1, 0, "ここから先輩の\n香りがすればなあ", 105501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (2998, 1, 0, 1, 0, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 105501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3000, 1, 0, 1, 0, "おいし～\nこれは\n先輩も好きな味かな", 105501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3001, 1, 0, 1, 0, "す、好きな人に\n近づく方法…", 105501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3003, 1, 0, 1, 0, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 109201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3004, 1, 0, 1, 0, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 109201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3005, 1, 0, 1, 0, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 109201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3006, 1, 0, 1, 0, "わー！\nあれもこれも\n気になっちゃうな！", 109201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3007, 1, 0, 1, 0, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 109201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3008, 4, 0, 1, 0, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 109201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3009, 8, 0, 1, 0, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 109201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3010, 1, 0, 1, 0, "歩き方がきれい？\nえへへ、そうかな…？", 109201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3011, 1, 0, 1, 0, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 109201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3012, 1, 0, 1, 0, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 109201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3013, 1, 0, 1, 0, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 109201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3014, 4, 0, 1, 0, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 109201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3015, 8, 0, 1, 0, "お茶の時間に\nしたいな～…\n一緒にどう？", 109201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3016, 1, 1, 1, 0, "おはようっ！\n今日も素敵な\n一日にしようね！", 109201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3017, 1, 2, 1, 0, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 109201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3018, 1, 3, 1, 0, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 109201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3020, 1, 0, 1, 0, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 109201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3021, 1, 0, 1, 0, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 109201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3022, 1, 0, 1, 0, "いい香り～！\nなんだか気持ちが\n落ち着くね", 109201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3023, 1, 0, 1, 0, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 109201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3025, 1, 0, 1, 0, "こ、これ美味し～！\n止められないよー！", 109201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3026, 1, 0, 1, 0, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 109201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3028, 1, 0, 1, 0, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 109301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3029, 1, 0, 1, 0, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 109301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3030, 1, 0, 1, 0, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 109301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3031, 1, 0, 1, 0, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 109301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3032, 1, 0, 1, 0, "なにか面白そうなもの\nないですかね～？", 109301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3033, 4, 0, 1, 0, "ここのみんなとも\nもうお友だちですぅ！", 109301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3034, 8, 0, 1, 0, "おなかが減ったです…", 109301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3035, 1, 0, 1, 0, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 109301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3036, 1, 0, 1, 0, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 109301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3037, 1, 0, 1, 0, "あなたもお勉強\n苦手ですか？", 109301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3038, 1, 0, 1, 0, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 109301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3039, 4, 0, 1, 0, "お友だちいっぱいで、\nルゥはうれしいです♪", 109301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3040, 8, 0, 1, 0, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 109301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3041, 1, 1, 1, 0, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 109301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3042, 1, 2, 1, 0, "午後もいっぱい\n遊ぶですよ～！", 109301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3043, 1, 3, 1, 0, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 109301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3045, 1, 0, 1, 0, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 109301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3046, 1, 0, 1, 0, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 109301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3047, 1, 0, 1, 0, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 109301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3048, 1, 0, 1, 0, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 109301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3050, 1, 0, 1, 0, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 109301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3051, 1, 0, 1, 0, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 109301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3053, 1, 0, 1, 0, "ピアノ…\nどこかにないかな？", 109401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3054, 1, 0, 1, 0, "ふぅ…\n落ち着くな…", 109401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3055, 1, 0, 1, 0, "マナリアはいつも\n賑やかなんだ…", 109401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3056, 1, 0, 1, 0, "みんな…\n今頃何してるかな？", 109401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3057, 1, 0, 1, 0, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 109401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3058, 4, 0, 1, 0, "人と話すのは\n得意じゃないな…", 109401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3059, 8, 0, 1, 0, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 109401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3060, 1, 0, 1, 0, "やっぱり、私の翼や\n尻尾…気になる？", 109401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3061, 1, 0, 1, 0, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 109401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3062, 1, 0, 1, 0, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 109401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3063, 1, 0, 1, 0, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 109401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3064, 4, 0, 1, 0, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 109401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3065, 8, 0, 1, 0, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 109401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3066, 1, 1, 1, 0, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 109401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3067, 1, 2, 1, 0, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 109401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3068, 1, 3, 1, 0, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 109401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3070, 1, 0, 1, 0, "羽を伸ばしたく\nなっちゃうね…ふふ", 109401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3071, 1, 0, 1, 0, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 109401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3072, 1, 0, 1, 0, "いい香り…\nこれ、\nアンも好きそう…", 109401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3073, 1, 0, 1, 0, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 109401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3075, 1, 0, 1, 0, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 109401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3076, 1, 0, 1, 0, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 109401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3078, 1, 0, 1, 0, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 109501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3079, 1, 0, 1, 0, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 109501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3080, 1, 0, 1, 0, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 109501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3081, 1, 0, 1, 0, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 109501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3082, 1, 0, 1, 0, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 109501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3083, 4, 0, 1, 0, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 109501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3084, 8, 0, 1, 0, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 109501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3085, 1, 0, 1, 0, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 109501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3086, 1, 0, 1, 0, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 109501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3087, 1, 0, 1, 0, "こんな場所で\nなんというプレイを！\n…妄想でした", 109501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3088, 1, 0, 1, 0, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 109501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3089, 4, 0, 1, 0, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 109501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3090, 8, 0, 1, 0, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 109501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3091, 1, 1, 1, 0, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 109501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3092, 1, 2, 1, 0, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 109501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3093, 1, 3, 1, 0, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 109501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3095, 1, 0, 1, 0, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 109501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3096, 1, 0, 1, 0, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 109501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3097, 1, 0, 1, 0, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 109501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3098, 1, 0, 1, 0, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 109501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3100, 1, 0, 1, 0, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 109501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3101, 1, 0, 1, 0, "クウカの探している\nジャンルはどこに\nありますかね？", 109501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3103, 1, 0, 1, 0, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 109601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3104, 1, 0, 1, 0, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 109601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3105, 1, 0, 1, 0, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 109601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3106, 1, 0, 1, 0, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 109601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3107, 1, 0, 1, 0, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 109601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3108, 4, 0, 1, 0, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 109601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3109, 8, 0, 1, 0, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 109601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3110, 1, 0, 1, 0, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 109601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3111, 1, 0, 1, 0, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 109601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3112, 1, 0, 1, 0, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 109601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3113, 1, 0, 1, 0, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 109601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3114, 4, 0, 1, 0, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 109601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3115, 8, 0, 1, 0, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 109601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3116, 1, 1, 1, 0, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 109601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3117, 1, 2, 1, 0, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 109601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3118, 1, 3, 1, 0, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 109601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3120, 1, 0, 1, 0, "天下分け目の\n戦いに備えて…\nマッタリデース", 109601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3121, 1, 0, 1, 0, "口寄せの術で\n召喚したいくらい\nかわいいデース", 109601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3122, 1, 0, 1, 0, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 109601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3123, 1, 0, 1, 0, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 109601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3125, 1, 0, 1, 0, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 109601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3126, 1, 0, 1, 0, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 109601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3128, 1, 0, 1, 0, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 109701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3129, 1, 0, 1, 0, "異世界の演劇…\n許されるならば、\n観てみたいです", 109701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3130, 1, 0, 1, 0, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 109701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3131, 1, 0, 1, 0, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 109701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3132, 1, 0, 1, 0, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 109701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3133, 4, 0, 1, 0, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 109701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3134, 8, 0, 1, 0, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 109701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3135, 1, 0, 1, 0, "お出かけでしたら、\nレムも同行\nさせてくださいね", 109701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3136, 1, 0, 1, 0, "レムにできること\nでしたら、なんでも\nお申し付けください", 109701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3137, 1, 0, 1, 0, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 109701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3138, 1, 0, 1, 0, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 109701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3139, 4, 0, 1, 0, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 109701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3140, 8, 0, 1, 0, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 109701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3141, 1, 1, 1, 0, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 109701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3142, 1, 2, 1, 0, "昼食の時間ですね\n何か食べたいものは\nありますか？", 109701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3143, 1, 3, 1, 0, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 109701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3145, 1, 0, 1, 0, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 109701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3146, 1, 0, 1, 0, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 109701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3147, 1, 0, 1, 0, "よい香りですね\n魔女教の悪臭とは\n大違いです", 109701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3148, 1, 0, 1, 0, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 109701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3150, 1, 0, 1, 0, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 109701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3151, 1, 0, 1, 0, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 109701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3153, 1, 0, 1, 0, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 109801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3154, 1, 0, 1, 0, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 109801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3155, 1, 0, 1, 0, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 109801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3156, 1, 0, 1, 0, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 109801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3157, 1, 0, 1, 0, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 109801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3158, 4, 0, 1, 0, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 109801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3159, 8, 0, 1, 0, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 109801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3160, 1, 0, 1, 0, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 109801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3161, 1, 0, 1, 0, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 109801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3162, 1, 0, 1, 0, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 109801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3163, 1, 0, 1, 0, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 109801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3164, 4, 0, 1, 0, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 109801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3165, 8, 0, 1, 0, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 109801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3166, 1, 1, 1, 0, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 109801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3167, 1, 2, 1, 0, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 109801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3168, 1, 3, 1, 0, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 109801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3170, 1, 0, 1, 0, "…ここから動かない\nという、誓いを\n立てたくなるわね", 109801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3171, 1, 0, 1, 0, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 109801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3172, 1, 0, 1, 0, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 109801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3173, 1, 0, 1, 0, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 109801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3175, 1, 0, 1, 0, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 109801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3176, 1, 0, 1, 0, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 109801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3178, 1, 0, 1, 0, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 109901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3179, 1, 0, 1, 0, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 109901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3180, 1, 0, 1, 0, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 109901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3181, 1, 0, 1, 0, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 109901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3182, 1, 0, 1, 0, "今日のご飯…\nピーマルは\n入ってないわよね…？", 109901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3183, 4, 0, 1, 0, "また、みんなで\n一緒に買い物に\n行きたいな", 109901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3184, 8, 0, 1, 0, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 109901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3185, 1, 0, 1, 0, "今からパックの\n毛繕いをするけど…\n一緒にする？", 109901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3186, 1, 0, 1, 0, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 109901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3187, 1, 0, 1, 0, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 109901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3188, 1, 0, 1, 0, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 109901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3189, 4, 0, 1, 0, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 109901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3190, 8, 0, 1, 0, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 109901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3191, 1, 1, 1, 0, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 109901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3192, 1, 2, 1, 0, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 109901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3193, 1, 3, 1, 0, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 109901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3195, 1, 0, 1, 0, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 109901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3196, 1, 0, 1, 0, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 109901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3197, 1, 0, 1, 0, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 109901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3198, 1, 0, 1, 0, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 109901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3200, 1, 0, 1, 0, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 109901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3201, 1, 0, 1, 0, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 109901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3203, 1, 0, 1, 0, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 110001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3204, 1, 0, 1, 0, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 110001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3205, 1, 0, 1, 0, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 110001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3206, 1, 0, 1, 0, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 110001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3207, 1, 0, 1, 0, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 110001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3208, 4, 0, 1, 0, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 110001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3209, 8, 0, 1, 0, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 110001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3210, 1, 0, 1, 0, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 110001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3211, 1, 0, 1, 0, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 110001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3212, 1, 0, 1, 0, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 110001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3213, 1, 0, 1, 0, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 110001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3214, 4, 0, 1, 0, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 110001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3215, 8, 0, 1, 0, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 110001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3216, 1, 1, 1, 0, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 110001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3217, 1, 2, 1, 0, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 110001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3218, 1, 3, 1, 0, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 110001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3220, 1, 0, 1, 0, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 110001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3221, 1, 0, 1, 0, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 110001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3222, 1, 0, 1, 0, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 110001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3223, 1, 0, 1, 0, "あったか～い♪\n夏の太陽みたい！", 110001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3225, 1, 0, 1, 0, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 110001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3226, 1, 0, 1, 0, "夏のために\n最新ファッションを\n勉強ちう！", 110001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3228, 1, 0, 1, 0, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 110001, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3229, 1, 0, 1, 0, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 110001, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3230, 1, 0, 1, 0, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 110101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3231, 1, 0, 1, 0, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 110101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3232, 1, 0, 1, 0, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 110101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3233, 1, 0, 1, 0, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 110101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3234, 1, 0, 1, 0, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 110101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3235, 4, 0, 1, 0, "海が楽しいって\n思えたのも\nあの子のおかげね", 110101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3236, 8, 0, 1, 0, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 110101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3237, 1, 0, 1, 0, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 110101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3238, 1, 0, 1, 0, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 110101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3239, 1, 0, 1, 0, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 110101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3240, 1, 0, 1, 0, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 110101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3241, 4, 0, 1, 0, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 110101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3242, 8, 0, 1, 0, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 110101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3243, 1, 1, 1, 0, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 110101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3244, 1, 2, 1, 0, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 110101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3245, 1, 3, 1, 0, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 110101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3247, 1, 0, 1, 0, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 110101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3248, 1, 0, 1, 0, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 110101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3249, 1, 0, 1, 0, "この匂い…\nなんだか夏の海を\n思い出すわ～", 110101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3250, 1, 0, 1, 0, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 110101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3252, 1, 0, 1, 0, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 110101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3253, 1, 0, 1, 0, "夏休み中でも\nしっかりお勉強\nしないと！", 110101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3255, 1, 0, 1, 0, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 110101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3256, 1, 0, 1, 0, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 110101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3257, 1, 0, 1, 0, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 110301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3258, 1, 0, 1, 0, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 110301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3259, 1, 0, 1, 0, "子どもたちのために\n浮き輪を用意して\nおかないと…", 110301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3260, 1, 0, 1, 0, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 110301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3261, 1, 0, 1, 0, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 110301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3262, 4, 0, 1, 0, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 110301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3263, 8, 0, 1, 0, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 110301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3264, 1, 0, 1, 0, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 110301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3265, 1, 0, 1, 0, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 110301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3266, 1, 0, 1, 0, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 110301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3267, 1, 0, 1, 0, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 110301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3268, 4, 0, 1, 0, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 110301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3269, 8, 0, 1, 0, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 110301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3270, 1, 1, 1, 0, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 110301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3271, 1, 2, 1, 0, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 110301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3272, 1, 3, 1, 0, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 110301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3274, 1, 0, 1, 0, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 110301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3275, 1, 0, 1, 0, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 110301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3276, 1, 0, 1, 0, "夏らしい\nいい香りね…\nなんの香りかしら？", 110301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3277, 1, 0, 1, 0, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 110301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3279, 1, 0, 1, 0, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 110301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3280, 1, 0, 1, 0, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 110301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3282, 1, 0, 1, 0, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 110301, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3283, 1, 0, 1, 0, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 110301, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3284, 1, 0, 1, 0, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 110401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3285, 1, 0, 1, 0, "いてて…\nちょっと日焼け\nしすぎたかな", 110401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3286, 1, 0, 1, 0, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 110401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3287, 1, 0, 1, 0, "夏ならではの\n料理…か\n何がいいかな…", 110401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3288, 1, 0, 1, 0, "やっぱりここは\n涼しくて快適だな", 110401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3289, 4, 0, 1, 0, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 110401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3290, 8, 0, 1, 0, "水着で部屋の中に\nいるのって\n変な感じだな…", 110401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3291, 1, 0, 1, 0, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 110401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3292, 1, 0, 1, 0, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 110401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3293, 1, 0, 1, 0, "夏バテしてないか？\nここで休んでけよ", 110401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3294, 1, 0, 1, 0, "…そう見られると\n照れるっていうか…", 110401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3295, 4, 0, 1, 0, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 110401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3296, 8, 0, 1, 0, "おまえと\n夏の思い出ができて\nよかったよ", 110401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3297, 1, 1, 1, 0, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 110401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3298, 1, 2, 1, 0, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 110401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3299, 1, 3, 1, 0, "今日もお疲れ\n明日のパトロールも\nよろしくな", 110401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3301, 1, 0, 1, 0, "泳ぎ疲れたからな\n少し休憩だ…", 110401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3302, 1, 0, 1, 0, "か、かわいい…\nいや、なんでもねぇ！", 110401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3303, 1, 0, 1, 0, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 110401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3304, 1, 0, 1, 0, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 110401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3306, 1, 0, 1, 0, "海で食べると\nまた一味違うよな", 110401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3307, 1, 0, 1, 0, "夏のサバイバル特集…\nちょっと読んでみるか", 110401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3309, 1, 0, 1, 0, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 110401, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3310, 1, 0, 1, 0, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 110401, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3311, 1, 0, 1, 0, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 110501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3312, 1, 0, 1, 0, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 110501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3313, 1, 0, 1, 0, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 110501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3314, 1, 0, 1, 0, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 110501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3315, 1, 0, 1, 0, "クロールも\n犬かきも\n大得意さ～！", 110501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3316, 4, 0, 1, 0, "なんだか歌いたい\n気分さ～\nキミもどう～？", 110501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3317, 8, 0, 1, 0, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 110501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3318, 1, 0, 1, 0, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 110501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3319, 1, 0, 1, 0, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 110501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3320, 1, 0, 1, 0, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 110501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3321, 1, 0, 1, 0, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 110501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3322, 4, 0, 1, 0, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 110501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3323, 8, 0, 1, 0, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 110501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3324, 1, 1, 1, 0, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 110501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3325, 1, 2, 1, 0, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 110501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3326, 1, 3, 1, 0, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 110501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3328, 1, 0, 1, 0, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 110501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3329, 1, 0, 1, 0, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 110501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3330, 1, 0, 1, 0, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 110501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3331, 1, 0, 1, 0, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 110501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3333, 1, 0, 1, 0, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 110501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3334, 1, 0, 1, 0, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 110501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3336, 1, 0, 1, 0, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 110501, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3337, 1, 0, 1, 0, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 110501, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3338, 12, 0, 1, 0, "あの人が\nわたしの騎士だったら\nいいのにな…", 105801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3339, 12, 0, 1, 0, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 105801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3340, 12, 0, 1, 0, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 105901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3341, 12, 0, 1, 0, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 105901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3342, 12, 0, 1, 0, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 106001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3343, 12, 0, 1, 0, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 106001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3344, 1, 0, 1, 0, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 110601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3345, 1, 0, 1, 0, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 110601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3346, 1, 0, 1, 0, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 110601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3347, 1, 0, 1, 0, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 110601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3348, 1, 0, 1, 0, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 110601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3349, 4, 0, 1, 0, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 110601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3350, 8, 0, 1, 0, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 110601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3351, 1, 0, 1, 0, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 110601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3352, 1, 0, 1, 0, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 110601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3353, 1, 0, 1, 0, "日が落ちたら\n線香花火\nやりたいわぁ♪", 110601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3354, 1, 0, 1, 0, "木陰でのんびり\nお昼寝…\n最高やね～", 110601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3355, 4, 0, 1, 0, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 110601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3356, 8, 0, 1, 0, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 110601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3357, 1, 1, 1, 0, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 110601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3358, 1, 2, 1, 0, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 110601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3359, 1, 3, 1, 0, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 110601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3361, 1, 0, 1, 0, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 110601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3362, 1, 0, 1, 0, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 110601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3363, 1, 0, 1, 0, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 110601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3364, 1, 0, 1, 0, "これで\n冷えた身体も\nポカポカやねぇ", 110601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3366, 1, 0, 1, 0, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 110601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3367, 1, 0, 1, 0, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 110601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3369, 1, 0, 1, 0, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 110601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3370, 1, 0, 1, 0, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 110601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3371, 1, 0, 1, 0, "真似したくなる\n人がここには\n大勢いますね", 107001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3372, 1, 0, 1, 0, "偽物を一人\n紛れこませました\n…冗談です", 107001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3373, 1, 0, 1, 0, "動物に変身して\nその辺りを\n散歩してきます", 107001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3374, 1, 0, 1, 0, "失った記憶は\nどの程度回復するの\nでしょうね", 107001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3375, 1, 0, 1, 0, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 107001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3376, 4, 0, 1, 0, "次は彼に\nどんなことを\n教えましょうか…", 107001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3377, 8, 0, 1, 0, "知識はともかく\n私の力はまだ\n必要なようですね", 107001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3378, 1, 0, 1, 0, "どうしました？\n…何でもない？\n…そうですか", 107001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3379, 1, 0, 1, 0, "退屈しています\n芸でもして\n楽しませなさい", 107001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3380, 1, 0, 1, 0, "研究のためです\n少し話し相手に\nなりなさい", 107001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3381, 1, 0, 1, 0, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 107001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3382, 4, 0, 1, 0, "貴方にはまだ色々と\n教えるべきことが\nありますね", 107001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3383, 8, 0, 1, 0, "随分多くの女性と\n知り合いなのですね\n驚きました", 107001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3384, 1, 1, 1, 0, "…\nおはようございます\n…まだ朝ですよ", 107001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3385, 1, 2, 1, 0, "食事にします\n…どうしました？\n早く持ってきなさい", 107001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3386, 1, 3, 1, 0, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 107001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3388, 1, 0, 1, 0, "マサキより\n座り心地は\nいいですね", 107001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3389, 1, 0, 1, 0, "意外に複雑な\n構造ですね", 107001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3390, 1, 0, 1, 0, "真似るなら\n香りまで\n再現しなくては", 107001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3391, 1, 0, 1, 0, "発火現象の\n模倣…なかなか\n難しいものです", 107001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3393, 1, 0, 1, 0, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 107001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3394, 1, 0, 1, 0, "暇つぶしには\nちょうどよさそうです\n…", 107001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3396, 1, 0, 1, 0, "学院かぁ…\n友達の作り方の\n授業はないかな？", 110701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3397, 1, 0, 1, 0, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 110701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3398, 1, 0, 1, 0, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 110701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3399, 1, 0, 1, 0, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 110701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3400, 1, 0, 1, 0, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 110701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3401, 4, 0, 1, 0, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 110701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3402, 8, 0, 1, 0, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 110701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3403, 1, 0, 1, 0, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 110701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3404, 1, 0, 1, 0, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 110701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3405, 1, 0, 1, 0, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 110701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3406, 1, 0, 1, 0, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 110701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3407, 4, 0, 1, 0, "学院でも、\nＢＢ団の活動を\nしていきましょうね♪", 110701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3408, 8, 0, 1, 0, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 110701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3409, 1, 1, 1, 0, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 110701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3410, 1, 2, 1, 0, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 110701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3411, 1, 3, 1, 0, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 110701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3413, 1, 0, 1, 0, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 110701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3414, 1, 0, 1, 0, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 110701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3415, 1, 0, 1, 0, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 110701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3416, 1, 0, 1, 0, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 110701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3418, 1, 0, 1, 0, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 110701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3419, 1, 0, 1, 0, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 110701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3421, 1, 0, 1, 0, "あんた、何してんの。\nいいけど、なんでも", 110801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3422, 1, 0, 1, 0, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 110801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3423, 1, 0, 1, 0, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 110801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3424, 1, 0, 1, 0, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 110801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3425, 1, 0, 1, 0, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 110801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3426, 4, 0, 1, 0, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 110801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3427, 8, 0, 1, 0, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 110801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3428, 1, 0, 1, 0, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 110801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3429, 1, 0, 1, 0, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 110801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3430, 1, 0, 1, 0, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 110801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3431, 1, 0, 1, 0, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 110801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3432, 4, 0, 1, 0, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 110801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3433, 8, 0, 1, 0, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 110801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3434, 1, 1, 1, 0, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 110801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3435, 1, 2, 1, 0, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 110801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3436, 1, 3, 1, 0, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 110801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3438, 1, 0, 1, 0, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 110801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3439, 1, 0, 1, 0, "なんだこれ\nかわいいかよ", 110801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3440, 1, 0, 1, 0, "ふーん。\nこの匂い…\nなんかいいじゃん", 110801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3441, 1, 0, 1, 0, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 110801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3443, 1, 0, 1, 0, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 110801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3444, 1, 0, 1, 0, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 110801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3446, 1, 0, 1, 0, "イタズラってなにを\nしたらいいのかな？", 111101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3447, 1, 0, 1, 0, "カップケーキ\n上手に作れました\n食べてくれるかな…", 111101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3448, 1, 0, 1, 0, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 111101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3449, 1, 0, 1, 0, "オバケ…いないよね？\n怖くなんてないけど！", 111101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3450, 1, 0, 1, 0, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 111101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3451, 4, 0, 1, 0, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 111101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3452, 8, 0, 1, 0, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 111101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3453, 1, 0, 1, 0, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 111101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3454, 1, 0, 1, 0, "触りましたね？\nイタズラしましたね？\nへんた～い！", 111101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3455, 1, 0, 1, 0, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 111101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3456, 1, 0, 1, 0, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 111101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3457, 4, 0, 1, 0, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 111101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3458, 8, 0, 1, 0, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 111101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3459, 1, 1, 1, 0, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 111101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3460, 1, 2, 1, 0, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 111101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3461, 1, 3, 1, 0, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 111101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3463, 1, 0, 1, 0, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 111101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3464, 1, 0, 1, 0, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 111101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3465, 1, 0, 1, 0, "甘い匂いがいっぱい…\nお菓子の香りかな？", 111101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3466, 1, 0, 1, 0, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 111101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3468, 1, 0, 1, 0, "ニンジンもこれくらい\nおいしければ\nいいのに…", 111101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3469, 1, 0, 1, 0, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 111101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3471, 1, 0, 1, 0, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 111101, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3473, 1, 0, 1, 0, "たくさんいたずら\nできるから\nハロウィン大好き！", 111201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3474, 1, 0, 1, 0, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 111201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3475, 1, 0, 1, 0, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 111201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3476, 1, 0, 1, 0, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 111201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3477, 1, 0, 1, 0, "どうすればみんなを\nびっくりさせられる\nかな～？", 111201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3478, 4, 0, 1, 0, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 111201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3479, 8, 0, 1, 0, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 111201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3480, 1, 0, 1, 0, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 111201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3481, 1, 0, 1, 0, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 111201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3482, 1, 0, 1, 0, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 111201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3483, 1, 0, 1, 0, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 111201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3484, 4, 0, 1, 0, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 111201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3485, 8, 0, 1, 0, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 111201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3486, 1, 1, 1, 0, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 111201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3487, 1, 2, 1, 0, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 111201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3488, 1, 3, 1, 0, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 111201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3490, 1, 0, 1, 0, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 111201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3491, 1, 0, 1, 0, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 111201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3492, 1, 0, 1, 0, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 111201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3493, 1, 0, 1, 0, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 111201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3495, 1, 0, 1, 0, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 111201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3496, 1, 0, 1, 0, "本のなかに\nいたずらのヒント\nないかな～？", 111201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3498, 1, 0, 1, 0, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 111201, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3500, 12, 0, 1, 0, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 101101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3501, 12, 0, 1, 0, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 101101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3502, 1, 0, 1, 0, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 111301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3503, 1, 0, 1, 0, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 111301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3504, 1, 0, 1, 0, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 111301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3505, 1, 0, 1, 0, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 111301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3506, 1, 0, 1, 0, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 111301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3507, 4, 0, 1, 0, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 111301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3508, 8, 0, 1, 0, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 111301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3509, 1, 0, 1, 0, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 111301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3510, 1, 0, 1, 0, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 111301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3511, 1, 0, 1, 0, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 111301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3512, 1, 0, 1, 0, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 111301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3513, 4, 0, 1, 0, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 111301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3514, 8, 0, 1, 0, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 111301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3515, 1, 1, 1, 0, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 111301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3516, 1, 2, 1, 0, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 111301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3517, 1, 3, 1, 0, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 111301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3519, 1, 0, 1, 0, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 111301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3520, 1, 0, 1, 0, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 111301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3521, 1, 0, 1, 0, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 111301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3522, 1, 0, 1, 0, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 111301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3524, 1, 0, 1, 0, "おいしい～♪\nおにいちゃんも\n食べようよー！", 111301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3525, 1, 0, 1, 0, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 111301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3527, 1, 0, 1, 0, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 111301, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3529, 1, 0, 1, 0, "ここ、好きだよ。\nみんな優しいから…", 111401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3530, 1, 0, 1, 0, "なんかさみしいな", 111401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3531, 1, 0, 1, 0, "知らない人怖いな", 111401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3532, 1, 0, 1, 0, "お部屋おっきいねー", 111401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3533, 1, 0, 1, 0, "ルナね…\nお昼寝したいかも", 111401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3534, 4, 0, 1, 0, "おにいさん！\n今日は何して遊ぶの？", 111401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3535, 8, 0, 1, 0, "あのね、\nお料理教えてほしい！", 111401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3536, 1, 0, 1, 0, "あっ、おにいさんだ！", 111401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3537, 1, 0, 1, 0, "ルナは元気だよ！\nいいこだもん！", 111401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3538, 1, 0, 1, 0, "今日は何しようかな", 111401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3539, 1, 0, 1, 0, "ルナと遊んでよ！", 111401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3540, 4, 0, 1, 0, "ねーねー、\nルナとお出かけして！", 111401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3541, 8, 0, 1, 0, "おにいさんと、\n遊びたいなー！", 111401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3542, 1, 1, 1, 0, "ふふっ！\nなんかまぶしいね！", 111401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3543, 1, 2, 1, 0, "ルナ、お腹空いたな", 111401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3544, 1, 3, 1, 0, "どこに行こうかな？", 111401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3546, 1, 0, 1, 0, "ルナ、これ好きだな", 111401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3547, 1, 0, 1, 0, "かわいい！　好き！", 111401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3548, 1, 0, 1, 0, "なんかいい匂い！", 111401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3549, 1, 0, 1, 0, "あったかいねー", 111401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3551, 1, 0, 1, 0, "ふふっ\nおいしいね", 111401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3552, 1, 0, 1, 0, "ルナ、\n眠くなっちゃう…", 111401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3554, 12, 0, 1, 0, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 105201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3555, 12, 0, 1, 0, "あなたとなら\nどんなことだって\nできる気がするわ♪", 105201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3556, 1, 0, 1, 0, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 106501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3557, 1, 0, 1, 0, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 106501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3558, 1, 0, 1, 0, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 106501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3559, 1, 0, 1, 0, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 106501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3560, 1, 0, 1, 0, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 106501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3561, 4, 0, 1, 0, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 106501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3562, 8, 0, 1, 0, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 106501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3563, 1, 0, 1, 0, "よっす！\nなんかオイシイ話でも\nあったりしない？", 106501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3564, 1, 0, 1, 0, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 106501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3565, 1, 0, 1, 0, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 106501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3566, 1, 0, 1, 0, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 106501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3567, 4, 0, 1, 0, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 106501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3568, 8, 0, 1, 0, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 106501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3569, 1, 1, 1, 0, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 106501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3570, 1, 2, 1, 0, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 106501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3571, 1, 3, 1, 0, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 106501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3573, 1, 0, 1, 0, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 106501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3574, 1, 0, 1, 0, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 106501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3575, 1, 0, 1, 0, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 106501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3576, 1, 0, 1, 0, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 106501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3578, 1, 0, 1, 0, "いつでも全力で\nケンカするために\n力を蓄えないとな", 106501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3579, 1, 0, 1, 0, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 106501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3581, 1, 0, 1, 0, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 111501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3582, 1, 0, 1, 0, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 111501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3583, 1, 0, 1, 0, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 111501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3584, 1, 0, 1, 0, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 111501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3585, 1, 0, 1, 0, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 111501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3586, 4, 0, 1, 0, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 111501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3587, 8, 0, 1, 0, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 111501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3588, 1, 0, 1, 0, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 111501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3589, 1, 0, 1, 0, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 111501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3590, 1, 0, 1, 0, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 111501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3591, 1, 0, 1, 0, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 111501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3592, 4, 0, 1, 0, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 111501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3593, 8, 0, 1, 0, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 111501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3594, 1, 1, 1, 0, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 111501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3595, 1, 2, 1, 0, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 111501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3596, 1, 3, 1, 0, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 111501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3598, 1, 0, 1, 0, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 111501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3599, 1, 0, 1, 0, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 111501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3600, 1, 0, 1, 0, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 111501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3601, 1, 0, 1, 0, "体が温まってくると\nひと暴れしたくなるな", 111501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3603, 1, 0, 1, 0, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 111501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3604, 1, 0, 1, 0, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 111501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3606, 1, 0, 1, 0, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 111501, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3607, 1, 0, 1, 0, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 111501, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3608, 1, 0, 1, 0, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 111601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3609, 1, 0, 1, 0, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 111601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3610, 1, 0, 1, 0, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 111601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3611, 1, 0, 1, 0, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 111601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3612, 1, 0, 1, 0, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 111601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3613, 4, 0, 1, 0, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 111601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3614, 8, 0, 1, 0, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 111601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3615, 1, 0, 1, 0, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 111601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3616, 1, 0, 1, 0, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 111601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3617, 1, 0, 1, 0, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 111601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3618, 1, 0, 1, 0, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 111601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3619, 4, 0, 1, 0, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 111601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3620, 8, 0, 1, 0, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 111601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3621, 1, 1, 1, 0, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 111601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3622, 1, 2, 1, 0, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 111601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3623, 1, 3, 1, 0, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 111601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3625, 1, 0, 1, 0, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 111601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3626, 1, 0, 1, 0, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 111601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3627, 1, 0, 1, 0, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 111601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3628, 1, 0, 1, 0, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 111601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3630, 1, 0, 1, 0, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 111601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3631, 1, 0, 1, 0, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 111601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3633, 1, 0, 1, 0, "面白ーい！\nこの演出、ライブでも\nできないかな？", 111601, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3634, 1, 0, 1, 0, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 111601, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3635, 12, 0, 1, 0, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 101801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3636, 12, 0, 1, 0, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 101801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3637, 1, 0, 1, 0, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 111701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3638, 1, 0, 1, 0, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 111701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3639, 1, 0, 1, 0, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 111701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3640, 1, 0, 1, 0, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 111701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3641, 1, 0, 1, 0, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 111701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3642, 4, 0, 1, 0, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 111701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3643, 8, 0, 1, 0, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 111701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3644, 1, 0, 1, 0, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 111701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3645, 1, 0, 1, 0, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 111701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3646, 1, 0, 1, 0, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 111701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3647, 1, 0, 1, 0, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 111701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3648, 4, 0, 1, 0, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 111701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3649, 8, 0, 1, 0, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 111701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3650, 1, 1, 1, 0, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 111701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3651, 1, 2, 1, 0, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 111701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3652, 1, 3, 1, 0, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 111701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3654, 1, 0, 1, 0, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 111701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3655, 1, 0, 1, 0, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 111701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3656, 1, 0, 1, 0, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 111701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3657, 1, 0, 1, 0, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 111701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3659, 1, 0, 1, 0, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 111701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3660, 1, 0, 1, 0, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 111701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3662, 1, 0, 1, 0, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 111701, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3663, 1, 0, 1, 0, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 111701, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3664, 1, 0, 1, 0, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 112001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3665, 1, 0, 1, 0, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 112001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3666, 1, 0, 1, 0, "初日の出って\n特別な感じがして\nいいわよね…", 112001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3667, 1, 0, 1, 0, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 112001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3668, 1, 0, 1, 0, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 112001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3669, 4, 0, 1, 0, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 112001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3670, 8, 0, 1, 0, "今年もあいつと\n居られること…\n結構うれしいわ", 112001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3671, 1, 0, 1, 0, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 112001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3672, 1, 0, 1, 0, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 112001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3673, 1, 0, 1, 0, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 112001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3674, 1, 0, 1, 0, "門松飾るから\nちょっと手伝って\nくれる？", 112001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3675, 4, 0, 1, 0, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 112001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3676, 8, 0, 1, 0, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 112001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3677, 1, 1, 1, 0, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 112001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3678, 1, 2, 1, 0, "おせちに飽きたら\n次は何を食べよう\nかしら…", 112001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3679, 1, 3, 1, 0, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 112001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3681, 1, 0, 1, 0, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 112001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3682, 1, 0, 1, 0, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 112001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3683, 1, 0, 1, 0, "いい匂いね\n落ち着いて\n眠れそう…", 112001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3684, 1, 0, 1, 0, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 112001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3686, 1, 0, 1, 0, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 112001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3687, 1, 0, 1, 0, "魔物を使わない\nおせちを\n調べておかなきゃ…", 112001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3689, 1, 0, 1, 0, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 112101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3690, 1, 0, 1, 0, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 112101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3691, 1, 0, 1, 0, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 112101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3692, 1, 0, 1, 0, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 112101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3693, 1, 0, 1, 0, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 112101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3694, 4, 0, 1, 0, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 112101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3695, 8, 0, 1, 0, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 112101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3696, 1, 0, 1, 0, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 112101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3697, 1, 0, 1, 0, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 112101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3698, 1, 0, 1, 0, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 112101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3699, 1, 0, 1, 0, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 112101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3700, 4, 0, 1, 0, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 112101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3701, 8, 0, 1, 0, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 112101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3702, 1, 1, 1, 0, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 112101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3703, 1, 2, 1, 0, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 112101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3704, 1, 3, 1, 0, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 112101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3706, 1, 0, 1, 0, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 112101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3707, 1, 0, 1, 0, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 112101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3708, 1, 0, 1, 0, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 112101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3709, 1, 0, 1, 0, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 112101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3711, 1, 0, 1, 0, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 112101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3712, 1, 0, 1, 0, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 112101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3714, 12, 0, 1, 0, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 103401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3715, 12, 0, 1, 0, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 103401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3716, 1, 0, 1, 0, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 111901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3717, 1, 0, 1, 0, "年を越したあとに\n食べるおそばも\nおいしいです", 111901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3718, 1, 0, 1, 0, "甘酒を飲むと\n心まで温まる\n気がいたします", 111901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3719, 1, 0, 1, 0, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 111901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3720, 1, 0, 1, 0, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 111901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3721, 4, 0, 1, 0, "主さまの温もりは\n心まで温めてくれる\nようです", 111901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3722, 8, 0, 1, 0, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 111901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3723, 1, 0, 1, 0, "主さま、この晴れ着は\n似合っている\nでしょうか？", 111901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3724, 1, 0, 1, 0, "お餅はゆっくりと\n召し上がって\nくださいまし", 111901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3725, 1, 0, 1, 0, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 111901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3726, 1, 0, 1, 0, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 111901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3727, 4, 0, 1, 0, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 111901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3728, 8, 0, 1, 0, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 111901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3729, 1, 1, 1, 0, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 111901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3730, 1, 2, 1, 0, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 111901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3731, 1, 3, 1, 0, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 111901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3733, 1, 0, 1, 0, "ふかふか…\nこのまま\n寝てしまいそうです", 111901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3734, 1, 0, 1, 0, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 111901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3735, 1, 0, 1, 0, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 111901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3736, 1, 0, 1, 0, "風邪をひきませんよう\n暖かくいたしましょう", 111901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3738, 1, 0, 1, 0, "食べ過ぎは\n正月太りの\nもとだというのに…", 111901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3739, 1, 0, 1, 0, "主さまのために\n参拝の作法を\n予習しておきましょう", 111901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3741, 1, 0, 1, 0, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 112201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3742, 1, 0, 1, 0, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 112201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3743, 1, 0, 1, 0, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 112201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3744, 1, 0, 1, 0, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 112201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3745, 1, 0, 1, 0, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 112201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3746, 4, 0, 1, 0, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 112201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3747, 8, 0, 1, 0, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 112201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3748, 1, 0, 1, 0, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 112201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3749, 1, 0, 1, 0, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 112201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3750, 1, 0, 1, 0, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 112201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3751, 1, 0, 1, 0, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 112201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3752, 4, 0, 1, 0, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 112201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3753, 8, 0, 1, 0, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 112201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3754, 1, 1, 1, 0, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 112201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3755, 1, 2, 1, 0, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 112201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3756, 1, 3, 1, 0, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 112201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3758, 1, 0, 1, 0, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 112201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3759, 1, 0, 1, 0, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 112201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3760, 1, 0, 1, 0, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 112201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3761, 1, 0, 1, 0, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 112201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3763, 1, 0, 1, 0, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 112201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3764, 1, 0, 1, 0, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 112201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3766, 1, 0, 1, 0, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 112301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3767, 1, 0, 1, 0, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 112301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3768, 1, 0, 1, 0, "ふふ…\n今日はなんだか\n調子がいいかも", 112301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3769, 1, 0, 1, 0, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 112301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3770, 1, 0, 1, 0, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 112301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3771, 4, 0, 1, 0, "あの人がいると\nなんだかホッとする\n不思議…", 112301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3772, 8, 0, 1, 0, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 112301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3773, 1, 0, 1, 0, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 112301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3774, 1, 0, 1, 0, "よかったら魔法の\n練習に付き合って\nくれませんか？", 112301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3775, 1, 0, 1, 0, "あの…\nこの格好どうですか？\n似合ってると…いいな", 112301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3776, 1, 0, 1, 0, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 112301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3777, 4, 0, 1, 0, "私、あなたのそばに\nいられたら、それで…", 112301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3778, 8, 0, 1, 0, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 112301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3779, 1, 1, 1, 0, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 112301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3780, 1, 2, 1, 0, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 112301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3781, 1, 3, 1, 0, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 112301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3783, 1, 0, 1, 0, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 112301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3784, 1, 0, 1, 0, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 112301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3785, 1, 0, 1, 0, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 112301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3786, 1, 0, 1, 0, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 112301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3788, 1, 0, 1, 0, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 112301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3789, 1, 0, 1, 0, "ふふ…\n魔法少女になっても\n読書はやめられないな", 112301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3791, 12, 0, 1, 0, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 101001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3792, 12, 0, 1, 0, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 101001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3793, 1, 0, 1, 0, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 180401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3794, 1, 0, 1, 0, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 180401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3795, 1, 0, 1, 0, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 180401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3796, 1, 0, 1, 0, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 180401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3797, 1, 0, 1, 0, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 180401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3798, 4, 0, 1, 0, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 180401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3799, 8, 0, 1, 0, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 180401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3800, 1, 0, 1, 0, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 180401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3801, 1, 0, 1, 0, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 180401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3802, 1, 0, 1, 0, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 180401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3803, 1, 0, 1, 0, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 180401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3804, 4, 0, 1, 0, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 180401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3805, 8, 0, 1, 0, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 180401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3806, 1, 1, 1, 0, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 180401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3807, 1, 2, 1, 0, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 180401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3808, 1, 3, 1, 0, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 180401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3810, 1, 0, 1, 0, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 180401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3811, 1, 0, 1, 0, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 180401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3812, 1, 0, 1, 0, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 180401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3813, 1, 0, 1, 0, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 180401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3815, 1, 0, 1, 0, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 180401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3816, 1, 0, 1, 0, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 180401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3818, 1, 0, 1, 0, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 112401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3819, 1, 0, 1, 0, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 112401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3820, 1, 0, 1, 0, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 112401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3821, 1, 0, 1, 0, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 112401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3822, 1, 0, 1, 0, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 112401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3823, 4, 0, 1, 0, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 112401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3824, 8, 0, 1, 0, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 112401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3825, 1, 0, 1, 0, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 112401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3826, 1, 0, 1, 0, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 112401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3827, 1, 0, 1, 0, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 112401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3828, 1, 0, 1, 0, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 112401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3829, 4, 0, 1, 0, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 112401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3830, 8, 0, 1, 0, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 112401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3831, 1, 1, 1, 0, "おはようございます！\n今日も一日\n頑張りましょうね！", 112401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3832, 1, 2, 1, 0, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 112401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3833, 1, 3, 1, 0, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 112401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3835, 1, 0, 1, 0, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 112401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3836, 1, 0, 1, 0, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 112401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3837, 1, 0, 1, 0, "リラックスできる\nいい香りですね～♪", 112401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3838, 1, 0, 1, 0, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 112401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3840, 1, 0, 1, 0, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 112401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3841, 1, 0, 1, 0, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 112401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3843, 1, 0, 1, 0, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 112601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3844, 1, 0, 1, 0, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 112601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3845, 1, 0, 1, 0, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 112601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3846, 1, 0, 1, 0, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 112601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3847, 1, 0, 1, 0, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 112601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3848, 4, 0, 1, 0, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 112601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3849, 8, 0, 1, 0, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 112601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3850, 1, 0, 1, 0, "なになにー？\nミオちゃんに\n何か用かな～？", 112601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3851, 1, 0, 1, 0, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 112601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3852, 1, 0, 1, 0, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 112601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3853, 1, 0, 1, 0, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 112601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3854, 4, 0, 1, 0, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 112601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3855, 8, 0, 1, 0, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 112601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3856, 1, 1, 1, 0, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 112601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3857, 1, 2, 1, 0, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 112601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3858, 1, 3, 1, 0, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 112601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3860, 1, 0, 1, 0, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 112601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3861, 1, 0, 1, 0, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 112601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3862, 1, 0, 1, 0, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 112601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3863, 1, 0, 1, 0, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 112601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3865, 1, 0, 1, 0, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 112601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3866, 1, 0, 1, 0, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 112601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3868, 12, 0, 1, 0, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 100101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3869, 12, 0, 1, 0, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 100101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3870, 12, 0, 1, 0, "騎士クンの前で\n好きって言えるように\n練習しないと…", 100201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3871, 12, 0, 1, 0, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 100201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3872, 12, 0, 1, 0, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 100301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3873, 12, 0, 1, 0, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 100301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3874, 1, 0, 1, 0, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 112501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3875, 1, 0, 1, 0, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 112501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3876, 1, 0, 1, 0, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 112501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3877, 1, 0, 1, 0, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 112501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3878, 1, 0, 1, 0, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 112501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3879, 4, 0, 1, 0, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 112501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3880, 8, 0, 1, 0, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 112501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3881, 1, 0, 1, 0, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 112501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3882, 1, 0, 1, 0, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 112501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3883, 1, 0, 1, 0, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 112501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3884, 1, 0, 1, 0, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 112501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3885, 4, 0, 1, 0, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 112501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3886, 8, 0, 1, 0, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 112501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3887, 1, 1, 1, 0, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 112501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3888, 1, 2, 1, 0, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 112501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3889, 1, 3, 1, 0, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 112501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3891, 1, 0, 1, 0, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 112501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3892, 1, 0, 1, 0, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 112501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3893, 1, 0, 1, 0, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 112501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3894, 1, 0, 1, 0, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 112501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3896, 1, 0, 1, 0, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 112501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3897, 1, 0, 1, 0, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 112501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3899, 1, 0, 1, 0, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 111001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3900, 1, 0, 1, 0, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 111001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3901, 1, 0, 1, 0, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 111001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3902, 1, 0, 1, 0, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 111001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3903, 1, 0, 1, 0, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 111001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3904, 4, 0, 1, 0, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 111001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3905, 8, 0, 1, 0, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 111001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3906, 1, 0, 1, 0, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 111001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3907, 1, 0, 1, 0, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 111001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3908, 1, 0, 1, 0, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 111001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3909, 1, 0, 1, 0, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 111001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3910, 4, 0, 1, 0, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 111001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3911, 8, 0, 1, 0, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 111001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3912, 1, 1, 1, 0, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 111001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3913, 1, 2, 1, 0, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 111001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3914, 1, 3, 1, 0, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 111001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3916, 1, 0, 1, 0, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 111001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3917, 1, 0, 1, 0, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 111001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3918, 1, 0, 1, 0, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 111001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3919, 1, 0, 1, 0, "炎はいいぞ\nそこには人類発展の\nドラマがある", 111001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3921, 1, 0, 1, 0, "フオオー！\n次から次へと固形食物\n原理気になるー！", 111001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3922, 1, 0, 1, 0, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 111001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3924, 12, 0, 1, 0, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 101201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3925, 12, 0, 1, 0, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 101201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3933, 1, 0, 1, 0, "ちぇるーん☆", 192101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3951, 1, 0, 1, 0, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 110901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3952, 1, 0, 1, 0, "どこかに楽しいもの\n落ちてないかにゃー", 110901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3953, 1, 0, 1, 0, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 110901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3954, 1, 0, 1, 0, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 110901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3955, 1, 0, 1, 0, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 110901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3956, 4, 0, 1, 0, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 110901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3957, 8, 0, 1, 0, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 110901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3958, 1, 0, 1, 0, "え、なんですか？\n告白だったら\n間に合ってますけど", 110901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3959, 1, 0, 1, 0, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 110901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3960, 1, 0, 1, 0, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 110901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3961, 1, 0, 1, 0, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 110901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3962, 4, 0, 1, 0, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 110901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3963, 8, 0, 1, 0, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 110901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3964, 1, 1, 1, 0, "おはちぇる～ん☆\nハイ、ご一緒に～？", 110901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3965, 1, 2, 1, 0, "今日のランチは\nどこで\n映えよっかにゃー", 110901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3966, 1, 3, 1, 0, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 110901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3968, 1, 0, 1, 0, "ふかふかチエルン\n略して、ふかルン♪", 110901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3969, 1, 0, 1, 0, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 110901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3970, 1, 0, 1, 0, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 110901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3971, 1, 0, 1, 0, "マシュマロは\n焼かない方のが\nかわいくないです？", 110901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3973, 1, 0, 1, 0, "チエル食べても\n太らない体質\nなんでぇ～！", 110901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3974, 1, 0, 1, 0, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 110901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3976, 1, 0, 1, 0, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 112701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3977, 1, 0, 1, 0, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 112701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3978, 1, 0, 1, 0, "あたしの代わりに誰か\n働いてくれないかね", 112701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3979, 1, 0, 1, 0, "労働の後に食べる\nあんパン最高～", 112701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3980, 1, 0, 1, 0, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 112701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3981, 4, 0, 1, 0, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 112701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3982, 8, 0, 1, 0, "次の休みはあいつと\n一日中だらだら\nしたいな～", 112701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3983, 1, 0, 1, 0, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 112701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3984, 1, 0, 1, 0, "お、差し入れ？\n気が利くねぇ\nあんがと", 112701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3985, 1, 0, 1, 0, "新しい依頼？\n今日はもう無理～\n閉店で～す", 112701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3986, 1, 0, 1, 0, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 112701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3987, 4, 0, 1, 0, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 112701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3988, 8, 0, 1, 0, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 112701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3989, 1, 1, 1, 0, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 112701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3990, 1, 2, 1, 0, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 112701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3991, 1, 3, 1, 0, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 112701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3993, 1, 0, 1, 0, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 112701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3994, 1, 0, 1, 0, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 112701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3995, 1, 0, 1, 0, "あんパンの香りの\nアロマとか\nあればいいのにな～", 112701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3996, 1, 0, 1, 0, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 112701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3998, 1, 0, 1, 0, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 112701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (3999, 1, 0, 1, 0, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 112701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4001, 12, 0, 1, 0, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 104601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4002, 12, 0, 1, 0, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 104601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4003, 1, 0, 1, 0, "フロンティア\nスピリッツを\nお見せするべ！", 112801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4004, 1, 0, 1, 0, "この鎧を着ると\n気が引き締まる\n感じがするべ", 112801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4005, 1, 0, 1, 0, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 112801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4006, 1, 0, 1, 0, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 112801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4007, 1, 0, 1, 0, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 112801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4008, 4, 0, 1, 0, "みんなオラより\n若ぇのに働いてて\n立派だべな", 112801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4009, 8, 0, 1, 0, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 112801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4010, 1, 0, 1, 0, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 112801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4011, 1, 0, 1, 0, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 112801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4012, 1, 0, 1, 0, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 112801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4013, 1, 0, 1, 0, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 112801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4014, 4, 0, 1, 0, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 112801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4015, 8, 0, 1, 0, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 112801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4016, 1, 1, 1, 0, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 112801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4017, 1, 2, 1, 0, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 112801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4018, 1, 3, 1, 0, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 112801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4020, 1, 0, 1, 0, "牧草みたいに\nふっかふかだべ～", 112801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4021, 1, 0, 1, 0, "この子、目の感じが\nエリザベスに似てるべ", 112801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4022, 1, 0, 1, 0, "いい匂いだべ～\n疲れた体に\nしみるべな～", 112801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4023, 1, 0, 1, 0, "あったけぇな～\n牧場にも\n置いてみっかな～", 112801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4025, 1, 0, 1, 0, "うまいべ！\n生産者のこだわりを\n感じるべさ", 112801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4026, 1, 0, 1, 0, "酪農も戦いも\nまずは知識だべ！", 112801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4028, 1, 0, 1, 0, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 113001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4029, 1, 0, 1, 0, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 113001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4030, 1, 0, 1, 0, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 113001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4031, 1, 0, 1, 0, "なんだか先輩の\n気配がするような…\n気のせいかな？", 113001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4032, 1, 0, 1, 0, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 113001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4033, 4, 0, 1, 0, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 113001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4034, 8, 0, 1, 0, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 113001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4035, 1, 0, 1, 0, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 113001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4036, 1, 0, 1, 0, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 113001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4037, 1, 0, 1, 0, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 113001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4038, 1, 0, 1, 0, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 113001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4039, 4, 0, 1, 0, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 113001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4040, 8, 0, 1, 0, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 113001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4041, 1, 1, 1, 0, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 113001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4042, 1, 2, 1, 0, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 113001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4043, 1, 3, 1, 0, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 113001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4045, 1, 0, 1, 0, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 113001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4046, 1, 0, 1, 0, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 113001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4047, 1, 0, 1, 0, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 113001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4048, 1, 0, 1, 0, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 113001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4050, 1, 0, 1, 0, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 113001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4051, 1, 0, 1, 0, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 113001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4053, 1, 0, 1, 0, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 180501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4054, 1, 0, 1, 0, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 180501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4055, 1, 0, 1, 0, "月々の食費が\n増えてしまいました\n申し訳ございません…", 180501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4056, 1, 0, 1, 0, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 180501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4057, 1, 0, 1, 0, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 180501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4058, 4, 0, 1, 0, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 180501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4059, 8, 0, 1, 0, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 180501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4060, 1, 0, 1, 0, "おでかけですか？\nではわたくしも\nお供いたします", 180501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4061, 1, 0, 1, 0, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 180501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4062, 1, 0, 1, 0, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 180501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4063, 1, 0, 1, 0, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 180501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4064, 4, 0, 1, 0, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 180501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4065, 8, 0, 1, 0, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 180501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4066, 1, 1, 1, 0, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 180501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4067, 1, 2, 1, 0, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 180501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4068, 1, 3, 1, 0, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 180501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4070, 1, 0, 1, 0, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 180501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4071, 1, 0, 1, 0, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 180501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4072, 1, 0, 1, 0, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 180501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4073, 1, 0, 1, 0, "主さまの心と\n繋がったときのような\n温もりを感じます", 180501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4075, 1, 0, 1, 0, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 180501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4076, 1, 0, 1, 0, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 180501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4078, 12, 0, 1, 0, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 104801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4079, 12, 0, 1, 0, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 104801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4080, 1, 0, 1, 0, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 112901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4081, 1, 0, 1, 0, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 112901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4082, 1, 0, 1, 0, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 112901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4083, 1, 0, 1, 0, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 112901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4084, 1, 0, 1, 0, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 112901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4085, 4, 0, 1, 0, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 112901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4086, 8, 0, 1, 0, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 112901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4087, 1, 0, 1, 0, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 112901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4088, 1, 0, 1, 0, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 112901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4089, 1, 0, 1, 0, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 112901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4090, 1, 0, 1, 0, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 112901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4091, 4, 0, 1, 0, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 112901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4092, 8, 0, 1, 0, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 112901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4093, 1, 1, 1, 0, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 112901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4094, 1, 2, 1, 0, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 112901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4095, 1, 3, 1, 0, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 112901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4097, 1, 0, 1, 0, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 112901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4098, 1, 0, 1, 0, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 112901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4099, 1, 0, 1, 0, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 112901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4100, 1, 0, 1, 0, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 112901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4102, 1, 0, 1, 0, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 112901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4103, 1, 0, 1, 0, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 112901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4105, 1, 0, 1, 0, "退屈です…\nなにか楽しいことは\nないですか？", 106601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4106, 1, 0, 1, 0, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 106601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4107, 1, 0, 1, 0, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 106601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4108, 1, 0, 1, 0, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 106601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4109, 1, 0, 1, 0, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 106601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4110, 4, 0, 1, 0, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 106601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4111, 8, 0, 1, 0, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 106601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4112, 1, 0, 1, 0, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 106601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4113, 1, 0, 1, 0, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 106601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4114, 1, 0, 1, 0, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 106601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4115, 1, 0, 1, 0, "お兄さんは\nヒマそうですね…\nうらやましいです", 106601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4116, 4, 0, 1, 0, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 106601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4117, 8, 0, 1, 0, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 106601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4118, 1, 1, 1, 0, "デキるギャングの\n朝は早いのです…\nふぁあ…", 106601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4119, 1, 2, 1, 0, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 106601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4120, 1, 3, 1, 0, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 106601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4122, 1, 0, 1, 0, "はぁ～…\nもうここから一歩も\n動きたくないです", 106601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4123, 1, 0, 1, 0, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 106601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4124, 1, 0, 1, 0, "なんかおしゃれな\n匂いがするですね…", 106601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4125, 1, 0, 1, 0, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 106601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4127, 1, 0, 1, 0, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 106601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4128, 1, 0, 1, 0, "ふーむ…\nもっと楽しい本は\nないですかね？", 106601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4130, 1, 0, 1, 0, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 113201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4131, 1, 0, 1, 0, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 113201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4132, 1, 0, 1, 0, "アターック！\nいや、もっとこう…\nアタァーック！", 113201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4133, 1, 0, 1, 0, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 113201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4134, 1, 0, 1, 0, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 113201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4135, 4, 0, 1, 0, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 113201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4136, 8, 0, 1, 0, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 113201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4137, 1, 0, 1, 0, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 113201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4138, 1, 0, 1, 0, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 113201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4139, 1, 0, 1, 0, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 113201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4140, 1, 0, 1, 0, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 113201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4141, 4, 0, 1, 0, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 113201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4142, 8, 0, 1, 0, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 113201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4143, 1, 1, 1, 0, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 113201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4144, 1, 2, 1, 0, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 113201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4145, 1, 3, 1, 0, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 113201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4147, 1, 0, 1, 0, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 113201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4148, 1, 0, 1, 0, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 113201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4149, 1, 0, 1, 0, "心地よい香りで\n我が精神も安らぐ…", 113201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4150, 1, 0, 1, 0, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 113201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4152, 1, 0, 1, 0, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 113201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4153, 1, 0, 1, 0, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 113201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4155, 1, 0, 1, 0, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 113201, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4156, 1, 0, 1, 0, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 113201, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4157, 1, 0, 1, 0, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 180201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4158, 1, 0, 1, 0, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 180201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4159, 1, 0, 1, 0, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 180201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4160, 1, 0, 1, 0, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 180201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4161, 1, 0, 1, 0, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 180201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4162, 4, 0, 1, 0, "騎士クン…\n今どこにいるの…？\n…会いたいな", 180201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4163, 8, 0, 1, 0, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 180201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4164, 1, 0, 1, 0, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 180201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4165, 1, 0, 1, 0, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 180201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4166, 1, 0, 1, 0, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 180201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4167, 1, 0, 1, 0, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 180201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4168, 4, 0, 1, 0, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 180201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4169, 8, 0, 1, 0, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 180201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4170, 1, 1, 1, 0, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 180201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4171, 1, 2, 1, 0, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 180201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4172, 1, 3, 1, 0, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 180201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4174, 1, 0, 1, 0, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 180201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4175, 1, 0, 1, 0, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 180201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4176, 1, 0, 1, 0, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 180201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4177, 1, 0, 1, 0, "温かい…\n騎士クンがくれる\n力みたい…", 180201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4179, 1, 0, 1, 0, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 180201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4180, 1, 0, 1, 0, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 180201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4182, 12, 0, 1, 0, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 104901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4183, 12, 0, 1, 0, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 104901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4184, 1, 0, 1, 0, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 113101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4185, 1, 0, 1, 0, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 113101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4186, 1, 0, 1, 0, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 113101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4187, 1, 0, 1, 0, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 113101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4188, 1, 0, 1, 0, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 113101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4189, 4, 0, 1, 0, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 113101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4190, 8, 0, 1, 0, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 113101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4191, 1, 0, 1, 0, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 113101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4192, 1, 0, 1, 0, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 113101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4193, 1, 0, 1, 0, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 113101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4194, 1, 0, 1, 0, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 113101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4195, 4, 0, 1, 0, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 113101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4196, 8, 0, 1, 0, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 113101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4197, 1, 1, 1, 0, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 113101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4198, 1, 2, 1, 0, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 113101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4199, 1, 3, 1, 0, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 113101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4201, 1, 0, 1, 0, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 113101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4202, 1, 0, 1, 0, "あんたたちも\n海を楽しみたいって\n思っているのかね", 113101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4203, 1, 0, 1, 0, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 113101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4204, 1, 0, 1, 0, "こういう格好だと\n少し冷えちまうんだよ", 113101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4206, 1, 0, 1, 0, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 113101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4207, 1, 0, 1, 0, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 113101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4209, 1, 0, 1, 0, "へえ…\nなかなかいい\n乗り心地じゃないか", 113101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4210, 1, 0, 1, 0, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 113101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4211, 1, 0, 1, 0, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 113301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4212, 1, 0, 1, 0, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 113301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4213, 1, 0, 1, 0, "今のナナカちゃんは\nたぶん水属性！", 113301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4214, 1, 0, 1, 0, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 113301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4215, 1, 0, 1, 0, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 113301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4216, 4, 0, 1, 0, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 113301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4217, 8, 0, 1, 0, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 113301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4218, 1, 0, 1, 0, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 113301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4219, 1, 0, 1, 0, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 113301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4220, 1, 0, 1, 0, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 113301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4221, 1, 0, 1, 0, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 113301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4222, 4, 0, 1, 0, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 113301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4223, 8, 0, 1, 0, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 113301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4224, 1, 1, 1, 0, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 113301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4225, 1, 2, 1, 0, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 113301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4226, 1, 3, 1, 0, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 113301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4228, 1, 0, 1, 0, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 113301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4229, 1, 0, 1, 0, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 113301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4230, 1, 0, 1, 0, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 113301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4231, 1, 0, 1, 0, "夜になると急に冷えて\n肌寒いんだよね～…", 113301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4233, 1, 0, 1, 0, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 113301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4234, 1, 0, 1, 0, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 113301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4236, 1, 0, 1, 0, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 113301, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4237, 1, 0, 1, 0, "ナナカちゃんの\n誕生～\nなんちゃって", 113301, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4238, 1, 0, 1, 0, "ファイトファイト☆\n一生懸命\n応援するね！", 113401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4239, 1, 0, 1, 0, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 113401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4240, 1, 0, 1, 0, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 113401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4241, 1, 0, 1, 0, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 113401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4242, 1, 0, 1, 0, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 113401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4243, 4, 0, 1, 0, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 113401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4244, 8, 0, 1, 0, "キミへの応援\nちゃんと\n届いてるかな…", 113401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4245, 1, 0, 1, 0, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 113401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4246, 1, 0, 1, 0, "どうしたの？\n元気がないなら\n私が応援するよ☆", 113401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4247, 1, 0, 1, 0, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 113401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4248, 1, 0, 1, 0, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 113401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4249, 4, 0, 1, 0, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 113401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4250, 8, 0, 1, 0, "キミを応援\nすることができて…\n私、すごく幸せ…", 113401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4251, 1, 1, 1, 0, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 113401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4252, 1, 2, 1, 0, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 113401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4253, 1, 3, 1, 0, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 113401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4255, 1, 0, 1, 0, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 113401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4256, 1, 0, 1, 0, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 113401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4257, 1, 0, 1, 0, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 113401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4258, 1, 0, 1, 0, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 113401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4260, 1, 0, 1, 0, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 113401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4261, 1, 0, 1, 0, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 113401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4263, 1, 0, 1, 0, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 113401, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4264, 1, 0, 1, 0, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 113401, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4265, 1, 0, 1, 0, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 113501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4266, 1, 0, 1, 0, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 113501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4267, 1, 0, 1, 0, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 113501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4268, 1, 0, 1, 0, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 113501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4269, 1, 0, 1, 0, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 113501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4270, 4, 0, 1, 0, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 113501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4271, 8, 0, 1, 0, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 113501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4272, 1, 0, 1, 0, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 113501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4273, 1, 0, 1, 0, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 113501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4274, 1, 0, 1, 0, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 113501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4275, 1, 0, 1, 0, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 113501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4276, 4, 0, 1, 0, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 113501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4277, 8, 0, 1, 0, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 113501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4278, 1, 1, 1, 0, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 113501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4279, 1, 2, 1, 0, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 113501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4280, 1, 3, 1, 0, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 113501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4282, 1, 0, 1, 0, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 113501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4283, 1, 0, 1, 0, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 113501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4284, 1, 0, 1, 0, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 113501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4285, 1, 0, 1, 0, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 113501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4287, 1, 0, 1, 0, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 113501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4288, 1, 0, 1, 0, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 113501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4290, 1, 0, 1, 0, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 113501, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4291, 1, 0, 1, 0, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 113501, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4292, 12, 0, 1, 0, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 102301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4293, 12, 0, 1, 0, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 102301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4294, 1, 0, 1, 0, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 113601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4295, 1, 0, 1, 0, "この格好で\n過ごすのは\n少し勇気がいるな…", 113601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4296, 1, 0, 1, 0, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 113601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4297, 1, 0, 1, 0, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 113601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4298, 1, 0, 1, 0, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 113601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4299, 4, 0, 1, 0, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 113601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4300, 8, 0, 1, 0, "少年との\n海のバカンス…\nまた行きたい\nものだな", 113601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4301, 1, 0, 1, 0, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 113601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4302, 1, 0, 1, 0, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 113601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4303, 1, 0, 1, 0, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 113601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4304, 1, 0, 1, 0, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 113601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4305, 4, 0, 1, 0, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 113601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4306, 8, 0, 1, 0, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 113601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4307, 1, 1, 1, 0, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 113601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4308, 1, 2, 1, 0, "昼食のお誘いかい？\nいいよ、付き合おう", 113601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4309, 1, 3, 1, 0, "もう夜か…\n一日が経つのは\nはやいものだね", 113601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4311, 1, 0, 1, 0, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 113601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4312, 1, 0, 1, 0, "うん…ふわふわの\n感触が\nよくわかるな…", 113601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4313, 1, 0, 1, 0, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 113601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4314, 1, 0, 1, 0, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 113601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4316, 1, 0, 1, 0, "兜を気にせず\n食べられるのは\n楽でいいな…", 113601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4317, 1, 0, 1, 0, "ふむ…鎧がないと\nページがめくりやすい", 113601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4319, 1, 0, 1, 0, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 113601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4320, 1, 0, 1, 0, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 113601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4321, 1, 0, 1, 0, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 106801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4322, 1, 0, 1, 0, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 106801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4323, 1, 0, 1, 0, "色んな人を観察できて\n眼福眼福…♪", 106801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4324, 1, 0, 1, 0, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 106801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4325, 1, 0, 1, 0, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 106801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4326, 4, 0, 1, 0, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 106801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4327, 8, 0, 1, 0, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 106801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4328, 1, 0, 1, 0, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 106801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4329, 1, 0, 1, 0, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 106801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4330, 1, 0, 1, 0, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 106801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4331, 1, 0, 1, 0, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 106801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4332, 4, 0, 1, 0, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 106801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4333, 8, 0, 1, 0, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 106801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4334, 1, 1, 1, 0, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 106801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4335, 1, 2, 1, 0, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 106801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4336, 1, 3, 1, 0, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 106801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4338, 1, 0, 1, 0, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 106801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4339, 1, 0, 1, 0, "…これ\nネネカが変身\nしてないよね？", 106801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4340, 1, 0, 1, 0, "いい香りだ…\n解析完了\n今度生成しようっと♪", 106801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4341, 1, 0, 1, 0, "安心するな～…\nアジトに置けないから\n羨ましいよ", 106801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4343, 1, 0, 1, 0, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 106801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4344, 1, 0, 1, 0, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 106801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4346, 1, 0, 1, 0, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 113701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4347, 1, 0, 1, 0, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 113701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4348, 1, 0, 1, 0, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 113701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4349, 1, 0, 1, 0, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 113701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4350, 1, 0, 1, 0, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 113701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4351, 4, 0, 1, 0, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 113701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4352, 8, 0, 1, 0, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 113701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4353, 1, 0, 1, 0, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 113701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4354, 1, 0, 1, 0, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 113701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4355, 1, 0, 1, 0, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 113701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4356, 1, 0, 1, 0, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 113701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4357, 4, 0, 1, 0, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 113701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4358, 8, 0, 1, 0, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 113701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4359, 1, 1, 1, 0, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 113701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4360, 1, 2, 1, 0, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 113701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4361, 1, 3, 1, 0, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 113701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4363, 1, 0, 1, 0, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 113701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4364, 1, 0, 1, 0, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 113701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4365, 1, 0, 1, 0, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 113701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4366, 1, 0, 1, 0, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 113701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4368, 1, 0, 1, 0, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 113701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4369, 1, 0, 1, 0, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 113701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4371, 12, 0, 1, 0, "あの方の前では\n常に気高く\nありたいですわね", 103201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4372, 12, 0, 1, 0, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 103201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4373, 12, 0, 1, 0, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 102801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4374, 12, 0, 1, 0, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 102801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4375, 1, 0, 1, 0, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 113801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4376, 1, 0, 1, 0, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 113801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4377, 1, 0, 1, 0, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 113801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4378, 1, 0, 1, 0, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 113801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4379, 1, 0, 1, 0, "エンジェルになると\n人見知りがなおる！\n…わけないか", 113801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4380, 4, 0, 1, 0, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 113801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4381, 8, 0, 1, 0, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 113801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4382, 1, 0, 1, 0, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 113801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4383, 1, 0, 1, 0, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 113801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4384, 1, 0, 1, 0, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 113801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4385, 1, 0, 1, 0, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 113801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4386, 4, 0, 1, 0, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 113801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4387, 8, 0, 1, 0, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 113801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4388, 1, 1, 1, 0, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 113801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4389, 1, 2, 1, 0, "ふぅ、いいことした\n後のランチは\nおいしいわね", 113801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4390, 1, 3, 1, 0, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 113801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4392, 1, 0, 1, 0, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 113801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4393, 1, 0, 1, 0, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 113801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4394, 1, 0, 1, 0, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 113801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4395, 1, 0, 1, 0, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 113801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4397, 1, 0, 1, 0, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 113801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4398, 1, 0, 1, 0, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 113801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4400, 1, 0, 1, 0, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 113901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4401, 1, 0, 1, 0, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 113901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4402, 1, 0, 1, 0, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 113901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4403, 1, 0, 1, 0, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 113901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4404, 1, 0, 1, 0, "マントは動きに\nメリハリがついて\nいいですね", 113901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4405, 4, 0, 1, 0, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 113901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4406, 8, 0, 1, 0, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 113901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4407, 1, 0, 1, 0, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 113901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4408, 1, 0, 1, 0, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 113901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4409, 1, 0, 1, 0, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 113901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4410, 1, 0, 1, 0, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 113901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4411, 4, 0, 1, 0, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 113901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4412, 8, 0, 1, 0, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 113901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4413, 1, 1, 1, 0, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 113901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4414, 1, 2, 1, 0, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 113901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4415, 1, 3, 1, 0, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 113901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4417, 1, 0, 1, 0, "このフィット感\nどうにか服作りにも\n活かせれば…！", 113901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4418, 1, 0, 1, 0, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 113901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4419, 1, 0, 1, 0, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 113901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4420, 1, 0, 1, 0, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 113901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4422, 1, 0, 1, 0, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 113901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4423, 1, 0, 1, 0, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 113901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4425, 1, 0, 1, 0, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 113901, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4427, 1, 0, 1, 0, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 114101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4428, 1, 0, 1, 0, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 114101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4429, 1, 0, 1, 0, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 114101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4430, 1, 0, 1, 0, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 114101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4431, 1, 0, 1, 0, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 114101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4432, 4, 0, 1, 0, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 114101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4433, 8, 0, 1, 0, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 114101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4434, 1, 0, 1, 0, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 114101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4435, 1, 0, 1, 0, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 114101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4436, 1, 0, 1, 0, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 114101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4437, 1, 0, 1, 0, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 114101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4438, 4, 0, 1, 0, "あんたも一緒に\n新しい技の練習\nどうッスか？", 114101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4439, 8, 0, 1, 0, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 114101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4440, 1, 1, 1, 0, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 114101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4441, 1, 2, 1, 0, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 114101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4442, 1, 3, 1, 0, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 114101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4444, 1, 0, 1, 0, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 114101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4445, 1, 0, 1, 0, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 114101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4446, 1, 0, 1, 0, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 114101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4447, 1, 0, 1, 0, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 114101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4449, 1, 0, 1, 0, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 114101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4450, 1, 0, 1, 0, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 114101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4452, 1, 0, 1, 0, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 114101, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4454, 12, 0, 1, 0, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 103001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4455, 12, 0, 1, 0, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 103001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4456, 1, 0, 1, 0, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 114001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4457, 1, 0, 1, 0, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 114001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4458, 1, 0, 1, 0, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 114001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4459, 1, 0, 1, 0, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 114001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4460, 1, 0, 1, 0, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 114001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4461, 4, 0, 1, 0, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 114001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4462, 8, 0, 1, 0, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 114001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4463, 1, 0, 1, 0, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 114001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4464, 1, 0, 1, 0, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 114001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4465, 1, 0, 1, 0, "どうしたの？\n出かけるなら\n私も同行しようか", 114001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4466, 1, 0, 1, 0, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 114001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4467, 4, 0, 1, 0, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 114001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4468, 8, 0, 1, 0, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 114001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4469, 1, 1, 1, 0, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 114001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4470, 1, 2, 1, 0, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 114001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4471, 1, 3, 1, 0, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 114001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4473, 1, 0, 1, 0, "ハロウィンはまだ\nこれからだ\n一休みしようか", 114001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4474, 1, 0, 1, 0, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 114001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4475, 1, 0, 1, 0, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 114001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4476, 1, 0, 1, 0, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 114001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4478, 1, 0, 1, 0, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 114001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4479, 1, 0, 1, 0, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 114001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4481, 1, 0, 1, 0, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 114001, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4483, 1, 0, 1, 0, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 114201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4484, 1, 0, 1, 0, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 114201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4485, 1, 0, 1, 0, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 114201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4486, 1, 0, 1, 0, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 114201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4487, 1, 0, 1, 0, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 114201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4488, 4, 0, 1, 0, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 114201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4489, 8, 0, 1, 0, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 114201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4490, 1, 0, 1, 0, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 114201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4491, 1, 0, 1, 0, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 114201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4492, 1, 0, 1, 0, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 114201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4493, 1, 0, 1, 0, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 114201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4494, 4, 0, 1, 0, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 114201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4495, 8, 0, 1, 0, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 114201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4496, 1, 1, 1, 0, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 114201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4497, 1, 2, 1, 0, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 114201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4498, 1, 3, 1, 0, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 114201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4500, 1, 0, 1, 0, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 114201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4501, 1, 0, 1, 0, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 114201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4502, 1, 0, 1, 0, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 114201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4503, 1, 0, 1, 0, "この温かさ…\n愛と希望に\n通じるものがある…", 114201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4505, 1, 0, 1, 0, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 114201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4506, 1, 0, 1, 0, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 114201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4508, 12, 0, 1, 0, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 103301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4509, 12, 0, 1, 0, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 103301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4510, 1, 0, 1, 0, "ぴっぷる！", 192501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4511, 1, 0, 1, 0, "ぴっぷるぴっぷる♪", 192501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4512, 1, 0, 1, 0, "ぴっ！", 192501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4513, 1, 0, 1, 0, "ぷっぷー", 192501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4514, 1, 0, 1, 0, "ぴぃ？", 192501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4515, 1, 0, 1, 0, "ぴっぷるんるん♪", 192501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4516, 1, 0, 1, 0, "ぷぅ！", 192501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4517, 1, 0, 1, 0, "ぴぴっ♪", 192501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4518, 1, 0, 1, 0, "ぷぅー…", 192501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4519, 1, 1, 1, 0, "ちゅ！", 192501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4520, 1, 2, 1, 0, "ぷはー", 192501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4521, 1, 3, 1, 0, "ぴぇー", 192501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4531, 1, 0, 1, 0, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 114301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4532, 1, 0, 1, 0, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 114301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4533, 1, 0, 1, 0, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 114301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4534, 1, 0, 1, 0, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 114301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4535, 1, 0, 1, 0, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 114301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4536, 4, 0, 1, 0, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 114301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4537, 8, 0, 1, 0, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 114301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4538, 1, 0, 1, 0, "もし時間があるなら、\n私と街の見回りに\n行かない？", 114301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4539, 1, 0, 1, 0, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 114301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4540, 1, 0, 1, 0, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 114301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4541, 1, 0, 1, 0, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 114301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4542, 4, 0, 1, 0, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 114301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4543, 8, 0, 1, 0, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 114301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4544, 1, 1, 1, 0, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 114301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4545, 1, 2, 1, 0, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 114301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4546, 1, 3, 1, 0, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 114301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4548, 1, 0, 1, 0, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 114301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4549, 1, 0, 1, 0, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 114301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4550, 1, 0, 1, 0, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 114301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4551, 1, 0, 1, 0, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 114301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4553, 1, 0, 1, 0, "こ、これは…\nおいしすぎて手が\n止まらないかも", 114301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4554, 1, 0, 1, 0, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 114301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4556, 1, 0, 1, 0, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 114401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4557, 1, 0, 1, 0, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 114401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4558, 1, 0, 1, 0, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 114401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4559, 1, 0, 1, 0, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 114401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4560, 1, 0, 1, 0, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 114401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4561, 4, 0, 1, 0, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 114401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4562, 8, 0, 1, 0, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 114401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4563, 1, 0, 1, 0, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 114401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4564, 1, 0, 1, 0, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 114401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4565, 1, 0, 1, 0, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 114401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4566, 1, 0, 1, 0, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 114401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4567, 4, 0, 1, 0, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 114401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4568, 8, 0, 1, 0, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 114401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4569, 1, 1, 1, 0, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 114401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4570, 1, 2, 1, 0, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 114401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4571, 1, 3, 1, 0, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 114401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4573, 1, 0, 1, 0, "適度な休息は\n成功の秘訣ですわ！", 114401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4574, 1, 0, 1, 0, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 114401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4575, 1, 0, 1, 0, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 114401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4576, 1, 0, 1, 0, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 114401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4578, 1, 0, 1, 0, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 114401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4579, 1, 0, 1, 0, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 114401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4581, 1, 0, 1, 0, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 114401, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4582, 1, 0, 1, 0, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 114401, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4583, 1, 0, 1, 0, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 114601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4584, 1, 0, 1, 0, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 114601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4585, 1, 0, 1, 0, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 114601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4586, 1, 0, 1, 0, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 114601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4587, 1, 0, 1, 0, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 114601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4588, 4, 0, 1, 0, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 114601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4589, 8, 0, 1, 0, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 114601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4590, 1, 0, 1, 0, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 114601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4591, 1, 0, 1, 0, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 114601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4592, 1, 0, 1, 0, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 114601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4593, 1, 0, 1, 0, "お姉さんの\n頼れるところ\n見せてあげるわね", 114601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4594, 4, 0, 1, 0, "何かあったら\n頼ってくれても\nいいんだからね？", 114601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4595, 8, 0, 1, 0, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 114601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4596, 1, 1, 1, 0, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 114601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4597, 1, 2, 1, 0, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 114601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4598, 1, 3, 1, 0, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 114601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4600, 1, 0, 1, 0, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 114601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4601, 1, 0, 1, 0, "私だって…\nかわいいものは\n好きでいいわよね？", 114601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4602, 1, 0, 1, 0, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 114601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4603, 1, 0, 1, 0, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 114601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4605, 1, 0, 1, 0, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 114601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4606, 1, 0, 1, 0, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 114601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4608, 1, 0, 1, 0, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 114601, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4609, 1, 0, 1, 0, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 114601, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4610, 12, 0, 1, 0, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 101601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4611, 12, 0, 1, 0, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 101601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4612, 1, 0, 1, 0, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 114501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4613, 1, 0, 1, 0, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 114501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4614, 1, 0, 1, 0, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 114501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4615, 1, 0, 1, 0, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 114501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4616, 1, 0, 1, 0, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 114501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4617, 4, 0, 1, 0, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 114501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4618, 8, 0, 1, 0, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 114501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4619, 1, 0, 1, 0, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 114501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4620, 1, 0, 1, 0, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 114501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4621, 1, 0, 1, 0, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 114501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4622, 1, 0, 1, 0, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 114501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4623, 4, 0, 1, 0, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 114501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4624, 8, 0, 1, 0, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 114501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4625, 1, 1, 1, 0, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 114501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4626, 1, 2, 1, 0, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 114501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4627, 1, 3, 1, 0, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 114501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4629, 1, 0, 1, 0, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 114501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4630, 1, 0, 1, 0, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 114501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4631, 1, 0, 1, 0, "華やかな香りね\nクリスマスに\nピッタリだわ", 114501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4632, 1, 0, 1, 0, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 114501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4634, 1, 0, 1, 0, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 114501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4635, 1, 0, 1, 0, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 114501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4637, 1, 0, 1, 0, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 114501, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4638, 1, 0, 1, 0, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 114501, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4639, 1, 0, 1, 0, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 114701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4640, 1, 0, 1, 0, "今年こそ、もう少し\n身長が伸びます\nように…", 114701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4641, 1, 0, 1, 0, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 114701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4642, 1, 0, 1, 0, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 114701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4643, 1, 0, 1, 0, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 114701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4644, 4, 0, 1, 0, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 114701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4645, 8, 0, 1, 0, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 114701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4646, 1, 0, 1, 0, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 114701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4647, 1, 0, 1, 0, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 114701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4648, 1, 0, 1, 0, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 114701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4649, 1, 0, 1, 0, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 114701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4650, 4, 0, 1, 0, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 114701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4651, 8, 0, 1, 0, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 114701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4652, 1, 1, 1, 0, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 114701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4653, 1, 2, 1, 0, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 114701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4654, 1, 3, 1, 0, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 114701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4656, 1, 0, 1, 0, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 114701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4657, 1, 0, 1, 0, "なんだ？\nオマエも初詣に\n行きたいのか？", 114701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4658, 1, 0, 1, 0, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 114701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4659, 1, 0, 1, 0, "コイツがあれば\nうまい魚が\n焼けそうだな…", 114701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4661, 1, 0, 1, 0, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 114701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4662, 1, 0, 1, 0, "おみくじって\n書いてあることが\nむずかしいんだよな…", 114701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4664, 1, 0, 1, 0, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 180101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4665, 1, 0, 1, 0, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 180101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4666, 1, 0, 1, 0, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 180101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4667, 1, 0, 1, 0, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 180101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4668, 1, 0, 1, 0, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 180101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4669, 4, 0, 1, 0, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 180101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4670, 8, 0, 1, 0, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 180101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4671, 1, 0, 1, 0, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 180101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4672, 1, 0, 1, 0, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 180101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4673, 1, 0, 1, 0, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 180101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4674, 1, 0, 1, 0, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 180101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4675, 4, 0, 1, 0, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 180101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4676, 8, 0, 1, 0, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 180101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4677, 1, 1, 1, 0, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 180101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4678, 1, 2, 1, 0, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 180101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4679, 1, 3, 1, 0, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 180101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4681, 1, 0, 1, 0, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 180101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4682, 1, 0, 1, 0, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 180101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4683, 1, 0, 1, 0, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 180101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4684, 1, 0, 1, 0, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 180101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4686, 1, 0, 1, 0, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 180101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4687, 1, 0, 1, 0, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 180101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4689, 1, 0, 1, 0, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 111801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4690, 1, 0, 1, 0, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 111801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4691, 1, 0, 1, 0, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 111801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4692, 1, 0, 1, 0, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 111801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4693, 1, 0, 1, 0, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 111801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4694, 4, 0, 1, 0, "ずっとこうして\n年の始まりを\n過ごしたいですね", 111801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4695, 8, 0, 1, 0, "いつか二人きりで\n初日の出を\n見たいです", 111801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4696, 1, 0, 1, 0, "お餅おかわりです！\nいくらでも\n食べられますよ！", 111801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4697, 1, 0, 1, 0, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 111801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4698, 1, 0, 1, 0, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 111801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4699, 1, 0, 1, 0, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 111801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4700, 4, 0, 1, 0, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 111801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4701, 8, 0, 1, 0, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 111801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4702, 1, 1, 1, 0, "年明けの朝は\n心なしか\n気持ちいいですね☆", 111801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4703, 1, 2, 1, 0, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 111801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4704, 1, 3, 1, 0, "あたたかくして\n幸せな初夢を\n見ましょうね", 111801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4706, 1, 0, 1, 0, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 111801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4707, 1, 0, 1, 0, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 111801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4708, 1, 0, 1, 0, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 111801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4709, 1, 0, 1, 0, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 111801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4711, 1, 0, 1, 0, "おいしすぎて\n食べる手が\n止まりません～", 111801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4712, 1, 0, 1, 0, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 111801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4714, 12, 0, 1, 0, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 100601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4715, 12, 0, 1, 0, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 100601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4716, 1, 0, 1, 0, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 115001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4717, 1, 0, 1, 0, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 115001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4718, 1, 0, 1, 0, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 115001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4719, 1, 0, 1, 0, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 115001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4720, 1, 0, 1, 0, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 115001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4721, 4, 0, 1, 0, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 115001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4722, 8, 0, 1, 0, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 115001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4723, 1, 0, 1, 0, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 115001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4724, 1, 0, 1, 0, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 115001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4725, 1, 0, 1, 0, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 115001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4726, 1, 0, 1, 0, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 115001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4727, 4, 0, 1, 0, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 115001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4728, 8, 0, 1, 0, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 115001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4729, 1, 1, 1, 0, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 115001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4730, 1, 2, 1, 0, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 115001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4731, 1, 3, 1, 0, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 115001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4733, 1, 0, 1, 0, "少し休みます…\n時間が来たら\n起こすように", 115001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4734, 1, 0, 1, 0, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 115001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4735, 1, 0, 1, 0, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 115001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4736, 1, 0, 1, 0, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 115001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4738, 1, 0, 1, 0, "いかなる強者でも\n兵站、補給は\n必要なものです", 115001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4739, 1, 0, 1, 0, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 115001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4741, 1, 0, 1, 0, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 115501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4742, 1, 0, 1, 0, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 115501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4743, 1, 0, 1, 0, "主さまも精霊の召喚を\n試してみますか？", 115501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4744, 1, 0, 1, 0, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 115501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4745, 1, 0, 1, 0, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 115501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4746, 4, 0, 1, 0, "進化した精霊に\n負けぬよう\n修行に励みます", 115501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4747, 8, 0, 1, 0, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 115501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4748, 1, 0, 1, 0, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 115501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4749, 1, 0, 1, 0, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 115501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4750, 1, 0, 1, 0, "里で採れた果物を\n持って参りました\n召し上がってください", 115501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4751, 1, 0, 1, 0, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 115501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4752, 4, 0, 1, 0, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 115501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4753, 8, 0, 1, 0, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 115501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4754, 1, 1, 1, 0, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 115501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4755, 1, 2, 1, 0, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 115501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4756, 1, 3, 1, 0, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 115501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4758, 1, 0, 1, 0, "この癒し…\n主さまにも味わって\nいただきたい…", 115501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4759, 1, 0, 1, 0, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 115501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4760, 1, 0, 1, 0, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 115501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4761, 1, 0, 1, 0, "瞑想するのに\nちょうどよい暖かさで\nございます", 115501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4763, 1, 0, 1, 0, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 115501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4764, 1, 0, 1, 0, "風の精霊について\nもっと詳しく知って\nおかなければ…", 115501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4766, 1, 0, 1, 0, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 115601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4767, 1, 0, 1, 0, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 115601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4768, 1, 0, 1, 0, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 115601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4769, 1, 0, 1, 0, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 115601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4770, 1, 0, 1, 0, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 115601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4771, 4, 0, 1, 0, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 115601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4772, 8, 0, 1, 0, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 115601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4773, 1, 0, 1, 0, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 115601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4774, 1, 0, 1, 0, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 115601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4775, 1, 0, 1, 0, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 115601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4776, 1, 0, 1, 0, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 115601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4777, 4, 0, 1, 0, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 115601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4778, 8, 0, 1, 0, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 115601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4779, 1, 1, 1, 0, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 115601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4780, 1, 2, 1, 0, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 115601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4781, 1, 3, 1, 0, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 115601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4783, 1, 0, 1, 0, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 115601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4784, 1, 0, 1, 0, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 115601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4785, 1, 0, 1, 0, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 115601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4786, 1, 0, 1, 0, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 115601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4788, 1, 0, 1, 0, "自然の恵みに\n感謝して…\nいただきます♪", 115601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4789, 1, 0, 1, 0, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 115601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4791, 12, 0, 1, 0, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 102201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4792, 12, 0, 1, 0, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 102201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4793, 1, 0, 1, 0, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 106401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4794, 1, 0, 1, 0, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 106401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4795, 1, 0, 1, 0, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 106401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4796, 1, 0, 1, 0, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 106401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4797, 1, 0, 1, 0, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 106401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4798, 4, 0, 1, 0, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 106401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4799, 8, 0, 1, 0, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 106401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4800, 1, 0, 1, 0, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 106401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4801, 1, 0, 1, 0, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 106401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4802, 1, 0, 1, 0, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 106401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4803, 1, 0, 1, 0, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 106401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4804, 4, 0, 1, 0, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 106401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4805, 8, 0, 1, 0, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 106401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4806, 1, 1, 1, 0, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 106401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4807, 1, 2, 1, 0, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 106401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4808, 1, 3, 1, 0, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 106401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4810, 1, 0, 1, 0, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 106401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4811, 1, 0, 1, 0, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 106401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4812, 1, 0, 1, 0, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 106401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4813, 1, 0, 1, 0, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 106401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4815, 1, 0, 1, 0, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 106401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4816, 1, 0, 1, 0, "ん…なるほどね。\n勉強になるわ…", 106401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4818, 1, 0, 1, 0, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 115701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4819, 1, 0, 1, 0, "依頼があれば\n海でも山でも\n赴くさ！", 115701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4820, 1, 0, 1, 0, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 115701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4821, 1, 0, 1, 0, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 115701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4822, 1, 0, 1, 0, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 115701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4823, 4, 0, 1, 0, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 115701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4824, 8, 0, 1, 0, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 115701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4825, 1, 0, 1, 0, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 115701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4826, 1, 0, 1, 0, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 115701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4827, 1, 0, 1, 0, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 115701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4828, 1, 0, 1, 0, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 115701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4829, 4, 0, 1, 0, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 115701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4830, 8, 0, 1, 0, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 115701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4831, 1, 1, 1, 0, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 115701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4832, 1, 2, 1, 0, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 115701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4833, 1, 3, 1, 0, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 115701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4835, 1, 0, 1, 0, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 115701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4836, 1, 0, 1, 0, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 115701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4837, 1, 0, 1, 0, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 115701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4838, 1, 0, 1, 0, "ポカポカするね…\n助手くん\n君もおいでよ", 115701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4840, 1, 0, 1, 0, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 115701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4841, 1, 0, 1, 0, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 115701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4843, 1, 0, 1, 0, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 115701, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4844, 1, 0, 1, 0, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 115701, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4845, 1, 0, 1, 0, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 180601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4846, 1, 0, 1, 0, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 180601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4847, 1, 0, 1, 0, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 180601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4848, 1, 0, 1, 0, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 180601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4849, 1, 0, 1, 0, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 180601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4850, 4, 0, 1, 0, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 180601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4851, 8, 0, 1, 0, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 180601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4852, 1, 0, 1, 0, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 180601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4853, 1, 0, 1, 0, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 180601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4854, 1, 0, 1, 0, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 180601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4855, 1, 0, 1, 0, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 180601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4856, 4, 0, 1, 0, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 180601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4857, 8, 0, 1, 0, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 180601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4858, 1, 1, 1, 0, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 180601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4859, 1, 2, 1, 0, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 180601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4860, 1, 3, 1, 0, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 180601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4862, 1, 0, 1, 0, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 180601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4863, 1, 0, 1, 0, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 180601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4864, 1, 0, 1, 0, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 180601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4865, 1, 0, 1, 0, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 180601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4867, 1, 0, 1, 0, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 180601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4868, 1, 0, 1, 0, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 180601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4870, 1, 0, 1, 0, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 115801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4871, 1, 0, 1, 0, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 115801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4872, 1, 0, 1, 0, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 115801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4873, 1, 0, 1, 0, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 115801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4874, 1, 0, 1, 0, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 115801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4875, 4, 0, 1, 0, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 115801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4876, 8, 0, 1, 0, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 115801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4877, 1, 0, 1, 0, "どうかしら？\n私、きちんとドレス\n着られてる？", 115801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4878, 1, 0, 1, 0, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 115801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4879, 1, 0, 1, 0, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 115801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4880, 1, 0, 1, 0, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 115801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4881, 4, 0, 1, 0, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 115801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4882, 8, 0, 1, 0, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 115801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4883, 1, 1, 1, 0, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 115801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4884, 1, 2, 1, 0, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 115801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4885, 1, 3, 1, 0, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 115801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4887, 1, 0, 1, 0, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 115801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4888, 1, 0, 1, 0, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 115801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4889, 1, 0, 1, 0, "すてきな香り…\nお花かしら？\n森で探してみようかな", 115801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4890, 1, 0, 1, 0, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 115801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4892, 1, 0, 1, 0, "レッスンのために\n栄養補給して\nおかないと！", 115801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4893, 1, 0, 1, 0, "教養を身に着ける\nことも大切よね！\nなになに…", 115801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4895, 1, 0, 1, 0, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 115901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4896, 1, 0, 1, 0, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 115901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4897, 1, 0, 1, 0, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 115901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4898, 1, 0, 1, 0, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 115901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4899, 1, 0, 1, 0, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 115901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4900, 4, 0, 1, 0, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 115901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4901, 8, 0, 1, 0, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 115901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4902, 1, 0, 1, 0, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 115901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4903, 1, 0, 1, 0, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 115901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4904, 1, 0, 1, 0, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 115901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4905, 1, 0, 1, 0, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 115901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4906, 4, 0, 1, 0, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 115901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4907, 8, 0, 1, 0, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 115901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4908, 1, 1, 1, 0, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 115901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4909, 1, 2, 1, 0, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 115901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4910, 1, 3, 1, 0, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 115901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4912, 1, 0, 1, 0, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 115901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4913, 1, 0, 1, 0, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 115901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4914, 1, 0, 1, 0, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 115901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4915, 1, 0, 1, 0, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 115901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4917, 1, 0, 1, 0, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 115901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4918, 1, 0, 1, 0, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 115901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4920, 12, 0, 1, 0, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 102001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4921, 12, 0, 1, 0, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 102001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4922, 12, 0, 1, 0, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 100401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4923, 12, 0, 1, 0, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 100401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4924, 12, 0, 1, 0, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 103601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4925, 12, 0, 1, 0, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 103601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4926, 1, 0, 1, 0, "くるくる〜♪\nうちのドレス\nかわええなぁ", 116001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4927, 1, 0, 1, 0, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 116001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4928, 1, 0, 1, 0, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 116001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4929, 1, 0, 1, 0, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 116001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4930, 1, 0, 1, 0, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 116001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4931, 4, 0, 1, 0, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 116001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4932, 8, 0, 1, 0, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 116001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4933, 1, 0, 1, 0, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 116001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4934, 1, 0, 1, 0, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 116001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4935, 1, 0, 1, 0, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 116001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4936, 1, 0, 1, 0, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 116001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4937, 4, 0, 1, 0, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 116001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4938, 8, 0, 1, 0, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 116001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4939, 1, 1, 1, 0, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 116001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4940, 1, 2, 1, 0, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 116001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4941, 1, 3, 1, 0, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 116001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4943, 1, 0, 1, 0, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 116001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4944, 1, 0, 1, 0, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 116001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4945, 1, 0, 1, 0, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 116001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4946, 1, 0, 1, 0, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 116001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4948, 1, 0, 1, 0, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 116001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4949, 1, 0, 1, 0, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 116001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4951, 1, 0, 1, 0, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 116301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4952, 1, 0, 1, 0, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 116301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4953, 1, 0, 1, 0, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 116301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4954, 1, 0, 1, 0, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 116301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4955, 1, 0, 1, 0, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 116301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4956, 4, 0, 1, 0, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 116301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4957, 8, 0, 1, 0, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 116301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4958, 1, 0, 1, 0, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 116301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4959, 1, 0, 1, 0, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 116301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4960, 1, 0, 1, 0, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 116301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4961, 1, 0, 1, 0, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 116301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4962, 4, 0, 1, 0, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 116301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4963, 8, 0, 1, 0, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 116301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4964, 1, 1, 1, 0, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 116301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4965, 1, 2, 1, 0, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 116301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4966, 1, 3, 1, 0, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 116301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4968, 1, 0, 1, 0, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 116301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4969, 1, 0, 1, 0, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 116301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4970, 1, 0, 1, 0, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 116301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4971, 1, 0, 1, 0, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 116301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4973, 1, 0, 1, 0, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 116301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4974, 1, 0, 1, 0, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 116301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4976, 12, 0, 1, 0, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 102901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4977, 12, 0, 1, 0, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 102901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4978, 1, 0, 1, 0, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 116201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4979, 1, 0, 1, 0, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 116201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4980, 1, 0, 1, 0, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 116201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4981, 1, 0, 1, 0, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 116201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4982, 1, 0, 1, 0, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 116201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4983, 4, 0, 1, 0, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 116201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4984, 8, 0, 1, 0, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 116201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4985, 1, 0, 1, 0, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 116201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4986, 1, 0, 1, 0, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 116201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4987, 1, 0, 1, 0, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 116201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4988, 1, 0, 1, 0, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 116201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4989, 4, 0, 1, 0, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 116201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4990, 8, 0, 1, 0, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 116201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4991, 1, 1, 1, 0, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 116201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4992, 1, 2, 1, 0, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 116201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4993, 1, 3, 1, 0, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 116201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4995, 1, 0, 1, 0, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 116201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4996, 1, 0, 1, 0, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 116201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4997, 1, 0, 1, 0, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 116201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (4998, 1, 0, 1, 0, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 116201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5000, 1, 0, 1, 0, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 116201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5001, 1, 0, 1, 0, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 116201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5003, 1, 0, 1, 0, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 116501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5004, 1, 0, 1, 0, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 116501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5005, 1, 0, 1, 0, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 116501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5006, 1, 0, 1, 0, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 116501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5007, 1, 0, 1, 0, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 116501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5008, 4, 0, 1, 0, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 116501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5009, 8, 0, 1, 0, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 116501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5010, 1, 0, 1, 0, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 116501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5011, 1, 0, 1, 0, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 116501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5012, 1, 0, 1, 0, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 116501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5013, 1, 0, 1, 0, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 116501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5014, 4, 0, 1, 0, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 116501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5015, 8, 0, 1, 0, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 116501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5016, 1, 1, 1, 0, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 116501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5017, 1, 2, 1, 0, "お腹が空きました…\nご飯食べにいきますよ", 116501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5018, 1, 3, 1, 0, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 116501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5020, 1, 0, 1, 0, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 116501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5021, 1, 0, 1, 0, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 116501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5022, 1, 0, 1, 0, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 116501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5023, 1, 0, 1, 0, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 116501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5025, 1, 0, 1, 0, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 116501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5026, 1, 0, 1, 0, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 116501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5028, 12, 0, 1, 0, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 102501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5029, 12, 0, 1, 0, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 102501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5030, 1, 0, 1, 0, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 116601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5031, 1, 0, 1, 0, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 116601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5032, 1, 0, 1, 0, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 116601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5033, 1, 0, 1, 0, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 116601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5034, 1, 0, 1, 0, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 116601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5035, 4, 0, 1, 0, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 116601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5036, 8, 0, 1, 0, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 116601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5037, 1, 0, 1, 0, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 116601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5038, 1, 0, 1, 0, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 116601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5039, 1, 0, 1, 0, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 116601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5040, 1, 0, 1, 0, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 116601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5041, 4, 0, 1, 0, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 116601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5042, 8, 0, 1, 0, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 116601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5043, 1, 1, 1, 0, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 116601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5044, 1, 2, 1, 0, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 116601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5045, 1, 3, 1, 0, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 116601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5047, 1, 0, 1, 0, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 116601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5048, 1, 0, 1, 0, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 116601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5049, 1, 0, 1, 0, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 116601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5050, 1, 0, 1, 0, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 116601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5052, 1, 0, 1, 0, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 116601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5053, 1, 0, 1, 0, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 116601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5055, 1, 0, 1, 0, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 116701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5056, 1, 0, 1, 0, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 116701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5057, 1, 0, 1, 0, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 116701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5058, 1, 0, 1, 0, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 116701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5059, 1, 0, 1, 0, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 116701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5060, 4, 0, 1, 0, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 116701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5061, 8, 0, 1, 0, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 116701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5062, 1, 0, 1, 0, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 116701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5063, 1, 0, 1, 0, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 116701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5064, 1, 0, 1, 0, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 116701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5065, 1, 0, 1, 0, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 116701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5066, 4, 0, 1, 0, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 116701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5067, 8, 0, 1, 0, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 116701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5068, 1, 1, 1, 0, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 116701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5069, 1, 2, 1, 0, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 116701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5070, 1, 3, 1, 0, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 116701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5072, 1, 0, 1, 0, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 116701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5073, 1, 0, 1, 0, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 116701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5074, 1, 0, 1, 0, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 116701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5075, 1, 0, 1, 0, "ぽかぽか…\n幸せなひとときですね", 116701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5077, 1, 0, 1, 0, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 116701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5078, 1, 0, 1, 0, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 116701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5080, 1, 0, 1, 0, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 116801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5081, 1, 0, 1, 0, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 116801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5082, 1, 0, 1, 0, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 116801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5083, 1, 0, 1, 0, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 116801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5084, 1, 0, 1, 0, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 116801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5085, 4, 0, 1, 0, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 116801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5086, 8, 0, 1, 0, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 116801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5087, 1, 0, 1, 0, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 116801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5088, 1, 0, 1, 0, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 116801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5089, 1, 0, 1, 0, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 116801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5090, 1, 0, 1, 0, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 116801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5091, 4, 0, 1, 0, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 116801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5092, 8, 0, 1, 0, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 116801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5093, 1, 1, 1, 0, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 116801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5094, 1, 2, 1, 0, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 116801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5095, 1, 3, 1, 0, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 116801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5097, 1, 0, 1, 0, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 116801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5098, 1, 0, 1, 0, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 116801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5099, 1, 0, 1, 0, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 116801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5100, 1, 0, 1, 0, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 116801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5102, 1, 0, 1, 0, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 116801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5103, 1, 0, 1, 0, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 116801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5105, 12, 0, 1, 0, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 102101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5106, 12, 0, 1, 0, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 102101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5107, 1, 0, 1, 0, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 116901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5108, 1, 0, 1, 0, "他のバイトとの\nスケジュールを\n調整しないと…", 116901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5109, 1, 0, 1, 0, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 116901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5110, 1, 0, 1, 0, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 116901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5111, 1, 0, 1, 0, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 116901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5112, 4, 0, 1, 0, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 116901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5113, 8, 0, 1, 0, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 116901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5114, 1, 0, 1, 0, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 116901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5115, 1, 0, 1, 0, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 116901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5116, 1, 0, 1, 0, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 116901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5117, 1, 0, 1, 0, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 116901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5118, 4, 0, 1, 0, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 116901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5119, 8, 0, 1, 0, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 116901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5120, 1, 1, 1, 0, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 116901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5121, 1, 2, 1, 0, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 116901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5122, 1, 3, 1, 0, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 116901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5124, 1, 0, 1, 0, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 116901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5125, 1, 0, 1, 0, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 116901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5126, 1, 0, 1, 0, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 116901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5127, 1, 0, 1, 0, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 116901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5129, 1, 0, 1, 0, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 116901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5130, 1, 0, 1, 0, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 116901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5132, 1, 0, 1, 0, "変身している間に\n色々と試しておこう\nまずは…", 180301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5133, 1, 0, 1, 0, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 180301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5134, 1, 0, 1, 0, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 180301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5135, 1, 0, 1, 0, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 180301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5136, 1, 0, 1, 0, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 180301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5137, 4, 0, 1, 0, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 180301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5138, 8, 0, 1, 0, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 180301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5139, 1, 0, 1, 0, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 180301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5140, 1, 0, 1, 0, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 180301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5141, 1, 0, 1, 0, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 180301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5142, 1, 0, 1, 0, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 180301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5143, 4, 0, 1, 0, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 180301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5144, 8, 0, 1, 0, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 180301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5145, 1, 1, 1, 0, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 180301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5146, 1, 2, 1, 0, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 180301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5147, 1, 3, 1, 0, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 180301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5149, 1, 0, 1, 0, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 180301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5150, 1, 0, 1, 0, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 180301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5151, 1, 0, 1, 0, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 180301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5152, 1, 0, 1, 0, "ありがたいな\n少し足元が\n冷えていたところだ", 180301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5154, 1, 0, 1, 0, "これは…！\n持ち運べるように\n改良できないかな？", 180301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5155, 1, 0, 1, 0, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 180301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5157, 1, 0, 1, 0, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 117001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5158, 1, 0, 1, 0, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 117001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5159, 1, 0, 1, 0, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 117001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5160, 1, 0, 1, 0, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 117001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5161, 1, 0, 1, 0, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 117001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5162, 4, 0, 1, 0, "あのおかたの全てが\n早く私のものに\nなりますように…", 117001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5163, 8, 0, 1, 0, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 117001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5164, 1, 0, 1, 0, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 117001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5165, 1, 0, 1, 0, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 117001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5166, 1, 0, 1, 0, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 117001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5167, 1, 0, 1, 0, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 117001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5168, 4, 0, 1, 0, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 117001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5169, 8, 0, 1, 0, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 117001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5170, 1, 1, 1, 0, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 117001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5171, 1, 2, 1, 0, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 117001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5172, 1, 3, 1, 0, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 117001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5174, 1, 0, 1, 0, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 117001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5175, 1, 0, 1, 0, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 117001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5176, 1, 0, 1, 0, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 117001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5177, 1, 0, 1, 0, "あのおかたの\nぬくもりを\n思い出しますわ…", 117001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5179, 1, 0, 1, 0, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 117001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5180, 1, 0, 1, 0, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 117001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5182, 1, 0, 1, 0, "二人乗りができると\nいいですわね…", 117001, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5183, 1, 0, 1, 0, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 117001, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5184, 12, 0, 1, 0, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 101701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5185, 12, 0, 1, 0, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 101701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5186, 1, 0, 1, 0, "この水着…\n一番に弟くんに\n見せたいな♪", 117101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5187, 1, 0, 1, 0, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 117101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5188, 1, 0, 1, 0, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 117101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5189, 1, 0, 1, 0, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 117101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5190, 1, 0, 1, 0, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 117101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5191, 4, 0, 1, 0, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 117101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5192, 8, 0, 1, 0, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 117101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5193, 1, 0, 1, 0, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 117101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5194, 1, 0, 1, 0, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 117101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5195, 1, 0, 1, 0, "海で迷子に\nならないように\n手を繋ごうね♪", 117101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5196, 1, 0, 1, 0, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 117101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5197, 4, 0, 1, 0, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 117101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5198, 8, 0, 1, 0, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 117101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5199, 1, 1, 1, 0, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 117101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5200, 1, 2, 1, 0, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 117101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5201, 1, 3, 1, 0, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 117101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5203, 1, 0, 1, 0, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 117101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5204, 1, 0, 1, 0, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 117101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5205, 1, 0, 1, 0, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 117101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5206, 1, 0, 1, 0, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 117101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5208, 1, 0, 1, 0, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 117101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5209, 1, 0, 1, 0, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 117101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5211, 1, 0, 1, 0, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 117101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5212, 1, 0, 1, 0, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 117101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5213, 1, 0, 1, 0, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 117201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5214, 1, 0, 1, 0, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 117201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5215, 1, 0, 1, 0, "とっておきの\nサマーソングを\nみんなに届けるよ！", 117201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5216, 1, 0, 1, 0, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 117201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5217, 1, 0, 1, 0, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 117201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5218, 4, 0, 1, 0, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 117201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5219, 8, 0, 1, 0, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 117201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5220, 1, 0, 1, 0, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 117201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5221, 1, 0, 1, 0, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 117201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5222, 1, 0, 1, 0, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 117201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5223, 1, 0, 1, 0, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 117201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5224, 4, 0, 1, 0, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 117201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5225, 8, 0, 1, 0, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 117201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5226, 1, 1, 1, 0, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 117201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5227, 1, 2, 1, 0, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 117201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5228, 1, 3, 1, 0, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 117201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5230, 1, 0, 1, 0, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 117201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5231, 1, 0, 1, 0, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 117201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5232, 1, 0, 1, 0, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 117201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5233, 1, 0, 1, 0, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 117201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5235, 1, 0, 1, 0, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 117201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5236, 1, 0, 1, 0, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 117201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5238, 1, 0, 1, 0, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 117201, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5239, 1, 0, 1, 0, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 117201, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5240, 1, 0, 1, 0, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 117401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5241, 1, 0, 1, 0, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 117401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5242, 1, 0, 1, 0, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 117401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5243, 1, 0, 1, 0, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 117401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5244, 1, 0, 1, 0, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 117401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5245, 4, 0, 1, 0, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 117401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5246, 8, 0, 1, 0, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 117401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5247, 1, 0, 1, 0, "騎士さんいいところに\nこの衣装\nどう思います？", 117401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5248, 1, 0, 1, 0, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 117401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5249, 1, 0, 1, 0, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 117401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5250, 1, 0, 1, 0, "仕立て屋も\nアイドルも\n諦めません！", 117401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5251, 4, 0, 1, 0, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 117401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5252, 8, 0, 1, 0, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 117401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5253, 1, 1, 1, 0, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 117401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5254, 1, 2, 1, 0, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 117401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5255, 1, 3, 1, 0, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 117401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5257, 1, 0, 1, 0, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 117401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5258, 1, 0, 1, 0, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 117401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5259, 1, 0, 1, 0, "いい香りですね～\n波の音が聞こえて\nきそうです…", 117401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5260, 1, 0, 1, 0, "泳いだ後は\n少し寒いので\nちょうどいいですね", 117401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5262, 1, 0, 1, 0, "夏バテしないように\n栄養はしっかり\n取らないと！", 117401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5263, 1, 0, 1, 0, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 117401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5265, 1, 0, 1, 0, "デザインはもちろん\n触り心地も\n最高ですね♪", 117401, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5266, 1, 0, 1, 0, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 117401, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5267, 12, 0, 1, 0, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 104001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5268, 12, 0, 1, 0, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 104001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5269, 1, 0, 1, 0, "この衣装で\nライブするのは\n恥ずかしいな…", 117301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5270, 1, 0, 1, 0, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 117301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5271, 1, 0, 1, 0, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 117301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5272, 1, 0, 1, 0, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 117301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5273, 1, 0, 1, 0, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 117301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5274, 4, 0, 1, 0, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 117301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5275, 8, 0, 1, 0, "水着、似合ってるって\n言ってもらえるかな…", 117301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5276, 1, 0, 1, 0, "サインして、ですか？\nいいですけど…\n少し照れますね…", 117301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5277, 1, 0, 1, 0, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 117301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5278, 1, 0, 1, 0, "なんでしょう？\nもしかして…\nボイトレに興味が？", 117301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5279, 1, 0, 1, 0, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 117301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5280, 4, 0, 1, 0, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 117301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5281, 8, 0, 1, 0, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 117301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5282, 1, 1, 1, 0, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 117301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5283, 1, 2, 1, 0, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 117301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5284, 1, 3, 1, 0, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 117301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5286, 1, 0, 1, 0, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 117301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5287, 1, 0, 1, 0, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 117301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5288, 1, 0, 1, 0, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 117301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5289, 1, 0, 1, 0, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 117301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5291, 1, 0, 1, 0, "アイドルとして、\n食べすぎには\n気をつけないと…", 117301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5292, 1, 0, 1, 0, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 117301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5294, 1, 0, 1, 0, "この子に乗りながら\nライブをする…\nというのはどうですか", 117301, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5295, 1, 0, 1, 0, "まるで人魚になった\n気分…なんて、ふふ", 117301, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5296, 1, 0, 1, 0, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 118301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5297, 1, 0, 1, 0, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 118301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5298, 1, 0, 1, 0, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 118301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5299, 1, 0, 1, 0, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 118301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5300, 1, 0, 1, 0, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 118301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5301, 4, 0, 1, 0, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 118301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5302, 8, 0, 1, 0, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 118301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5303, 1, 0, 1, 0, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 118301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5304, 1, 0, 1, 0, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 118301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5305, 1, 0, 1, 0, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 118301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5306, 1, 0, 1, 0, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 118301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5307, 4, 0, 1, 0, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 118301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5308, 8, 0, 1, 0, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 118301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5309, 1, 1, 1, 0, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 118301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5310, 1, 2, 1, 0, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 118301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5311, 1, 3, 1, 0, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 118301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5313, 1, 0, 1, 0, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 118301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5314, 1, 0, 1, 0, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 118301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5315, 1, 0, 1, 0, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 118301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5316, 1, 0, 1, 0, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 118301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5318, 1, 0, 1, 0, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 118301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5319, 1, 0, 1, 0, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 118301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5322, 1, 0, 1, 0, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 118401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5323, 1, 0, 1, 0, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 118401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5324, 1, 0, 1, 0, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 118401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5325, 1, 0, 1, 0, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 118401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5326, 1, 0, 1, 0, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 118401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5327, 4, 0, 1, 0, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 118401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5328, 8, 0, 1, 0, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 118401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5329, 1, 0, 1, 0, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 118401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5330, 1, 0, 1, 0, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 118401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5331, 1, 0, 1, 0, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 118401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5332, 1, 0, 1, 0, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 118401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5333, 4, 0, 1, 0, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 118401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5334, 8, 0, 1, 0, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 118401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5335, 1, 1, 1, 0, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 118401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5336, 1, 2, 1, 0, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 118401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5337, 1, 3, 1, 0, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 118401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5339, 1, 0, 1, 0, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 118401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5340, 1, 0, 1, 0, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 118401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5341, 1, 0, 1, 0, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 118401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5342, 1, 0, 1, 0, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 118401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5344, 1, 0, 1, 0, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 118401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5345, 1, 0, 1, 0, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 118401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5348, 1, 0, 1, 0, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 117501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5349, 1, 0, 1, 0, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 117501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5350, 1, 0, 1, 0, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 117501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5351, 1, 0, 1, 0, "新薬開発のために\n薬草を採取して\nおこうかしら", 117501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5352, 1, 0, 1, 0, "いつ急患が来ても\n対応できるように\nしておきましょうか", 117501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5353, 4, 0, 1, 0, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 117501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5354, 8, 0, 1, 0, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 117501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5355, 1, 0, 1, 0, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 117501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5356, 1, 0, 1, 0, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 117501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5357, 1, 0, 1, 0, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 117501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5358, 1, 0, 1, 0, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 117501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5359, 4, 0, 1, 0, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 117501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5360, 8, 0, 1, 0, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 117501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5361, 1, 1, 1, 0, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 117501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5362, 1, 2, 1, 0, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 117501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5363, 1, 3, 1, 0, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 117501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5365, 1, 0, 1, 0, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 117501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5366, 1, 0, 1, 0, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 117501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5367, 1, 0, 1, 0, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 117501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5368, 1, 0, 1, 0, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 117501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5370, 1, 0, 1, 0, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 117501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5371, 1, 0, 1, 0, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 117501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5400, 1, 0, 1, 0, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 117601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5401, 1, 0, 1, 0, "美しさとは\n日々の努力が\n作るものなんだよ", 117601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5402, 1, 0, 1, 0, "大変だ…\nボクは美しく\nなりすぎたかも", 117601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5403, 1, 0, 1, 0, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 117601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5404, 1, 0, 1, 0, "どんな服装をしても\nボクの美しさは\n変わらないね", 117601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5405, 4, 0, 1, 0, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 117601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5406, 8, 0, 1, 0, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 117601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5407, 1, 0, 1, 0, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 117601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5408, 1, 0, 1, 0, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 117601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5409, 1, 0, 1, 0, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 117601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5410, 1, 0, 1, 0, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 117601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5411, 4, 0, 1, 0, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 117601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5412, 8, 0, 1, 0, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 117601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5413, 1, 1, 1, 0, "さすがボク\n目が覚めるような\n美しさでしょ？", 117601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5414, 1, 2, 1, 0, "美は食で作られる\n美容にいいものを\n食べようよ！", 117601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5415, 1, 3, 1, 0, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 117601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5417, 1, 0, 1, 0, "ふう～…\nリラックスする\nボクも美しい", 117601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5418, 1, 0, 1, 0, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 117601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5419, 1, 0, 1, 0, "美しいボクは\n常にいい香りを\nしていなくちゃね", 117601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5420, 1, 0, 1, 0, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 117601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5422, 1, 0, 1, 0, "バランスのいい食事が\n美しいボクを\n作るのさ", 117601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5423, 1, 0, 1, 0, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 117601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5425, 12, 0, 1, 0, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 105701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5426, 12, 0, 1, 0, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 105701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5427, 1, 0, 1, 0, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 118001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5428, 1, 0, 1, 0, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 118001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5429, 1, 0, 1, 0, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 118001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5430, 1, 0, 1, 0, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 118001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5431, 1, 0, 1, 0, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 118001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5432, 4, 0, 1, 0, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 118001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5433, 8, 0, 1, 0, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 118001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5434, 1, 0, 1, 0, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 118001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5435, 1, 0, 1, 0, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 118001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5436, 1, 0, 1, 0, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 118001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5437, 1, 0, 1, 0, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 118001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5438, 4, 0, 1, 0, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 118001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5439, 8, 0, 1, 0, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 118001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5440, 1, 1, 1, 0, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 118001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5441, 1, 2, 1, 0, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 118001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5442, 1, 3, 1, 0, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 118001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5444, 1, 0, 1, 0, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 118001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5445, 1, 0, 1, 0, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 118001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5446, 1, 0, 1, 0, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 118001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5447, 1, 0, 1, 0, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 118001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5449, 1, 0, 1, 0, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 118001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5450, 1, 0, 1, 0, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 118001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5452, 1, 0, 1, 0, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 117701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5453, 1, 0, 1, 0, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 117701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5454, 1, 0, 1, 0, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 117701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5455, 1, 0, 1, 0, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 117701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5456, 1, 0, 1, 0, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 117701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5457, 4, 0, 1, 0, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 117701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5458, 8, 0, 1, 0, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 117701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5459, 1, 0, 1, 0, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 117701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5460, 1, 0, 1, 0, "この制服、\nハロウィンらしくて\n大好きさ～♪", 117701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5461, 1, 0, 1, 0, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 117701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5462, 1, 0, 1, 0, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 117701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5463, 4, 0, 1, 0, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 117701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5464, 8, 0, 1, 0, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 117701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5465, 1, 1, 1, 0, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 117701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5466, 1, 2, 1, 0, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 117701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5467, 1, 3, 1, 0, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 117701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5469, 1, 0, 1, 0, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 117701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5470, 1, 0, 1, 0, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 117701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5471, 1, 0, 1, 0, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 117701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5472, 1, 0, 1, 0, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 117701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5474, 1, 0, 1, 0, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 117701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5475, 1, 0, 1, 0, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 117701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5477, 1, 0, 1, 0, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 117701, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5479, 1, 0, 1, 0, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 117801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5480, 1, 0, 1, 0, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 117801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5481, 1, 0, 1, 0, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 117801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5482, 1, 0, 1, 0, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 117801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5483, 1, 0, 1, 0, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 117801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5484, 4, 0, 1, 0, "幽霊になる前に\n天下統一を\n果たしたいデスね", 117801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5485, 8, 0, 1, 0, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 117801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5486, 1, 0, 1, 0, "ショーグン！\n悪霊退治に\n出かけるデース！", 117801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5487, 1, 0, 1, 0, "妖魔を家来に\nしてみるのは\nどうデス？", 117801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5488, 1, 0, 1, 0, "マントを使って\nウツセミの術の精度を\n高めるデス！", 117801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5489, 1, 0, 1, 0, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 117801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5490, 4, 0, 1, 0, "あの世でも\nショーグンに\nお仕えしマスよー！", 117801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5491, 8, 0, 1, 0, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 117801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5492, 1, 1, 1, 0, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 117801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5493, 1, 2, 1, 0, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 117801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5494, 1, 3, 1, 0, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 117801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5496, 1, 0, 1, 0, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 117801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5497, 1, 0, 1, 0, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 117801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5498, 1, 0, 1, 0, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 117801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5499, 1, 0, 1, 0, "パレードの熱気に\n負けないくらい\n熱いデス！", 117801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5501, 1, 0, 1, 0, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 117801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5502, 1, 0, 1, 0, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 117801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5504, 1, 0, 1, 0, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 117801, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5531, 12, 0, 1, 0, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 101301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5532, 12, 0, 1, 0, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 101301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5533, 1, 0, 1, 0, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 117901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5534, 1, 0, 1, 0, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 117901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5535, 1, 0, 1, 0, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 117901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5536, 1, 0, 1, 0, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 117901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5537, 1, 0, 1, 0, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 117901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5538, 4, 0, 1, 0, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 117901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5539, 8, 0, 1, 0, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 117901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5540, 1, 0, 1, 0, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 117901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5541, 1, 0, 1, 0, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 117901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5542, 1, 0, 1, 0, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 117901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5543, 1, 0, 1, 0, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 117901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5544, 4, 0, 1, 0, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 117901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5545, 8, 0, 1, 0, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 117901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5546, 1, 1, 1, 0, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 117901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5547, 1, 2, 1, 0, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 117901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5548, 1, 3, 1, 0, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 117901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5550, 1, 0, 1, 0, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 117901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5551, 1, 0, 1, 0, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 117901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5552, 1, 0, 1, 0, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 117901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5553, 1, 0, 1, 0, "やばばば…\nあったかくて\n眠くなっちゃう～", 117901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5555, 1, 0, 1, 0, "これ食べて\nもうひとがんばり\nしよーっと！", 117901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5556, 1, 0, 1, 0, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 117901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5558, 1, 0, 1, 0, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 117901, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5560, 1, 0, 1, 0, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 118501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5561, 1, 0, 1, 0, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 118501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5562, 1, 0, 1, 0, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 118501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5563, 1, 0, 1, 0, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 118501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5564, 1, 0, 1, 0, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 118501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5565, 4, 0, 1, 0, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 118501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5566, 8, 0, 1, 0, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 118501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5567, 1, 0, 1, 0, "何かわからないことが\nあるときは私に\n聞いてくださいね", 118501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5568, 1, 0, 1, 0, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 118501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5569, 1, 0, 1, 0, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 118501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5570, 1, 0, 1, 0, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 118501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5571, 4, 0, 1, 0, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 118501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5572, 8, 0, 1, 0, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 118501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5573, 1, 1, 1, 0, "今日も一日\n管理人として\nがんばります♪", 118501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5574, 1, 2, 1, 0, "お腹がすきました…\nそろそろお昼に\nしませんか？", 118501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5575, 1, 3, 1, 0, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 118501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5577, 1, 0, 1, 0, "良質なお仕事は\n良質な休憩から\nです♪", 118501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5578, 1, 0, 1, 0, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 118501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5579, 1, 0, 1, 0, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 118501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5580, 1, 0, 1, 0, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 118501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5582, 1, 0, 1, 0, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 118501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5583, 1, 0, 1, 0, "新条例はこのように\n活用されている、と…\nふむふむ…", 118501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5585, 1, 0, 1, 0, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 106701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5586, 1, 0, 1, 0, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 106701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5587, 1, 0, 1, 0, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 106701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5588, 1, 0, 1, 0, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 106701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5589, 1, 0, 1, 0, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 106701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5590, 4, 0, 1, 0, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 106701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5591, 8, 0, 1, 0, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 106701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5592, 1, 0, 1, 0, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 106701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5593, 1, 0, 1, 0, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 106701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5594, 1, 0, 1, 0, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 106701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5595, 1, 0, 1, 0, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 106701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5596, 4, 0, 1, 0, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 106701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5597, 8, 0, 1, 0, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 106701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5598, 1, 1, 1, 0, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 106701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5599, 1, 2, 1, 0, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 106701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5600, 1, 3, 1, 0, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 106701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5602, 1, 0, 1, 0, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 106701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5603, 1, 0, 1, 0, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 106701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5604, 1, 0, 1, 0, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 106701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5605, 1, 0, 1, 0, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 106701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5607, 1, 0, 1, 0, "この味付け…\nあっちでも再現\nできるかな？", 106701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5608, 1, 0, 1, 0, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 106701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5610, 1, 0, 1, 0, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 119001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5611, 1, 0, 1, 0, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 119001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5612, 1, 0, 1, 0, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 119001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5613, 1, 0, 1, 0, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 119001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5614, 1, 0, 1, 0, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 119001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5615, 4, 0, 1, 0, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 119001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5616, 8, 0, 1, 0, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 119001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5617, 1, 0, 1, 0, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 119001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5618, 1, 0, 1, 0, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 119001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5619, 1, 0, 1, 0, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 119001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5620, 1, 0, 1, 0, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 119001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5621, 4, 0, 1, 0, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 119001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5622, 8, 0, 1, 0, "君との秘密が\nまた一つ\n増えちゃったね♪", 119001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5623, 1, 1, 1, 0, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 119001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5624, 1, 2, 1, 0, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 119001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5625, 1, 3, 1, 0, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 119001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5627, 1, 0, 1, 0, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 119001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5628, 1, 0, 1, 0, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 119001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5629, 1, 0, 1, 0, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 119001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5630, 1, 0, 1, 0, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 119001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5632, 1, 0, 1, 0, "生徒の見本に\nなれるように\n好き嫌いはしないの", 119001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5633, 1, 0, 1, 0, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 119001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5635, 12, 0, 1, 0, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 106301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5636, 12, 0, 1, 0, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 106301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5637, 12, 0, 1, 0, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 105301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5638, 12, 0, 1, 0, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 105301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5639, 1, 0, 1, 0, "ああ～\nドＭミストが\nあふれてしまいます～", 119101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5640, 1, 0, 1, 0, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 119101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5641, 1, 0, 1, 0, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 119101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5642, 1, 0, 1, 0, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 119101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5643, 1, 0, 1, 0, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 119101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5644, 4, 0, 1, 0, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 119101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5645, 8, 0, 1, 0, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 119101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5646, 1, 0, 1, 0, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 119101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5647, 1, 0, 1, 0, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 119101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5648, 1, 0, 1, 0, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 119101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5649, 1, 0, 1, 0, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 119101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5650, 4, 0, 1, 0, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 119101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5651, 8, 0, 1, 0, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 119101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5652, 1, 1, 1, 0, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 119101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5653, 1, 2, 1, 0, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 119101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5654, 1, 3, 1, 0, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 119101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5656, 1, 0, 1, 0, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 119101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5657, 1, 0, 1, 0, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 119101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5658, 1, 0, 1, 0, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 119101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5659, 1, 0, 1, 0, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 119101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5661, 1, 0, 1, 0, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 119101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5662, 1, 0, 1, 0, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 119101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5664, 1, 0, 1, 0, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 119901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5665, 1, 0, 1, 0, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 119901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5666, 1, 0, 1, 0, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 119901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5667, 1, 0, 1, 0, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 119901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5668, 1, 0, 1, 0, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 119901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5669, 4, 0, 1, 0, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 119901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5670, 8, 0, 1, 0, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 119901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5671, 1, 0, 1, 0, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 119901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5672, 1, 0, 1, 0, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 119901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5673, 1, 0, 1, 0, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 119901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5674, 1, 0, 1, 0, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 119901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5675, 4, 0, 1, 0, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 119901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5676, 8, 0, 1, 0, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 119901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5677, 1, 1, 1, 0, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 119901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5678, 1, 2, 1, 0, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 119901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5679, 1, 3, 1, 0, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 119901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5681, 1, 0, 1, 0, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 119901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5682, 1, 0, 1, 0, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 119901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5683, 1, 0, 1, 0, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 119901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5684, 1, 0, 1, 0, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 119901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5686, 1, 0, 1, 0, "これはオマエがくれる\nプリンとは\n別腹なの～", 119901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5687, 1, 0, 1, 0, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 119901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5689, 1, 0, 1, 0, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 119901, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5690, 1, 0, 1, 0, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 119901, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5691, 1, 0, 1, 0, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 119201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5692, 1, 0, 1, 0, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 119201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5693, 1, 0, 1, 0, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 119201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5694, 1, 0, 1, 0, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 119201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5695, 1, 0, 1, 0, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 119201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5696, 4, 0, 1, 0, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 119201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5697, 8, 0, 1, 0, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 119201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5698, 1, 0, 1, 0, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 119201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5699, 1, 0, 1, 0, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 119201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5700, 1, 0, 1, 0, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 119201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5701, 1, 0, 1, 0, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 119201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5702, 4, 0, 1, 0, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 119201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5703, 8, 0, 1, 0, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 119201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5704, 1, 1, 1, 0, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 119201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5705, 1, 2, 1, 0, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 119201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5706, 1, 3, 1, 0, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 119201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5708, 1, 0, 1, 0, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 119201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5709, 1, 0, 1, 0, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 119201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5710, 1, 0, 1, 0, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 119201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5711, 1, 0, 1, 0, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 119201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5713, 1, 0, 1, 0, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 119201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5714, 1, 0, 1, 0, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 119201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5716, 1, 0, 1, 0, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 119201, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5717, 1, 0, 1, 0, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 119201, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5718, 12, 0, 1, 0, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 105001, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5719, 12, 0, 1, 0, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 105001, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5720, 1, 0, 1, 0, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 119301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5721, 1, 0, 1, 0, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 119301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5722, 1, 0, 1, 0, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 119301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5723, 1, 0, 1, 0, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 119301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5724, 1, 0, 1, 0, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 119301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5725, 4, 0, 1, 0, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 119301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5726, 8, 0, 1, 0, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 119301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5727, 1, 0, 1, 0, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 119301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5728, 1, 0, 1, 0, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 119301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5729, 1, 0, 1, 0, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 119301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5730, 1, 0, 1, 0, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 119301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5731, 4, 0, 1, 0, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 119301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5732, 8, 0, 1, 0, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 119301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5733, 1, 1, 1, 0, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 119301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5734, 1, 2, 1, 0, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 119301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5735, 1, 3, 1, 0, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 119301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5737, 1, 0, 1, 0, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 119301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5738, 1, 0, 1, 0, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 119301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5739, 1, 0, 1, 0, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 119301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5740, 1, 0, 1, 0, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 119301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5742, 1, 0, 1, 0, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 119301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5743, 1, 0, 1, 0, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 119301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5745, 1, 0, 1, 0, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 119301, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5746, 1, 0, 1, 0, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 119301, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5747, 1, 0, 1, 0, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 120601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5748, 1, 0, 1, 0, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 120601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5749, 1, 0, 1, 0, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 120601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5750, 1, 0, 1, 0, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 120601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5751, 1, 0, 1, 0, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 120601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5752, 4, 0, 1, 0, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 120601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5753, 8, 0, 1, 0, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 120601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5754, 1, 0, 1, 0, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 120601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5755, 1, 0, 1, 0, "きゃあっ！？\nへ、変なところを\n触らないでください！", 120601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5756, 1, 0, 1, 0, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 120601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5757, 1, 0, 1, 0, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 120601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5758, 4, 0, 1, 0, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 120601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5759, 8, 0, 1, 0, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 120601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5760, 1, 1, 1, 0, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 120601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5761, 1, 2, 1, 0, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 120601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5762, 1, 3, 1, 0, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 120601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5764, 1, 0, 1, 0, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 120601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5765, 1, 0, 1, 0, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 120601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5766, 1, 0, 1, 0, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 120601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5767, 1, 0, 1, 0, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 120601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5769, 1, 0, 1, 0, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 120601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5770, 1, 0, 1, 0, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 120601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5773, 1, 0, 1, 0, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 120401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5774, 1, 0, 1, 0, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 120401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5775, 1, 0, 1, 0, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 120401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5776, 1, 0, 1, 0, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 120401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5777, 1, 0, 1, 0, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 120401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5778, 4, 0, 1, 0, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 120401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5779, 8, 0, 1, 0, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 120401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5780, 1, 0, 1, 0, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 120401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5781, 1, 0, 1, 0, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 120401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5782, 1, 0, 1, 0, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 120401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5783, 1, 0, 1, 0, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 120401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5784, 4, 0, 1, 0, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 120401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5785, 8, 0, 1, 0, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 120401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5786, 1, 1, 1, 0, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 120401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5787, 1, 2, 1, 0, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 120401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5788, 1, 3, 1, 0, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 120401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5790, 1, 0, 1, 0, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 120401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5791, 1, 0, 1, 0, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 120401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5792, 1, 0, 1, 0, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 120401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5793, 1, 0, 1, 0, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 120401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5795, 1, 0, 1, 0, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 120401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5796, 1, 0, 1, 0, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 120401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5799, 1, 0, 1, 0, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 120501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5800, 1, 0, 1, 0, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 120501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5801, 1, 0, 1, 0, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 120501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5802, 1, 0, 1, 0, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 120501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5803, 1, 0, 1, 0, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 120501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5804, 4, 0, 1, 0, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 120501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5805, 8, 0, 1, 0, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 120501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5806, 1, 0, 1, 0, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 120501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5807, 1, 0, 1, 0, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 120501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5808, 1, 0, 1, 0, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 120501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5809, 1, 0, 1, 0, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 120501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5810, 4, 0, 1, 0, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 120501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5811, 8, 0, 1, 0, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 120501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5812, 1, 1, 1, 0, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 120501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5813, 1, 2, 1, 0, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 120501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5814, 1, 3, 1, 0, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 120501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5816, 1, 0, 1, 0, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 120501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5817, 1, 0, 1, 0, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 120501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5818, 1, 0, 1, 0, "においをうまく使った\nイタズラって…\nなにかないかな？", 120501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5819, 1, 0, 1, 0, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 120501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5821, 1, 0, 1, 0, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 120501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5822, 1, 0, 1, 0, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 120501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5825, 1, 0, 1, 0, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 120801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5826, 1, 0, 1, 0, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 120801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5827, 1, 0, 1, 0, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 120801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5828, 1, 0, 1, 0, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 120801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5829, 1, 0, 1, 0, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 120801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5830, 4, 0, 1, 0, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 120801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5831, 8, 0, 1, 0, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 120801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5832, 1, 0, 1, 0, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 120801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5833, 1, 0, 1, 0, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 120801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5834, 1, 0, 1, 0, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 120801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5835, 1, 0, 1, 0, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 120801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5836, 4, 0, 1, 0, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 120801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5837, 8, 0, 1, 0, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 120801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5838, 1, 1, 1, 0, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 120801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5839, 1, 2, 1, 0, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 120801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5840, 1, 3, 1, 0, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 120801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5842, 1, 0, 1, 0, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 120801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5843, 1, 0, 1, 0, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 120801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5844, 1, 0, 1, 0, "これは冬の花の香り…\n風情があって\nいいじゃないか", 120801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5845, 1, 0, 1, 0, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 120801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5847, 1, 0, 1, 0, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 120801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5848, 1, 0, 1, 0, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 120801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5850, 1, 0, 1, 0, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 120701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5851, 1, 0, 1, 0, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 120701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5852, 1, 0, 1, 0, "この格好も\n慣れてくれば\n動きやすいわ", 120701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5853, 1, 0, 1, 0, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 120701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5854, 1, 0, 1, 0, "おせち料理\nネビアの分も\n用意しなくちゃね", 120701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5855, 4, 0, 1, 0, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 120701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5856, 8, 0, 1, 0, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 120701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5857, 1, 0, 1, 0, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 120701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5858, 1, 0, 1, 0, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 120701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5859, 1, 0, 1, 0, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 120701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5860, 1, 0, 1, 0, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 120701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5861, 4, 0, 1, 0, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 120701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5862, 8, 0, 1, 0, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 120701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5863, 1, 1, 1, 0, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 120701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5864, 1, 2, 1, 0, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 120701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5865, 1, 3, 1, 0, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 120701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5867, 1, 0, 1, 0, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 120701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5868, 1, 0, 1, 0, "ふふっ\nかわいいものって\n癒されるわね…", 120701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5869, 1, 0, 1, 0, "…ん\nこれが梅の香り？\n…いい匂いね", 120701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5870, 1, 0, 1, 0, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 120701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5872, 1, 0, 1, 0, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 120701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5873, 1, 0, 1, 0, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 120701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5875, 12, 0, 1, 0, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 104201, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5876, 12, 0, 1, 0, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 104201, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5877, 1, 0, 1, 0, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 120901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5878, 1, 0, 1, 0, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 120901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5879, 1, 0, 1, 0, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 120901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5880, 1, 0, 1, 0, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 120901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5881, 1, 0, 1, 0, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 120901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5882, 4, 0, 1, 0, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 120901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5883, 8, 0, 1, 0, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 120901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5884, 1, 0, 1, 0, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 120901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5885, 1, 0, 1, 0, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 120901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5886, 1, 0, 1, 0, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 120901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5887, 1, 0, 1, 0, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 120901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5888, 4, 0, 1, 0, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 120901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5889, 8, 0, 1, 0, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 120901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5890, 1, 1, 1, 0, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 120901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5891, 1, 2, 1, 0, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 120901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5892, 1, 3, 1, 0, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 120901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5894, 1, 0, 1, 0, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 120901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5895, 1, 0, 1, 0, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 120901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5896, 1, 0, 1, 0, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 120901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5897, 1, 0, 1, 0, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 120901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5899, 1, 0, 1, 0, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 120901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5900, 1, 0, 1, 0, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 120901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5902, 1, 0, 1, 0, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 121001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5903, 1, 0, 1, 0, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 121001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5904, 1, 0, 1, 0, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 121001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5905, 1, 0, 1, 0, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 121001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5906, 1, 0, 1, 0, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 121001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5907, 4, 0, 1, 0, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 121001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5908, 8, 0, 1, 0, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 121001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5909, 1, 0, 1, 0, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 121001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5910, 1, 0, 1, 0, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 121001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5911, 1, 0, 1, 0, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 121001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5912, 1, 0, 1, 0, "あなたの想いが\n全力全開を超える\n力をくれました！", 121001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5913, 4, 0, 1, 0, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 121001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5914, 8, 0, 1, 0, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 121001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5915, 1, 1, 1, 0, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 121001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5916, 1, 2, 1, 0, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 121001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5917, 1, 3, 1, 0, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 121001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5919, 1, 0, 1, 0, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 121001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5920, 1, 0, 1, 0, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 121001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5921, 1, 0, 1, 0, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 121001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5922, 1, 0, 1, 0, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 121001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5924, 1, 0, 1, 0, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 121001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5925, 1, 0, 1, 0, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 121001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5927, 1, 0, 1, 0, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 121201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5928, 1, 0, 1, 0, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 121201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5929, 1, 0, 1, 0, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 121201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5930, 1, 0, 1, 0, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 121201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5931, 1, 0, 1, 0, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 121201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5932, 4, 0, 1, 0, "少年が\n目を覚ましてくれて\n本当によかった…", 121201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5933, 8, 0, 1, 0, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 121201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5934, 1, 0, 1, 0, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 121201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5935, 1, 0, 1, 0, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 121201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5936, 1, 0, 1, 0, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 121201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5937, 1, 0, 1, 0, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 121201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5938, 4, 0, 1, 0, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 121201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5939, 8, 0, 1, 0, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 121201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5940, 1, 1, 1, 0, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 121201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5941, 1, 2, 1, 0, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 121201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5942, 1, 3, 1, 0, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 121201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5944, 1, 0, 1, 0, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 121201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5945, 1, 0, 1, 0, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 121201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5946, 1, 0, 1, 0, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 121201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5947, 1, 0, 1, 0, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 121201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5949, 1, 0, 1, 0, "食の大切さは\nいつの食事からも\n感じられるね♪", 121201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5950, 1, 0, 1, 0, "ふむ…面白い！\n今度全巻\n借りにこようっと", 121201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5952, 1, 0, 1, 0, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 116401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5953, 1, 0, 1, 0, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 116401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5954, 1, 0, 1, 0, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 116401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5955, 1, 0, 1, 0, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 116401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5956, 1, 0, 1, 0, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 116401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5957, 4, 0, 1, 0, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 116401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5958, 8, 0, 1, 0, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 116401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5959, 1, 0, 1, 0, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 116401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5960, 1, 0, 1, 0, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 116401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5961, 1, 0, 1, 0, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 116401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5962, 1, 0, 1, 0, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 116401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5963, 4, 0, 1, 0, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 116401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5964, 8, 0, 1, 0, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 116401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5965, 1, 1, 1, 0, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 116401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5966, 1, 2, 1, 0, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 116401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5967, 1, 3, 1, 0, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 116401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5969, 1, 0, 1, 0, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 116401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5970, 1, 0, 1, 0, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 116401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5971, 1, 0, 1, 0, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 116401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5972, 1, 0, 1, 0, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 116401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5974, 1, 0, 1, 0, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 116401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5975, 1, 0, 1, 0, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 116401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5977, 1, 0, 1, 0, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 121101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5978, 1, 0, 1, 0, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 121101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5979, 1, 0, 1, 0, "あいつのこと、\n全部受け入れるって\n決めたから！", 121101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5980, 1, 0, 1, 0, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 121101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5981, 1, 0, 1, 0, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 121101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5982, 4, 0, 1, 0, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 121101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5983, 8, 0, 1, 0, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 121101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5984, 1, 0, 1, 0, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 121101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5985, 1, 0, 1, 0, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 121101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5986, 1, 0, 1, 0, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 121101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5987, 1, 0, 1, 0, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 121101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5988, 4, 0, 1, 0, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 121101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5989, 8, 0, 1, 0, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 121101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5990, 1, 1, 1, 0, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 121101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5991, 1, 2, 1, 0, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 121101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5992, 1, 3, 1, 0, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 121101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5994, 1, 0, 1, 0, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 121101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5995, 1, 0, 1, 0, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 121101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5996, 1, 0, 1, 0, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 121101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5997, 1, 0, 1, 0, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 121101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (5999, 1, 0, 1, 0, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 121101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6000, 1, 0, 1, 0, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 121101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6002, 1, 0, 1, 0, "お友だち…\nできるかしら…？\nこんなわたしでも…", 118101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6003, 1, 0, 1, 0, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 118101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6004, 1, 0, 1, 0, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 118101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6005, 1, 0, 1, 0, "～♪", 118101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6006, 1, 0, 1, 0, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 118101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6007, 4, 0, 1, 0, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 118101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6008, 8, 0, 1, 0, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 118101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6009, 1, 0, 1, 0, "ええと…\nお腹、空いてる？\nおねむじゃない？", 118101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6010, 1, 0, 1, 0, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 118101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6011, 1, 0, 1, 0, "お喋り…？\nわたしでいいの…？\nうれしい…", 118101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6012, 1, 0, 1, 0, "あなたの好きな歌…\n教えて…？\n練習、するから…", 118101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6013, 4, 0, 1, 0, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 118101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6014, 8, 0, 1, 0, "安心、して…\nわたしの歌で、\nあなたを守るから…", 118101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6015, 1, 1, 1, 0, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 118101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6016, 1, 2, 1, 0, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 118101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6017, 1, 3, 1, 0, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 118101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6019, 1, 0, 1, 0, "こんな特等席…\n恐縮、です…", 118101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6020, 1, 0, 1, 0, "い、今のうちに、\nこの子でお世話の\n練習を…", 118101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6021, 1, 0, 1, 0, "いい香り…\n…つらい気持ちも\n癒してくれる", 118101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6022, 1, 0, 1, 0, "あったかい…\nいつまでも、\nこうしていられそう…", 118101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6024, 1, 0, 1, 0, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 118101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6025, 1, 0, 1, 0, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 118101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6027, 1, 0, 1, 0, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 121301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6028, 1, 0, 1, 0, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 121301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6029, 1, 0, 1, 0, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 121301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6030, 1, 0, 1, 0, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 121301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6031, 1, 0, 1, 0, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 121301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6032, 4, 0, 1, 0, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 121301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6033, 8, 0, 1, 0, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 121301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6034, 1, 0, 1, 0, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 121301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6035, 1, 0, 1, 0, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 121301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6036, 1, 0, 1, 0, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 121301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6037, 1, 0, 1, 0, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 121301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6038, 4, 0, 1, 0, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 121301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6039, 8, 0, 1, 0, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 121301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6040, 1, 1, 1, 0, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 121301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6041, 1, 2, 1, 0, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 121301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6042, 1, 3, 1, 0, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 121301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6044, 1, 0, 1, 0, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 121301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6045, 1, 0, 1, 0, "今日も演技の\n練習に付き合ってね♪", 121301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6046, 1, 0, 1, 0, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 121301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6047, 1, 0, 1, 0, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 121301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6049, 1, 0, 1, 0, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 121301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6050, 1, 0, 1, 0, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 121301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6052, 12, 0, 1, 0, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 107501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6053, 12, 0, 1, 0, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 107501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6054, 12, 0, 1, 0, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 107601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6055, 12, 0, 1, 0, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 107601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6056, 12, 0, 1, 0, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 107801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6057, 12, 0, 1, 0, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 107801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6058, 1, 0, 1, 0, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 121401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6059, 1, 0, 1, 0, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 121401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6060, 1, 0, 1, 0, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 121401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6061, 1, 0, 1, 0, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 121401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6062, 1, 0, 1, 0, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 121401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6063, 4, 0, 1, 0, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 121401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6064, 8, 0, 1, 0, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 121401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6065, 1, 0, 1, 0, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 121401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6066, 1, 0, 1, 0, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 121401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6067, 1, 0, 1, 0, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 121401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6068, 1, 0, 1, 0, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 121401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6069, 4, 0, 1, 0, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 121401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6070, 8, 0, 1, 0, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 121401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6071, 1, 1, 1, 0, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 121401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6072, 1, 2, 1, 0, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 121401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6073, 1, 3, 1, 0, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 121401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6075, 1, 0, 1, 0, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 121401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6076, 1, 0, 1, 0, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 121401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6077, 1, 0, 1, 0, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 121401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6078, 1, 0, 1, 0, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 121401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6080, 1, 0, 1, 0, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 121401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6081, 1, 0, 1, 0, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 121401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6083, 1, 0, 1, 0, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 121501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6084, 1, 0, 1, 0, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 121501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6085, 1, 0, 1, 0, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 121501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6086, 1, 0, 1, 0, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 121501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6087, 1, 0, 1, 0, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 121501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6088, 4, 0, 1, 0, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 121501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6089, 8, 0, 1, 0, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 121501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6090, 1, 0, 1, 0, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 121501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6091, 1, 0, 1, 0, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 121501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6092, 1, 0, 1, 0, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 121501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6093, 1, 0, 1, 0, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 121501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6094, 4, 0, 1, 0, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 121501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6095, 8, 0, 1, 0, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 121501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6096, 1, 1, 1, 0, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 121501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6097, 1, 2, 1, 0, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 121501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6098, 1, 3, 1, 0, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 121501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6100, 1, 0, 1, 0, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 121501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6101, 1, 0, 1, 0, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 121501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6102, 1, 0, 1, 0, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 121501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6103, 1, 0, 1, 0, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 121501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6105, 1, 0, 1, 0, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 121501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6106, 1, 0, 1, 0, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 121501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6108, 12, 0, 1, 0, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 104501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6109, 12, 0, 1, 0, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 104501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6110, 1, 0, 1, 0, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 121601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6111, 1, 0, 1, 0, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 121601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6112, 1, 0, 1, 0, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 121601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6113, 1, 0, 1, 0, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 121601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6114, 1, 0, 1, 0, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 121601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6115, 4, 0, 1, 0, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 121601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6116, 8, 0, 1, 0, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 121601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6117, 1, 0, 1, 0, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 121601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6118, 1, 0, 1, 0, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 121601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6119, 1, 0, 1, 0, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 121601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6120, 1, 0, 1, 0, "しーっ！\n今、尾行中なんです\n話はあとで…", 121601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6121, 4, 0, 1, 0, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 121601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6122, 8, 0, 1, 0, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 121601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6123, 1, 1, 1, 0, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 121601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6124, 1, 2, 1, 0, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 121601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6125, 1, 3, 1, 0, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 121601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6127, 1, 0, 1, 0, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 121601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6128, 1, 0, 1, 0, "くう…！\nこのかわいさは\n盗めないです…♪", 121601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6129, 1, 0, 1, 0, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 121601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6130, 1, 0, 1, 0, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 121601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6132, 1, 0, 1, 0, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 121601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6133, 1, 0, 1, 0, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 121601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6135, 1, 0, 1, 0, "あら、サレンさんは\nどこに行かれた\nのかしら？", 121701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6136, 1, 0, 1, 0, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 121701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6137, 1, 0, 1, 0, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 121701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6138, 1, 0, 1, 0, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 121701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6139, 1, 0, 1, 0, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 121701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6140, 4, 0, 1, 0, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 121701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6141, 8, 0, 1, 0, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 121701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6142, 1, 0, 1, 0, "ふふ、ごきげんよう\n私、絶好調でしてよ", 121701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6143, 1, 0, 1, 0, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 121701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6144, 1, 0, 1, 0, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 121701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6145, 1, 0, 1, 0, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 121701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6146, 4, 0, 1, 0, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 121701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6147, 8, 0, 1, 0, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 121701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6148, 1, 1, 1, 0, "さあ！\n今日も一日\n全力で参りますわ！", 121701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6149, 1, 2, 1, 0, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 121701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6150, 1, 3, 1, 0, "もう少しだけ…\n明日の準備をして\nおかないと…", 121701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6152, 1, 0, 1, 0, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 121701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6153, 1, 0, 1, 0, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 121701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6154, 1, 0, 1, 0, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 121701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6155, 1, 0, 1, 0, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 121701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6157, 1, 0, 1, 0, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 121701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6158, 1, 0, 1, 0, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 121701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6161, 1, 0, 1, 0, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 121801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6162, 1, 0, 1, 0, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 121801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6163, 1, 0, 1, 0, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 121801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6164, 1, 0, 1, 0, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 121801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6165, 1, 0, 1, 0, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 121801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6166, 4, 0, 1, 0, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 121801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6167, 8, 0, 1, 0, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 121801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6168, 1, 0, 1, 0, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 121801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6169, 1, 0, 1, 0, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 121801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6170, 1, 0, 1, 0, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 121801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6171, 1, 0, 1, 0, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 121801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6172, 4, 0, 1, 0, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 121801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6173, 8, 0, 1, 0, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 121801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6174, 1, 1, 1, 0, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 121801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6175, 1, 2, 1, 0, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 121801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6176, 1, 3, 1, 0, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 121801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6178, 1, 0, 1, 0, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 121801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6179, 1, 0, 1, 0, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 121801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6180, 1, 0, 1, 0, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 121801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6181, 1, 0, 1, 0, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 121801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6183, 1, 0, 1, 0, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 121801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6184, 1, 0, 1, 0, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 121801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6187, 1, 0, 1, 0, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 122001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6188, 1, 0, 1, 0, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 122001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6189, 1, 0, 1, 0, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 122001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6190, 1, 0, 1, 0, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 122001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6191, 1, 0, 1, 0, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 122001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6192, 4, 0, 1, 0, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 122001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6193, 8, 0, 1, 0, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 122001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6194, 1, 0, 1, 0, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 122001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6195, 1, 0, 1, 0, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 122001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6196, 1, 0, 1, 0, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 122001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6197, 1, 0, 1, 0, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 122001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6198, 4, 0, 1, 0, "船の上では食材が\n限られるので料理の\nいい練習になります", 122001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6199, 8, 0, 1, 0, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 122001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6200, 1, 1, 1, 0, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 122001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6201, 1, 2, 1, 0, "船の上では\n規則正しい生活が\n重要ですから", 122001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6202, 1, 3, 1, 0, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 122001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6204, 1, 0, 1, 0, "海に揺られるような\n心地よさですね\n気が休まります…", 122001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6205, 1, 0, 1, 0, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 122001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6206, 1, 0, 1, 0, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 122001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6207, 1, 0, 1, 0, "温かい…\n潮風で冷えた体に\n沁みますね", 122001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6209, 1, 0, 1, 0, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 122001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6210, 1, 0, 1, 0, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 122001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6212, 12, 0, 1, 0, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 102601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6213, 12, 0, 1, 0, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 102601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6214, 1, 0, 1, 0, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 121901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6215, 1, 0, 1, 0, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 121901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6216, 1, 0, 1, 0, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 121901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6217, 1, 0, 1, 0, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 121901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6218, 1, 0, 1, 0, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 121901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6219, 4, 0, 1, 0, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 121901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6220, 8, 0, 1, 0, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 121901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6221, 1, 0, 1, 0, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 121901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6222, 1, 0, 1, 0, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 121901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6223, 1, 0, 1, 0, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 121901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6224, 1, 0, 1, 0, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 121901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6225, 4, 0, 1, 0, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 121901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6226, 8, 0, 1, 0, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 121901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6227, 1, 1, 1, 0, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 121901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6228, 1, 2, 1, 0, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 121901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6229, 1, 3, 1, 0, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 121901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6231, 1, 0, 1, 0, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 121901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6232, 1, 0, 1, 0, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 121901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6233, 1, 0, 1, 0, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 121901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6234, 1, 0, 1, 0, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 121901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6236, 1, 0, 1, 0, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 121901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6237, 1, 0, 1, 0, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 121901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6239, 1, 0, 1, 0, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 122101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6240, 1, 0, 1, 0, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 122101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6241, 1, 0, 1, 0, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 122101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6242, 1, 0, 1, 0, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 122101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6243, 1, 0, 1, 0, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 122101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6244, 4, 0, 1, 0, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 122101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6245, 8, 0, 1, 0, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 122101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6246, 1, 0, 1, 0, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 122101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6247, 1, 0, 1, 0, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 122101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6248, 1, 0, 1, 0, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 122101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6249, 1, 0, 1, 0, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 122101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6250, 4, 0, 1, 0, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 122101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6251, 8, 0, 1, 0, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 122101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6252, 1, 1, 1, 0, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 122101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6253, 1, 2, 1, 0, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 122101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6254, 1, 3, 1, 0, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 122101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6256, 1, 0, 1, 0, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 122101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6257, 1, 0, 1, 0, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 122101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6258, 1, 0, 1, 0, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 122101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6259, 1, 0, 1, 0, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 122101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6261, 1, 0, 1, 0, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 122101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6262, 1, 0, 1, 0, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 122101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6264, 1, 0, 1, 0, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 122201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6265, 1, 0, 1, 0, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 122201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6266, 1, 0, 1, 0, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 122201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6267, 1, 0, 1, 0, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 122201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6268, 1, 0, 1, 0, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 122201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6269, 4, 0, 1, 0, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 122201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6270, 8, 0, 1, 0, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 122201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6271, 1, 0, 1, 0, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 122201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6272, 1, 0, 1, 0, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 122201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6273, 1, 0, 1, 0, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 122201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6274, 1, 0, 1, 0, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 122201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6275, 4, 0, 1, 0, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 122201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6276, 8, 0, 1, 0, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 122201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6277, 1, 1, 1, 0, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 122201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6278, 1, 2, 1, 0, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 122201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6279, 1, 3, 1, 0, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 122201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6281, 1, 0, 1, 0, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 122201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6282, 1, 0, 1, 0, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 122201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6283, 1, 0, 1, 0, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 122201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6284, 1, 0, 1, 0, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 122201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6286, 1, 0, 1, 0, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 122201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6287, 1, 0, 1, 0, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 122201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6289, 12, 0, 1, 0, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 100701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6290, 12, 0, 1, 0, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 100701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6291, 1, 0, 1, 0, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 122301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6292, 1, 0, 1, 0, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 122301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6293, 1, 0, 1, 0, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 122301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6294, 1, 0, 1, 0, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 122301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6295, 1, 0, 1, 0, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 122301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6296, 4, 0, 1, 0, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 122301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6297, 8, 0, 1, 0, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 122301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6298, 1, 0, 1, 0, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 122301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6299, 1, 0, 1, 0, "よしよし！\n今日のけんぞくも\nいい子だね♪", 122301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6300, 1, 0, 1, 0, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 122301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6301, 1, 0, 1, 0, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 122301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6302, 4, 0, 1, 0, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 122301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6303, 8, 0, 1, 0, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 122301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6304, 1, 1, 1, 0, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 122301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6305, 1, 2, 1, 0, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 122301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6306, 1, 3, 1, 0, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 122301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6308, 1, 0, 1, 0, "わー！\nふかふかだ！\nきもちぃー♪", 122301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6309, 1, 0, 1, 0, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 122301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6310, 1, 0, 1, 0, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 122301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6311, 1, 0, 1, 0, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 122301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6313, 1, 0, 1, 0, "はむはむ…\nんー！\nおいしー♪", 122301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6314, 1, 0, 1, 0, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 122301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6316, 1, 0, 1, 0, "夏といえば\nかき氷だよね\n近くにお店は…", 122501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6317, 1, 0, 1, 0, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 122501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6318, 1, 0, 1, 0, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 122501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6319, 1, 0, 1, 0, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 122501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6320, 1, 0, 1, 0, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 122501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6321, 4, 0, 1, 0, "終わらない夏は\nないけれど…\nつい願ってしまうな", 122501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6322, 8, 0, 1, 0, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 122501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6323, 1, 0, 1, 0, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 122501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6324, 1, 0, 1, 0, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 122501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6325, 1, 0, 1, 0, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 122501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6326, 1, 0, 1, 0, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 122501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6327, 4, 0, 1, 0, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 122501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6328, 8, 0, 1, 0, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 122501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6329, 1, 1, 1, 0, "潮騒の音で\n目覚めるのも\nいいものだね", 122501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6330, 1, 2, 1, 0, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 122501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6331, 1, 3, 1, 0, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 122501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6333, 1, 0, 1, 0, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 122501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6334, 1, 0, 1, 0, "肌触りのよさが\nこの格好だと\nよく分かるよ", 122501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6335, 1, 0, 1, 0, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 122501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6336, 1, 0, 1, 0, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 122501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6338, 1, 0, 1, 0, "夏を楽しみきるには\nエネルギーが\n必要だからね", 122501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6339, 1, 0, 1, 0, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 122501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6341, 1, 0, 1, 0, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 122501, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6342, 1, 0, 1, 0, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 122501, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6343, 1, 0, 1, 0, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 122401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6344, 1, 0, 1, 0, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 122401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6345, 1, 0, 1, 0, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 122401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6346, 1, 0, 1, 0, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 122401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6347, 1, 0, 1, 0, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 122401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6348, 4, 0, 1, 0, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 122401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6349, 8, 0, 1, 0, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 122401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6350, 1, 0, 1, 0, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 122401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6351, 1, 0, 1, 0, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 122401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6352, 1, 0, 1, 0, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 122401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6353, 1, 0, 1, 0, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 122401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6354, 4, 0, 1, 0, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 122401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6355, 8, 0, 1, 0, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 122401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6356, 1, 1, 1, 0, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 122401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6357, 1, 2, 1, 0, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 122401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6358, 1, 3, 1, 0, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 122401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6360, 1, 0, 1, 0, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 122401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6361, 1, 0, 1, 0, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 122401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6362, 1, 0, 1, 0, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 122401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6363, 1, 0, 1, 0, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 122401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6365, 1, 0, 1, 0, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 122401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6366, 1, 0, 1, 0, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 122401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6368, 1, 0, 1, 0, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 122401, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6369, 1, 0, 1, 0, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 122401, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6370, 12, 0, 1, 0, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 101501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6371, 12, 0, 1, 0, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 101501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6372, 1, 0, 1, 0, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 122601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6373, 1, 0, 1, 0, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 122601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6374, 1, 0, 1, 0, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 122601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6375, 1, 0, 1, 0, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 122601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6376, 1, 0, 1, 0, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 122601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6377, 4, 0, 1, 0, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 122601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6378, 8, 0, 1, 0, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 122601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6379, 1, 0, 1, 0, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 122601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6380, 1, 0, 1, 0, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 122601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6381, 1, 0, 1, 0, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 122601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6382, 1, 0, 1, 0, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 122601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6383, 4, 0, 1, 0, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 122601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6384, 8, 0, 1, 0, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 122601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6385, 1, 1, 1, 0, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 122601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6386, 1, 2, 1, 0, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 122601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6387, 1, 3, 1, 0, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 122601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6389, 1, 0, 1, 0, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 122601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6390, 1, 0, 1, 0, "かわいい～♪\n日光浴させて\nあげたいな♪", 122601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6391, 1, 0, 1, 0, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 122601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6392, 1, 0, 1, 0, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 122601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6394, 1, 0, 1, 0, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 122601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6395, 1, 0, 1, 0, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 122601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6397, 1, 0, 1, 0, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 122601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6398, 1, 0, 1, 0, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 122601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6399, 1, 0, 1, 0, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 122901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6400, 1, 0, 1, 0, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 122901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6401, 1, 0, 1, 0, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 122901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6402, 1, 0, 1, 0, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 122901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6403, 1, 0, 1, 0, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 122901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6404, 4, 0, 1, 0, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 122901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6405, 8, 0, 1, 0, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 122901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6406, 1, 0, 1, 0, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 122901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6407, 1, 0, 1, 0, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 122901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6408, 1, 0, 1, 0, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 122901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6409, 1, 0, 1, 0, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 122901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6410, 4, 0, 1, 0, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 122901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6411, 8, 0, 1, 0, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 122901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6412, 1, 1, 1, 0, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 122901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6413, 1, 2, 1, 0, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 122901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6414, 1, 3, 1, 0, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 122901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6416, 1, 0, 1, 0, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 122901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6417, 1, 0, 1, 0, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 122901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6418, 1, 0, 1, 0, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 122901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6419, 1, 0, 1, 0, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 122901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6421, 1, 0, 1, 0, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 122901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6422, 1, 0, 1, 0, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 122901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6424, 1, 0, 1, 0, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 122901, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6425, 1, 0, 1, 0, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 122901, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6426, 1, 0, 1, 0, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 122801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6427, 1, 0, 1, 0, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 122801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6428, 1, 0, 1, 0, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 122801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6429, 1, 0, 1, 0, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 122801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6430, 1, 0, 1, 0, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 122801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6431, 4, 0, 1, 0, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 122801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6432, 8, 0, 1, 0, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 122801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6433, 1, 0, 1, 0, "にいちゃん！\n早く海に行く準備\nしなよ～！", 122801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6434, 1, 0, 1, 0, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 122801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6435, 1, 0, 1, 0, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 122801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6436, 1, 0, 1, 0, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 122801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6437, 4, 0, 1, 0, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 122801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6438, 8, 0, 1, 0, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 122801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6439, 1, 1, 1, 0, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 122801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6440, 1, 2, 1, 0, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 122801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6441, 1, 3, 1, 0, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 122801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6443, 1, 0, 1, 0, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 122801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6444, 1, 0, 1, 0, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 122801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6445, 1, 0, 1, 0, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 122801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6446, 1, 0, 1, 0, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 122801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6448, 1, 0, 1, 0, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 122801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6449, 1, 0, 1, 0, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 122801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6451, 1, 0, 1, 0, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 122801, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6452, 1, 0, 1, 0, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 122801, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6453, 12, 0, 1, 0, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 102701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6454, 12, 0, 1, 0, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 102701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6455, 1, 0, 1, 0, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 122701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6456, 1, 0, 1, 0, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 122701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6457, 1, 0, 1, 0, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 122701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6458, 1, 0, 1, 0, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 122701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6459, 1, 0, 1, 0, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 122701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6460, 4, 0, 1, 0, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 122701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6461, 8, 0, 1, 0, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 122701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6462, 1, 0, 1, 0, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 122701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6463, 1, 0, 1, 0, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 122701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6464, 1, 0, 1, 0, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 122701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6465, 1, 0, 1, 0, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 122701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6466, 4, 0, 1, 0, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 122701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6467, 8, 0, 1, 0, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 122701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6468, 1, 1, 1, 0, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 122701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6469, 1, 2, 1, 0, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 122701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6470, 1, 3, 1, 0, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 122701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6472, 1, 0, 1, 0, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 122701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6473, 1, 0, 1, 0, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 122701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6474, 1, 0, 1, 0, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 122701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6475, 1, 0, 1, 0, "濡れた体を\n乾かしましょう\nタオル、タオル…", 122701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6477, 1, 0, 1, 0, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 122701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6478, 1, 0, 1, 0, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 122701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6480, 1, 0, 1, 0, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 122701, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6481, 1, 0, 1, 0, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 122701, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6482, 1, 0, 1, 0, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 123001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6483, 1, 0, 1, 0, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 123001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6484, 1, 0, 1, 0, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 123001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6485, 1, 0, 1, 0, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 123001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6486, 1, 0, 1, 0, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 123001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6487, 4, 0, 1, 0, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 123001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6488, 8, 0, 1, 0, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 123001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6489, 1, 0, 1, 0, "おいしい店とか\n知らないの？\n食べに行きましょ", 123001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6490, 1, 0, 1, 0, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 123001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6491, 1, 0, 1, 0, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 123001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6492, 1, 0, 1, 0, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 123001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6493, 4, 0, 1, 0, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 123001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6494, 8, 0, 1, 0, "どこまでだって\nあんたと一緒に\nいてやるんだから", 123001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6495, 1, 1, 1, 0, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 123001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6496, 1, 2, 1, 0, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 123001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6497, 1, 3, 1, 0, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 123001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6499, 1, 0, 1, 0, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 123001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6500, 1, 0, 1, 0, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 123001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6501, 1, 0, 1, 0, "いい香りね\n塔にもこういうの\n置いておこうかしら", 123001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6502, 1, 0, 1, 0, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 123001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6504, 1, 0, 1, 0, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 123001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6505, 1, 0, 1, 0, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 123001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6507, 1, 0, 1, 0, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 123101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6508, 1, 0, 1, 0, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 123101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6509, 1, 0, 1, 0, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 123101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6510, 1, 0, 1, 0, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 123101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6511, 1, 0, 1, 0, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 123101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6512, 4, 0, 1, 0, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 123101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6513, 8, 0, 1, 0, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 123101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6514, 1, 0, 1, 0, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 123101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6515, 1, 0, 1, 0, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 123101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6516, 1, 0, 1, 0, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 123101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6517, 1, 0, 1, 0, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 123101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6518, 4, 0, 1, 0, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 123101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6519, 8, 0, 1, 0, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 123101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6520, 1, 1, 1, 0, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 123101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6521, 1, 2, 1, 0, "支度が終わったら\n今日の冒険に\n出かけまひょか", 123101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6522, 1, 3, 1, 0, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 123101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6524, 1, 0, 1, 0, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 123101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6525, 1, 0, 1, 0, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 123101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6526, 1, 0, 1, 0, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 123101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6527, 1, 0, 1, 0, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 123101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6529, 1, 0, 1, 0, "この料理…\n冒険するときに\n作ってみまひょか", 123101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6530, 1, 0, 1, 0, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 123101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6532, 12, 0, 1, 0, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 103101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6533, 12, 0, 1, 0, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 103101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6534, 12, 0, 1, 0, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 104401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6535, 12, 0, 1, 0, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 104401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6536, 1, 0, 1, 0, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 123201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6537, 1, 0, 1, 0, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 123201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6538, 1, 0, 1, 0, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 123201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6539, 1, 0, 1, 0, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 123201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6540, 1, 0, 1, 0, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 123201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6541, 4, 0, 1, 0, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 123201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6542, 8, 0, 1, 0, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 123201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6543, 1, 0, 1, 0, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 123201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6544, 1, 0, 1, 0, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 123201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6545, 1, 0, 1, 0, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 123201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6546, 1, 0, 1, 0, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 123201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6547, 4, 0, 1, 0, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 123201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6548, 8, 0, 1, 0, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 123201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6549, 1, 1, 1, 0, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 123201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6550, 1, 2, 1, 0, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 123201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6551, 1, 3, 1, 0, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 123201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6553, 1, 0, 1, 0, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 123201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6554, 1, 0, 1, 0, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 123201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6555, 1, 0, 1, 0, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 123201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6556, 1, 0, 1, 0, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 123201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6558, 1, 0, 1, 0, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 123201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6559, 1, 0, 1, 0, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 123201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6561, 1, 0, 1, 0, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 123301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6562, 1, 0, 1, 0, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 123301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6563, 1, 0, 1, 0, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 123301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6564, 1, 0, 1, 0, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 123301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6565, 1, 0, 1, 0, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 123301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6566, 4, 0, 1, 0, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 123301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6567, 8, 0, 1, 0, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 123301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6568, 1, 0, 1, 0, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 123301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6569, 1, 0, 1, 0, "んー？\n今はだるいからパース\nあとでねー", 123301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6570, 1, 0, 1, 0, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 123301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6571, 1, 0, 1, 0, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 123301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6572, 4, 0, 1, 0, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 123301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6573, 8, 0, 1, 0, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 123301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6574, 1, 1, 1, 0, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 123301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6575, 1, 2, 1, 0, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 123301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6576, 1, 3, 1, 0, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 123301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6578, 1, 0, 1, 0, "あーラクー\nいっしょーここで\nキューケーできるわー", 123301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6579, 1, 0, 1, 0, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 123301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6580, 1, 0, 1, 0, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 123301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6581, 1, 0, 1, 0, "あったか～♪\n溶けてスライム\n戻りそ～", 123301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6583, 1, 0, 1, 0, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 123301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6584, 1, 0, 1, 0, "わ～なんかムズー\n読めなー\nウケるー", 123301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6586, 1, 0, 1, 0, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 123501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6587, 1, 0, 1, 0, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 123501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6588, 1, 0, 1, 0, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 123501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6589, 1, 0, 1, 0, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 123501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6590, 1, 0, 1, 0, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 123501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6591, 4, 0, 1, 0, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 123501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6592, 8, 0, 1, 0, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 123501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6593, 1, 0, 1, 0, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 123501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6594, 1, 0, 1, 0, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 123501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6595, 1, 0, 1, 0, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 123501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6596, 1, 0, 1, 0, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 123501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6597, 4, 0, 1, 0, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 123501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6598, 8, 0, 1, 0, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 123501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6599, 1, 1, 1, 0, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 123501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6600, 1, 2, 1, 0, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 123501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6601, 1, 3, 1, 0, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 123501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6603, 1, 0, 1, 0, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 123501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6604, 1, 0, 1, 0, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 123501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6605, 1, 0, 1, 0, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 123501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6606, 1, 0, 1, 0, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 123501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6608, 1, 0, 1, 0, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 123501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6609, 1, 0, 1, 0, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 123501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6611, 1, 0, 1, 0, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 123501, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6613, 1, 0, 1, 0, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 123601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6614, 1, 0, 1, 0, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 123601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6615, 1, 0, 1, 0, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 123601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6616, 1, 0, 1, 0, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 123601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6617, 1, 0, 1, 0, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 123601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6618, 4, 0, 1, 0, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 123601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6619, 8, 0, 1, 0, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 123601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6620, 1, 0, 1, 0, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 123601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6621, 1, 0, 1, 0, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 123601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6622, 1, 0, 1, 0, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 123601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6623, 1, 0, 1, 0, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 123601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6624, 4, 0, 1, 0, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 123601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6625, 8, 0, 1, 0, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 123601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6626, 1, 1, 1, 0, "おはよう！\n今日も朝から\nパトロールに行こう！", 123601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6627, 1, 2, 1, 0, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 123601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6628, 1, 3, 1, 0, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 123601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6630, 1, 0, 1, 0, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 123601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6631, 1, 0, 1, 0, "…かわいい\nこの子もパレードに\n連れて行きたいな", 123601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6632, 1, 0, 1, 0, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 123601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6633, 1, 0, 1, 0, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 123601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6635, 1, 0, 1, 0, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 123601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6636, 1, 0, 1, 0, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 123601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6638, 1, 0, 1, 0, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 123601, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6640, 12, 0, 1, 0, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 103701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6641, 12, 0, 1, 0, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 103701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6642, 1, 0, 1, 0, "やっぱり\nコスプレしてると\nテンションあがる～♪", 123701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6643, 1, 0, 1, 0, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 123701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6644, 1, 0, 1, 0, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 123701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6645, 1, 0, 1, 0, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 123701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6646, 1, 0, 1, 0, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 123701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6647, 4, 0, 1, 0, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 123701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6648, 8, 0, 1, 0, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 123701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6649, 1, 0, 1, 0, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 123701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6650, 1, 0, 1, 0, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 123701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6651, 1, 0, 1, 0, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 123701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6652, 1, 0, 1, 0, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 123701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6653, 4, 0, 1, 0, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 123701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6654, 8, 0, 1, 0, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 123701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6655, 1, 1, 1, 0, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 123701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6656, 1, 2, 1, 0, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 123701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6657, 1, 3, 1, 0, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 123701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6659, 1, 0, 1, 0, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 123701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6660, 1, 0, 1, 0, "いや～\nかわいいものをみると\n癒されますな～", 123701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6661, 1, 0, 1, 0, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 123701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6662, 1, 0, 1, 0, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 123701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6664, 1, 0, 1, 0, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 123701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6665, 1, 0, 1, 0, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 123701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6667, 1, 0, 1, 0, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 123701, 12, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6669, 1, 0, 1, 0, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 123801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6670, 1, 0, 1, 0, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 123801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6671, 1, 0, 1, 0, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 123801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6672, 1, 0, 1, 0, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 123801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6673, 1, 0, 1, 0, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 123801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6674, 4, 0, 1, 0, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 123801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6675, 8, 0, 1, 0, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 123801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6676, 1, 0, 1, 0, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 123801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6677, 1, 0, 1, 0, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 123801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6678, 1, 0, 1, 0, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 123801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6679, 1, 0, 1, 0, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 123801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6680, 4, 0, 1, 0, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 123801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6681, 8, 0, 1, 0, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 123801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6682, 1, 1, 1, 0, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 123801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6683, 1, 2, 1, 0, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 123801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6684, 1, 3, 1, 0, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 123801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6686, 1, 0, 1, 0, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 123801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6687, 1, 0, 1, 0, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 123801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6688, 1, 0, 1, 0, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 123801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6689, 1, 0, 1, 0, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 123801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6691, 1, 0, 1, 0, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 123801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6692, 1, 0, 1, 0, "ウサギ族は地獄耳？\nワタシとの共通点だな", 123801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6694, 12, 0, 1, 0, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 106401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6695, 12, 0, 1, 0, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 106401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6696, 1, 0, 1, 0, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 123901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6697, 1, 0, 1, 0, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 123901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6698, 1, 0, 1, 0, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 123901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6699, 1, 0, 1, 0, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 123901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6700, 1, 0, 1, 0, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 123901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6701, 4, 0, 1, 0, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 123901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6702, 8, 0, 1, 0, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 123901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6703, 1, 0, 1, 0, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 123901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6704, 1, 0, 1, 0, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 123901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6705, 1, 0, 1, 0, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 123901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6706, 1, 0, 1, 0, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 123901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6707, 4, 0, 1, 0, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 123901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6708, 8, 0, 1, 0, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 123901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6709, 1, 1, 1, 0, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 123901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6710, 1, 2, 1, 0, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 123901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6711, 1, 3, 1, 0, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 123901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6713, 1, 0, 1, 0, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 123901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6714, 1, 0, 1, 0, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 123901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6715, 1, 0, 1, 0, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 123901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6716, 1, 0, 1, 0, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 123901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6718, 1, 0, 1, 0, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 123901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6719, 1, 0, 1, 0, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 123901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6721, 1, 0, 1, 0, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 106901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6722, 1, 0, 1, 0, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 106901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6723, 1, 0, 1, 0, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 106901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6724, 1, 0, 1, 0, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 106901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6725, 1, 0, 1, 0, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 106901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6726, 4, 0, 1, 0, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 106901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6727, 8, 0, 1, 0, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 106901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6728, 1, 0, 1, 0, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 106901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6729, 1, 0, 1, 0, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 106901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6730, 1, 0, 1, 0, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 106901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6731, 1, 0, 1, 0, "視力は権能に\n影響しないわよ\n当たり前じゃない", 106901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6732, 4, 0, 1, 0, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 106901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6733, 8, 0, 1, 0, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 106901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6734, 1, 1, 1, 0, "人生朝露…\n悔いのない一日を\n過ごしなさい", 106901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6735, 1, 2, 1, 0, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 106901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6736, 1, 3, 1, 0, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 106901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6738, 1, 0, 1, 0, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 106901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6739, 1, 0, 1, 0, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 106901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6740, 1, 0, 1, 0, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 106901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6741, 1, 0, 1, 0, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 106901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6743, 1, 0, 1, 0, "栄養価はともかく\n味のバランスは\n悪くないわね", 106901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6744, 1, 0, 1, 0, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 106901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6746, 1, 0, 1, 0, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 124001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6747, 1, 0, 1, 0, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 124001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6748, 1, 0, 1, 0, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 124001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6749, 1, 0, 1, 0, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 124001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6750, 1, 0, 1, 0, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 124001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6751, 4, 0, 1, 0, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 124001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6752, 8, 0, 1, 0, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 124001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6753, 1, 0, 1, 0, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 124001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6754, 1, 0, 1, 0, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 124001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6755, 1, 0, 1, 0, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 124001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6756, 1, 0, 1, 0, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 124001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6757, 4, 0, 1, 0, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 124001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6758, 8, 0, 1, 0, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 124001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6759, 1, 1, 1, 0, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 124001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6760, 1, 2, 1, 0, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 124001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6761, 1, 3, 1, 0, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 124001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6763, 1, 0, 1, 0, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 124001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6764, 1, 0, 1, 0, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 124001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6765, 1, 0, 1, 0, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 124001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6766, 1, 0, 1, 0, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 124001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6768, 1, 0, 1, 0, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 124001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6769, 1, 0, 1, 0, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 124001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6771, 1, 0, 1, 0, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 124001, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6772, 1, 0, 1, 0, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 124001, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6773, 1, 0, 1, 0, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 124101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6774, 1, 0, 1, 0, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 124101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6775, 1, 0, 1, 0, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 124101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6776, 1, 0, 1, 0, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 124101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6777, 1, 0, 1, 0, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 124101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6778, 4, 0, 1, 0, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 124101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6779, 8, 0, 1, 0, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 124101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6780, 1, 0, 1, 0, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 124101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6781, 1, 0, 1, 0, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 124101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6782, 1, 0, 1, 0, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 124101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6783, 1, 0, 1, 0, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 124101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6784, 4, 0, 1, 0, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 124101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6785, 8, 0, 1, 0, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 124101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6786, 1, 1, 1, 0, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 124101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6787, 1, 2, 1, 0, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 124101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6788, 1, 3, 1, 0, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 124101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6790, 1, 0, 1, 0, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 124101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6791, 1, 0, 1, 0, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 124101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6792, 1, 0, 1, 0, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 124101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6793, 1, 0, 1, 0, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 124101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6795, 1, 0, 1, 0, "お腹いっぱい\n食べられるのも\n幸せなことよね", 124101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6796, 1, 0, 1, 0, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 124101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6798, 1, 0, 1, 0, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 124101, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6799, 1, 0, 1, 0, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 124101, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6800, 12, 0, 1, 0, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 105101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6801, 12, 0, 1, 0, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 105101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6802, 1, 0, 1, 0, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 124201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6803, 1, 0, 1, 0, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 124201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6804, 1, 0, 1, 0, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 124201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6805, 1, 0, 1, 0, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 124201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6806, 1, 0, 1, 0, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 124201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6807, 4, 0, 1, 0, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 124201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6808, 8, 0, 1, 0, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 124201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6809, 1, 0, 1, 0, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 124201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6810, 1, 0, 1, 0, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 124201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6811, 1, 0, 1, 0, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 124201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6812, 1, 0, 1, 0, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 124201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6813, 4, 0, 1, 0, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 124201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6814, 8, 0, 1, 0, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 124201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6815, 1, 1, 1, 0, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 124201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6816, 1, 2, 1, 0, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 124201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6817, 1, 3, 1, 0, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 124201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6819, 1, 0, 1, 0, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 124201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6820, 1, 0, 1, 0, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 124201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6821, 1, 0, 1, 0, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 124201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6822, 1, 0, 1, 0, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 124201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6824, 1, 0, 1, 0, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 124201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6825, 1, 0, 1, 0, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 124201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6827, 1, 0, 1, 0, "わっ！\n…………いや\n驚いてなどいない", 124201, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6828, 1, 0, 1, 0, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 124201, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6829, 1, 0, 1, 0, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 124301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6830, 1, 0, 1, 0, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 124301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6831, 1, 0, 1, 0, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 124301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6832, 1, 0, 1, 0, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 124301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6833, 1, 0, 1, 0, "グレアも誘って\nランドソルをもっと\n探索したい！", 124301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6834, 4, 0, 1, 0, "私たちも\nあの人を見習って\nバイトとかしよう！", 124301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6835, 8, 0, 1, 0, "いつかあの人を\n私たちの学院に\n招待したいな！", 124301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6836, 1, 0, 1, 0, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 124301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6837, 1, 0, 1, 0, "これからなにするの？\n私たちもついて行って\nいいかな！？", 124301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6838, 1, 0, 1, 0, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 124301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6839, 1, 0, 1, 0, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 124301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6840, 4, 0, 1, 0, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 124301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6841, 8, 0, 1, 0, "あなたは\nグレアと私の友達！\nこれからもずっと！", 124301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6842, 1, 1, 1, 0, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 124301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6843, 1, 2, 1, 0, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 124301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6844, 1, 3, 1, 0, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 124301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6846, 1, 0, 1, 0, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 124301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6847, 1, 0, 1, 0, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 124301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6848, 1, 0, 1, 0, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 124301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6849, 1, 0, 1, 0, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 124301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6851, 1, 0, 1, 0, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 124301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6852, 1, 0, 1, 0, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 124301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6855, 1, 0, 1, 0, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 124401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6856, 1, 0, 1, 0, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 124401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6857, 1, 0, 1, 0, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 124401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6858, 1, 0, 1, 0, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 124401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6859, 1, 0, 1, 0, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 124401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6860, 4, 0, 1, 0, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 124401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6861, 8, 0, 1, 0, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 124401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6862, 1, 0, 1, 0, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 124401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6863, 1, 0, 1, 0, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 124401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6864, 1, 0, 1, 0, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 124401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6865, 1, 0, 1, 0, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 124401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6866, 4, 0, 1, 0, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 124401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6867, 8, 0, 1, 0, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 124401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6868, 1, 1, 1, 0, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 124401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6869, 1, 2, 1, 0, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 124401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6870, 1, 3, 1, 0, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 124401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6872, 1, 0, 1, 0, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 124401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6873, 1, 0, 1, 0, "これ…\nアンに見せたら\n喜ぶだろうな…", 124401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6874, 1, 0, 1, 0, "いい香り…\nアンの感想も\n聞いてみたいな", 124401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6875, 1, 0, 1, 0, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 124401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6877, 1, 0, 1, 0, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 124401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6878, 1, 0, 1, 0, "アンがおススメ\nしてくれた本は\nこれかな…？", 124401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6881, 1, 0, 1, 0, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 124701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6882, 1, 0, 1, 0, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 124701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6883, 1, 0, 1, 0, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 124701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6884, 1, 0, 1, 0, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 124701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6885, 1, 0, 1, 0, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 124701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6886, 4, 0, 1, 0, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 124701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6887, 8, 0, 1, 0, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 124701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6888, 1, 0, 1, 0, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 124701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6889, 1, 0, 1, 0, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 124701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6890, 1, 0, 1, 0, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 124701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6891, 1, 0, 1, 0, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 124701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6892, 4, 0, 1, 0, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 124701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6893, 8, 0, 1, 0, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 124701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6894, 1, 1, 1, 0, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 124701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6895, 1, 2, 1, 0, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 124701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6896, 1, 3, 1, 0, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 124701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6898, 1, 0, 1, 0, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 124701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6899, 1, 0, 1, 0, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 124701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6900, 1, 0, 1, 0, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 124701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6901, 1, 0, 1, 0, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 124701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6903, 1, 0, 1, 0, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 124701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6904, 1, 0, 1, 0, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 124701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6906, 1, 0, 1, 0, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 124601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6907, 1, 0, 1, 0, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 124601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6908, 1, 0, 1, 0, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 124601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6909, 1, 0, 1, 0, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 124601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6910, 1, 0, 1, 0, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 124601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6911, 4, 0, 1, 0, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 124601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6912, 8, 0, 1, 0, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 124601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6913, 1, 0, 1, 0, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 124601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6914, 1, 0, 1, 0, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 124601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6915, 1, 0, 1, 0, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 124601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6916, 1, 0, 1, 0, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 124601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6917, 4, 0, 1, 0, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 124601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6918, 8, 0, 1, 0, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 124601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6919, 1, 1, 1, 0, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 124601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6920, 1, 2, 1, 0, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 124601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6921, 1, 3, 1, 0, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 124601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6923, 1, 0, 1, 0, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 124601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6924, 1, 0, 1, 0, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 124601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6925, 1, 0, 1, 0, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 124601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6926, 1, 0, 1, 0, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 124601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6928, 1, 0, 1, 0, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 124601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6929, 1, 0, 1, 0, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 124601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6931, 12, 0, 1, 0, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 104701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6932, 12, 0, 1, 0, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 104701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6933, 1, 0, 1, 0, "お正月だからって\nギャングのボスは\n油断できないよ～？", 124501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6934, 1, 0, 1, 0, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 124501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6935, 1, 0, 1, 0, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 124501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6936, 1, 0, 1, 0, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 124501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6937, 1, 0, 1, 0, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 124501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6938, 4, 0, 1, 0, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 124501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6939, 8, 0, 1, 0, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 124501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6940, 1, 0, 1, 0, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 124501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6941, 1, 0, 1, 0, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 124501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6942, 1, 0, 1, 0, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 124501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6943, 1, 0, 1, 0, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 124501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6944, 4, 0, 1, 0, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 124501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6945, 8, 0, 1, 0, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 124501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6946, 1, 1, 1, 0, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 124501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6947, 1, 2, 1, 0, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 124501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6948, 1, 3, 1, 0, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 124501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6950, 1, 0, 1, 0, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 124501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6951, 1, 0, 1, 0, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 124501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6952, 1, 0, 1, 0, "いい香りだけど\n着物に移らないように\n気をつけないとね", 124501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6953, 1, 0, 1, 0, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 124501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6955, 1, 0, 1, 0, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 124501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6956, 1, 0, 1, 0, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 124501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6958, 1, 0, 1, 0, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 124801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6959, 1, 0, 1, 0, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 124801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6960, 1, 0, 1, 0, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 124801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6961, 1, 0, 1, 0, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 124801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6962, 1, 0, 1, 0, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 124801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6963, 4, 0, 1, 0, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 124801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6964, 8, 0, 1, 0, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 124801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6965, 1, 0, 1, 0, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 124801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6966, 1, 0, 1, 0, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 124801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6967, 1, 0, 1, 0, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 124801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6968, 1, 0, 1, 0, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 124801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6969, 4, 0, 1, 0, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 124801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6970, 8, 0, 1, 0, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 124801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6971, 1, 1, 1, 0, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 124801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6972, 1, 2, 1, 0, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 124801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6973, 1, 3, 1, 0, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 124801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6975, 1, 0, 1, 0, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 124801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6976, 1, 0, 1, 0, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 124801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6977, 1, 0, 1, 0, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 124801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6978, 1, 0, 1, 0, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 124801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6980, 1, 0, 1, 0, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 124801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6981, 1, 0, 1, 0, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 124801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6983, 1, 0, 1, 0, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 124901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6984, 1, 0, 1, 0, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 124901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6985, 1, 0, 1, 0, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 124901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6986, 1, 0, 1, 0, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 124901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6987, 1, 0, 1, 0, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 124901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6988, 4, 0, 1, 0, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 124901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6989, 8, 0, 1, 0, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 124901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6990, 1, 0, 1, 0, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 124901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6991, 1, 0, 1, 0, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 124901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6992, 1, 0, 1, 0, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 124901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6993, 1, 0, 1, 0, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 124901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6994, 4, 0, 1, 0, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 124901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6995, 8, 0, 1, 0, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 124901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6996, 1, 1, 1, 0, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 124901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6997, 1, 2, 1, 0, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 124901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (6998, 1, 3, 1, 0, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 124901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7000, 1, 0, 1, 0, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 124901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7001, 1, 0, 1, 0, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 124901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7002, 1, 0, 1, 0, "嗅いだことない\n香りだな\nなかなか気に入った！", 124901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7003, 1, 0, 1, 0, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 124901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7005, 1, 0, 1, 0, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 124901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7006, 1, 0, 1, 0, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 124901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7008, 12, 0, 1, 0, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 105501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7009, 12, 0, 1, 0, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 105501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7010, 1, 0, 1, 0, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 125001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7011, 1, 0, 1, 0, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 125001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7012, 1, 0, 1, 0, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 125001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7013, 1, 0, 1, 0, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 125001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7014, 1, 0, 1, 0, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 125001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7015, 4, 0, 1, 0, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 125001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7016, 8, 0, 1, 0, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 125001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7017, 1, 0, 1, 0, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 125001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7018, 1, 0, 1, 0, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 125001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7019, 1, 0, 1, 0, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 125001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7020, 1, 0, 1, 0, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 125001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7021, 4, 0, 1, 0, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 125001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7022, 8, 0, 1, 0, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 125001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7023, 1, 1, 1, 0, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 125001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7024, 1, 2, 1, 0, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 125001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7025, 1, 3, 1, 0, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 125001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7027, 1, 0, 1, 0, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 125001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7028, 1, 0, 1, 0, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 125001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7029, 1, 0, 1, 0, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 125001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7030, 1, 0, 1, 0, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 125001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7032, 1, 0, 1, 0, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 125001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7033, 1, 0, 1, 0, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 125001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7035, 1, 0, 1, 0, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 118201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7036, 1, 0, 1, 0, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 118201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7037, 1, 0, 1, 0, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 118201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7038, 1, 0, 1, 0, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 118201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7039, 1, 0, 1, 0, "オトモダチでも\n作ってみますかー\nくすっ★", 118201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7040, 4, 0, 1, 0, "退屈ですねー\n騎士さん、構って\nくれないかなー", 118201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7041, 8, 0, 1, 0, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 118201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7042, 1, 0, 1, 0, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 118201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7043, 1, 0, 1, 0, "お出かけします？\nそれともおうちで\nまったりしますー？", 118201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7044, 1, 0, 1, 0, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 118201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7045, 1, 0, 1, 0, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 118201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7046, 4, 0, 1, 0, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 118201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7047, 8, 0, 1, 0, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 118201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7048, 1, 1, 1, 0, "おはようございます★\nあれれー？\nまだおねむですかー？", 118201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7049, 1, 2, 1, 0, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 118201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7050, 1, 3, 1, 0, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 118201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7052, 1, 0, 1, 0, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 118201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7053, 1, 0, 1, 0, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 118201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7054, 1, 0, 1, 0, "くんくん★\nうーん…\nたぶん素敵な香り♪", 118201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7055, 1, 0, 1, 0, "あったかーい★\n心も体も\nぽっかぽかですねー★", 118201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7057, 1, 0, 1, 0, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 118201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7058, 1, 0, 1, 0, "へー★\nほほー★\nふんふーん★", 118201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7060, 1, 0, 1, 0, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 125101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7061, 1, 0, 1, 0, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 125101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7062, 1, 0, 1, 0, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 125101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7063, 1, 0, 1, 0, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 125101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7064, 1, 0, 1, 0, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 125101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7065, 4, 0, 1, 0, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 125101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7066, 8, 0, 1, 0, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 125101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7067, 1, 0, 1, 0, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 125101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7068, 1, 0, 1, 0, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 125101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7069, 1, 0, 1, 0, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 125101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7070, 1, 0, 1, 0, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 125101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7071, 4, 0, 1, 0, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 125101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7072, 8, 0, 1, 0, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 125101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7073, 1, 1, 1, 0, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 125101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7074, 1, 2, 1, 0, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 125101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7075, 1, 3, 1, 0, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 125101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7077, 1, 0, 1, 0, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 125101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7078, 1, 0, 1, 0, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 125101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7079, 1, 0, 1, 0, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 125101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7080, 1, 0, 1, 0, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 125101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7082, 1, 0, 1, 0, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 125101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7083, 1, 0, 1, 0, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 125101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7085, 12, 0, 1, 0, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 107101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7086, 12, 0, 1, 0, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 107101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7087, 1, 0, 1, 0, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 125201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7088, 1, 0, 1, 0, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 125201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7089, 1, 0, 1, 0, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 125201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7090, 1, 0, 1, 0, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 125201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7091, 1, 0, 1, 0, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 125201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7092, 4, 0, 1, 0, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 125201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7093, 8, 0, 1, 0, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 125201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7094, 1, 0, 1, 0, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 125201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7095, 1, 0, 1, 0, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 125201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7096, 1, 0, 1, 0, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 125201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7097, 1, 0, 1, 0, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 125201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7098, 4, 0, 1, 0, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 125201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7099, 8, 0, 1, 0, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 125201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7100, 1, 1, 1, 0, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 125201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7101, 1, 2, 1, 0, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 125201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7102, 1, 3, 1, 0, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 125201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7104, 1, 0, 1, 0, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 125201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7105, 1, 0, 1, 0, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 125201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7106, 1, 0, 1, 0, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 125201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7107, 1, 0, 1, 0, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 125201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7109, 1, 0, 1, 0, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 125201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7110, 1, 0, 1, 0, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 125201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7112, 1, 0, 1, 0, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 120001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7113, 1, 0, 1, 0, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 120001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7114, 1, 0, 1, 0, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 120001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7115, 1, 0, 1, 0, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 120001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7116, 1, 0, 1, 0, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 120001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7117, 4, 0, 1, 0, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 120001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7118, 8, 0, 1, 0, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 120001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7119, 1, 0, 1, 0, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 120001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7120, 1, 0, 1, 0, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 120001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7121, 1, 0, 1, 0, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 120001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7122, 1, 0, 1, 0, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 120001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7123, 4, 0, 1, 0, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 120001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7124, 8, 0, 1, 0, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 120001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7125, 1, 1, 1, 0, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 120001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7126, 1, 2, 1, 0, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 120001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7127, 1, 3, 1, 0, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 120001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7129, 1, 0, 1, 0, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 120001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7130, 1, 0, 1, 0, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 120001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7131, 1, 0, 1, 0, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 120001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7132, 1, 0, 1, 0, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 120001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7134, 1, 0, 1, 0, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 120001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7135, 1, 0, 1, 0, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 120001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7137, 1, 0, 1, 0, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 125301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7138, 1, 0, 1, 0, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 125301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7139, 1, 0, 1, 0, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 125301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7140, 1, 0, 1, 0, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 125301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7141, 1, 0, 1, 0, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 125301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7142, 4, 0, 1, 0, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 125301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7143, 8, 0, 1, 0, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 125301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7144, 1, 0, 1, 0, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 125301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7145, 1, 0, 1, 0, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 125301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7146, 1, 0, 1, 0, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 125301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7147, 1, 0, 1, 0, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 125301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7148, 4, 0, 1, 0, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 125301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7149, 8, 0, 1, 0, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 125301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7150, 1, 1, 1, 0, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 125301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7151, 1, 2, 1, 0, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 125301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7152, 1, 3, 1, 0, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 125301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7154, 1, 0, 1, 0, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 125301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7155, 1, 0, 1, 0, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 125301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7156, 1, 0, 1, 0, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 125301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7157, 1, 0, 1, 0, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 125301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7159, 1, 0, 1, 0, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 125301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7160, 1, 0, 1, 0, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 125301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7162, 12, 0, 1, 0, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 100901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7163, 12, 0, 1, 0, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 100901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7164, 1, 0, 1, 0, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 125401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7165, 1, 0, 1, 0, "ふふ…\nこれがレンジャーの\n制服ですか", 125401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7166, 1, 0, 1, 0, "そろそろ\nパトロールの\n時間だ…", 125401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7167, 1, 0, 1, 0, "魔物について\nもっと知りたいな\n本を探しに行こう", 125401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7168, 1, 0, 1, 0, "レンジャーになって\n体調を崩す日が\n減った気がします…", 125401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7169, 4, 0, 1, 0, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 125401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7170, 8, 0, 1, 0, "こんな私にも\nできることはある…\nそう信じています", 125401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7171, 1, 0, 1, 0, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 125401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7172, 1, 0, 1, 0, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 125401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7173, 1, 0, 1, 0, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 125401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7174, 1, 0, 1, 0, "ニャットちゃん\nあなたにも\n懐いてきましたね", 125401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7175, 4, 0, 1, 0, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 125401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7176, 8, 0, 1, 0, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 125401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7177, 1, 1, 1, 0, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 125401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7178, 1, 2, 1, 0, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 125401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7179, 1, 3, 1, 0, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 125401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7181, 1, 0, 1, 0, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 125401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7182, 1, 0, 1, 0, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 125401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7183, 1, 0, 1, 0, "森の香りがします…\n今日はよく\n眠れそうです…", 125401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7184, 1, 0, 1, 0, "ニャットちゃんと\n同じぐらい\n暖かいです…", 125401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7186, 1, 0, 1, 0, "レンジャーの仕事を\nしていると\nお腹が空きます", 125401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7187, 1, 0, 1, 0, "どんなに忙しくても\n読書だけは\nやめられませんね", 125401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7189, 1, 0, 1, 0, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 125801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7190, 1, 0, 1, 0, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 125801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7191, 1, 0, 1, 0, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 125801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7192, 1, 0, 1, 0, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 125801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7193, 1, 0, 1, 0, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 125801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7194, 4, 0, 1, 0, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 125801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7195, 8, 0, 1, 0, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 125801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7196, 1, 0, 1, 0, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 125801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7197, 1, 0, 1, 0, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 125801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7198, 1, 0, 1, 0, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 125801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7199, 1, 0, 1, 0, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 125801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7200, 4, 0, 1, 0, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 125801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7201, 8, 0, 1, 0, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 125801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7202, 1, 1, 1, 0, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 125801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7203, 1, 2, 1, 0, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 125801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7204, 1, 3, 1, 0, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 125801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7206, 1, 0, 1, 0, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 125801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7207, 1, 0, 1, 0, "と、飛びかかっては\nこない…ですよね…？", 125801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7208, 1, 0, 1, 0, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 125801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7209, 1, 0, 1, 0, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 125801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7211, 1, 0, 1, 0, "こんなに食べていたら\nはしたない…\nでしょうか？", 125801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7212, 1, 0, 1, 0, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 125801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7214, 1, 0, 1, 0, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 125501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7215, 1, 0, 1, 0, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 125501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7216, 1, 0, 1, 0, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 125501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7217, 1, 0, 1, 0, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 125501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7218, 1, 0, 1, 0, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 125501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7219, 4, 0, 1, 0, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 125501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7220, 8, 0, 1, 0, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 125501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7221, 1, 0, 1, 0, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 125501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7222, 1, 0, 1, 0, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 125501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7223, 1, 0, 1, 0, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 125501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7224, 1, 0, 1, 0, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 125501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7225, 4, 0, 1, 0, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 125501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7226, 8, 0, 1, 0, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 125501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7227, 1, 1, 1, 0, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 125501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7228, 1, 2, 1, 0, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 125501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7229, 1, 3, 1, 0, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 125501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7231, 1, 0, 1, 0, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 125501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7232, 1, 0, 1, 0, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 125501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7233, 1, 0, 1, 0, "いい匂い…\nそれになんだか\n集中力が増したかも", 125501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7234, 1, 0, 1, 0, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 125501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7236, 1, 0, 1, 0, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 125501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7237, 1, 0, 1, 0, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 125501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7239, 1, 0, 1, 0, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 125701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7240, 1, 0, 1, 0, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 125701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7241, 1, 0, 1, 0, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 125701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7242, 1, 0, 1, 0, "せっかくですから\nより良い素材を\n自分で集めたいですね", 125701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7243, 1, 0, 1, 0, "このポーションが\nみなさんの役に立つと\nうれしいですね", 125701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7244, 4, 0, 1, 0, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 125701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7245, 8, 0, 1, 0, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 125701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7246, 1, 0, 1, 0, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 125701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7247, 1, 0, 1, 0, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 125701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7248, 1, 0, 1, 0, "依頼で気になる\nところがあれば\n何でもご確認ください", 125701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7249, 1, 0, 1, 0, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 125701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7250, 4, 0, 1, 0, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 125701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7251, 8, 0, 1, 0, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 125701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7252, 1, 1, 1, 0, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 125701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7253, 1, 2, 1, 0, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 125701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7254, 1, 3, 1, 0, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 125701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7256, 1, 0, 1, 0, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 125701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7257, 1, 0, 1, 0, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 125701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7258, 1, 0, 1, 0, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 125701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7259, 1, 0, 1, 0, "この火力…\n錬金術の調合に\n使えそうですね", 125701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7261, 1, 0, 1, 0, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 125701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7262, 1, 0, 1, 0, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 125701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7264, 12, 0, 1, 0, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 106101, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7265, 12, 0, 1, 0, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 106101, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7266, 1, 0, 1, 0, "みんなー！\nギルドハウスで\nぼくと握手！", 125601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7267, 1, 0, 1, 0, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 125601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7268, 1, 0, 1, 0, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 125601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7269, 1, 0, 1, 0, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 125601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7270, 1, 0, 1, 0, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 125601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7271, 4, 0, 1, 0, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 125601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7272, 8, 0, 1, 0, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 125601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7273, 1, 0, 1, 0, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 125601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7274, 1, 0, 1, 0, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 125601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7275, 1, 0, 1, 0, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 125601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7276, 1, 0, 1, 0, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 125601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7277, 4, 0, 1, 0, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 125601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7278, 8, 0, 1, 0, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 125601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7279, 1, 1, 1, 0, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 125601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7280, 1, 2, 1, 0, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 125601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7281, 1, 3, 1, 0, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 125601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7283, 1, 0, 1, 0, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 125601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7284, 1, 0, 1, 0, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 125601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7285, 1, 0, 1, 0, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 125601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7286, 1, 0, 1, 0, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 125601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7288, 1, 0, 1, 0, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 125601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7289, 1, 0, 1, 0, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 125601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7291, 1, 0, 1, 0, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 126201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7292, 1, 0, 1, 0, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 126201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7293, 1, 0, 1, 0, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 126201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7294, 1, 0, 1, 0, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 126201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7295, 1, 0, 1, 0, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 126201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7296, 4, 0, 1, 0, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 126201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7297, 8, 0, 1, 0, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 126201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7298, 1, 0, 1, 0, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 126201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7299, 1, 0, 1, 0, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 126201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7300, 1, 0, 1, 0, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 126201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7301, 1, 0, 1, 0, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 126201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7302, 4, 0, 1, 0, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 126201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7303, 8, 0, 1, 0, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 126201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7304, 1, 1, 1, 0, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 126201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7305, 1, 2, 1, 0, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 126201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7306, 1, 3, 1, 0, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 126201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7308, 1, 0, 1, 0, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 126201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7309, 1, 0, 1, 0, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 126201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7310, 1, 0, 1, 0, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 126201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7311, 1, 0, 1, 0, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 126201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7313, 1, 0, 1, 0, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 126201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7314, 1, 0, 1, 0, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 126201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7316, 12, 0, 1, 0, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 100801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7317, 12, 0, 1, 0, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 100801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7318, 1, 0, 1, 0, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 126301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7319, 1, 0, 1, 0, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 126301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7320, 1, 0, 1, 0, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 126301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7321, 1, 0, 1, 0, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 126301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7322, 1, 0, 1, 0, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 126301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7323, 4, 0, 1, 0, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 126301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7324, 8, 0, 1, 0, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 126301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7325, 1, 0, 1, 0, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 126301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7326, 1, 0, 1, 0, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 126301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7327, 1, 0, 1, 0, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 126301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7328, 1, 0, 1, 0, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 126301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7329, 4, 0, 1, 0, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 126301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7330, 8, 0, 1, 0, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 126301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7331, 1, 1, 1, 0, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 126301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7332, 1, 2, 1, 0, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 126301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7333, 1, 3, 1, 0, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 126301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7335, 1, 0, 1, 0, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 126301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7336, 1, 0, 1, 0, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 126301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7337, 1, 0, 1, 0, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 126301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7338, 1, 0, 1, 0, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 126301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7340, 1, 0, 1, 0, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 126301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7341, 1, 0, 1, 0, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 126301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7343, 1, 0, 1, 0, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 126401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7344, 1, 0, 1, 0, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 126401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7345, 1, 0, 1, 0, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 126401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7346, 1, 0, 1, 0, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 126401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7347, 1, 0, 1, 0, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 126401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7348, 4, 0, 1, 0, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 126401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7349, 8, 0, 1, 0, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 126401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7350, 1, 0, 1, 0, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 126401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7351, 1, 0, 1, 0, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 126401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7352, 1, 0, 1, 0, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 126401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7353, 1, 0, 1, 0, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 126401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7354, 4, 0, 1, 0, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 126401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7355, 8, 0, 1, 0, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 126401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7356, 1, 1, 1, 0, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 126401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7357, 1, 2, 1, 0, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 126401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7358, 1, 3, 1, 0, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 126401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7360, 1, 0, 1, 0, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 126401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7361, 1, 0, 1, 0, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 126401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7362, 1, 0, 1, 0, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 126401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7363, 1, 0, 1, 0, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 126401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7365, 1, 0, 1, 0, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 126401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7366, 1, 0, 1, 0, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 126401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7368, 1, 0, 1, 0, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 126701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7369, 1, 0, 1, 0, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 126701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7370, 1, 0, 1, 0, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 126701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7371, 1, 0, 1, 0, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 126701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7372, 1, 0, 1, 0, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 126701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7373, 4, 0, 1, 0, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 126701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7374, 8, 0, 1, 0, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 126701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7375, 1, 0, 1, 0, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 126701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7376, 1, 0, 1, 0, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 126701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7377, 1, 0, 1, 0, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 126701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7378, 1, 0, 1, 0, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 126701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7379, 4, 0, 1, 0, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 126701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7380, 8, 0, 1, 0, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 126701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7381, 1, 1, 1, 0, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 126701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7382, 1, 2, 1, 0, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 126701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7383, 1, 3, 1, 0, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 126701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7385, 1, 0, 1, 0, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 126701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7386, 1, 0, 1, 0, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 126701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7387, 1, 0, 1, 0, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 126701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7388, 1, 0, 1, 0, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 126701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7390, 1, 0, 1, 0, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 126701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7391, 1, 0, 1, 0, "夏のトレンドを\n押さえておくのも\n大事ですわね", 126701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7393, 1, 0, 1, 0, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 126701, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7394, 1, 0, 1, 0, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 126701, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7395, 1, 0, 1, 0, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 126601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7396, 1, 0, 1, 0, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 126601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7397, 1, 0, 1, 0, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 126601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7398, 1, 0, 1, 0, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 126601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7399, 1, 0, 1, 0, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 126601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7400, 4, 0, 1, 0, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 126601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7401, 8, 0, 1, 0, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 126601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7402, 1, 0, 1, 0, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 126601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7403, 1, 0, 1, 0, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 126601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7404, 1, 0, 1, 0, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 126601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7405, 1, 0, 1, 0, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 126601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7406, 4, 0, 1, 0, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 126601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7407, 8, 0, 1, 0, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 126601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7408, 1, 1, 1, 0, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 126601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7409, 1, 2, 1, 0, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 126601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7410, 1, 3, 1, 0, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 126601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7412, 1, 0, 1, 0, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 126601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7413, 1, 0, 1, 0, "なかなか気に入った…\nというのは\n誰にも秘密です", 126601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7414, 1, 0, 1, 0, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 126601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7415, 1, 0, 1, 0, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 126601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7417, 1, 0, 1, 0, "私がどのような味が\n好きなのかは…\n当然秘密です", 126601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7418, 1, 0, 1, 0, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 126601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7420, 1, 0, 1, 0, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 126601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7421, 1, 0, 1, 0, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 126601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7422, 12, 0, 1, 0, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 105401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7423, 12, 0, 1, 0, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 105401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7424, 1, 0, 1, 0, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 126801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7425, 1, 0, 1, 0, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 126801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7426, 1, 0, 1, 0, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 126801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7427, 1, 0, 1, 0, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 126801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7428, 1, 0, 1, 0, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 126801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7429, 4, 0, 1, 0, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 126801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7430, 8, 0, 1, 0, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 126801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7431, 1, 0, 1, 0, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 126801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7432, 1, 0, 1, 0, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 126801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7433, 1, 0, 1, 0, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 126801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7434, 1, 0, 1, 0, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 126801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7435, 4, 0, 1, 0, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 126801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7436, 8, 0, 1, 0, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 126801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7437, 1, 1, 1, 0, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 126801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7438, 1, 2, 1, 0, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 126801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7439, 1, 3, 1, 0, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 126801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7441, 1, 0, 1, 0, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 126801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7442, 1, 0, 1, 0, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 126801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7443, 1, 0, 1, 0, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 126801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7444, 1, 0, 1, 0, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 126801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7446, 1, 0, 1, 0, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 126801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7447, 1, 0, 1, 0, "試験に使った参考書、\nたまには読み返して\nおかないとね", 126801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7449, 1, 0, 1, 0, "ふふ♪\nいい手触りね～", 126801, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7450, 1, 0, 1, 0, "…はふう～…\nゴロゴロするのって\n最高ね～", 126801, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7451, 1, 0, 1, 0, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 126901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7452, 1, 0, 1, 0, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 126901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7453, 1, 0, 1, 0, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 126901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7454, 1, 0, 1, 0, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 126901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7455, 1, 0, 1, 0, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 126901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7456, 4, 0, 1, 0, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 126901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7457, 8, 0, 1, 0, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 126901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7458, 1, 0, 1, 0, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 126901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7459, 1, 0, 1, 0, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 126901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7460, 1, 0, 1, 0, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 126901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7461, 1, 0, 1, 0, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 126901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7462, 4, 0, 1, 0, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 126901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7463, 8, 0, 1, 0, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 126901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7464, 1, 1, 1, 0, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 126901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7465, 1, 2, 1, 0, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 126901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7466, 1, 3, 1, 0, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 126901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7468, 1, 0, 1, 0, "素肌にふわふわが…\n気持ちいい…♪", 126901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7469, 1, 0, 1, 0, "うふふ…\n一緒に海…行く？", 126901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7470, 1, 0, 1, 0, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 126901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7471, 1, 0, 1, 0, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 126901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7473, 1, 0, 1, 0, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 126901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7474, 1, 0, 1, 0, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 126901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7476, 1, 0, 1, 0, "これで合ってる…？\n遊び方わからなくて…", 126901, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7477, 1, 0, 1, 0, "ふわふわ…♪\n海の底にいるみたい…", 126901, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7478, 1, 0, 1, 0, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 127001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7479, 1, 0, 1, 0, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 127001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7480, 1, 0, 1, 0, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 127001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7481, 1, 0, 1, 0, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 127001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7482, 1, 0, 1, 0, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 127001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7483, 4, 0, 1, 0, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 127001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7484, 8, 0, 1, 0, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 127001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7485, 1, 0, 1, 0, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 127001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7486, 1, 0, 1, 0, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 127001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7487, 1, 0, 1, 0, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 127001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7488, 1, 0, 1, 0, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 127001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7489, 4, 0, 1, 0, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 127001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7490, 8, 0, 1, 0, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 127001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7491, 1, 1, 1, 0, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 127001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7492, 1, 2, 1, 0, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 127001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7493, 1, 3, 1, 0, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 127001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7495, 1, 0, 1, 0, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 127001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7496, 1, 0, 1, 0, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 127001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7497, 1, 0, 1, 0, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 127001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7498, 1, 0, 1, 0, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 127001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7500, 1, 0, 1, 0, "クウカの口の中に\nたくさん…♪\n止まりません…！", 127001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7501, 1, 0, 1, 0, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 127001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7503, 1, 0, 1, 0, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 127001, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7504, 1, 0, 1, 0, "これは…\n妄想に役立ちそうな\n感触です…！", 127001, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7505, 12, 0, 1, 0, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 100501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7506, 12, 0, 1, 0, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 100501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7507, 1, 0, 1, 0, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 127101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7508, 1, 0, 1, 0, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 127101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7509, 1, 0, 1, 0, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 127101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7510, 1, 0, 1, 0, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 127101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7511, 1, 0, 1, 0, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 127101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7512, 4, 0, 1, 0, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 127101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7513, 8, 0, 1, 0, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 127101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7514, 1, 0, 1, 0, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 127101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7515, 1, 0, 1, 0, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 127101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7516, 1, 0, 1, 0, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 127101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7517, 1, 0, 1, 0, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 127101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7518, 4, 0, 1, 0, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 127101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7519, 8, 0, 1, 0, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 127101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7520, 1, 1, 1, 0, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 127101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7521, 1, 2, 1, 0, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 127101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7522, 1, 3, 1, 0, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 127101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7524, 1, 0, 1, 0, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 127101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7525, 1, 0, 1, 0, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 127101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7526, 1, 0, 1, 0, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 127101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7527, 1, 0, 1, 0, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 127101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7529, 1, 0, 1, 0, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 127101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7530, 1, 0, 1, 0, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 127101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7532, 1, 0, 1, 0, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 127101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7533, 1, 0, 1, 0, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 127101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7534, 1, 0, 1, 0, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 126501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7535, 1, 0, 1, 0, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 126501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7536, 1, 0, 1, 0, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 126501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7537, 1, 0, 1, 0, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 126501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7538, 1, 0, 1, 0, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 126501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7539, 4, 0, 1, 0, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 126501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7540, 8, 0, 1, 0, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 126501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7541, 1, 0, 1, 0, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 126501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7542, 1, 0, 1, 0, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 126501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7543, 1, 0, 1, 0, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 126501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7544, 1, 0, 1, 0, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 126501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7545, 4, 0, 1, 0, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 126501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7546, 8, 0, 1, 0, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 126501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7547, 1, 1, 1, 0, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 126501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7548, 1, 2, 1, 0, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 126501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7549, 1, 3, 1, 0, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 126501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7551, 1, 0, 1, 0, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 126501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7552, 1, 0, 1, 0, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 126501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7553, 1, 0, 1, 0, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 126501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7554, 1, 0, 1, 0, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 126501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7556, 1, 0, 1, 0, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 126501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7557, 1, 0, 1, 0, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 126501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7559, 1, 0, 1, 0, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 127201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7560, 1, 0, 1, 0, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 127201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7561, 1, 0, 1, 0, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 127201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7562, 1, 0, 1, 0, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 127201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7563, 1, 0, 1, 0, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 127201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7564, 4, 0, 1, 0, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 127201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7565, 8, 0, 1, 0, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 127201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7566, 1, 0, 1, 0, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 127201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7567, 1, 0, 1, 0, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 127201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7568, 1, 0, 1, 0, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 127201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7569, 1, 0, 1, 0, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 127201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7570, 4, 0, 1, 0, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 127201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7571, 8, 0, 1, 0, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 127201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7572, 1, 1, 1, 0, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 127201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7573, 1, 2, 1, 0, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 127201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7574, 1, 3, 1, 0, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 127201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7576, 1, 0, 1, 0, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 127201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7577, 1, 0, 1, 0, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 127201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7578, 1, 0, 1, 0, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 127201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7579, 1, 0, 1, 0, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 127201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7581, 1, 0, 1, 0, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 127201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7582, 1, 0, 1, 0, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 127201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7584, 12, 0, 1, 0, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 104301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7585, 12, 0, 1, 0, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 104301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7586, 1, 0, 1, 0, "魔法の練習！\nむむむ～…\nそれっ…！", 127301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7587, 1, 0, 1, 0, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 127301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7588, 1, 0, 1, 0, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 127301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7589, 1, 0, 1, 0, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 127301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7590, 1, 0, 1, 0, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 127301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7591, 4, 0, 1, 0, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 127301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7592, 8, 0, 1, 0, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 127301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7593, 1, 0, 1, 0, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 127301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7594, 1, 0, 1, 0, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 127301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7595, 1, 0, 1, 0, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 127301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7596, 1, 0, 1, 0, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 127301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7597, 4, 0, 1, 0, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 127301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7598, 8, 0, 1, 0, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 127301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7599, 1, 1, 1, 0, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 127301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7600, 1, 2, 1, 0, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 127301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7601, 1, 3, 1, 0, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 127301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7603, 1, 0, 1, 0, "充電ちう～\n…ガッコーのイスも\nこれにして～", 127301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7604, 1, 0, 1, 0, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 127301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7605, 1, 0, 1, 0, "リフレッシュの\n魔法発見！\nいい香り～♪", 127301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7606, 1, 0, 1, 0, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 127301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7608, 1, 0, 1, 0, "超おいし～♪\n学食にあったら\n毎日リピする～", 127301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7609, 1, 0, 1, 0, "ふむふむ…\nねむみパない…\n続きはまた今度～", 127301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7611, 1, 0, 1, 0, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 127501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7612, 1, 0, 1, 0, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 127501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7613, 1, 0, 1, 0, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 127501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7614, 1, 0, 1, 0, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 127501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7615, 1, 0, 1, 0, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 127501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7616, 4, 0, 1, 0, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 127501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7617, 8, 0, 1, 0, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 127501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7618, 1, 0, 1, 0, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 127501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7619, 1, 0, 1, 0, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 127501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7620, 1, 0, 1, 0, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 127501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7621, 1, 0, 1, 0, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 127501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7622, 4, 0, 1, 0, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 127501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7623, 8, 0, 1, 0, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 127501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7624, 1, 1, 1, 0, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 127501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7625, 1, 2, 1, 0, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 127501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7626, 1, 3, 1, 0, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 127501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7628, 1, 0, 1, 0, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 127501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7629, 1, 0, 1, 0, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 127501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7630, 1, 0, 1, 0, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 127501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7631, 1, 0, 1, 0, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 127501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7633, 1, 0, 1, 0, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 127501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7634, 1, 0, 1, 0, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 127501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7636, 12, 0, 1, 0, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 101401, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7637, 12, 0, 1, 0, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 101401, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7638, 1, 0, 1, 0, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 127601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7639, 1, 0, 1, 0, "たまには違う服装\n違う得物を使うのも\n悪くないね", 127601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7640, 1, 0, 1, 0, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 127601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7641, 1, 0, 1, 0, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 127601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7642, 1, 0, 1, 0, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 127601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7643, 4, 0, 1, 0, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 127601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7644, 8, 0, 1, 0, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 127601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7645, 1, 0, 1, 0, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 127601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7646, 1, 0, 1, 0, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 127601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7647, 1, 0, 1, 0, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 127601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7648, 1, 0, 1, 0, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 127601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7649, 4, 0, 1, 0, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 127601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7650, 8, 0, 1, 0, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 127601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7651, 1, 1, 1, 0, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 127601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7652, 1, 2, 1, 0, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 127601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7653, 1, 3, 1, 0, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 127601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7655, 1, 0, 1, 0, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 127601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7656, 1, 0, 1, 0, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 127601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7657, 1, 0, 1, 0, "この香り…\nサラサリア土産の\n香木かい？", 127601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7658, 1, 0, 1, 0, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 127601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7660, 1, 0, 1, 0, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 127601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7661, 1, 0, 1, 0, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 127601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7663, 1, 0, 1, 0, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 126001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7664, 1, 0, 1, 0, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 126001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7665, 1, 0, 1, 0, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 126001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7666, 1, 0, 1, 0, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 126001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7667, 1, 0, 1, 0, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 126001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7668, 4, 0, 1, 0, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 126001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7669, 8, 0, 1, 0, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 126001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7670, 1, 0, 1, 0, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 126001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7671, 1, 0, 1, 0, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 126001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7672, 1, 0, 1, 0, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 126001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7673, 1, 0, 1, 0, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 126001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7674, 4, 0, 1, 0, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 126001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7675, 8, 0, 1, 0, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 126001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7676, 1, 1, 1, 0, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 126001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7677, 1, 2, 1, 0, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 126001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7678, 1, 3, 1, 0, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 126001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7680, 1, 0, 1, 0, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 126001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7681, 1, 0, 1, 0, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 126001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7682, 1, 0, 1, 0, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 126001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7683, 1, 0, 1, 0, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 126001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7685, 1, 0, 1, 0, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 126001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7686, 1, 0, 1, 0, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 126001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7688, 12, 0, 1, 0, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 103801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7689, 12, 0, 1, 0, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 103801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7690, 1, 0, 1, 0, "お兄の、けはい…\nん、みつけた\nぎゅ", 126101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7691, 1, 0, 1, 0, "きょろきょろ\nここも\nぱとろる、するよ", 126101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7692, 1, 0, 1, 0, "おうち\nにぎやか\nさみしく、ない", 126101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7693, 1, 0, 1, 0, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 126101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7694, 1, 0, 1, 0, "はっぴーちゃん\nどこ？\nお兄、たべた？", 126101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7695, 4, 0, 1, 0, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 126101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7696, 8, 0, 1, 0, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 126101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7697, 1, 0, 1, 0, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 126101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7698, 1, 0, 1, 0, "くろぷれしあ\nかっこいい？\nん…あざます", 126101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7699, 1, 0, 1, 0, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 126101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7700, 1, 0, 1, 0, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 126101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7701, 4, 0, 1, 0, "かみ、りぼん\nおてつだい\nよろ\nです", 126101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7702, 8, 0, 1, 0, "ここ\nすむ\nね？\nまいにち、いっしょ", 126101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7703, 1, 1, 1, 0, "ぐっもにん\nあさごはん\nあさごはん", 126101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7704, 1, 2, 1, 0, "お兄\nおつかれ？\nれっつ、まいぺーす", 126101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7705, 1, 3, 1, 0, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 126101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7707, 1, 0, 1, 0, "ふかふか\nパパの、おなか\nみたい", 126101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7708, 1, 0, 1, 0, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 126101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7709, 1, 0, 1, 0, "いい、におい\nでも、お兄\nもっと、いい、におい", 126101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7710, 1, 0, 1, 0, "とりま\nお肉、やこ", 126101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7712, 1, 0, 1, 0, "もぐもぐ…\nむげん肉まん、も\nきぼう", 126101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7713, 1, 0, 1, 0, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 126101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7715, 1, 0, 1, 0, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 127901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7716, 1, 0, 1, 0, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 127901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7717, 1, 0, 1, 0, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 127901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7718, 1, 0, 1, 0, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 127901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7719, 1, 0, 1, 0, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 127901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7720, 4, 0, 1, 0, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 127901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7721, 8, 0, 1, 0, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 127901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7722, 1, 0, 1, 0, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 127901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7723, 1, 0, 1, 0, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 127901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7724, 1, 0, 1, 0, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 127901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7725, 1, 0, 1, 0, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 127901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7726, 4, 0, 1, 0, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 127901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7727, 8, 0, 1, 0, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 127901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7728, 1, 1, 1, 0, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 127901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7729, 1, 2, 1, 0, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 127901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7730, 1, 3, 1, 0, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 127901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7732, 1, 0, 1, 0, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 127901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7733, 1, 0, 1, 0, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 127901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7734, 1, 0, 1, 0, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 127901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7735, 1, 0, 1, 0, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 127901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7737, 1, 0, 1, 0, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 127901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7738, 1, 0, 1, 0, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 127901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7740, 1, 0, 1, 0, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 127901, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7741, 1, 0, 1, 0, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 127901, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7742, 12, 0, 1, 0, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 106601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7743, 12, 0, 1, 0, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 106601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7744, 1, 0, 1, 0, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 128001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7745, 1, 0, 1, 0, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 128001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7746, 1, 0, 1, 0, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 128001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7747, 1, 0, 1, 0, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 128001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7748, 1, 0, 1, 0, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 128001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7749, 4, 0, 1, 0, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 128001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7750, 8, 0, 1, 0, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 128001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7751, 1, 0, 1, 0, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 128001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7752, 1, 0, 1, 0, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 128001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7753, 1, 0, 1, 0, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 128001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7754, 1, 0, 1, 0, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 128001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7755, 4, 0, 1, 0, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 128001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7756, 8, 0, 1, 0, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 128001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7757, 1, 1, 1, 0, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 128001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7758, 1, 2, 1, 0, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 128001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7759, 1, 3, 1, 0, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 128001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7761, 1, 0, 1, 0, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 128001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7762, 1, 0, 1, 0, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 128001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7763, 1, 0, 1, 0, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 128001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7764, 1, 0, 1, 0, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 128001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7766, 1, 0, 1, 0, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 128001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7767, 1, 0, 1, 0, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 128001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7769, 1, 0, 1, 0, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 128001, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7770, 1, 0, 1, 0, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 128001, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7771, 1, 0, 1, 0, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 128101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7772, 1, 0, 1, 0, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 128101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7773, 1, 0, 1, 0, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 128101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7774, 1, 0, 1, 0, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 128101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7775, 1, 0, 1, 0, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 128101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7776, 4, 0, 1, 0, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 128101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7777, 8, 0, 1, 0, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 128101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7778, 1, 0, 1, 0, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 128101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7779, 1, 0, 1, 0, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 128101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7780, 1, 0, 1, 0, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 128101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7781, 1, 0, 1, 0, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 128101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7782, 4, 0, 1, 0, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 128101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7783, 8, 0, 1, 0, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 128101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7784, 1, 1, 1, 0, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 128101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7785, 1, 2, 1, 0, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 128101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7786, 1, 3, 1, 0, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 128101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7788, 1, 0, 1, 0, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 128101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7789, 1, 0, 1, 0, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 128101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7790, 1, 0, 1, 0, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 128101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7791, 1, 0, 1, 0, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 128101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7793, 1, 0, 1, 0, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 128101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7794, 1, 0, 1, 0, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 128101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7797, 1, 0, 1, 0, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 128201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7798, 1, 0, 1, 0, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 128201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7799, 1, 0, 1, 0, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 128201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7800, 1, 0, 1, 0, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 128201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7801, 1, 0, 1, 0, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 128201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7802, 4, 0, 1, 0, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 128201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7803, 8, 0, 1, 0, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 128201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7804, 1, 0, 1, 0, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 128201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7805, 1, 0, 1, 0, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 128201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7806, 1, 0, 1, 0, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 128201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7807, 1, 0, 1, 0, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 128201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7808, 4, 0, 1, 0, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 128201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7809, 8, 0, 1, 0, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 128201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7810, 1, 1, 1, 0, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 128201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7811, 1, 2, 1, 0, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 128201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7812, 1, 3, 1, 0, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 128201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7814, 1, 0, 1, 0, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 128201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7815, 1, 0, 1, 0, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 128201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7816, 1, 0, 1, 0, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 128201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7817, 1, 0, 1, 0, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 128201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7819, 1, 0, 1, 0, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 128201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7820, 1, 0, 1, 0, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 128201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7823, 1, 0, 1, 0, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 128301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7824, 1, 0, 1, 0, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 128301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7825, 1, 0, 1, 0, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 128301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7826, 1, 0, 1, 0, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 128301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7827, 1, 0, 1, 0, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 128301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7828, 4, 0, 1, 0, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 128301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7829, 8, 0, 1, 0, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 128301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7830, 1, 0, 1, 0, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 128301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7831, 1, 0, 1, 0, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 128301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7832, 1, 0, 1, 0, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 128301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7833, 1, 0, 1, 0, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 128301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7834, 4, 0, 1, 0, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 128301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7835, 8, 0, 1, 0, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 128301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7836, 1, 1, 1, 0, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 128301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7837, 1, 2, 1, 0, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 128301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7838, 1, 3, 1, 0, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 128301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7840, 1, 0, 1, 0, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 128301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7841, 1, 0, 1, 0, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 128301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7842, 1, 0, 1, 0, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 128301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7843, 1, 0, 1, 0, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 128301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7845, 1, 0, 1, 0, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 128301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7846, 1, 0, 1, 0, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 128301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7848, 1, 0, 1, 0, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 128401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7849, 1, 0, 1, 0, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 128401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7850, 1, 0, 1, 0, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 128401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7851, 1, 0, 1, 0, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 128401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7852, 1, 0, 1, 0, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 128401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7853, 4, 0, 1, 0, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 128401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7854, 8, 0, 1, 0, "来年も彼の隣で\n初日の出が\n見られるといいな～", 128401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7855, 1, 0, 1, 0, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 128401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7856, 1, 0, 1, 0, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 128401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7857, 1, 0, 1, 0, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 128401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7858, 1, 0, 1, 0, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 128401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7859, 4, 0, 1, 0, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 128401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7860, 8, 0, 1, 0, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 128401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7861, 1, 1, 1, 0, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 128401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7862, 1, 2, 1, 0, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 128401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7863, 1, 3, 1, 0, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 128401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7865, 1, 0, 1, 0, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 128401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7866, 1, 0, 1, 0, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 128401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7867, 1, 0, 1, 0, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 128401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7868, 1, 0, 1, 0, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 128401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7870, 1, 0, 1, 0, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 128401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7871, 1, 0, 1, 0, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 128401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7873, 12, 0, 1, 0, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 106501, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7874, 12, 0, 1, 0, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 106501, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7875, 1, 0, 1, 0, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 128501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7876, 1, 0, 1, 0, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 128501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7877, 1, 0, 1, 0, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 128501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7878, 1, 0, 1, 0, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 128501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7879, 1, 0, 1, 0, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 128501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7880, 4, 0, 1, 0, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 128501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7881, 8, 0, 1, 0, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 128501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7882, 1, 0, 1, 0, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 128501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7883, 1, 0, 1, 0, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 128501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7884, 1, 0, 1, 0, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 128501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7885, 1, 0, 1, 0, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 128501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7886, 4, 0, 1, 0, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 128501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7887, 8, 0, 1, 0, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 128501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7888, 1, 1, 1, 0, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 128501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7889, 1, 2, 1, 0, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 128501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7890, 1, 3, 1, 0, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 128501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7892, 1, 0, 1, 0, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 128501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7893, 1, 0, 1, 0, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 128501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7894, 1, 0, 1, 0, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 128501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7895, 1, 0, 1, 0, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 128501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7897, 1, 0, 1, 0, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 128501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7898, 1, 0, 1, 0, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 128501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7900, 1, 0, 1, 0, "この新しい力…\nさらに使いこなせる\nようになるには…", 128801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7901, 1, 0, 1, 0, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 128801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7902, 1, 0, 1, 0, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 128801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7903, 1, 0, 1, 0, "風の力の制御…\n段々とコツが\n掴めてきたな", 128801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7904, 1, 0, 1, 0, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 128801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7905, 4, 0, 1, 0, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 128801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7906, 8, 0, 1, 0, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 128801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7907, 1, 0, 1, 0, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 128801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7908, 1, 0, 1, 0, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 128801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7909, 1, 0, 1, 0, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 128801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7910, 1, 0, 1, 0, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 128801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7911, 4, 0, 1, 0, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 128801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7912, 8, 0, 1, 0, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 128801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7913, 1, 1, 1, 0, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 128801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7914, 1, 2, 1, 0, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 128801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7915, 1, 3, 1, 0, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 128801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7917, 1, 0, 1, 0, "休める時は休む…\n戦い続けるには\n必要なことだからね", 128801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7918, 1, 0, 1, 0, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 128801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7919, 1, 0, 1, 0, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 128801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7920, 1, 0, 1, 0, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 128801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7922, 1, 0, 1, 0, "この味付け…\n今度試してみようか\n味見は任せたからね", 128801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7923, 1, 0, 1, 0, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 128801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7925, 1, 0, 1, 0, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 128901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7926, 1, 0, 1, 0, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 128901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7927, 1, 0, 1, 0, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 128901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7928, 1, 0, 1, 0, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 128901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7929, 1, 0, 1, 0, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 128901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7930, 4, 0, 1, 0, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 128901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7931, 8, 0, 1, 0, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 128901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7932, 1, 0, 1, 0, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 128901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7933, 1, 0, 1, 0, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 128901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7934, 1, 0, 1, 0, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 128901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7935, 1, 0, 1, 0, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 128901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7936, 4, 0, 1, 0, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 128901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7937, 8, 0, 1, 0, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 128901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7938, 1, 1, 1, 0, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 128901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7939, 1, 2, 1, 0, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 128901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7940, 1, 3, 1, 0, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 128901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7942, 1, 0, 1, 0, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 128901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7943, 1, 0, 1, 0, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 128901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7944, 1, 0, 1, 0, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 128901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7945, 1, 0, 1, 0, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 128901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7947, 1, 0, 1, 0, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 128901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7948, 1, 0, 1, 0, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 128901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7950, 1, 0, 1, 0, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 128701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7951, 1, 0, 1, 0, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 128701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7952, 1, 0, 1, 0, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 128701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7953, 1, 0, 1, 0, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 128701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7954, 1, 0, 1, 0, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 128701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7955, 4, 0, 1, 0, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 128701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7956, 8, 0, 1, 0, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 128701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7957, 1, 0, 1, 0, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 128701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7958, 1, 0, 1, 0, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 128701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7959, 1, 0, 1, 0, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 128701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7960, 1, 0, 1, 0, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 128701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7961, 4, 0, 1, 0, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 128701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7962, 8, 0, 1, 0, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 128701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7963, 1, 1, 1, 0, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 128701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7964, 1, 2, 1, 0, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 128701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7965, 1, 3, 1, 0, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 128701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7967, 1, 0, 1, 0, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 128701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7968, 1, 0, 1, 0, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 128701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7969, 1, 0, 1, 0, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 128701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7970, 1, 0, 1, 0, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 128701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7972, 1, 0, 1, 0, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 128701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7973, 1, 0, 1, 0, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 128701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7975, 1, 0, 1, 0, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 129001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7976, 1, 0, 1, 0, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 129001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7977, 1, 0, 1, 0, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 129001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7978, 1, 0, 1, 0, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 129001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7979, 1, 0, 1, 0, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 129001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7980, 4, 0, 1, 0, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 129001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7981, 8, 0, 1, 0, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 129001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7982, 1, 0, 1, 0, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 129001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7983, 1, 0, 1, 0, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 129001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7984, 1, 0, 1, 0, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 129001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7985, 1, 0, 1, 0, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 129001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7986, 4, 0, 1, 0, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 129001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7987, 8, 0, 1, 0, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 129001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7988, 1, 1, 1, 0, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 129001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7989, 1, 2, 1, 0, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 129001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7990, 1, 3, 1, 0, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 129001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7992, 1, 0, 1, 0, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 129001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7993, 1, 0, 1, 0, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 129001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7994, 1, 0, 1, 0, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 129001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7995, 1, 0, 1, 0, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 129001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7997, 1, 0, 1, 0, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 129001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (7998, 1, 0, 1, 0, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 129001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8000, 1, 0, 1, 0, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 129301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8001, 1, 0, 1, 0, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 129301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8002, 1, 0, 1, 0, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 129301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8003, 1, 0, 1, 0, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 129301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8004, 1, 0, 1, 0, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 129301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8005, 4, 0, 1, 0, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 129301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8006, 8, 0, 1, 0, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 129301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8007, 1, 0, 1, 0, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 129301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8008, 1, 0, 1, 0, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 129301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8009, 1, 0, 1, 0, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 129301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8010, 1, 0, 1, 0, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 129301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8011, 4, 0, 1, 0, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 129301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8012, 8, 0, 1, 0, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 129301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8013, 1, 1, 1, 0, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 129301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8014, 1, 2, 1, 0, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 129301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8015, 1, 3, 1, 0, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 129301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8017, 1, 0, 1, 0, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 129301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8018, 1, 0, 1, 0, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 129301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8019, 1, 0, 1, 0, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 129301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8020, 1, 0, 1, 0, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 129301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8022, 1, 0, 1, 0, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 129301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8023, 1, 0, 1, 0, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 129301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8025, 1, 0, 1, 0, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 129301, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8026, 1, 0, 1, 0, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 129301, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8027, 12, 0, 1, 0, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 108901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8028, 12, 0, 1, 0, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 108901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8029, 12, 0, 1, 0, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 108801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8030, 12, 0, 1, 0, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 108801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8031, 12, 0, 1, 0, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 108701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8032, 12, 0, 1, 0, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 108701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8033, 1, 0, 1, 0, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 129401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8034, 1, 0, 1, 0, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 129401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8035, 1, 0, 1, 0, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 129401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8036, 1, 0, 1, 0, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 129401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8037, 1, 0, 1, 0, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 129401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8038, 4, 0, 1, 0, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 129401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8039, 8, 0, 1, 0, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 129401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8040, 1, 0, 1, 0, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 129401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8041, 1, 0, 1, 0, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 129401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8042, 1, 0, 1, 0, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 129401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8043, 1, 0, 1, 0, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 129401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8044, 4, 0, 1, 0, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 129401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8045, 8, 0, 1, 0, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 129401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8046, 1, 1, 1, 0, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 129401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8047, 1, 2, 1, 0, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 129401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8048, 1, 3, 1, 0, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 129401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8050, 1, 0, 1, 0, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 129401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8051, 1, 0, 1, 0, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 129401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8052, 1, 0, 1, 0, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 129401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8053, 1, 0, 1, 0, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 129401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8055, 1, 0, 1, 0, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 129401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8056, 1, 0, 1, 0, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 129401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8058, 1, 0, 1, 0, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 129401, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8059, 1, 0, 1, 0, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 129401, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8060, 1, 0, 1, 0, "さてと…\n明日の訓練メニュー\n考えねえとな", 129801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8061, 1, 0, 1, 0, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 129801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8062, 1, 0, 1, 0, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 129801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8063, 1, 0, 1, 0, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 129801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8064, 1, 0, 1, 0, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 129801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8065, 4, 0, 1, 0, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 129801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8066, 8, 0, 1, 0, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 129801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8067, 1, 0, 1, 0, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 129801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8068, 1, 0, 1, 0, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 129801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8069, 1, 0, 1, 0, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 129801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8070, 1, 0, 1, 0, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 129801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8071, 4, 0, 1, 0, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 129801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8072, 8, 0, 1, 0, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 129801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8073, 1, 1, 1, 0, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 129801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8074, 1, 2, 1, 0, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 129801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8075, 1, 3, 1, 0, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 129801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8077, 1, 0, 1, 0, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 129801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8078, 1, 0, 1, 0, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 129801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8079, 1, 0, 1, 0, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 129801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8080, 1, 0, 1, 0, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 129801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8082, 1, 0, 1, 0, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 129801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8083, 1, 0, 1, 0, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 129801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8085, 1, 0, 1, 0, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 129901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8086, 1, 0, 1, 0, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 129901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8087, 1, 0, 1, 0, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 129901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8088, 1, 0, 1, 0, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 129901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8089, 1, 0, 1, 0, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 129901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8090, 4, 0, 1, 0, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 129901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8091, 8, 0, 1, 0, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 129901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8092, 1, 0, 1, 0, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 129901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8093, 1, 0, 1, 0, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 129901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8094, 1, 0, 1, 0, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 129901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8095, 1, 0, 1, 0, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 129901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8096, 4, 0, 1, 0, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 129901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8097, 8, 0, 1, 0, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 129901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8098, 1, 1, 1, 0, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 129901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8099, 1, 2, 1, 0, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 129901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8100, 1, 3, 1, 0, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 129901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8102, 1, 0, 1, 0, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 129901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8103, 1, 0, 1, 0, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 129901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8104, 1, 0, 1, 0, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 129901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8105, 1, 0, 1, 0, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 129901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8107, 1, 0, 1, 0, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 129901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8108, 1, 0, 1, 0, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 129901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8110, 1, 0, 1, 0, "……（そわそわ）", 129601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8111, 1, 0, 1, 0, "ここに寄らずの花陣…\n作ったらダメですよね", 129601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8112, 1, 0, 1, 0, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 129601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8113, 1, 0, 1, 0, "身を隠せる場所が\nあると…\n助かるんですけど", 129601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8114, 1, 0, 1, 0, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 129601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8115, 4, 0, 1, 0, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 129601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8116, 8, 0, 1, 0, "この家なら安全─\nかもしれません\n魔物が出ないので", 129601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8117, 1, 0, 1, 0, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 129601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8118, 1, 0, 1, 0, "長話─苦手です\n疲れるので…\nふぅ…", 129601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8119, 1, 0, 1, 0, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 129601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8120, 1, 0, 1, 0, "世話をするのは\n得意です\n…花の、ですよ？", 129601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8121, 4, 0, 1, 0, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 129601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8122, 8, 0, 1, 0, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 129601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8123, 1, 1, 1, 0, "花に、お水─？\n…いりません", 129601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8124, 1, 2, 1, 0, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 129601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8125, 1, 3, 1, 0, "夜番。私がします\nあまり眠らない\n性質なので…", 129601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8127, 1, 0, 1, 0, "ここなら根を張れる\nかもしれません\n…張りませんけど", 129601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8128, 1, 0, 1, 0, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 129601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8129, 1, 0, 1, 0, "花みたいに─\nいい匂い、です", 129601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8130, 1, 0, 1, 0, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 129601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8132, 1, 0, 1, 0, "食事も立派な戦支度\n媛が言ってました", 129601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8133, 1, 0, 1, 0, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 129601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8135, 1, 0, 1, 0, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 130701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8136, 1, 0, 1, 0, "あ、どこからか\nお花の匂いが…\nいい香りですね", 130701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8137, 1, 0, 1, 0, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 130701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8138, 1, 0, 1, 0, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 130701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8139, 1, 0, 1, 0, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 130701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8140, 4, 0, 1, 0, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 130701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8141, 8, 0, 1, 0, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 130701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8142, 1, 0, 1, 0, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 130701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8143, 1, 0, 1, 0, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 130701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8144, 1, 0, 1, 0, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 130701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8145, 1, 0, 1, 0, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 130701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8146, 4, 0, 1, 0, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 130701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8147, 8, 0, 1, 0, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 130701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8148, 1, 1, 1, 0, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 130701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8149, 1, 2, 1, 0, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 130701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8150, 1, 3, 1, 0, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 130701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8152, 1, 0, 1, 0, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 130701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8153, 1, 0, 1, 0, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 130701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8154, 1, 0, 1, 0, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 130701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8155, 1, 0, 1, 0, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 130701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8157, 1, 0, 1, 0, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 130701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8158, 1, 0, 1, 0, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 130701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8160, 1, 0, 1, 0, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 130801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8161, 1, 0, 1, 0, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 130801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8162, 1, 0, 1, 0, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 130801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8163, 1, 0, 1, 0, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 130801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8164, 1, 0, 1, 0, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 130801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8165, 4, 0, 1, 0, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 130801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8166, 8, 0, 1, 0, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 130801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8167, 1, 0, 1, 0, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 130801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8168, 1, 0, 1, 0, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 130801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8169, 1, 0, 1, 0, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 130801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8170, 1, 0, 1, 0, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 130801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8171, 4, 0, 1, 0, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 130801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8172, 8, 0, 1, 0, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 130801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8173, 1, 1, 1, 0, "はう、そうでした\nお花にお水を\nあげる時間です！", 130801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8174, 1, 2, 1, 0, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 130801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8175, 1, 3, 1, 0, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 130801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8177, 1, 0, 1, 0, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 130801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8178, 1, 0, 1, 0, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 130801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8179, 1, 0, 1, 0, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 130801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8180, 1, 0, 1, 0, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 130801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8182, 1, 0, 1, 0, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 130801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8183, 1, 0, 1, 0, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 130801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8185, 1, 0, 1, 0, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 131001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8186, 1, 0, 1, 0, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 131001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8187, 1, 0, 1, 0, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 131001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8188, 1, 0, 1, 0, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 131001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8189, 1, 0, 1, 0, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 131001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8190, 4, 0, 1, 0, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 131001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8191, 8, 0, 1, 0, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 131001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8192, 1, 0, 1, 0, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 131001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8193, 1, 0, 1, 0, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 131001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8194, 1, 0, 1, 0, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 131001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8195, 1, 0, 1, 0, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 131001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8196, 4, 0, 1, 0, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 131001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8197, 8, 0, 1, 0, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 131001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8198, 1, 1, 1, 0, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 131001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8199, 1, 2, 1, 0, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 131001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8200, 1, 3, 1, 0, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 131001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8202, 1, 0, 1, 0, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 131001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8203, 1, 0, 1, 0, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 131001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8204, 1, 0, 1, 0, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 131001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8205, 1, 0, 1, 0, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 131001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8207, 1, 0, 1, 0, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 131001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8208, 1, 0, 1, 0, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 131001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8210, 12, 0, 1, 0, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 105601, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8211, 12, 0, 1, 0, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 105601, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8212, 1, 0, 1, 0, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 130901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8213, 1, 0, 1, 0, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 130901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8214, 1, 0, 1, 0, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 130901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8215, 1, 0, 1, 0, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 130901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8216, 1, 0, 1, 0, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 130901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8217, 4, 0, 1, 0, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 130901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8218, 8, 0, 1, 0, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 130901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8219, 1, 0, 1, 0, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 130901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8220, 1, 0, 1, 0, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 130901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8221, 1, 0, 1, 0, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 130901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8222, 1, 0, 1, 0, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 130901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8223, 4, 0, 1, 0, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 130901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8224, 8, 0, 1, 0, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 130901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8225, 1, 1, 1, 0, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 130901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8226, 1, 2, 1, 0, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 130901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8227, 1, 3, 1, 0, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 130901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8229, 1, 0, 1, 0, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 130901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8230, 1, 0, 1, 0, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 130901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8231, 1, 0, 1, 0, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 130901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8232, 1, 0, 1, 0, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 130901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8234, 1, 0, 1, 0, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 130901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8235, 1, 0, 1, 0, "パークの\nガイドブックとか\nあったらいいですね♪", 130901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8237, 1, 0, 1, 0, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 131101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8238, 1, 0, 1, 0, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 131101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8239, 1, 0, 1, 0, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 131101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8240, 1, 0, 1, 0, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 131101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8241, 1, 0, 1, 0, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 131101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8242, 4, 0, 1, 0, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 131101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8243, 8, 0, 1, 0, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 131101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8244, 1, 0, 1, 0, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 131101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8245, 1, 0, 1, 0, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 131101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8246, 1, 0, 1, 0, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 131101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8247, 1, 0, 1, 0, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 131101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8248, 4, 0, 1, 0, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 131101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8249, 8, 0, 1, 0, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 131101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8250, 1, 1, 1, 0, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 131101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8251, 1, 2, 1, 0, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 131101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8252, 1, 3, 1, 0, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 131101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8254, 1, 0, 1, 0, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 131101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8255, 1, 0, 1, 0, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 131101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8256, 1, 0, 1, 0, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 131101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8257, 1, 0, 1, 0, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 131101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8259, 1, 0, 1, 0, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 131101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8260, 1, 0, 1, 0, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 131101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8262, 1, 0, 1, 0, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 131101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8263, 1, 0, 1, 0, "あーやべ\n一瞬で寝そー\nヤバだわ～", 131101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8264, 1, 0, 1, 0, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 129701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8265, 1, 0, 1, 0, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 129701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8266, 1, 0, 1, 0, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 129701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8267, 1, 0, 1, 0, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 129701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8268, 1, 0, 1, 0, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 129701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8269, 4, 0, 1, 0, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 129701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8270, 8, 0, 1, 0, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 129701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8271, 1, 0, 1, 0, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 129701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8272, 1, 0, 1, 0, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 129701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8273, 1, 0, 1, 0, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 129701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8274, 1, 0, 1, 0, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 129701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8275, 4, 0, 1, 0, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 129701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8276, 8, 0, 1, 0, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 129701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8277, 1, 1, 1, 0, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 129701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8278, 1, 2, 1, 0, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 129701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8279, 1, 3, 1, 0, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 129701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8281, 1, 0, 1, 0, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 129701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8282, 1, 0, 1, 0, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 129701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8283, 1, 0, 1, 0, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 129701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8284, 1, 0, 1, 0, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 129701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8286, 1, 0, 1, 0, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 129701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8287, 1, 0, 1, 0, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 129701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8289, 1, 0, 1, 0, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 131201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8290, 1, 0, 1, 0, "海への行き来は\nテレポーテーションに\nお任せです★", 131201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8291, 1, 0, 1, 0, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 131201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8292, 1, 0, 1, 0, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 131201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8293, 1, 0, 1, 0, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 131201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8294, 4, 0, 1, 0, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 131201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8295, 8, 0, 1, 0, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 131201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8296, 1, 0, 1, 0, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 131201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8297, 1, 0, 1, 0, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 131201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8298, 1, 0, 1, 0, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 131201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8299, 1, 0, 1, 0, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 131201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8300, 4, 0, 1, 0, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 131201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8301, 8, 0, 1, 0, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 131201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8302, 1, 1, 1, 0, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 131201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8303, 1, 2, 1, 0, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 131201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8304, 1, 3, 1, 0, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 131201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8306, 1, 0, 1, 0, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 131201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8307, 1, 0, 1, 0, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 131201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8308, 1, 0, 1, 0, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 131201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8309, 1, 0, 1, 0, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 131201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8311, 1, 0, 1, 0, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 131201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8312, 1, 0, 1, 0, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 131201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8314, 1, 0, 1, 0, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 131201, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8315, 1, 0, 1, 0, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 131201, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8316, 1, 0, 1, 0, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 131601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8317, 1, 0, 1, 0, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 131601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8318, 1, 0, 1, 0, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 131601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8319, 1, 0, 1, 0, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 131601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8320, 1, 0, 1, 0, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 131601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8321, 4, 0, 1, 0, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 131601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8322, 8, 0, 1, 0, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 131601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8323, 1, 0, 1, 0, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 131601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8324, 1, 0, 1, 0, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 131601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8325, 1, 0, 1, 0, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 131601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8326, 1, 0, 1, 0, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 131601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8327, 4, 0, 1, 0, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 131601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8328, 8, 0, 1, 0, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 131601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8329, 1, 1, 1, 0, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 131601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8330, 1, 2, 1, 0, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 131601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8331, 1, 3, 1, 0, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 131601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8333, 1, 0, 1, 0, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 131601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8334, 1, 0, 1, 0, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 131601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8335, 1, 0, 1, 0, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 131601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8336, 1, 0, 1, 0, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 131601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8338, 1, 0, 1, 0, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 131601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8339, 1, 0, 1, 0, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 131601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8341, 1, 0, 1, 0, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 131601, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8342, 1, 0, 1, 0, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 131601, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8343, 1, 0, 1, 0, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 131701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8344, 1, 0, 1, 0, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 131701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8345, 1, 0, 1, 0, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 131701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8346, 1, 0, 1, 0, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 131701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8347, 1, 0, 1, 0, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 131701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8348, 4, 0, 1, 0, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 131701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8349, 8, 0, 1, 0, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 131701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8350, 1, 0, 1, 0, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 131701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8351, 1, 0, 1, 0, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 131701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8352, 1, 0, 1, 0, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 131701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8353, 1, 0, 1, 0, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 131701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8354, 4, 0, 1, 0, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 131701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8355, 8, 0, 1, 0, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 131701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8356, 1, 1, 1, 0, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 131701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8357, 1, 2, 1, 0, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 131701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8358, 1, 3, 1, 0, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 131701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8360, 1, 0, 1, 0, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 131701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8361, 1, 0, 1, 0, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 131701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8362, 1, 0, 1, 0, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 131701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8363, 1, 0, 1, 0, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 131701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8365, 1, 0, 1, 0, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 131701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8366, 1, 0, 1, 0, "文字を読むのと\nデータを読むのは\nまったく違うわ", 131701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8368, 1, 0, 1, 0, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 131701, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8369, 1, 0, 1, 0, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 131701, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8370, 1, 0, 1, 0, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 118601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8371, 1, 0, 1, 0, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 118601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8372, 1, 0, 1, 0, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 118601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8373, 1, 0, 1, 0, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 118601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8374, 1, 0, 1, 0, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 118601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8375, 4, 0, 1, 0, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 118601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8376, 8, 0, 1, 0, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 118601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8377, 1, 0, 1, 0, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 118601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8378, 1, 0, 1, 0, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 118601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8379, 1, 0, 1, 0, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 118601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8380, 1, 0, 1, 0, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 118601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8381, 4, 0, 1, 0, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 118601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8382, 8, 0, 1, 0, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 118601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8383, 1, 1, 1, 0, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 118601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8384, 1, 2, 1, 0, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 118601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8385, 1, 3, 1, 0, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 118601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8387, 1, 0, 1, 0, "悪くはないけど…\nあいつの頭の方が\n座り心地いいかも", 118601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8388, 1, 0, 1, 0, "この子を使った\n悪戯は…\n…止めとこっと\nこんなにかわいいし♪", 118601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8389, 1, 0, 1, 0, "このギルドハウスに\nピッタリな匂いね♪\n一言で言えば…\nカオスって感じ？", 118601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8390, 1, 0, 1, 0, "なんていうか…\n心があたたかいわ\n…変な感じ！", 118601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8392, 1, 0, 1, 0, "ガイド妖精にも\n食事の機能があって\nよかったのかも\nしれないわね…もぐ…", 118601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8393, 1, 0, 1, 0, "じれったい物語ね…\nこんなの霧を使えば\n一瞬で終わるのに！", 118601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8395, 1, 0, 1, 0, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 131801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8396, 1, 0, 1, 0, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 131801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8397, 1, 0, 1, 0, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 131801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8398, 1, 0, 1, 0, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 131801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8399, 1, 0, 1, 0, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 131801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8400, 4, 0, 1, 0, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 131801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8401, 8, 0, 1, 0, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 131801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8402, 1, 0, 1, 0, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 131801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8403, 1, 0, 1, 0, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 131801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8404, 1, 0, 1, 0, "熱中症には\n気を付けないと\nだめだよ～？", 131801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8405, 1, 0, 1, 0, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 131801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8406, 4, 0, 1, 0, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 131801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8407, 8, 0, 1, 0, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 131801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8408, 1, 1, 1, 0, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 131801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8409, 1, 2, 1, 0, "ふふ♪\n波音が心地良い\n時間だね～☆", 131801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8410, 1, 3, 1, 0, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 131801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8412, 1, 0, 1, 0, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 131801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8413, 1, 0, 1, 0, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 131801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8414, 1, 0, 1, 0, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 131801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8415, 1, 0, 1, 0, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 131801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8417, 1, 0, 1, 0, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 131801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8418, 1, 0, 1, 0, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 131801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8420, 1, 0, 1, 0, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 131801, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8421, 1, 0, 1, 0, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 131801, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8422, 1, 0, 1, 0, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 181201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8423, 1, 0, 1, 0, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 181201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8424, 1, 0, 1, 0, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 181201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8425, 1, 0, 1, 0, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 181201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8426, 1, 0, 1, 0, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 181201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8427, 4, 0, 1, 0, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 181201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8428, 8, 0, 1, 0, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 181201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8429, 1, 0, 1, 0, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 181201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8430, 1, 0, 1, 0, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 181201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8431, 1, 0, 1, 0, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 181201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8432, 1, 0, 1, 0, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 181201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8433, 4, 0, 1, 0, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 181201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8434, 8, 0, 1, 0, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 181201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8435, 1, 1, 1, 0, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 181201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8436, 1, 2, 1, 0, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 181201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8437, 1, 3, 1, 0, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 181201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8439, 1, 0, 1, 0, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 181201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8440, 1, 0, 1, 0, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 181201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8441, 1, 0, 1, 0, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 181201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8442, 1, 0, 1, 0, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 181201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8444, 1, 0, 1, 0, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 181201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8445, 1, 0, 1, 0, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 181201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8447, 1, 0, 1, 0, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 131901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8448, 1, 0, 1, 0, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 131901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8449, 1, 0, 1, 0, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 131901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8450, 1, 0, 1, 0, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 131901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8451, 1, 0, 1, 0, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 131901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8452, 4, 0, 1, 0, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 131901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8453, 8, 0, 1, 0, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 131901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8454, 1, 0, 1, 0, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 131901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8455, 1, 0, 1, 0, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 131901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8456, 1, 0, 1, 0, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 131901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8457, 1, 0, 1, 0, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 131901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8458, 4, 0, 1, 0, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 131901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8459, 8, 0, 1, 0, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 131901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8460, 1, 1, 1, 0, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 131901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8461, 1, 2, 1, 0, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 131901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8462, 1, 3, 1, 0, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 131901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8464, 1, 0, 1, 0, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 131901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8465, 1, 0, 1, 0, "細かいところまで\nすごーく丁寧に\n作られてる…", 131901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8466, 1, 0, 1, 0, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 131901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8467, 1, 0, 1, 0, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 131901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8469, 1, 0, 1, 0, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 131901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8470, 1, 0, 1, 0, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 131901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8472, 1, 0, 1, 0, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 131901, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8473, 1, 0, 1, 0, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 131901, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8474, 1, 0, 1, 0, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 132101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8475, 1, 0, 1, 0, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 132101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8476, 1, 0, 1, 0, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 132101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8477, 1, 0, 1, 0, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 132101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8478, 1, 0, 1, 0, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 132101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8479, 4, 0, 1, 0, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 132101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8480, 8, 0, 1, 0, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 132101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8481, 1, 0, 1, 0, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 132101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8482, 1, 0, 1, 0, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 132101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8483, 1, 0, 1, 0, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 132101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8484, 1, 0, 1, 0, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 132101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8485, 4, 0, 1, 0, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 132101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8486, 8, 0, 1, 0, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 132101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8487, 1, 1, 1, 0, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 132101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8488, 1, 2, 1, 0, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 132101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8489, 1, 3, 1, 0, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 132101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8491, 1, 0, 1, 0, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 132101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8492, 1, 0, 1, 0, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 132101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8493, 1, 0, 1, 0, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 132101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8494, 1, 0, 1, 0, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 132101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8496, 1, 0, 1, 0, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 132101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8497, 1, 0, 1, 0, "海に関する詩文なども\nあるのでしょうか…\n気になります", 132101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8499, 1, 0, 1, 0, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 132101, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8500, 1, 0, 1, 0, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 132101, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8501, 1, 0, 1, 0, "これがこの世界の\n居住環境か…\n面白い構造だね", 132001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8502, 1, 0, 1, 0, "暇を持て余していてね\n誰か話し相手に\nなってくれないかな", 132001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8503, 1, 0, 1, 0, "目新しいものが\nたくさんあるね\n胸が躍るよ", 132001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8504, 1, 0, 1, 0, "頑丈そうな建物だ\nここなら彼女たちが\n暴れても大丈夫かな？", 132001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8505, 1, 0, 1, 0, "力が制限されていて\n良かった面もある\n君も吐かずにすんだし", 132001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8506, 4, 0, 1, 0, "悪さなんてするわけ\nないじゃないか\n傷つくなぁ", 132001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8507, 8, 0, 1, 0, "普段と違う格好で\n歩き回るのも\n悪くない経験だ", 132001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8508, 1, 0, 1, 0, "ボクに聞きたいことが\nあるなら遠慮なく\n質問するといい", 132001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8509, 1, 0, 1, 0, "気軽に魔女に\n話しかけるなんて\n君は物好きだね", 132001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8510, 1, 0, 1, 0, "この世界においては\nボクは異物だからね\n大人しくしているさ", 132001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8511, 1, 0, 1, 0, "ボクの反応を\n見たいのかい？\n君の好奇心には\n恐れ入るよ", 132001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8512, 4, 0, 1, 0, "ボクの手を借りたい？\nもちろん構わないさ\n対価はいただくけどね", 132001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8513, 8, 0, 1, 0, "ボクと海を見たい？\nふふ、魅力的な\nお誘いだね", 132001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8514, 1, 1, 1, 0, "お目覚めかな？\nさて、君の見た夢を\n教えてくれないかな？", 132001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8515, 1, 2, 1, 0, "食後のお茶を\n一緒にどうだろうか？\nさあ、席に座ってよ", 132001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8516, 1, 3, 1, 0, "おや、お休みかな？\n魔女の前で無防備だね\n…無論、何もしないさ", 132001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8518, 1, 0, 1, 0, "こういう椅子は\n茶会には向かないね\nつい気が緩んでしまう", 132001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8519, 1, 0, 1, 0, "こういうものを好きな\n女の子がいいのかな？\n君の嗜好を知りたいな", 132001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8520, 1, 0, 1, 0, "いい香りだ\nこれにはどんな\n効能があるんだろう？", 132001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8521, 1, 0, 1, 0, "火の点け方に\nボクのいた世界との\n違いはないのかな？", 132001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8523, 1, 0, 1, 0, "これもまた未知の味だ\n存分に味わわせて\nもらうとしよう", 132001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8524, 1, 0, 1, 0, "素晴らしいね\nここには叡智が\n詰まっている", 132001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8526, 1, 0, 1, 0, "魔物を模倣した\nぬいぐるみか…\n実物を見たくなるね", 132001, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8527, 1, 0, 1, 0, "いい気持ちだ…ボクと\nしたことが、何も考え\nられなくなりそうだ", 132001, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8528, 12, 0, 1, 0, "願わずにいられません\nどうかこちらの\n世界には…悲劇が\n生まれませんようにと", 109701, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8529, 12, 0, 1, 0, "食事のご希望があれば\nなんなりと。お腹が\nいっぱいになるまで\n沢山ご用意しますね", 109701, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8530, 12, 0, 1, 0, "あの男、これからも\n観察が必要ね\nラムの目の届く範囲に\n置く必要があるわ", 109801, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8531, 12, 0, 1, 0, "素直なところは\nあの男の美点ね\n癪だけど、偶然にも\nラムと同じだわ", 109801, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8532, 12, 0, 1, 0, "迷惑かけちゃったけど\n雪遊びは楽しかったわ\nまたやりたい…なんて\n言ったら怒られるかも", 109901, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8533, 12, 0, 1, 0, "数えきれないくらいの\n思い出を、みんなで\n作りましょう！", 109901, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8534, 1, 0, 1, 0, "わわっ、今すごく\nいい香りがしたけど…\n誰の香水かな\n教えてほしいな…♪", 132201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8535, 1, 0, 1, 0, "錬金術用の釜、\n置くならどこかな？\n自分専用の錬金釜、\n憧れちゃうよね♪", 132201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8536, 1, 0, 1, 0, "この匂い…誰か\n唐揚げ食べたみたい\nうぅ…お腹空いて\nきちゃうよ", 132201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8537, 1, 0, 1, 0, "次の新曲のタイトルは\n『恋の錬金術』！\nうーん、ちょっと\nベタ過ぎるかな…", 132201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8538, 1, 0, 1, 0, "あ、錬金の素材\n切らしてたんだ…\n彼を誘って採集に\n行ってこようかな", 132201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8539, 4, 0, 1, 0, "瞬時に着替えができる\n薬とかできないかな？\n舞台で衣装チェンジ！\nなーんて、楽しそう♪", 132201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8540, 8, 0, 1, 0, "彼が遊びに来た後は\n部屋の空気がほんのり\n彼の匂いなんだ…\nなんだか幸せだな♪", 132201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8541, 1, 0, 1, 0, "今日もバイト？\nそれならこれ飲んで\n私のお手製ポーション\nがんばってきてね！", 132201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8542, 1, 0, 1, 0, "新しくできたカレー屋\n行ってみない？\n時々スパイスの香りが\n流れてくるんだよ", 132201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8543, 1, 0, 1, 0, "私のフィギュア\nそっくりでしょ！\nルイちゃんが作って\nくれたんだ", 132201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8544, 1, 0, 1, 0, "あれ、シャンプー\n変えた？　私と同じの\nだよ！　どうしよう\nドキドキしちゃう…", 132201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8545, 4, 0, 1, 0, "いらっしゃいませ♪\n何を作りますか？\nなーんてね。錬金堂で\nまた一日店長したいな", 132201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8546, 8, 0, 1, 0, "クンクン…彼の\nハンカチ…いい匂い♪\n私だけの秘密のお守り\nにしてるんだ", 132201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8547, 1, 1, 1, 0, "ねえねえ、天気も\nいいし一緒に錬金の\n素材集めに行こうよ！", 132201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8548, 1, 2, 1, 0, "お昼ご飯、用意したよ\nあはは、さすがに\nランチは錬成しないよ\n私の手作りだよ♪", 132201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8549, 1, 3, 1, 0, "今日も一日お疲れさま\nこれ飲んでゆっくり\n休んで。薬じゃないよ\nホットミルクだよ？", 132201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8551, 1, 0, 1, 0, "昨日、遅くまで\n錬金術の勉強してた\nから眠くって…\nちょっと一休みっと", 132201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8552, 1, 0, 1, 0, "この子にちょっとだけ\n香水かけて、と…\nんー！　だっこした時\nすっごくいい匂い♪", 132201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8553, 1, 0, 1, 0, "花みたいな、\nお菓子みたいな\nいい香り。気持ちが\n穏やかになるね♪", 132201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8554, 1, 0, 1, 0, "外でもこの暖かさを\n感じられる道具、\n作れたりしないかな？", 132201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8556, 1, 0, 1, 0, "素材集めに行くと\nお腹が空くんだよね\nイロハちゃんも\nこの味好きそう", 132201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8557, 1, 0, 1, 0, "錬金術にはたくさんの\n知識が必要だからね\nさあ、今日も調合書を\n読まなくちゃ", 132201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8559, 1, 0, 1, 0, "ここが騎士さんたちの\n住んでる場所…！\n粗相がないように\n気をつけなきゃ", 127701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8560, 1, 0, 1, 0, "ジオ・テオゴニアの\nみんな、私がいない間\nちゃんとお仕事\nしてればいいけど…", 127701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8561, 1, 0, 1, 0, "コッコロさんも\n従者として長いって\n聞いたような…\nあの年ですごい…！", 127701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8562, 1, 0, 1, 0, "自分がもてなされる\n側になるのって\nやっぱりちょっと\n落ち着かないかも…", 127701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8563, 1, 0, 1, 0, "今度のお休みの日は\n何しよう…？\nあっ、最近できたお店\nヴルムと行こうかな？", 127701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8564, 4, 0, 1, 0, "お買い物したら\nかわいいからって\nたくさんおまけして\nもらっちゃった…♪", 127701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8565, 8, 0, 1, 0, "ここにいると、つい\nぼんやりしちゃう…\nこれも騎士さんの力…\nなのかな", 127701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8566, 1, 0, 1, 0, "お手伝いできることは\nありませんか？\nお世話でもなんでも\n任せてくださいっ", 127701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8567, 1, 0, 1, 0, "ランドソルに初めて\n来たときは驚きました\n砂糖の山と思ったら…\nあんなに寒いなんて！", 127701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8568, 1, 0, 1, 0, "あの、ヴルムを\n見ませんでしたか？\nもう…あの子ってば\nまた仕事をサボって…", 127701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8569, 1, 0, 1, 0, "家具や家が壊れても\n食べられないなんて\nなんだかもったいない\nですね", 127701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8570, 4, 0, 1, 0, "とっておきの紅茶を\n手に入れたんです\n一杯いかがですか？\nその、日頃のお礼です", 127701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8571, 8, 0, 1, 0, "頑張ってて偉いって…\nほ、褒めてもなんにも\n出ませんからね！？\n…えへへ♪", 127701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8572, 1, 1, 1, 0, "おはようございます！\nモーニングティーは\nいかがですか？\n私、準備しますねっ", 127701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8573, 1, 2, 1, 0, "ライラエルさまも\nお昼寝はお好きです\nけど…だからって\n寝すぎはダメですよ！", 127701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8574, 1, 3, 1, 0, "おやすみなさい\nくれぐれも寝不足には\n気をつけてくださいね", 127701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8576, 1, 0, 1, 0, "しっかりしないと…\nメイドの私が\n気を抜くなんて…\nそんな…ふぅ…", 127701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8577, 1, 0, 1, 0, "わぁ、かわいい…♪\nってダメダメ！\nメイドなんだから\n落ち着かないと…！", 127701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8578, 1, 0, 1, 0, "いい匂い…♪\nこの香り、\nライラエルさまも\n好きそうです！", 127701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8579, 1, 0, 1, 0, "ランドソルの環境に\n触れると、この光の\nありがたみが\n身に沁みますね…", 127701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8581, 1, 0, 1, 0, "甘くない食べ物が\n一般的なのも\n慣れてきました\nあむ…おいひぃ…♪", 127701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8582, 1, 0, 1, 0, "ランドソルの文字、\n私たちのところと\nちょっと違いますね…\n覚えられるかなぁ", 127701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8584, 1, 0, 1, 0, "甘い匂いがしない…\nジオ・テオゴニアとは\n別の世界なんですね", 127801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8585, 1, 0, 1, 0, "掃除めんど…\nん…このくらいで\nまー十分でしょ", 127801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8586, 1, 0, 1, 0, "そろそろ食事の\n用意もしなきゃ…\nあーやる事多い…", 127801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8587, 1, 0, 1, 0, "秘密の隠れ場所、\nまた姉さんにバレた…\n次のを探さないと", 127801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8588, 1, 0, 1, 0, "知らない間に仕事が\n終わったり…しないか\nしてくれたらいいのに", 127801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8589, 4, 0, 1, 0, "きちんともてなすには\n騎士さんの好みも\n覚えてかないと…", 127801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8590, 8, 0, 1, 0, "ご主人サマに\nご奉仕します～♪\n…キャラじゃないか\nやめやめ、恥ずかし…", 127801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8591, 1, 0, 1, 0, "居心地いいっすね…\n今日からここに\n住んでもいいですか", 127801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8592, 1, 0, 1, 0, "あの、ちょっと姉さん\n怒らせちゃったんで\n匿ってくれません？", 127801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8593, 1, 0, 1, 0, "羽と尻尾が格好いい？\n意外と邪魔ですよ\n仰向けで寝にくいし", 127801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8594, 1, 0, 1, 0, "ワークライフバランス\nっていい言葉ですね\nぜひライフ多めで", 127801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8595, 4, 0, 1, 0, "騎士さんって\n働き者ですね\nそのままあたしの\n仕事も…いえなんでも", 127801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8596, 8, 0, 1, 0, "熱心に見てても\n尻尾は触らせて\nあげませんってば\n角？　角なら…まぁ", 127801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8597, 1, 1, 1, 0, "寝ても寝ても\n寝足りない…ふぁ…\nあー…眠たい…", 127801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8598, 1, 2, 1, 0, "アフタヌーンティーの\n準備は得意ですよ\nつまみ食いとか\n…つまみ食いとか", 127801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8599, 1, 3, 1, 0, "よく休んでください\n布団は干しといたんで\n…姉さんが", 127801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8601, 1, 0, 1, 0, "ふー…\nこのままずーっと\nここに居たい…", 127801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8602, 1, 0, 1, 0, "あんたも一緒に\nサボっちゃうか～\n…なんて", 127801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8603, 1, 0, 1, 0, "落ち着く匂い…\n騎士さんの好み\nなんですかね", 127801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8604, 1, 0, 1, 0, "こういうとこの\n掃除って面倒だし…\n今度でいっか", 127801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8606, 1, 0, 1, 0, "ご飯の前に\nこんな食べたら\n姉さんきっと怒るな…", 127801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8607, 1, 0, 1, 0, "食に関する本が\nやけに多い…\nお国柄ですかね", 127801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8609, 1, 0, 1, 0, "すんすん…\nお兄さんの匂いが\nぷんぷんする～♡\nザコくっさぁ♡", 130201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8610, 1, 0, 1, 0, "ぴこーん！\nいひひっ、新作メカの\n面白いアイデア、\n思いついちった～♡", 130201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8611, 1, 0, 1, 0, "メカもいじりたいし\n服もアクセもほしー…\nあ～、おこづかい\n足んないよ～！", 130201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8612, 1, 0, 1, 0, "お勉強でもしよっかな\nザコお兄さんには\n絶対わかんない\nよーなやつ♡", 130201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8613, 1, 0, 1, 0, "お兄さんの部屋って\nどこどこぉ？\nメカの実験室に\n使えそうだよね～♡", 130201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8614, 4, 0, 1, 0, "次のデートコースは\nどんなのかなー？\n…いや、むしろ次は\nぼくが決めちゃうっ？", 130201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8615, 8, 0, 1, 0, "強奪計画は念入りに♡\nお兄さんのハートも\n全力でザコザコにする\n計画、立てないとね♡", 130201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8616, 1, 0, 1, 0, "ねー、ヒマヒマヒマ～\nかまってかまってー♡\nどっか連れてけー♡", 130201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8617, 1, 0, 1, 0, "今日はこっちで\nご飯食べたいなー♡\n…帰っても５日連続の\nバカナ姉カレーだし…", 130201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8618, 1, 0, 1, 0, "依頼行くならぼくも\n連れてってよー♡\nお兄さんのザコっぷり\n見ててあげる～♡", 130201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8619, 1, 0, 1, 0, "お兄さんはぼくより\nオトナだもんね～♡\nじゃあオモテナシ、\n期待してるから～♡", 130201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8620, 4, 0, 1, 0, "ヤマ姉になんかお土産\n持って帰ろうかなー？\nザコお兄さん、選ぶの\n付きあって～♡", 130201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8621, 8, 0, 1, 0, "遊びにいくときは\nザコお兄さんも\n誘ってあげるよ♡\nどうせヒマでしょ～？", 130201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8622, 1, 1, 1, 0, "ふぁ～、ねむぅ～…\n夜更かしぶん寝るから\nあとで起こして～♡\nあ、変なこと禁止～♡", 130201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8623, 1, 2, 1, 0, "おなかすいたぁ～\nなんか食べ行こ♡\n甘いのおごって～♡", 130201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8624, 1, 3, 1, 0, "えー、もう寝る時間？\nじゃあ…添い寝して\nあげよっか？\nなーんて、ウッソー♡", 130201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8626, 1, 0, 1, 0, "おお～ふわっふわ～♪\nバンディシャーク号の\n座席に使いたーい！", 130201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8627, 1, 0, 1, 0, "カワイイ～♡\n改造したらもっと\nカワイくなるかな～？", 130201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8628, 1, 0, 1, 0, "くんくん…いい匂い♡\nザコお兄さんって\nこーゆー香りが\n好きなのかなぁ～", 130201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8629, 1, 0, 1, 0, "はー、あったかーい♡\nバンディシャーク号\nにも欲しいかもー♪", 130201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8631, 1, 0, 1, 0, "食べれるときに\n食べるのが大事って\nヤマ姉言ってたしね～", 130201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8632, 1, 0, 1, 0, "あ、この本読んだよ♡\n参考文献リストが\nしっかりしてて\n勉強になったなー♪", 130201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8634, 1, 0, 1, 0, "部屋は多少ゴチャつい\nてる方が落ち着くのさ\nバンディシャーク号\nみたいにね♪", 130001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8635, 1, 0, 1, 0, "呆けてるのもいいが\nちいと物足りないねぇ\n麦しゅわもブドウ朱も\n無いなんてさ", 130001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8636, 1, 0, 1, 0, "この家セキュリティが\nなってないねぇ…\n盗賊目線でちょっくら\n指南でもしてやるか", 130001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8637, 1, 0, 1, 0, "不思議だねぇ\nここで飲むと、酔いが\n回るのが早い気が\nするよ…ヒック", 130001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8638, 1, 0, 1, 0, "ふむ…\nクルマが壊れた時は\nアジト代わりに\n使えそうだ♪", 130001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8639, 4, 0, 1, 0, "カカッ、いい部屋だ！\n妹を迎える準備は\nできてるみたいだねぇ", 130001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8640, 8, 0, 1, 0, "…っと、油断したら\n落ち着いちまうね\nボーヤの甘っちょろい\n雰囲気のせいだよ", 130001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8641, 1, 0, 1, 0, "さすがにこの家から\n盗みはしないさ\nいずれは妹たちの\n共有財産になるからね", 130001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8642, 1, 0, 1, 0, "ボーヤ、暇なら\n博打でもやるかい？\nアタシが負けたら\n掃除でもしてやるよ", 130001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8643, 1, 0, 1, 0, "立派な家じゃないか！\n一国一城の主とは\nちょっとボーヤのこと\n見くびってたかもねぇ", 130001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8644, 1, 0, 1, 0, "かわいい義弟のためだ\nアタシが盗ってきた\nお宝をくれてやるよ\n好きな場所に飾りな！", 130001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8645, 4, 0, 1, 0, "妹と結ばれる前に\n他の女との関係は\n清算しとくように\nわかったね、ボーヤ？", 130001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8646, 8, 0, 1, 0, "お、ヤシオーリかい？\nフフン、義弟からの\nお酌とあっちゃあ\n断れないねぇ♪", 130001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8647, 1, 1, 1, 0, "うう…清々しい朝だが\n頭がいてぇ…\nちっと昨日は\n飲みすぎたか…？", 130001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8648, 1, 2, 1, 0, "ほらこれ、ワカナに\n作らせた弁当だ\nこれで昼からも\nバリバリやりな！", 130001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8649, 1, 3, 1, 0, "まだまだ夜はこれから\n…と言いたいとこだが\n早寝して立派に育ちな\nそれが妹たちの為だよ", 130001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8651, 1, 0, 1, 0, "中々いい座り心地だが\n盗賊が腑抜ける訳には\nいかないね…が…\nもう少しだけ座るか…", 130001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8652, 1, 0, 1, 0, "こういうのの中に\n実はレアなブツが\n隠されてるとか\nありがちだねぇ", 130001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8653, 1, 0, 1, 0, "ふうん、ボーヤは\nこういう匂いが\n好きなのかい？\n妹たちに教えてやろう", 130001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8654, 1, 0, 1, 0, "体を温めんにゃ\n酵露をカッとやるのが\n一番だが、これも存外\n悪くないねぇ…♪", 130001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8656, 1, 0, 1, 0, "ガツガツ…\nん、悪くねェ味だ\nこれにヤシオーリでも\nありゃ文句はないね", 130001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8657, 1, 0, 1, 0, "本にも載ってない\nレアもんのお宝か…\nフッ、想像しただけで\n血沸き肉躍るねぇ…！", 130001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8659, 1, 0, 1, 0, "今日の買い物は\nいつものお野菜と…\nあっ、確か特売が\nあったはず…！", 130101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8660, 1, 0, 1, 0, "お掃除、お掃除…♪\nきゅっきゅっ…\nよいしょ、っと…", 130101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8661, 1, 0, 1, 0, "洗濯ものも\nたまらないうちに\nやっちゃわないと…", 130101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8662, 1, 0, 1, 0, "夕飯のメニュー…\n何にしよう？\n期限の早い食材は…", 130101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8663, 1, 0, 1, 0, "色々お菓子も\n作ってみたいけど…\n予算…足りるかなぁ…", 130101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8664, 4, 0, 1, 0, "騎士さん…どんな\n料理が好きなんだろ？\n前に作ったコロッケは\n評判よかったけど…", 130101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8665, 8, 0, 1, 0, "騎士さんと\nおしゃべりすると、\n胸があたたかくなる…\nなんでだろ…？", 130101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8666, 1, 0, 1, 0, "あっ、騎士さん\nどうかしましたか？\n御用ならいつでも…♪", 130101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8667, 1, 0, 1, 0, "削っても削っても\n家計簿は赤字…\n騎士さ～ん…\n助けてください～！", 130101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8668, 1, 0, 1, 0, "手が空いてたら\n日用品の買い出し、\n手伝ってもらっても\nいいですか…？", 130101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8669, 1, 0, 1, 0, "やっと一息つけそう…\nあっ、わたし\nお茶を淹れてきますね", 130101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8670, 4, 0, 1, 0, "困りごとがあったら\n言ってくださいね\nわたし…騎士さんの\n力になりたいです", 130101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8671, 8, 0, 1, 0, "助けてもらった分は\n必ず返しますから…\nそれまでわたしのこと\n見ててくださいね…♪", 130101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8672, 1, 1, 1, 0, "忘れ物はないですか？\n今日のお仕事も\n頑張りましょうね…！", 130101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8673, 1, 2, 1, 0, "お昼ご飯できてますよ\n大したものじゃなくて\n恐縮ですけど…", 130101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8674, 1, 3, 1, 0, "夜更かしはだめですよ\nわたしも家事を\n終わらせたら寝ないと", 130101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8676, 1, 0, 1, 0, "これ…割れ物防止の\nクッションに\n使えるかも…？", 130101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8677, 1, 0, 1, 0, "こういうのいいなぁ…\nけど…フブキちゃんに\n絶対改造されちゃう…", 130101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8678, 1, 0, 1, 0, "いい匂い…でも\n切れかけてるかも？\nえっと補充するには…", 130101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8679, 1, 0, 1, 0, "あったかい…\nちょっとだけなら\n休んでも…いいよね", 130101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8681, 1, 0, 1, 0, "これがあったら\nもうお料理を\n作らなくて済むかも？", 130101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8682, 1, 0, 1, 0, "新しい節約レシピは…\nあ、この草って\n食べられるんだ…", 130101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8684, 1, 0, 1, 0, "愛溢れるランドソルの\nクリスマス…ですか\nいけませんね、つい\n胸が躍ってしまいます", 132801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8685, 1, 0, 1, 0, "ツリーの飾りと\nわたしの髪…\nくるくるしていて\n似ているかも…くすっ", 132801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8686, 1, 0, 1, 0, "この姿で傲慢な\nわたしになったら…\nいつもより怖がらせて\nしまうでしょうね…", 132801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8687, 1, 0, 1, 0, "悪魔として、人を\nドキドキさせなくては\nい、イオから教わった\n…投げキスの練習を…", 132801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8688, 1, 0, 1, 0, "ジオ・テオゴニアの\nお菓子は、ツリーの\n飾りに向いていそう\nですね…ふふっ", 132801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8689, 4, 0, 1, 0, "騎士のギルドハウス…\nクリスマスの時期も\n暖かいのですね…", 132801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8690, 8, 0, 1, 0, "城の皆と過ごす宴も\n楽しいですが…\n騎士と二人の宴も\n幸せです…", 132801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8691, 1, 0, 1, 0, "騎士、あなたの\nもてなしに感謝します\nクリスマスの何たるか\n教えてくださいね", 132801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8692, 1, 0, 1, 0, "この黒い羽は\nどう手入れすれば？\nイオなら知っている\nでしょうか…", 132801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8693, 1, 0, 1, 0, "城の皆も連れてきたい\nほど、クリスマスは\n楽しい行事ですね\nお土産も迷います", 132801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8694, 1, 0, 1, 0, "クリスマスの晩餐に\n招待されたのですが…\nその…辛いものは\nありませんよね…？", 132801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8695, 4, 0, 1, 0, "クリスマスと言わず…\nいつでも星に祈って\nいますよ？　騎士達に\n幸せがあるように、と", 132801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8696, 8, 0, 1, 0, "悪魔として騎士を\nドキドキさせる…？\nそ、そのようなこと\nわたしには…うぅ…", 132801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8697, 1, 1, 1, 0, "朝は寒いですね…\n雪…積もっている\nでしょうか？", 132801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8698, 1, 2, 1, 0, "ふあぁ…いけません\n公務の滞在先で\nお昼寝など…", 132801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8699, 1, 3, 1, 0, "夜は悪魔として\n夢を見せましょう\n騎士はどんな夢が\n見たいですか？", 132801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8701, 1, 0, 1, 0, "ふぅ…落ち着きます…\n家でゆっくりと過ごす\nクリスマスも\n良いものですね", 132801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8702, 1, 0, 1, 0, "リンドのプレゼントに\nぬいぐるみは…？\n…子ども扱いすると\n落ち込むでしょうか…", 132801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8703, 1, 0, 1, 0, "暖かいお部屋に\nこの香りを漂わせたら\nふふっ…素敵な時間を\n過ごせそうです", 132801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8704, 1, 0, 1, 0, "これがあれば、\n寒い冬も安心ですね\n暖かくて…このまま\n眠ってしまいそうです", 132801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8706, 1, 0, 1, 0, "悪魔の力を使ったら\nお腹が空いてきて\nしまって…\n恥ずかしいです…", 132801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8707, 1, 0, 1, 0, "クリスマスには多くの\n伝承があるのですね…\n天使の伝承まで\nありましたし…", 132801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8709, 1, 0, 1, 0, "まぁ…！\nよくできていますね\nサプライズプレゼント\n…でしょうか。ふふ♪", 132803, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8710, 1, 0, 1, 0, "ど、どうでしょう…？\n親善交流の催しに\nなりそうでしょうか…", 132804, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8711, 1, 0, 1, 0, "ギルドハウスって\nあったかぁ～い♪\n心がぽかぽかするの！", 132901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8712, 1, 0, 1, 0, "クリスマスの悪魔の\nお仕事はないわね！\nだってここのみんな\nいい子なんですもの♪", 132901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8713, 1, 0, 1, 0, "みんなちゃんと\n寝られてるかしら？\n夜更かしせずに、\nいい夢見てね♪", 132901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8714, 1, 0, 1, 0, "毎回、来年が\n楽しみになるような\n素敵なクリスマスを\n過ごしたいわね♪", 132901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8715, 1, 0, 1, 0, "訳ありケーキを見ると\n刑務作業を思い出すわ\n…あれも貴重な体験\nだったのかしら…？", 132901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8716, 4, 0, 1, 0, "ロマンチックな\nクリスマスに備えて\n今のうちに少女漫画で\n予習しておかないと！", 132901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8717, 8, 0, 1, 0, "ほっぺにチューなら\nもう一回しても…\nや、やっぱり無理！\n恥ずかしいわ～！", 132901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8718, 1, 0, 1, 0, "またみんなで\nセッションしたいわね\nカラーベルの練習した\n成果を見せたいの♪", 132901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8719, 1, 0, 1, 0, "君はサンタさんに\nどんなプレゼントを\n頼んでいたのかしら？\n気になるわ～！", 132901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8720, 1, 0, 1, 0, "愛が壊れても、\n何度でも治してあげる\n私たちの間に、\n愛の尽きる日はないわ", 132901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8721, 1, 0, 1, 0, "冬休みって短いようで\n長く感じちゃうわよね\n学校のみんなと\n早く会いたいな～♪", 132901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8722, 4, 0, 1, 0, "愛をテーマにした\n冬休みの作文の宿題、\n君の分を読むのが\n今から楽しみね♪", 132901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8723, 8, 0, 1, 0, "よかったらまた\n私をハニーって呼んで\nほしいの。ダーリン♪", 132901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8724, 1, 1, 1, 0, "おはよう！\n冬の朝は冷えるから\n風邪を引かないように\n対策しましょうね", 132901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8725, 1, 2, 1, 0, "お昼ご飯は一緒に\n食べに行きましょう！\nクリスマス限定の\nランチコースよ♪", 132901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8726, 1, 3, 1, 0, "ねえねえ、キャンドル\nイルミネーションを\n見に行きましょうよ！\nロマンチックよ～♪", 132901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8728, 1, 0, 1, 0, "暖かいクリスマスを\n過ごすのに必須よね♪\nふふ、柔らかぁい！", 132901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8729, 1, 0, 1, 0, "どの子もかわいい～♪\nクリスマスツリーの\n下で、一緒にお祝い\nしてもらいたいな♪", 132901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8730, 1, 0, 1, 0, "なんだか胸の奥が\n温かくなる匂いね～\nリラックスしちゃう♪", 132901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8731, 1, 0, 1, 0, "あったかぁい♪\nあ、でも夜になったら\nサンタさんが入れる\nように、火を消すのよ", 132901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8733, 1, 0, 1, 0, "寒い冬だからこそ\nしっかり食べて\n備えておかなきゃ！", 132901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8734, 1, 0, 1, 0, "『聖夜の６時間』…？\n気になる小説だけど…\n勝手に読んじゃ悪い\nわよね…ガマンよ…！", 132901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8736, 1, 0, 1, 0, "きゃっ…！\nこんな不思議な本が\nあったのね～\nちょっと面白いかも♪", 132901, 14, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8737, 1, 0, 1, 0, "もふもふ、ふわふわで\nあったかくて\n気持ちいい～…♪", 132901, 15, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8738, 1, 0, 1, 0, "冬といえばサマー！\n…って言うけど夏は\n何をＰＲするんだろ\nメルクリゾート…", 129501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8739, 1, 0, 1, 0, "奨学金の道は遠いな…\nあまりに遠すぎて\n最近忘れてたまである", 129501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8740, 1, 0, 1, 0, "暇だし鏡でも見よっと\nはー、今日も顔が良…\nげ、若干むくんでる？\nはー、今日終わった", 129501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8741, 1, 0, 1, 0, "水着…お姉ちゃんと\n貸し借りするんだけど\nこれって少数派姉妹？", 129501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8742, 1, 0, 1, 0, "ちゅむ、ツムギちゃん\n今日も可愛いねデュフ\n…なんで推し語りの声\nキモくなるんだろ私…", 129501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8743, 4, 0, 1, 0, "あれ…なんで私\n水着のまま部屋の中\nうろついてるんだっけ\n…こわ！　てか寒っ！", 129501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8744, 8, 0, 1, 0, "先輩よく友達と海に\n行くって言ってたけど\n…え、誰とだろ？\nまあ誰でもいいけどさ", 129501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8745, 1, 0, 1, 0, "始まる…\nチエルの夏が始まる…\nあ冬でしたっけ？\nまどっちでもいいです", 129501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8746, 1, 0, 1, 0, "こないだトミヱ先輩\n壁に喧嘩売ってました\n相変わらず元気ですね", 129501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8747, 1, 0, 1, 0, "ユニ先輩にお芝居の\n出来を聞かれたんで\n可愛いって答えました\n嘘は言ってません", 129501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8748, 1, 0, 1, 0, "クロエ先輩の腹筋\nかっちょいーですよね\nさりげ触りたいけど\nあいつ隙ないんですよ", 129501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8749, 4, 0, 1, 0, "先輩もうカメラ係は\nやらないんです？\nチエルはやってますよ\n最高の被写体を常に", 129501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8750, 8, 0, 1, 0, "メリゾでカップル割の\n企画あるらしいんです\nちなみに先輩は…\nあ、いえ、何でも…", 129501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8751, 1, 1, 1, 0, "朝ごはんは抜いても\n朝ちぇるだけは\n抜いちゃ駄目ですよ", 129501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8752, 1, 2, 1, 0, "お昼なに食べるか\n悩んでます？\nおごってくれるなら\nいい店教えますけど", 129501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8753, 1, 3, 1, 0, "この時間帯になると\nユニ先輩がごそごそ\n蠢きだすんですよね…", 129501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8755, 1, 0, 1, 0, "世界の美少女が…\nこんな格好で\nくつろいでたら…\nまずい…事案になる", 129501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8756, 1, 0, 1, 0, "え、この子って\nウォータープルーフ？\nメリゾにも連れて\nいけちゃうカンジ？", 129501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8757, 1, 0, 1, 0, "はぁ～、いい香り…\nそういえばメリゾの\nスタッフルームも\n『贅』な匂いしてた…", 129501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8758, 1, 0, 1, 0, "薄着で暖まるとか…\n冷静になっちゃ\n駄目なやつですよね\nこれ…", 129501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8760, 1, 0, 1, 0, "おにぎり１個は\nちぇるに換算すると…\n２５ちぇるくらい？", 129501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8761, 1, 0, 1, 0, "あれー？　この辞書\nまだちぇるの項目\n載ってないんだ？", 129501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8763, 1, 0, 1, 0, "何これ…地味に楽しい\nあとたぶん\nクロエ先輩が好きそう", 129501, 10, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8764, 1, 0, 1, 0, "これは良きですねー\n自宅でメリゾ気分とか\nユニ先輩もにっこり", 129501, 11, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8765, 1, 0, 1, 0, "ここが騎士氏の\nお住まいですか\nふむ…地縛霊は\nいなさそうですね", 133001, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8766, 1, 0, 1, 0, "ここは土足厳禁では\nないみたいですね\nま、私には関係ないか\n足、透っけ透けなので", 133001, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8767, 1, 0, 1, 0, "ランドソルの裏路地\nあそこは最高ですね\n暗くてじめじめしてて\n癒されるー", 133001, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8768, 1, 0, 1, 0, "っ！\n今ラップ音が…？\nまさか幽霊？\nやだ、怖い", 133001, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8769, 1, 0, 1, 0, "獣人族にエルフ族…\nなるほど騎士氏が\n幽霊程度では驚かない\n訳です…ちぇ", 133001, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8770, 4, 0, 1, 0, "今日の深夜\n騎士氏が起きるまで\n枕元でずーっと\n立っててみようかな", 133001, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8771, 8, 0, 1, 0, "騎士氏の驚く顔を\n見ると胸が高鳴る…\n変ですね、心臓なんて\n動いてないのに", 133001, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8772, 1, 0, 1, 0, "安心して下さい\n勝手にすり抜けて\n騎士氏の部屋は\n覗きませんよ…多分", 133001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8773, 1, 0, 1, 0, "なんですか騎士氏\n怖い話でも聞きます？\nじゃあいきますね…\n今貴方の前に幽霊がっ", 133001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8774, 1, 0, 1, 0, "この家…幽霊が\n寄ってくる間取りに\nなってますね\nまあ、嘘ですけど", 133001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8775, 1, 0, 1, 0, "料理はしませんよ\n指きったら痛いので\nえ…透ければいい？\n……天才ですか？", 133001, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8776, 4, 0, 1, 0, "お散歩ですか？\n深夜なら付き合います\nあ、やっぱり断ります\n幽霊出たら怖いので", 133001, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8777, 8, 0, 1, 0, "騎士氏の部屋のセンス\n好きですよ…もっと\nじめじめしていれば\nなおよし、ですが", 133001, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8778, 1, 1, 1, 0, "レイスになってから\nあまり言わなくなった\n挨拶第一位はこちら…\nおはようございます", 133001, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8779, 1, 2, 1, 0, "騎士氏。午後からも\n頑張って下さいね\n私は日陰で休みます\nでは、おつです", 133001, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8780, 1, 3, 1, 0, "夜も更けてきましたね\nここからがレイスの\n本領発揮…では早速\nう　ら　め　し　や～", 133001, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8782, 1, 0, 1, 0, "中々座り心地が\nよろしいですね\nブロッケンに跨る方が\n安心はしますが", 133001, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8783, 1, 0, 1, 0, "知ってますよ\nどうせ貴方も\n夜中になったら\n動き回るんでしょ？", 133001, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8784, 1, 0, 1, 0, "すう…はあ…\nすごく落ち着く香り\n…落ち着き過ぎて\n成仏しそうでした", 133001, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8785, 1, 0, 1, 0, "温度は感じませんが\nなぜかほっこり\n温かい気持ちです\nなんででしょうね？", 133001, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8787, 1, 0, 1, 0, "もぐもぐ…\n食べ物は基本好きです\n消化の方法は…\n企業秘密で", 133001, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8788, 1, 0, 1, 0, "苦手なんですよね\nホラー小説\nだって幽霊とか\n怖いじゃないですか", 133001, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8790, 1, 0, 1, 0, "この得も言われぬ\n高揚感…やはり\n正月はいいものだな！", 133201, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8791, 1, 0, 1, 0, "む、いかんな\n気付くと黒豆と\n栗きんとんばかり\n食べてしまう…", 133201, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8792, 1, 0, 1, 0, "ギルドの連中にも\nお年玉を渡してみるか\n少しは士気が上がる\nかもしれないな！", 133201, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8793, 1, 0, 1, 0, "正月くらいはのんびり\n羽を伸ばすとしよう\n適切な休暇も軍人の\n大事な責務だからな", 133201, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8794, 1, 0, 1, 0, "正月で浮かれすぎた\n者も多いはずだ\n念のため街を見回りに\n行くとしよう", 133201, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8795, 4, 0, 1, 0, "む、このおせち料理は\n絶品だな！\nあいつの分も\nとっておいてやろう", 133201, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8796, 8, 0, 1, 0, "今年の抱負は…\nそうだな、あいつと\nさらに高みを目指す\nこれでいこう", 133201, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8797, 1, 0, 1, 0, "えっ、お年玉！\n…い、いや、私は\n子どもではないからな\nえ、遠慮させてもらう", 133201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8798, 1, 0, 1, 0, "凧あげすごろく\nこま回し、たとえ遊び\nでも手は抜かんぞ\nさあ、勝負だ！", 133201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8799, 1, 0, 1, 0, "そのきな粉餅も\nうまそうだな…\n私のあんこと交換\nしないか？", 133201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8800, 1, 0, 1, 0, "貴公、正月料理を\n食べ過ぎてないか？\nじ、実は私も少々、な", 133201, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8801, 4, 0, 1, 0, "貴公、お参りに行くぞ\n何？　外が寒いだと？\n仕方ない、手ぐらいは\nつないでやろう", 133201, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8802, 8, 0, 1, 0, "貴公と新たな年を\n迎えられてうれしいぞ\n今年も一つ\nよろしくたのむ！", 133201, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8803, 1, 1, 1, 0, "起きろ貴公。早朝なら\n参拝客も少ないはず\n今のうちにお参りを\n済ませよう", 133201, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8804, 1, 2, 1, 0, "昼食に餅を焼いたぞ\nのどに詰まらせない\nようにゆっくり噛んで\n食べるんだ", 133201, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8805, 1, 3, 1, 0, "夜ふかしはあまり\n褒められたものでは\nないが…正月くらいは\n目をつむろう", 133201, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8807, 1, 0, 1, 0, "これに座って飲む\n甘酒は格別だな", 133201, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8808, 1, 0, 1, 0, "せっかくの正月だし\nこいつにも着物を\n着せてやりたいな", 133201, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8809, 1, 0, 1, 0, "これは梅の香り…？\nふふ、なかなか\n風流ではないか", 133201, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8810, 1, 0, 1, 0, "冬の寒さが堪えるな…\nここでしっかりと\n暖をとるとしよう", 133201, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8812, 1, 0, 1, 0, "正月はのんびり\nしたいからな、勝手に\n食事が出てくるのは\n本当に助かる", 133201, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8813, 1, 0, 1, 0, "ふむ、これが世界の\n新年のお祝いか\nなかなか興味深いな", 133201, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8815, 1, 0, 1, 0, "一年の計は元旦にあり\n計画を立てて、\n有意義な一年を\n過ごしましょ", 133501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8816, 1, 0, 1, 0, "年末年始はどうしても\n出費が増えちゃうし\nしばらくはきっちり\n節約しなきゃ…！", 133501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8817, 1, 0, 1, 0, "タタミにフトン、\nトーゴク文化の内装…\nランドソルでももっと\n流行らないかしら", 133501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8818, 1, 0, 1, 0, "子どもたちが凧を\n手作りして遊んでたの\n偉いわよね、みんな…", 133501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8819, 1, 0, 1, 0, "お年玉をあげる側に\nなって、分かる苦労…\n大人になるって\nこういうことかしら", 133501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8820, 4, 0, 1, 0, "一応実家にも\n顔を出さないと…\nよかったらだけど、\nあんたも来る？", 133501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8821, 8, 0, 1, 0, "毎日一緒にいて、\nささやかな幸せを\n同じように感じて…\nそれだけで十分ね", 133501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8822, 1, 0, 1, 0, "着物姿、もっと\nじっくり見たいの？\nさすがに照れるわよ、\nもう…", 133501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8823, 1, 0, 1, 0, "整理整頓、できてる？\nもし散らかってるなら\nお正月休みのうちに\n片づけちゃいましょ", 133501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8824, 1, 0, 1, 0, "今年もみんなが無事に\n過ごせますように…\nほら、一緒に\nお祈りしましょ", 133501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8825, 1, 0, 1, 0, "みんな、あんたと\n遊ぶのが楽しみだって\n子どもに好かれる\nタイプなのよね", 133501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8826, 4, 0, 1, 0, "あんたの運勢は大吉！\nラッキーパーソンは\n金髪の幼なじみよ\n…なんてねっ", 133501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8827, 8, 0, 1, 0, "初夢は実現するのよね\nそれじゃ、あたしは\nあんたと…んんっ\nなんでもないわ", 133501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8828, 1, 1, 1, 0, "外が寒いと、その分\n目も冴えるわよね\nこのまま朝の散歩に\n行こうかしら", 133501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8829, 1, 2, 1, 0, "お正月休みでも、\n食べてばっかじゃダメ\nしっかり動いて\n体を引き締めましょ", 133501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8830, 1, 3, 1, 0, "ちゃんとあったかく\nして寝るのよ\nお正月から\n風邪ひかないようにね", 133501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8832, 1, 0, 1, 0, "着物、しわに\nなっちゃいそう…\nでもそれ以上に、\n心地よくって…はぁ", 133501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8833, 1, 0, 1, 0, "門松みたいに\nこの子も飾ってみる？\n運勢がよくなるかも♪", 133501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8834, 1, 0, 1, 0, "この匂い、もしかして\nトーゴクのお香？\nワビサビを感じるわ", 133501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8835, 1, 0, 1, 0, "ふぅ、あったかい…\n冷えた体に\n染み渡ってくるわ…", 133501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8837, 1, 0, 1, 0, "んっ、おいしい！\nおせちばっかじゃ\nやっぱり飽きちゃう\nものね…", 133501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8838, 1, 0, 1, 0, "験担ぎとか縁起物とか\n調べてみると\nいろいろあって\n面白いのよね～", 133501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8840, 1, 0, 1, 0, "ふーん、いい家ね\nアタシの巣に\nしちゃおうかしら？", 133601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8841, 1, 0, 1, 0, "お姉ちゃん…\n早く会いたいなぁ…", 133601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8842, 1, 0, 1, 0, "メドゥシアナも\n気に入るかしら？\n今度連れてこよっと！", 133601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8843, 1, 0, 1, 0, "お邪魔してるだけ\nなのも悪いわよね…\nお返しはしないとね\n…ネズミ退治とか？", 133601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8844, 1, 0, 1, 0, "何か平和よね\nランドソルって…\nアタシ的にはもう少し\n荒っぽくてもいいわ", 133601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8845, 4, 0, 1, 0, "ランドソルも\n楽しい街よね…\n案内してくれる\nアイツのおかげ？", 133601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8846, 8, 0, 1, 0, "アタシ、アイツに\n頼りすぎてるかも…？\nで、でも！　断らない\nアイツも問題だわ！", 133601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8847, 1, 0, 1, 0, "ねぇ、つまんなーい！\nもっとアタシを\n楽しませなさいよねー", 133601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8848, 1, 0, 1, 0, "この家悪くないけど…\nもっとムシムシ\nジメジメにできない？", 133601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8849, 1, 0, 1, 0, "何よ？\n用もないのに声掛けた\n…とか言ったら\n容赦しないわよ？", 133601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8850, 1, 0, 1, 0, "少し部屋が寂しいわね\nそうだ、アンタを\n石にして家具に…\n冗談よ、ふふんっ♪", 133601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8851, 4, 0, 1, 0, "ねぇ、もう少し\n話していかない？\nアンタの話なら…\n特別に聞いてあげるわ", 133601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8852, 8, 0, 1, 0, "い、いつも\nあ、あ、ありが…\nうぅ、何でもないー！", 133601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8853, 1, 1, 1, 0, "やっと起きたわね？\n寝ぼすけなんだから！\n尻尾でひっぱたいて\n起こそうかと思ったわ", 133601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8854, 1, 2, 1, 0, "人捜しは日の高い\nうちに限るわ！\nさぁ、メドゥシアナ！\n全速力で出発進行よ!", 133601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8855, 1, 3, 1, 0, "アタシ…\n子どもじゃないもん…\n眠くなんか…\nないんだからぁ…", 133601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8857, 1, 0, 1, 0, "ふわふわであったかい\nつい、とぐろを\n巻きたくなるわね…", 133601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8858, 1, 0, 1, 0, "アンタ、こんな\nヤワな体じゃ駄目よ\nウロコくらい\n生やしたらどうなの？", 133601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8859, 1, 0, 1, 0, "くんくん…\n…ふん\n悪くないじゃない\n…くんくんくんくん…", 133601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8860, 1, 0, 1, 0, "あったかいの大好き！\nジオ・ゲヘナでも\nよく日向ぼっこ\nしたもんだわ…ふわぁ", 133601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8862, 1, 0, 1, 0, "お姉ちゃんを捜すなら\n…もぐ\n体力つけないとね\n…もぐもぐ", 133601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8863, 1, 0, 1, 0, "何よ？　たまには\n勉強くらいするわよ\nお姉ちゃんに\n笑われちゃうもの", 133601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8865, 1, 0, 1, 0, "この装いと力…\n今の世界との\n整合性を上手いこと\n合わせておかないとだ", 133701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8866, 1, 0, 1, 0, "この格好…開発してた\n当時のまんまだ\nサイズもそのまま♪\n体型維持も完璧っ！", 133701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8867, 1, 0, 1, 0, "あとで装備の手入れも\nやっとかないと\n…剣なら研ぐだけで\n終わるんだけどなぁ", 133701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8868, 1, 0, 1, 0, "自分の原点を\n振り返ってみるのも\nたまには必要だね", 133701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8869, 1, 0, 1, 0, "完璧じゃなくても\n今できる全力を尽くす\nその事に意味がある…\nそうだよね、少年", 133701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8870, 4, 0, 1, 0, "始まりのあの日、\nそして向かうべき道…\nいつも忘れずにいよう", 133701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8871, 8, 0, 1, 0, "誰かを救うために\n許容される犠牲なんて\nあっちゃいけない…\n全部、助けてやる", 133701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8872, 1, 0, 1, 0, "おっと少年、\n何か御用かな？\nキミに関わる事なら\n最優先で対応するよ", 133701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8873, 1, 0, 1, 0, "困った事があれば\n頼ってもらって\n構わないからね！\nむしろもっと頼って！", 133701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8874, 1, 0, 1, 0, "そうだ、時間あるなら\n武器のメンテを\n手伝ってくれないかな\n数が多くてさ～", 133701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8875, 1, 0, 1, 0, "遠距離戦の心得、\n少年にもレクチャー\nしてあげよっか？", 133701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8876, 4, 0, 1, 0, "無茶しないで、って\n言ってもするだろうし\nせめてアタシの手が\n届く範囲で頼むよ", 133701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8877, 8, 0, 1, 0, "虚実入り混じる\nこの世界でキミが…\n進むべきルートを\n照らしてくれたんだ", 133701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8878, 1, 1, 1, 0, "グッモーニン！\n朝だよ少年、今日も\n張り切っていこう！", 133701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8879, 1, 2, 1, 0, "クレープの代わりに\nガレットを焼いたんだ\nこの味で少年を\n唸らせてみせよう！", 133701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8880, 1, 3, 1, 0, "もうこんな時間だ\n遅くならないうちに\n作業を切り上げようか\n少年も早く寝なよ？", 133701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8882, 1, 0, 1, 0, "あえて緊張を解くのも\n戦いには必要だし…\nそうそう…大事大事…", 133701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8883, 1, 0, 1, 0, "愛着を抱かせる事を\n意図したデザインだね\n心理的効果の観点から\n分析してみようか…", 133701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8884, 1, 0, 1, 0, "香りは記憶と密接に\n結びつく事がある…\n懐かしい匂いだね…", 133701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8885, 1, 0, 1, 0, "癒されるな…\n何がこの感情変化を\n引き起こすんだろう？", 133701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8887, 1, 0, 1, 0, "食の偉大さを感じるな\n戦闘糧食ってやっぱり\n味気ないしなぁ…", 133701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8888, 1, 0, 1, 0, "実用ばかりじゃなく\n詩や物語ってのも\n人には必要だよね", 133701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8890, 1, 0, 1, 0, "この場所の開発版も\n探せばあるかもな\nさて、どんなデザイン\nだったのやら…", 133901, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8891, 1, 0, 1, 0, "今まで数々の波乱に\n身を投じたが…やはり\n波乱にはこの姿が一番\n似合いだよ☆", 133901, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8892, 1, 0, 1, 0, "むっ…この角に羽と\n尻尾は家具とぶつかり\nやすいな。兎の時は\n気にならなかったが…", 133901, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8893, 1, 0, 1, 0, "いつもより闘争を\n求める気分になるな\n初心に帰るとは\nこのことか…？", 133901, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8894, 1, 0, 1, 0, "未調整上等☆\nそれを上手く使い\nこなしてこその\nワタシだろう？", 133901, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8895, 4, 0, 1, 0, "坊やとできそうな\n愉しいことは…\nふむ、多すぎて迷うな", 133901, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8896, 8, 0, 1, 0, "時折、坊やの居心地の\nよさに絆されそうに\nなってしまうな\nワタシらしくもない…", 133901, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8897, 1, 0, 1, 0, "どうだ、おろした\n髪型も艶やかだろう？\nそんじょそこらの\n賞賛では満足せんぞ☆", 133901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8898, 1, 0, 1, 0, "外に出るぞ！\n共に退屈を殺しに\n行こうじゃないか☆", 133901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8899, 1, 0, 1, 0, "角と翼も触りたい？\nワタシの虚数魔境に\n挑むというなら\n止めはせんよ☆", 133901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8900, 1, 0, 1, 0, "なんだ、困りごとか？\nクリスティーナさんが\n何でも聞いてやろう☆\n解決するかは別だがな", 133901, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8901, 4, 0, 1, 0, "力が欲しいか？\nならば呼ぶがいい！\nワタシの名を☆", 133901, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8902, 8, 0, 1, 0, "悪魔との契約は絶対\nなのだそうだが…\nどうだ、坊や\n一つ契ってみるか？", 133901, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8903, 1, 1, 1, 0, "まだ眠い？　甘えるな\n寝首を掻かれたく\nなければ飛び起きて\nこい…☆", 133901, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8904, 1, 2, 1, 0, "昼食の準備を\nしてやろう。\n毒見はいらんぞ？\n安心するといい！", 133901, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8905, 1, 3, 1, 0, "坊やは寝る時間だ\n寝ないのならば…\n大人の時間に\n付き合ってもらうか☆", 133901, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8906, 1, 0, 1, 0, "角と翼、尻尾の\nコンディションまで\n完璧だな☆", 133901, 1, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8907, 1, 0, 1, 0, "なかなかのフワモコ\n具合だが…ううむ…\n翼と尻尾が邪魔だな", 133901, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8908, 1, 0, 1, 0, "うーむ、角と尻尾を\n追加して…さらに槍を\n持たせた方が見栄えが\nよさそうだ", 133901, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8909, 1, 0, 1, 0, "いい香りだな\n血と砂塵の臭いで\n上書きするまでの\n間に合わせに最適だ☆", 133901, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8910, 1, 0, 1, 0, "生温いな\nもっと焦がれるほどの\n熱量が欲しいところだ", 133901, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8911, 1, 0, 1, 0, "普段はないものが\n生えているのだから、\n運動でしっかり感覚を\n掴んでおかなければな", 133901, 6, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8912, 1, 0, 1, 0, "腹が減っては戦は…\nと言うからな\nしっかり食べて\nおくとしようか☆", 133901, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8913, 1, 0, 1, 0, "『家庭を築くための\n絶対条件』…？\nふむ…興味がある\nタイトルだ", 133901, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8915, 1, 0, 1, 0, "奇妙な感覚です\nまるであの頃の自分に\n変装しているかのよう", 133801, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8916, 1, 0, 1, 0, "グレゴール？\nおっと、そうでした\n今は昔の姿になって\n離れていましたね", 133801, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8917, 1, 0, 1, 0, "このアバターでいると\n妙に子どもたちからの\n視線を感じますね…", 133801, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8918, 1, 0, 1, 0, "リソースの割り振りが\n変わっただけなのに\n随分と身体が軽く\n感じますね", 133801, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8919, 1, 0, 1, 0, "普段グレゴールに\n任せている分身の管理\n自分でやるには\nいささか煩雑ですね…", 133801, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8920, 4, 0, 1, 0, "長老に頼まれて描いた\n妖精の絵…いったい\n何に使ったのでしょう", 133801, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8921, 8, 0, 1, 0, "私自身をモデルにした\n完全なるNPCたち\nそれが私の分身です", 133801, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8922, 1, 0, 1, 0, "このアバターが\n気になるのですか？\n貴方はつくづく\n妖精に縁がありますね", 133801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8923, 1, 0, 1, 0, "ちょうどよかった\nグレゴールを見かけ\nませんでしたか？", 133801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8924, 1, 0, 1, 0, "あの翅は飾りでは\nありません。ただ…\nグレゴールに乗って\n飛ぶ方が楽なだけです", 133801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8925, 1, 0, 1, 0, "妖精とは本来神話に\nおけるトリックスター\n運命をかき回すことが\n存在意義なのです", 133801, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8926, 4, 0, 1, 0, "変貌大妃の権能との\n相性を考えなければ\nこの姿こそが私たちの\n最強の姿です", 133801, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8927, 8, 0, 1, 0, "変化とは一種の喪失\nだからこそ得られる物\nがある…貴方のそれも\n大切にするのですよ", 133801, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8928, 1, 1, 1, 0, "マサキからの定時連絡\nが来ませんね…心配は\n不要ですが、様子を\n見に行きましょうか", 133801, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8929, 1, 2, 1, 0, "昼食の時間ですね\nグレゴールのために\nカロリーの高い物を\n食べに行きましょう", 133801, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8930, 1, 3, 1, 0, "夜更かしはダメですよ\n悪いスパイが暗躍して\nいる時間帯ですからね", 133801, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8932, 1, 0, 1, 0, "ふむ、座り心地は中々\nですが…私にはやはり\n玉座の方があいますね", 133801, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8933, 1, 0, 1, 0, "ふふ、いいでしょう\n我が配下に加えます\n存分に働きなさい", 133801, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8934, 1, 0, 1, 0, "時折晶からしていた\nあの甘い匂いは何の\nアロマだったのか…", 133801, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8935, 1, 0, 1, 0, "燃え盛る炎もこうして\n暖炉の中なら安全です\nクリスもあの衝動を\n管理さえできれば…", 133801, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8937, 1, 0, 1, 0, "そういえば真那が\n獄中で手持無沙汰に\nひたすらおにぎりを\n食べていましたね…", 133801, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8938, 1, 0, 1, 0, "これは、妖精学の本…\n長老の蔵書から借りて\n読みふけったものです", 133801, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8940, 1, 0, 1, 0, "どんな詩を\n作ろうかしら…？\n…大体、暗く\nなっちゃうけど…", 134301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8941, 1, 0, 1, 0, "唱喚術の練習…\nここでやったら\n迷惑よね…", 134301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8942, 1, 0, 1, 0, "チカちゃんとの\nデュエット…\n足をひっぱってない…\nかしら…？", 134301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8943, 1, 0, 1, 0, "唱喚士の修行…\n滝に打たれるとか…\n崖を登るとか…\nなくて、よかった…", 134301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8944, 1, 0, 1, 0, "精霊さん、肉じゃが\n食べる、かしら…？\n魔力だけだと…\n飽きちゃいそうだし…", 134301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8945, 4, 0, 1, 0, "精霊さんのお世話に\n慣れたぶん…\nあのひとのお世話も\n上手にできる…はず？", 134301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8946, 8, 0, 1, 0, "これからも、歌うわ…\nこの声が、彼の力に\nなる、限り──", 134301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8947, 1, 0, 1, 0, "この服…踊り子さんに\n間違われちゃって…\nあ…お、お触りしちゃ\nだめよ…？", 134301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8948, 1, 0, 1, 0, "あなたはどんな\n精霊が…好き…？\n唱喚…してみたいわ…", 134301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8949, 1, 0, 1, 0, "あんまり白い服って…\n着ないの…その…\nこ、ころんだりして…\n汚すから…", 134301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8950, 1, 0, 1, 0, "じ、実はね…\nこの体の模様…筆で\n書いてもらうたび…\nくすぐったい…の…", 134301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8951, 4, 0, 1, 0, "精霊さん…あなたと\n遊びたいみたい…\nや、やっぱり…\nパパ…だから…？", 134301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8952, 8, 0, 1, 0, "あなたを歌にしたら…\n…ふふっ、だめね\n一曲じゃ…あなたを\n表現できない、わ…", 134301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8953, 1, 1, 1, 0, "朝の修行は…\nうとうと…し…ちゃ…\n……（かくん）\n……あぅっ？", 134301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8954, 1, 2, 1, 0, "せ、精霊さんと…\nかくれんぼ中なの…\nうぅ…見つからない…\n日が暮れちゃいそう…", 134301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8955, 1, 3, 1, 0, "精霊さん、寝たから…\nふふっ…ここからは\nパパとふたりの時間…\n…ね？", 134301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8957, 1, 0, 1, 0, "ふぅ…修行と育児は…\nちょっとだけ…\n休憩…ふぁ…", 134301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8958, 1, 0, 1, 0, "あなたとも契約…\n…できる、かしら？\nふふっ…", 134301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8959, 1, 0, 1, 0, "いい香りに包まれたら\n精神統一も\nはかどりそう…♪", 134301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8960, 1, 0, 1, 0, "あたたかい場所…\nここで作詞したら…\n穏やかな詩が\n書ける、かも…", 134301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8962, 1, 0, 1, 0, "精霊さんのお世話を\nしていると…\nお腹、空いちゃって…", 134301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8963, 1, 0, 1, 0, "ふふっ…♪\n色んな楽譜…\n集めるの、楽しい…♪", 134301, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8965, 12, 0, 1, 0, "けんぞくまだかな？\nはやく会いたいなー\nヴァンピィちゃん\nずっと待ってるからね", 122301, 103, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8966, 12, 0, 1, 0, "けんぞくのこと\nずっとずっと守るよ！\nだからいつまでも\n一緒にいてね", 122301, 103, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8967, 1, 0, 1, 0, "私の歌や想いを\n誰かに託せる…\nこれほどうれしい\nことはありません", 134401, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8968, 1, 0, 1, 0, "コッコロさんの\n包容力はすごいです\nあんなふうに私も\n人を包み込めたら…", 134401, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8969, 1, 0, 1, 0, "最近のボイトレは\nランファさんと一緒に\nやるんです。お互いに\n切磋琢磨しますよ！", 134401, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8970, 1, 0, 1, 0, "先輩として相応しい\n立ち振る舞いを\n心がけたいですね\nしっかりしなきゃ…！", 134401, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8971, 1, 0, 1, 0, "ランファさんが\n踊ったら映えるかも…\nアイドルにも興味を\n持ってもらえないかな", 134401, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8972, 4, 0, 1, 0, "支え導くために\n寄り添って考える…\nあの人からたくさん\n教わったことでしたね", 134401, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8973, 8, 0, 1, 0, "いつだってあなたに\n恥じることのない\n姿をお見せしたいです", 134401, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8974, 1, 0, 1, 0, "あなたの手は温かくて\n触れていると\n落ち着きますね…♪", 134401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8975, 1, 0, 1, 0, "里のみんなから修行の\n差し入れを頂きました\nよろしければ、一緒に\n食べましょう♪", 134401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8976, 1, 0, 1, 0, "ノゾミにもお土産を\n用意したいですね\n里のお土産選び、\n付き合ってください♪", 134401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8977, 1, 0, 1, 0, "精霊たちも、あなたが\n大好きみたいです♪\n優しさが伝わって\nいるんでしょうね", 134401, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8978, 4, 0, 1, 0, "特訓が終わったら、\n二人でどこかに\n出かけましょうか♪", 134401, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8979, 8, 0, 1, 0, "ひゃ…す、すみません\n肌の模様を\nなぞられると、\nくすぐったくて…", 134401, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8980, 1, 1, 1, 0, "おはようございます\n精霊たちと一緒に、\n今日も頑張ります\n応援してくださいね♪", 134401, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8981, 1, 2, 1, 0, "お昼の時間です\n今日も精いっぱい\n修行を頑張りましょう", 134401, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8982, 1, 3, 1, 0, "もうすっかり夜ですね\n眠るまで子守唄を\n歌ってあげますよ？\nふふ♪", 134401, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8983, 1, 0, 1, 0, "無理せず休憩するのも\n大切ですよね\nふかふか～…ふふっ♪", 134401, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8984, 1, 0, 1, 0, "今日はこの子に\n歌の特訓を聞いて\nもらいましょう♪", 134401, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8985, 1, 0, 1, 0, "素敵な香り…\nふふ、精霊たちも\n気に入ったみたいです", 134401, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8986, 1, 0, 1, 0, "あったかい…\nこの衣装、やっぱり\n少し冷えやすいものね", 134401, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8987, 1, 0, 1, 0, "特訓の後のご飯は\n美味しいですね♪\nこれでまた頑張れます", 134401, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8988, 1, 0, 1, 0, "唱喚士についての\n書籍はないみたい\nいつか私がここに\n楽譜を入れたいな", 134401, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8992, 1, 0, 1, 0, "ギルドハウスの平和も\n任せてください\n何が来たって\n守ってみせますよ！", 134601, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8993, 1, 0, 1, 0, "このギルドハウスも\nいつの間にか出撃台に\n改造されてたり\nするんでしょうか…？", 134601, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8994, 1, 0, 1, 0, "戦いを経験してこそ\n実感できますね\n平和はいいもの、\n友達は素晴らしいもの", 134601, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8995, 1, 0, 1, 0, "次はランドソルで\n巨大ロボットが\nブームになったり？\n…さすがにないか", 134601, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8996, 1, 0, 1, 0, "改めて見るとこの服…\nか、体の線とか\n結構目立ってるかも…", 134601, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8997, 4, 0, 1, 0, "団長さんって\n知らない人とも\n話せる系ですよね\nコツはいったい…", 134601, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8998, 8, 0, 1, 0, "ＢＢ団、世界を救う！\nまだ信じられないけど\n本当にあった出来事\nなんですよね…！", 134601, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (8999, 1, 0, 1, 0, "パイロットも\n視力が大事なんです\n弓使いの経験が\n生きました！", 134601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9000, 1, 0, 1, 0, "ギガンティック\nマイフレンドくん、\n出撃！\nあ、ただの妄想です", 134601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9001, 1, 0, 1, 0, "コクピットから\n降りたあとに残る\nふわふわ感…ちょっと\n癖になりませんか？", 134601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9002, 1, 0, 1, 0, "ひえっ、敵襲！？\nあっすみません、\nちょっと\n寝ぼけてたかも…", 134601, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9003, 4, 0, 1, 0, "共に戦場を駆け抜け\nＢＢ団の絆もより強く\nこれからも結束を\n強めましょうね！", 134601, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9004, 8, 0, 1, 0, "同じコクピットに\n乗ったのに比べれば、\nスキンシップくらい…\nや、やっぱ緊張します", 134601, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9005, 1, 1, 1, 0, "おはようございます！\n今日もがんばりますっ\n…クローチェさんを\n見習ってみました…", 134601, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9006, 1, 2, 1, 0, "お昼ご飯の時間ですね\nがんばって他の人\n誘ってみようかな…", 134601, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9007, 1, 3, 1, 0, "夜の見張りは\n任せてください\n一人きりの夜も\n慣れてますから！", 134601, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9009, 1, 0, 1, 0, "休めるときに\n休んでおかなくちゃ\nいつ何が起こるか\n分かりませんからね", 134601, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9010, 1, 0, 1, 0, "師匠もこういうの\n好きだったのかな…\nなんだかしんみり\nしちゃいます", 134601, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9011, 1, 0, 1, 0, "香りでリラックス…\n余裕のない時でも\nこういう安らぎは\n大切ですよね", 134601, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9012, 1, 0, 1, 0, "あったかい…\nこれはきっと友情と\n同じぬくもり…", 134601, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9014, 1, 0, 1, 0, "新鮮なご飯、感動…！\n師匠たちの世界だと\nご飯は保存食ばかり\nでしたからね…", 134601, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9015, 1, 0, 1, 0, "操縦マニュアル、\nしっかり読んで\n性能を引き出さなきゃ", 134601, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9017, 1, 0, 1, 0, "屋内で飛べなくても\nここなら平気ですっ\n助けてくれる人が\nたくさんいますからっ", 134501, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9018, 1, 0, 1, 0, "新ボディになってから\n人や物が前より身近に\n感じられて、わたし、\n幸せですっ♪", 134501, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9019, 1, 0, 1, 0, "配達以外にも何か\nお仕事したいですっ！\nハグで遊覧飛行とか、\n喜んでもらえますか？", 134501, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9020, 1, 0, 1, 0, "むむむ～…\n文字を書くのって\n難しいですね～っ\n練習しなくちゃっ！", 134501, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9021, 1, 0, 1, 0, "簡易点検、よしっ！\nポポおじいさんに\n点検の仕方を教わって\n今日も絶好調ですっ", 134501, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9022, 4, 0, 1, 0, "明日は北の空、\n明後日は南の空っ♪\nえへへっ、見せたい\n景色がいっぱいですっ", 134501, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9023, 8, 0, 1, 0, "ついナイトさんを\n探しちゃいます…\nわたし、寂しんぼさん\nだったんでしょうかっ", 134501, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9024, 1, 0, 1, 0, "どんなところでも\nひとっ飛びっ♪\nわたしに任せて\nくださいねっ！", 134501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9025, 1, 0, 1, 0, "まだ知らない機能が\nエアリアルには\n隠されてるかもっ…\nワクワクしますねっ！", 134501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9026, 1, 0, 1, 0, "ひゃんっ！　…あはは\nエアリアルになっても\nケーブルに触られると\nくすぐったいですねっ", 134501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9027, 1, 0, 1, 0, "ヴォルガーノさんに\nそっくりの子猫さんに\n最近触れたんですっ！\nかわいかったな～っ♪", 134501, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9028, 4, 0, 1, 0, "ハグしてくれるん\nですかっ？\nえへへっ、ぎゅーっ♪", 134501, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9029, 8, 0, 1, 0, "ナイトさんと一緒に\nいるだけで、胸が\nぽかぽか～って\nあったかいですっ♪", 134501, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9030, 1, 1, 1, 0, "おはようございますっ\n目覚まし飛行は\nいかがでしょうかっ？", 134501, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9031, 1, 2, 1, 0, "お昼ですよっ！\nいっぱい補給して\nまたフルパワーで\nがんばりましょうっ♪", 134501, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9032, 1, 3, 1, 0, "眠れない夜はわたしに\n任せてくださいっ！\n充填さえあればずっと\n一緒にいられますよっ", 134501, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9034, 1, 0, 1, 0, "歩きづらいので\nこういう休憩ポイント\nって助かります～っ", 134501, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9035, 1, 0, 1, 0, "スラスターを付けたら\nこの子たちも空を\n自由に飛べたり\nするんでしょうかっ？", 134501, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9036, 1, 0, 1, 0, "あっ、これに似た\n香りを近くのお店で\n検知したことが\nありますっ♪", 134501, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9037, 1, 0, 1, 0, "高熱源反応を検知っ\nわたしのスラスターも\n熱量の高さなら\nかなりものですよっ♪", 134501, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9039, 1, 0, 1, 0, "もぐもぐもぐ…っ\n食べたぶんはあとで\n真空パックにして\nお返ししますねっ", 134501, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9040, 1, 0, 1, 0, "本を読むのって\n楽しいですねっ♪\n前はびりびりーって\nしちゃってたのでっ", 134501, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9042, 1, 0, 1, 0, "なかなかの住処ですね\nこの私の別邸にして\nあげてもいいですよ♪", 134701, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9043, 1, 0, 1, 0, "はぁ…いつになったら\nすべての神力が\n戻るのでしょう？", 134701, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9044, 1, 0, 1, 0, "ここも悪くないですが\n昔私が作った庭園は、\nもっと良い場所でした\n焼けちゃいましたが…", 134701, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9045, 1, 0, 1, 0, "うう…\nおなかが空きました…\nお米が食べたいです…", 134701, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9046, 1, 0, 1, 0, "ここは平和ですねえ…\nジオ・ゲヘナも\nこのくらい穏やかなら\n住みやすいのですが", 134701, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9047, 4, 0, 1, 0, "騎士さんのおかげで\n神力が戻ってきました\n良い働きですよ♪", 134701, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9048, 8, 0, 1, 0, "騎士さんのいる場所が\n今の私がいるべき場所\nなのかもしれませんね", 134701, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9049, 1, 0, 1, 0, "頭がたかーい！\n…ですよっ！", 134701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9050, 1, 0, 1, 0, "ほーちゃんと呼ぶ権利\n安売りはしませんよ？\n特別ですから！", 134701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9051, 1, 0, 1, 0, "私とおしゃべりを？\nまあ、いいでしょう♪\n何を話しましょうか？\nこの羽の秘密とか？", 134701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9052, 1, 0, 1, 0, "小鳥扱いは\nやめなさーい！\nこれでも神様ですよ？", 134701, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9053, 4, 0, 1, 0, "き、気安く触るのは…\n…い、いえ、\n騎士さんなら…まぁ…", 134701, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9054, 8, 0, 1, 0, "絢爛豪華な絆！\nこの私と結べるなんて\n光栄でしょう？", 134701, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9055, 1, 1, 1, 0, "ふわぁぁ～\nおはようごじゃいます\n…んむむ", 134701, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9056, 1, 2, 1, 0, "お昼ご飯は何を…\nえっ？　私？\nそそんな゛あ゛！", 134701, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9057, 1, 3, 1, 0, "あの…騎士さん？\n添い寝する権利とか…\nほしくないですか？", 134701, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9059, 1, 0, 1, 0, "はぁ～ふかふか…\n…へ？\n鳥の巣にいるみたい？", 134701, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9060, 1, 0, 1, 0, "ふむ…\nそばに置いてあげても\nいいでしょう", 134701, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9061, 1, 0, 1, 0, "ほわぁ～\nいいかおり～", 134701, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9062, 1, 0, 1, 0, "あたたかい…\nなんだか元気が\nでてきますねっ", 134701, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9064, 1, 0, 1, 0, "たくさん食べて力を\n取り戻さなくてはっ\n…んがぐぐっ", 134701, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9065, 1, 0, 1, 0, "ふむふむ…\n人の子にしては\nよく書けていますね♪", 134701, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9067, 1, 0, 1, 0, "素敵なお家ですね\n温かみがあって\nどこか騎士様に\n似ております。ふふ", 133101, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9068, 1, 0, 1, 0, "賑やかな街ですね\nこれほど活気豊かだと\nつい外に出てみたく\nなってしまいます", 133101, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9069, 1, 0, 1, 0, "別世界の存在は知って\nおりましたが、いざ\n目の当たりにすると\n驚きの連続ですわ", 133101, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9070, 1, 0, 1, 0, "この街は様々な種族が\nなにはばかることなく\n混在しているのですね\n…我々も、いつかは…", 133101, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9071, 1, 0, 1, 0, "ランドソルにも香水の\nお店があるのですね\nどんな香りが揃って\nいるのでしょうか…♪", 133101, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9072, 4, 0, 1, 0, "昨日、素敵な花園を\n見つけたのです\n今日のお茶会は\nそちらでしましょうか", 133101, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9073, 8, 0, 1, 0, "気を抜いてはいけない\nとわかっていても、\nここだとつい安心して\nしまいます…ふう…", 133101, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9074, 1, 0, 1, 0, "ごきげんよう\n街で見つけた茶葉を\n試しているんです\n騎士様も一杯どうぞ♪", 133101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9075, 1, 0, 1, 0, "親切な方ばかりですね\n素敵なお友だちが\nできそうな予感で、\nワクワクしてます…♪", 133101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9076, 1, 0, 1, 0, "黄泉の食卓の皆様用に\nお土産を用意しないと\n騎士様のお知恵も\n頼りにしておりますわ", 133101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9077, 1, 0, 1, 0, "ジオ・ニヴルヘルと\n比べると、この世界は\n本当に明るいですね\n少し眩しいくらいです", 133101, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9078, 4, 0, 1, 0, "あまり、血の巡りが\n良くなくて…\n騎士様の手は\nとても温かいですね", 133101, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9079, 8, 0, 1, 0, "今度はわたくしに\nジオ・ニヴルヘルを\n案内させてくださいね\n観光案内のお返しです", 133101, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9080, 1, 1, 1, 0, "おはようございます\n朝の眩しい太陽には\nなかなか慣れません…", 133101, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9081, 1, 2, 1, 0, "騎士様、お茶は\nいかがでしょうか？\n一度一休みして、\n英気を養いましょう", 133101, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9082, 1, 3, 1, 0, "眠れないのでしょうか\nまぶたが重くなるまで\nわたくしをお話し\n相手になさって下さい", 133101, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9084, 1, 0, 1, 0, "思わず気が抜けそうに\nなってしまいます…\nふふ、ふわふわ…♪", 133101, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9085, 1, 0, 1, 0, "まあ、かわいらしい\nグレイスもきっと\n気に入りそうな\nデザインですわ", 133101, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9086, 1, 0, 1, 0, "まあ、いい香り…\n心安らぐようですわ\n素敵なセンスを\nお持ちなのですね♪", 133101, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9087, 1, 0, 1, 0, "燃え移りや乾燥がある\nから近づかないほうが\nいいとわかりつつも…\nああ、温かい…♪", 133101, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9089, 1, 0, 1, 0, "食が細いというよりは\n消化があまりうまく\nできなくて…本当は\nまだ頂きたいのですが", 133101, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9090, 1, 0, 1, 0, "『薔薇の下の契り』？\nまあ、男性同士で…！\nそんな…こ、これ以上\nここで読むわけには！", 133101, 8, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9092, 1, 0, 1, 0, "ダーリンが帰ってくる\n前に部屋の掃除しよ♪\n…ふふ、これってもう\n新婚生活だよね…♪", 132301, 1, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9093, 1, 0, 1, 0, "家具のセンスまで\n私と同じだなんて\n運命って怖いわ…♪", 132301, 2, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9094, 1, 0, 1, 0, "あの人はベッドの下に\n何か隠してるのかな…\nな、なんて全っ然\n気にならないし…っ！", 132301, 3, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9095, 1, 0, 1, 0, "うっかりして\n角引っかけないように\nしないとね…", 132301, 4, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9096, 1, 0, 1, 0, "錬金術を使わなくても\nあなたの心を鷲掴む\n私は恋も錬金術も\n失敗しないんだから！", 132301, 5, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9097, 4, 0, 1, 0, "あの人って破廉恥なの\n興味あるのかしら…？\n基本破廉恥禁止だけど\nあの人だけは…許す…", 132301, 101, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9098, 8, 0, 1, 0, "手狭になれば錬金術で\n新しい部屋作るから\nいつ家族が増えても\n安心だよね…♪", 132301, 102, 1);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9099, 1, 0, 1, 0, "錬金術でカレーライス\n作っちゃおっかな♪\n具材を釜でグツグツ…\nま、料理とも言うわね", 132301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9100, 1, 0, 1, 0, "錬金術に興味がある？\nじゃあさっそく私が\n手取り足取り教えるわ\nって…やだ破廉恥っ！", 132301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9101, 1, 0, 1, 0, "イロハちゃんとの\n虫捕りは命懸けよ！\n一万匹捕れるまで\n帰れないんだから…", 132301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9102, 1, 0, 1, 0, "ソノさんって\n体は小さいけど\nとっても頼れる私の\nお姉さん的存在なの♪", 132301, 4, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9103, 4, 0, 1, 0, "はあ…部屋中に広がる\nダーリンの香り…♪\n…ってノゾミみたいに\nなっちゃってる！？", 132301, 101, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9104, 8, 0, 1, 0, "鹿の獣人族の特徴は\nこのエッチな角…\n違っ！　叡智よ叡智！\n噛んだだけだからっ！", 132301, 102, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9105, 1, 1, 1, 0, "あ、おはよう！\n寝る前に飲んだ\n目覚めすっきり秘薬の\n効果はバッチリかな？", 132301, 1, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9106, 1, 2, 1, 0, "午後からも頑張る\nあなたのために\n愛妻弁当作ったの♪\n一緒に食べよ？", 132301, 2, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9107, 1, 3, 1, 0, "今日も一日お疲れさま\n眠れないなら隣で…\nじゃなかった早寝して\n明日も頑張ろうね！", 132301, 3, 2);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9109, 1, 0, 1, 0, "いい錬成をするには\n適度な休憩も必要よね\n…気持ち良くてたまに\n寝落ちしちゃうけど…", 132301, 2, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9110, 1, 0, 1, 0, "かわいいぬいぐるみ\n錬成じゃなくて\n手編みで作ったら\n驚いてくれるかも♪", 132301, 3, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9111, 1, 0, 1, 0, "くん、くん…\nん～いい匂い♪\nノゾミの香水と\nいい勝負だわ", 132301, 4, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9112, 1, 0, 1, 0, "ああ、暖かい\nでもソノさんの作った\nミートパイの方が\nもっと温かいわ…♪", 132301, 5, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9114, 1, 0, 1, 0, "もぐもぐ…私も\n食べても減らない秘薬\n錬成して他の食べ物も\n作れないかな？", 132301, 7, 3);
INSERT INTO `v1_ca2f9ed37c9c2e8023d159381deb116c8cc8686a00044747390a7b2a3096d08d` VALUES (9115, 1, 0, 1, 0, "ふむふむ…\nこのレシピ面白いわね\n今度錬成してみよっと", 132301, 8, 3);
