INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122601, 110, 0, 101, 105, 1, 1, 0.029, 1, 0, 1, 1.5, 160, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122401, 105, 0, 102, 111, 0, 2, 0.014, 3, 1, 2, 2.5, 120, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122501, 110, 1, 103, 105, 0, 3, 0.017, 2, 0, 3, 2.5, 350, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (107501, 110, 0, 104, 105, 0, 4, 0.016, 3, 1, 4, 1.5, 130, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (107601, 110, 0, 105, 105, 1, 5, 0.014, 1, 0, 5, 2.5, 160, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (107801, 120, 1, 106, 103, 0, 6, 0.029, 2, 0, 6, 3.5, 255, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110601, 120, 0, 107, 103, 1, 7, 0.013, 1, 0, 7, 3.5, 160, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110401, 110, 1, 108, 103, 0, 8, 0.014, 2, 0, 8, 2.5, 350, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110501, 105, 0, 109, 100, 0, 9, 0.012, 3, 1, 9, 1.5, 130, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (115701, 105, 0, 110, 103, 1, 10, 0.014, 1, 0, 10, 2.5, 200, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113601, 105, 1, 111, 103, 0, 11, 0.071, 2, 0, 11, 2.5, 350, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (107901, 120, 0, 112, 103, 0, 12, 0.014, 3, 1, 12, 1.5, 130, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (108001, 110, 0, 113, 105, 1, 13, 0.022, 2, 0, 13, 1.5, 200, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110301, 110, 0, 114, 108, 1, 14, 0.031, 1, 0, 14, 2.5, 160, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (107701, 110, 0, 115, 105, 0, 15, 0.036, 1, 1, 15, 0.5, 130, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113101, 110, 1, 116, 95, 0, 16, 0.048, 2, 0, 16, 3.5, 350, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113301, 105, 0, 117, 105, 0, 17, 0.043, 3, 1, 17, 1.5, 130, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113201, 105, 0, 118, 95, 0, 18, 0.018, 2, 1, 18, 1.5, 130, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (117001, 110, 0, 119, 108, 1, 19, 0.021, 2, 0, 19, 2.5, 160, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113501, 110, 1, 120, 105, 0, 20, 0.036, 1, 0, 20, 2.5, 255, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (113401, 105, 1, 121, 105, 0, 21, 0.02, 3, 0, 21, 2.5, 350, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110101, 110, 0, 122, 105, 0, 22, 0.018, 1, 1, 22, 2.5, 130, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110001, 105, 0, 123, 111, 1, 23, 0.03, 3, 0, 23, 3.5, 160, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (117201, 110, 0, 124, 103, 0, 24, 0.027, 1, 1, 24, 1.5, 130, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (117301, 105, 0, 125, 108, 1, 25, 0.02, 2, 0, 25, 3.5, 160, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (117401, 110, 1, 126, 105, 0, 26, 0.04, 3, 0, 26, 2.5, 350, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (117101, 110, 1, 127, 105, 0, 27, 0.009, 1, 0, 27, 3.5, 350, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122901, 120, 1, 128, 105, 0, 28, 0.022, 1, 0, 28, 3.5, 255, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122801, 110, 0, 129, 105, 0, 29, 0.028, 3, 1, 29, 2.5, 130, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (122701, 105, 0, 130, 108, 1, 30, 0.023, 2, 0, 30, 3.5, 130, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
