INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 0, 0.029, 105, 0, 160, 1, 122601, 101, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 110, 1, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 1, 0.014, 111, 0, 120, 2, 122401, 102, 0, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 105, 2, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.017, 105, 1, 350, 3, 122501, 103, 0, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 110, 3, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.016, 105, 0, 130, 4, 107501, 104, 0, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 110, 4, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.014, 105, 0, 160, 5, 107601, 105, 1, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 110, 5, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.029, 103, 1, 255, 6, 107801, 106, 0, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 120, 6, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.013, 103, 0, 160, 7, 110601, 107, 1, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 120, 7, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.014, 103, 1, 350, 8, 110401, 108, 0, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 110, 8, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.012, 100, 0, 130, 9, 110501, 109, 0, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 105, 9, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.014, 103, 0, 200, 10, 115701, 110, 1, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 105, 10, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.071, 103, 1, 350, 11, 113601, 111, 0, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 105, 11, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.014, 103, 0, 130, 12, 107901, 112, 0, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 120, 12, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 0, 0.022, 105, 0, 200, 13, 108001, 113, 1, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 110, 13, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.031, 108, 0, 160, 14, 110301, 114, 1, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 110, 14, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (0.5, 1, 0.036, 105, 0, 130, 15, 107701, 115, 0, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 110, 15, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.048, 95, 1, 350, 16, 113101, 116, 0, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 110, 16, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.043, 105, 0, 130, 17, 113301, 117, 0, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 105, 17, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.018, 95, 0, 130, 18, 113201, 118, 0, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 105, 18, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.021, 108, 0, 160, 19, 117001, 119, 1, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 110, 19, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.036, 105, 1, 255, 20, 113501, 120, 0, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 110, 20, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.02, 105, 1, 350, 21, 113401, 121, 0, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 105, 21, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 1, 0.018, 105, 0, 130, 22, 110101, 122, 0, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 110, 22, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.03, 111, 0, 160, 23, 110001, 123, 1, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 105, 23, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (1.5, 1, 0.027, 103, 0, 130, 24, 117201, 124, 0, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 110, 24, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.02, 108, 0, 160, 25, 117301, 125, 1, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 105, 25, 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 0, 0.04, 105, 1, 350, 26, 117401, 126, 0, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 110, 26, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.009, 105, 1, 350, 27, 117101, 127, 0, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 110, 27, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.022, 105, 1, 255, 28, 122901, 128, 0, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 120, 28, 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (2.5, 1, 0.028, 105, 0, 130, 29, 122801, 129, 0, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 110, 29, 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (3.5, 0, 0.023, 108, 0, 130, 30, 122701, 130, 1, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 105, 30, 2);
