INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 160, 0.029, 1, 1, 101, 105, 1, 1.5, 122601, 0, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 120, 0.014, 2, 2, 102, 111, 0, 2.5, 122401, 1, 0, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 350, 0.017, 3, 3, 103, 105, 0, 2.5, 122501, 0, 1, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 130, 0.016, 4, 4, 104, 105, 0, 1.5, 107501, 1, 0, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 160, 0.014, 5, 5, 105, 105, 1, 2.5, 107601, 0, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (120, 255, 0.029, 6, 6, 106, 103, 0, 3.5, 107801, 0, 1, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (120, 160, 0.013, 7, 7, 107, 103, 1, 3.5, 110601, 0, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 350, 0.014, 8, 8, 108, 103, 0, 2.5, 110401, 0, 1, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 130, 0.012, 9, 9, 109, 100, 0, 1.5, 110501, 1, 0, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 200, 0.014, 10, 10, 110, 103, 1, 2.5, 115701, 0, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 350, 0.071, 11, 11, 111, 103, 0, 2.5, 113601, 0, 1, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (120, 130, 0.014, 12, 12, 112, 103, 0, 1.5, 107901, 1, 0, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 200, 0.022, 13, 13, 113, 105, 1, 1.5, 108001, 0, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 160, 0.031, 14, 14, 114, 108, 1, 2.5, 110301, 0, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 130, 0.036, 15, 15, 115, 105, 0, 0.5, 107701, 1, 0, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 350, 0.048, 16, 16, 116, 95, 0, 3.5, 113101, 0, 1, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 130, 0.043, 17, 17, 117, 105, 0, 1.5, 113301, 1, 0, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 130, 0.018, 18, 18, 118, 95, 0, 1.5, 113201, 1, 0, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 160, 0.021, 19, 19, 119, 108, 1, 2.5, 117001, 0, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 255, 0.036, 20, 20, 120, 105, 0, 2.5, 113501, 0, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 350, 0.02, 21, 21, 121, 105, 0, 2.5, 113401, 0, 1, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 130, 0.018, 22, 22, 122, 105, 0, 2.5, 110101, 1, 0, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 160, 0.03, 23, 23, 123, 111, 1, 3.5, 110001, 0, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 130, 0.027, 24, 24, 124, 103, 0, 1.5, 117201, 1, 0, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 160, 0.02, 25, 25, 125, 108, 1, 3.5, 117301, 0, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 2);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 350, 0.04, 26, 26, 126, 105, 0, 2.5, 117401, 0, 1, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 350, 0.009, 27, 27, 127, 105, 0, 3.5, 117101, 0, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (120, 255, 0.022, 28, 28, 128, 105, 0, 3.5, 122901, 0, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 1);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (110, 130, 0.028, 29, 29, 129, 105, 0, 2.5, 122801, 1, 0, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 3);
INSERT INTO `v1_ca91bfe1895e62aa90e36ac3b4d6c63629e3ed91ce750f2342ed536715c354ee` VALUES (105, 130, 0.023, 30, 30, 130, 108, 1, 3.5, 122701, 0, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 2);
