INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 100, 100, 10142, 129901, 10.85, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ", 7, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 101, 101, 10142, 129901, 11.15, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 7, 0.0, "2024/03/31 12:00:00", 24, 102, 102, 10142, 129901, 6.4, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ", 5, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 3, 0.0, "2024/03/31 12:00:00", 24, 103, 103, 10142, 129901, 0.0, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ", 3, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 104, 104, 10142, 129901, 2.43, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ", 8, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 4, 0.0, "2024/03/31 12:00:00", 24, 105, 105, 10142, 129901, 9.08, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ", 3, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 6, 0.0, "2024/03/31 12:00:00", 24, 106, 106, 10142, 129901, 0.0, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ", 6, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 3, 0.0, "2024/03/31 12:00:00", 24, 107, 107, 10142, 129901, 10.6, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ", 8, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 6, 0.0, "2024/03/31 12:00:00", 24, 108, 108, 10142, 129901, 7.54, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ", 4, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 109, 109, 10142, 129901, 11.5, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ", 7, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 110, 110, 10142, 129901, 8.83, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 111, 111, 10142, 129901, 6.68, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 6, 0.0, "2024/03/31 12:00:00", 24, 112, 112, 10142, 129901, 8.8, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 113, 113, 10142, 129901, 8.9, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 114, 114, 10142, 129901, 3.7, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ", 5, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 200, 200, 10142, 129801, 7.6, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 201, 201, 10142, 129801, 2.25, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 202, 202, 10142, 129801, 3.28, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 4, 0.0, "2024/03/31 12:00:00", 24, 203, 203, 10142, 129801, 2.98, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 4, 0.0, "2024/03/31 12:00:00", 24, 204, 204, 10142, 129801, 3.0, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 205, 205, 10142, 129801, 10.3, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 6, 0.0, "2024/03/31 12:00:00", 24, 206, 206, 10142, 129801, 2.93, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 3, 0.0, "2024/03/31 12:00:00", 24, 207, 207, 10142, 129801, 9.3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 4, 0.0, "2024/03/31 12:00:00", 24, 208, 208, 10142, 129801, 6.9, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 209, 209, 10142, 129801, 0.0, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 210, 210, 10142, 129801, 5.95, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 211, 211, 10142, 129801, 6.73, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト", 1, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 2, 0.0, "2024/03/31 12:00:00", 24, 212, 212, 10142, 129801, 0.0, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 1, 0.0, "2024/03/31 12:00:00", 24, 213, 213, 10142, 129801, 6.65, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト", 2, "2030/12/17 14:59:59");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (0.0, 6, 0.0, "2024/03/31 12:00:00", 24, 214, 214, 10142, 129801, 3.05, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト", 1, "2030/12/17 14:59:59");
