INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (100, "エリコ", 129901, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10.85, 100, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 7, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (101, "エリコ", 129901, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 11.15, 101, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (102, "エリコ", 129901, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 6.4, 102, 7, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 5, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (103, "エリコ", 129901, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 103, 3, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 3, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (104, "エリコ", 129901, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 2.43, 104, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 8, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (105, "エリコ", 129901, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 9.08, 105, 4, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 3, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (106, "エリコ", 129901, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 106, 6, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 6, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (107, "エリコ", 129901, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10.6, 107, 3, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 8, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (108, "エリコ", 129901, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 7.54, 108, 6, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 4, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (109, "エリコ", 129901, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 11.5, 109, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 7, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (110, "エリコ", 129901, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 8.83, 110, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (111, "エリコ", 129901, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 6.68, 111, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (112, "エリコ", 129901, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 8.8, 112, 6, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (113, "エリコ", 129901, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 8.9, 113, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (114, "エリコ", 129901, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 3.7, 114, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 5, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (200, "マコト", 129801, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 7.6, 200, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (201, "マコト", 129801, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 2.25, 201, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (202, "マコト", 129801, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 3.28, 202, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (203, "マコト", 129801, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 2.98, 203, 4, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (204, "マコト", 129801, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 3.0, 204, 4, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (205, "マコト", 129801, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10.3, 205, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (206, "マコト", 129801, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 2.93, 206, 6, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (207, "マコト", 129801, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 9.3, 207, 3, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (208, "マコト", 129801, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 6.9, 208, 4, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (209, "マコト", 129801, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 209, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (210, "マコト", 129801, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 5.95, 210, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (211, "マコト", 129801, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 6.73, 211, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (212, "マコト", 129801, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 212, 2, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (213, "マコト", 129801, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 6.65, 213, 1, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 2, 0.0, 0.0);
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES (214, "マコト", 129801, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 3.05, 214, 6, "2030/12/17 14:59:59", "2024/03/31 12:00:00", 24, 10142, 1, 0.0, 0.0);
