INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 7, 10.85, "2024/03/31 12:00:00", 100, 100, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 11.15, "2024/03/31 12:00:00", 101, 101, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 10142, 7, 0.0, "2030/12/17 14:59:59", 0.0, 5, 6.4, "2024/03/31 12:00:00", 102, 102, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 10142, 3, 0.0, "2030/12/17 14:59:59", 0.0, 3, 0.0, "2024/03/31 12:00:00", 103, 103, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 8, 2.43, "2024/03/31 12:00:00", 104, 104, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 10142, 4, 0.0, "2030/12/17 14:59:59", 0.0, 3, 9.08, "2024/03/31 12:00:00", 105, 105, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 10142, 6, 0.0, "2030/12/17 14:59:59", 0.0, 6, 0.0, "2024/03/31 12:00:00", 106, 106, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 10142, 3, 0.0, "2030/12/17 14:59:59", 0.0, 8, 10.6, "2024/03/31 12:00:00", 107, 107, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 10142, 6, 0.0, "2030/12/17 14:59:59", 0.0, 4, 7.54, "2024/03/31 12:00:00", 108, 108, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 7, 11.5, "2024/03/31 12:00:00", 109, 109, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 1, 8.83, "2024/03/31 12:00:00", 110, 110, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 6.68, "2024/03/31 12:00:00", 111, 111, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 10142, 6, 0.0, "2030/12/17 14:59:59", 0.0, 2, 8.8, "2024/03/31 12:00:00", 112, 112, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 1, 8.9, "2024/03/31 12:00:00", 113, 113, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 5, 3.7, "2024/03/31 12:00:00", 114, 114, 129901, 24, "エリコ");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 7.6, "2024/03/31 12:00:00", 200, 200, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 1, 2.25, "2024/03/31 12:00:00", 201, 201, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 1, 3.28, "2024/03/31 12:00:00", 202, 202, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 10142, 4, 0.0, "2030/12/17 14:59:59", 0.0, 1, 2.98, "2024/03/31 12:00:00", 203, 203, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 10142, 4, 0.0, "2030/12/17 14:59:59", 0.0, 1, 3.0, "2024/03/31 12:00:00", 204, 204, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 10.3, "2024/03/31 12:00:00", 205, 205, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 10142, 6, 0.0, "2030/12/17 14:59:59", 0.0, 1, 2.93, "2024/03/31 12:00:00", 206, 206, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 10142, 3, 0.0, "2030/12/17 14:59:59", 0.0, 1, 9.3, "2024/03/31 12:00:00", 207, 207, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 10142, 4, 0.0, "2030/12/17 14:59:59", 0.0, 1, 6.9, "2024/03/31 12:00:00", 208, 208, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 2, 0.0, "2024/03/31 12:00:00", 209, 209, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 5.95, "2024/03/31 12:00:00", 210, 210, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 1, 6.73, "2024/03/31 12:00:00", 211, 211, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 10142, 2, 0.0, "2030/12/17 14:59:59", 0.0, 2, 0.0, "2024/03/31 12:00:00", 212, 212, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 10142, 1, 0.0, "2030/12/17 14:59:59", 0.0, 2, 6.65, "2024/03/31 12:00:00", 213, 213, 129801, 24, "マコト");
INSERT INTO `v1_cc60575afb64598dc5860f79c68a63320ff96f462810250e2b31fe49ba9e536e` VALUES ("あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 10142, 6, 0.0, "2030/12/17 14:59:59", 0.0, 1, 3.05, "2024/03/31 12:00:00", 214, 214, 129801, 24, "マコト");
