INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", "アヤネ", 2, 0.0, 0.0, 0.0, 24, 10046, 102301, 2, 401, 24001, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", "アヤネ", 2, 2.21, 0.0, 0.0, 24, 10046, 102301, 1, 402, 24002, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", "アヤネ", 2, 0.0, 0.0, 0.0, 24, 10046, 102301, 2, 403, 24003, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", "アサヒ", 1, 1.99, 0.0, 0.0, 24, 10046, 192201, 2, 701, 24004, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", "アサヒ", 2, 2.21, 0.0, 0.0, 24, 10046, 192201, 1, 702, 24005, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", "アサヒ", 1, 3.33, 0.0, 0.0, 24, 10046, 192201, 2, 703, 24006, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", "部員３", 2, 0.0, 0.0, 0.0, 24, 10046, 192301, 2, 801, 24007, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "オレだって\nビーチベースボール選手だ\nどんな球だろうが\n全部スタンドまで飛ばしてやる", "部員３", 2, 2.68, 0.0, 0.0, 24, 10046, 192301, 1, 802, 24008, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", "部員３", 2, 0.0, 0.0, 0.0, 24, 10046, 192301, 2, 803, 24009, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "やった！", "", 1, 0.0, 0.0, 0.0, 24, 10046, 192501, 1, 501, 24010, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "勝ったよ！", "", 1, 0.0, 0.0, 0.0, 24, 10046, 192501, 1, 502, 24011, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい調子！", "", 1, 0.0, 0.0, 0.0, 24, 10046, 192501, 1, 503, 24012, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", "ミサト", 2, 5.33, 0.0, 0.0, 24, 10046, 113501, 6, 111, 24013, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", "ミサト", 2, 6.85, 0.0, 0.0, 24, 10046, 113501, 1, 112, 24014, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "練習お疲れさま～！\n先生も協力できて\nとってもうれしいわ～♪\n次も頑張りましょうね～！", "ミサト", 2, 3.0, 0.0, 0.0, 24, 10046, 113501, 1, 113, 24015, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "まだ練習が足りねえみてえだな\nこれっぽっちも本気で\n投げてねえっつーの\nもう一回やんぞ！", "ダイチ", 2, 6.11, 0.0, 0.0, 24, 10046, 192401, 1, 611, 24016, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "ふん、当然の結果だな\n俺の球は、誰にも打てねえよ！\nまたやりてえっつーなら\nとことん付き合ってやるぜ！", "ダイチ", 2, 6.75, 0.0, 0.0, 24, 10046, 192401, 1, 612, 24017, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", "ダイチ", 2, 0.0, 0.0, 0.0, 24, 10046, 192401, 2, 613, 24018, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "きらーん☆\nハツネちゃんの大勝利なのだ～！\nみんながもっと上達できるように\n私もまだまだ頑張るからね！", "ハツネ", 1, 4.52, 0.0, 0.0, 24, 10046, 113401, 2, 211, 24019, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "やったあ！　私の勝ちだね！\nチアリーダーもピッチャーも\nハツネちゃんに任せてよ！", "ハツネ", 1, 3.89, 0.0, 0.0, 24, 10046, 113401, 2, 212, 24020, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "よお～し、この調子でもっと\n頑張ろう…と思ったけど\n超能力を使ったから…\n眠くなってきちゃった…すやぁ…", "ハツネ", 2, 13.49, 0.0, 0.0, 24, 10046, 113401, 1, 213, 24021, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい勝負だったぜ！\nたぎっちまって\nつい本気で投げちまったよ", "カヤ", 2, 3.17, 0.0, 0.0, 24, 10046, 106501, 1, 311, 24022, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "どうだ？\nオレ様のボールは熱かっただろ！\nま、でもおまえらの根性も\nなかなかのもんだったぜ", "カヤ", 2, 4.42, 0.0, 0.0, 24, 10046, 106501, 1, 312, 24023, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "なんだよ、そんなもんか？\nこっちはまだまだ投げたりねえ\nもっと勝負しようぜ！", "カヤ", 1, 2.89, 0.0, 0.0, 24, 10046, 106501, 6, 313, 24024, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "あとちょっとだったのに～！\n次こそぷうきちと一緒に\n勝ってみせるからね！", "アヤネ", 3, 3.71, 0.0, 0.0, 24, 10046, 102301, 4, 421, 24025, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい勝負だったね！\nでも、やっぱり悔しいな…\nもっと練習して\n今度は絶対に俺が勝つから！", "アサヒ", 1, 5.1, 0.0, 0.0, 24, 10046, 192201, 2, 721, 24026, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "ちっ…あと一歩足りなかったか\n次こそは絶対に勝つから\nそのつもりでいろ", "部員３", 3, 4.34, 0.0, 0.0, 24, 10046, 192301, 1, 821, 24027, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい勝負だったよ\nまたやろうね", "", 1, 0.0, 0.0, 0.0, 24, 10046, 192501, 1, 521, 24028, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "とってもよくできました～♪\n頑張ってくれて先生うれしいわ\nこれからも試合に向けて\n練習していきましょうね～", "ミサト", 2, 5.62, -360.0, 0.0, 24, 10046, 113501, 1, 101, 24029, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "頑張った成果ね～！\nこのまま練習を続ければ\nもっとすごい成績を残せそうよ\n期待してますからね～！", "ミサト", 2, 9.58, -360.0, 0.0, 24, 10046, 113501, 1, 102, 24030, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "いい子、いい子～♪\n頑張り屋さんのあなたを\nたっくさん褒めてあげますからね", "ミサト", 2, 0.0, -360.0, 0.0, 24, 10046, 113501, 2, 103, 24031, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "夢に向けて、一歩前進できたわね\nこの調子で、夢を叶えましょう\n目指せ、全国大会～！", "ミサト", 1, 9.13, -360.0, 0.0, 24, 10046, 113501, 2, 104, 24032, "2020/07/31 12:00:00");
INSERT INTO `v1_cd5e981c22a0f5542353157284755c5f57ff85d1ba67433bfa20f1a7ab97b5af` VALUES ("2030/12/17 14:59:59", "絶好調ね～♪\nきっと試合でも大活躍\n間違いなしだわ\n楽しみにしていますからね～！", "ミサト", 2, 8.27, -360.0, 0.0, 24, 10046, 113501, 1, 105, 24033, "2020/07/31 12:00:00");
