INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1000, "2020-02-15 15:00:00", "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？", 0, 190801, 1000001, 13, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1000, "2020-02-15 15:00:00", "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？", 0, 190801, 1000002, 13, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1001, "2020-02-15 15:00:00", "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", 0, 190801, 1001001, 13, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1002, "2020-02-15 15:00:00", "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", 0, 190801, 1002001, 12, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1003, "2020-02-15 15:00:00", "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 0, 190801, 1003001, 11, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1004, "2020-02-15 15:00:00", "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", 0, 190801, 1004001, 3, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1005, "2020-02-15 15:00:00", "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", 0, 190801, 1005001, 7, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1006, "2020-05-31 12:00:00", "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", 0, 190801, 1006001, 2, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1007, "2020-08-15 15:00:00", "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", 0, 190801, 1007001, 4, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1008, "2021-08-31 12:00:00", "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", 0, 190801, 1008001, 6, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1009, "2021-12-18 15:00:00", "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", 0, 190801, 1009001, 13, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1010, "2022-02-15 15:00:00", "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", 0, 190801, 1010001, 4, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1011, "2022-07-18 15:00:00", "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", 0, 190801, 1011001, 6, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1012, "2023-09-18 15:00:00", "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", 0, 190801, 1012001, 6, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1013, "2024-04-18 15:00:00", "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", 0, 190801, 1013001, 6, "2030-04-01 14:00:00");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1014, "2024-11-18 15:00:00", "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪", 0, 190801, 1014001, 6, "2030-04-01 14:00:00");
