INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (4, 0, 1010001, "2030-04-01 14:00:00", "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", "2022-02-15 15:00:00", 1010, 190801);
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (6, 0, 1011001, "2030-04-01 14:00:00", "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", "2022-07-18 15:00:00", 1011, 190801);
