INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1000, 13, 1000001, "2020-02-15 15:00:00", "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1000, 13, 1000002, "2020-02-15 15:00:00", "楽しいミニゲームをご紹介！\nあなたはどのミニゲームで\n遊びますか？");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1001, 13, 1001001, "2020-02-15 15:00:00", "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1002, 12, 1002001, "2020-02-15 15:00:00", "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1003, 11, 1003001, "2020-02-15 15:00:00", "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1004, 3, 1004001, "2020-02-15 15:00:00", "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1005, 7, 1005001, "2020-02-15 15:00:00", "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1006, 2, 1006001, "2020-05-31 12:00:00", "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1007, 4, 1007001, "2020-08-15 15:00:00", "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1008, 6, 1008001, "2021-08-31 12:00:00", "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1009, 13, 1009001, "2021-12-18 15:00:00", "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1010, 4, 1010001, "2022-02-15 15:00:00", "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1011, 6, 1011001, "2022-07-18 15:00:00", "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1012, 6, 1012001, "2023-09-18 15:00:00", "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1013, 6, 1013001, "2024-04-18 15:00:00", "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪");
INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (190801, "2030-04-01 14:00:00", 0, 1014, 6, 1014001, "2024-11-18 15:00:00", "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪");
