INSERT INTO `v1_cf08ece02be2fe4cedd569f3ec14da30150ae6978eefb148788da4bf1cb754e3` VALUES (1003001, 190801, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 1003, 11, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 0);
