INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/01/31 12:00:00", 5, "カリン", 2.12, 64, -363.0, "2030/01/01 14:59:59", 2, 1, 0.0, 1, 190804, 1000005, "ギルド管理協会です！\nイベント報酬券をお持ちでしたら、様々なアイテムと交換できますよ");
