INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/01/31 12:00:00", 7, "カリン", 2.33, 64, -363.0, "2030/01/01 14:59:59", 2, 1, 0.0, 1, 190804, 1000007, "いつもありがとうございます！\n報酬交換ですか？\nでは、イベント報酬券とお引き換えいたしますね");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/01/31 12:00:00", 101, "ラビリスタ", 5.18, 61, -285.0, "2030/01/01 14:59:59", 3, 10201, -6.0, 7, 133701, 1020101, "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/01/31 12:00:00", 201, "クリスティーナ", 8.77, 61, -285.0, "2030/01/01 14:59:59", 1, 10201, -2.0, 2, 133901, 1020102, "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/01/31 12:00:00", 301, "ネネカ", 15.5, 61, -285.0, "2030/01/01 14:59:59", 6, 10201, 0.0, 1, 133801, 1020103, "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/02/28 12:00:00", 101, "ランファ", 9.061, 61, -285.0, "2030/01/01 14:59:59", 2, 10202, 0.0, 5, 134301, 1020201, "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/02/28 12:00:00", 201, "チカ", 7.699, 61, -285.0, "2030/01/01 14:59:59", 1, 10202, 0.0, 2, 134401, 1020202, "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/03/31 12:00:00", 101, "アオイ", 8.595, 61, -285.0, "2030/01/01 14:59:59", 1, 10203, 0.0, 2, 134601, 1020301, "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/03/31 12:00:00", 201, "クローチェ", 7.758, 61, -285.0, "2030/01/01 14:59:59", 1, 10203, 0.0, 2, 134501, 1020302, "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/04/30 12:00:00", 101, "ルイズマリー", 7.0, 61, -285.0, "2030/01/01 14:59:59", 2, 10204, 0.0, 6, 132302, 1020401, "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/04/30 12:00:00", 201, "イロハ", 7.904, 61, -285.0, "2030/01/01 14:59:59", 1, 10204, 0.0, 2, 132402, 1020402, "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/04/30 12:00:00", 301, "ソノ", 8.487, 61, -285.0, "2030/01/01 14:59:59", 4, 10204, 0.0, 2, 132502, 1020403, "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/05/31 12:00:00", 101, "カノン", 8.763, 61, -285.0, "2030/01/01 14:59:59", 1, 10205, 0.0, 2, 134901, 1020502, "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/05/31 12:00:00", 201, "ツムギ", 6.417, 61, -285.0, "2030/01/01 14:59:59", 1, 10205, 0.0, 3, 134801, 1020503, "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/06/30 12:00:00", 101, "エリス", 10.335, 61, -285.0, "2030/01/01 14:59:59", 3, 10206, 0.0, 7, 135001, 1020601, "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/06/30 12:00:00", 201, "シェフィ", 6.281, 61, -285.0, "2030/01/01 14:59:59", 2, 10206, 0.0, 4, 135101, 1020602, "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/06/30 12:00:00", 301, "プレシア", 12.244, 61, -285.0, "2030/01/01 14:59:59", 1, 10206, 0.0, 2, 135201, 1020603, "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/07/31 12:00:00", 101, "クリア", 6.339, 61, -285.0, "2030/01/01 14:59:59", 2, 10207, 0.0, 8, 135301, 1020701, "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/07/31 12:00:00", 201, "アネモネ", 8.184, 61, -285.0, "2030/01/01 14:59:59", 7, 10207, 0.0, 9, 135401, 1020702, "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/08/31 12:00:00", 101, "アイラ", 3.906, 61, -285.0, "2030/01/01 14:59:59", 9, 10208, 0.0, 2, 129101, 1020801, "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/08/31 12:00:00", 201, "シナツ", 14.465, 61, -285.0, "2030/01/01 14:59:59", 1, 10208, 0.0, 2, 129201, 1020802, "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/08/31 12:00:00", 301, "ヒヨリ", 8.662, 61, -285.0, "2030/01/01 14:59:59", 4, 10208, 0.0, 3, 100101, 1020803, "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/08/31 12:00:00", 401, "レイ", 10.915, 61, -285.0, "2030/01/01 14:59:59", 1, 10208, 0.0, 3, 100301, 1020804, "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/09/30 12:00:00", 101, "ユキノ", 6.042, 61, -285.0, "2030/01/01 14:59:59", 1, 10209, 0.0, 7, 135802, 1020901, "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/09/30 12:00:00", 201, "サツキ", 9.402, 61, -285.0, "2030/01/01 14:59:59", 4, 10209, 0.0, 1, 135903, 1020902, "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/09/30 12:00:00", 301, "ルルィ", 8.16, 61, -285.0, "2030/01/01 14:59:59", 8, 10209, 0.0, 1, 136902, 1020903, "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/10/31 12:00:00", 101, "カスミ", 5.0, 61, -285.0, "2030/01/01 14:59:59", 3, 10210, 0.0, 2, 137401, 1021001, "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/10/31 12:00:00", 201, "アラクネ", 8.12, 61, -285.0, "2030/01/01 14:59:59", 4, 10210, 0.0, 1, 136001, 1021002, "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/10/31 12:00:00", 301, "ナーナ", 6.386, 61, -285.0, "2030/01/01 14:59:59", 4, 10210, 0.0, 2, 136202, 1021003, "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/11/30 12:00:00", 101, "ユイ", 7.438, 61, -285.0, "2030/01/01 14:59:59", 3, 10211, 0.0, 4, 136402, 1021101, "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/11/30 12:00:00", 201, "ミソラ", 8.35, 61, -285.0, "2030/01/01 14:59:59", 1, 10211, 0.0, 5, 136302, 1021102, "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/12/31 12:00:00", 101, "シズル", 5.0, 61, -285.0, "2030/01/01 14:59:59", 1, 10212, 0.0, 2, 136501, 1021201, "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/12/31 12:00:00", 201, "リノ", 5.25, 61, -285.0, "2030/01/01 14:59:59", 1, 10212, 0.0, 2, 137902, 1021202, "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！");
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("2025/12/31 12:00:00", 301, "ラビリスタ", 7.5, 61, -285.0, "2030/01/01 14:59:59", 1, 10212, 0.0, 2, 106801, 1021203, "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！");
