INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (-6.0, "ラビリスタ", "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を", 7, 10201, 5.18, 1020101, 133701, 101, "2025/01/31 12:00:00", -285.0, 3, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (-2.0, "クリスティーナ", "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな", 2, 10201, 8.77, 1020102, 133901, 201, "2025/01/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ネネカ", "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます", 1, 10201, 15.5, 1020103, 133801, 301, "2025/01/31 12:00:00", -285.0, 6, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ランファ", "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…", 5, 10202, 9.061, 1020201, 134301, 101, "2025/02/28 12:00:00", -285.0, 2, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "チカ", "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ", 2, 10202, 7.699, 1020202, 134401, 201, "2025/02/28 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "アオイ", "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！", 2, 10203, 8.595, 1020301, 134601, 101, "2025/03/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "クローチェ", "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ", 2, 10203, 7.758, 1020302, 134501, 201, "2025/03/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ルイズマリー", "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！", 6, 10204, 7.0, 1020401, 132302, 101, "2025/04/30 12:00:00", -285.0, 2, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "イロハ", "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪", 2, 10204, 7.904, 1020402, 132402, 201, "2025/04/30 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ソノ", "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～", 2, 10204, 8.487, 1020403, 132502, 301, "2025/04/30 12:00:00", -285.0, 4, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "カノン", "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪", 2, 10205, 8.763, 1020502, 134901, 101, "2025/05/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ツムギ", "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！", 3, 10205, 6.417, 1020503, 134801, 201, "2025/05/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "エリス", "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる", 7, 10206, 10.335, 1020601, 135001, 101, "2025/06/30 12:00:00", -285.0, 3, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "シェフィ", "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？", 4, 10206, 6.281, 1020602, 135101, 201, "2025/06/30 12:00:00", -285.0, 2, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "プレシア", "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ", 2, 10206, 12.244, 1020603, 135201, 301, "2025/06/30 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "クリア", "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…", 8, 10207, 6.339, 1020701, 135301, 101, "2025/07/31 12:00:00", -285.0, 2, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "アネモネ", "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです", 9, 10207, 8.184, 1020702, 135401, 201, "2025/07/31 12:00:00", -285.0, 7, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "アイラ", "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！", 2, 10208, 3.906, 1020801, 129101, 101, "2025/08/31 12:00:00", -285.0, 9, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "シナツ", "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？", 2, 10208, 14.465, 1020802, 129201, 201, "2025/08/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ヒヨリ", "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！", 3, 10208, 8.662, 1020803, 100101, 301, "2025/08/31 12:00:00", -285.0, 4, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "レイ", "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！", 3, 10208, 10.915, 1020804, 100301, 401, "2025/08/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ユキノ", "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…", 7, 10209, 6.042, 1020901, 135802, 101, "2025/09/30 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "サツキ", "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公", 1, 10209, 9.402, 1020902, 135903, 201, "2025/09/30 12:00:00", -285.0, 4, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ルルィ", "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい", 1, 10209, 8.16, 1020903, 136902, 301, "2025/09/30 12:00:00", -285.0, 8, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "カスミ", "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！", 2, 10210, 5.0, 1021001, 137401, 101, "2025/10/31 12:00:00", -285.0, 3, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "アラクネ", "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします", 1, 10210, 8.12, 1021002, 136001, 201, "2025/10/31 12:00:00", -285.0, 4, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ナーナ", "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！", 2, 10210, 6.386, 1021003, 136202, 301, "2025/10/31 12:00:00", -285.0, 4, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ユイ", "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？", 4, 10211, 7.438, 1021101, 136402, 101, "2025/11/30 12:00:00", -285.0, 3, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ミソラ", "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね", 5, 10211, 8.35, 1021102, 136302, 201, "2025/11/30 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "シズル", "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！", 2, 10212, 5.0, 1021201, 136501, 101, "2025/12/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "リノ", "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！", 2, 10212, 5.25, 1021202, 137902, 201, "2025/12/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES (0.0, "ラビリスタ", "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！", 2, 10212, 7.5, 1021203, 106801, 301, "2025/12/31 12:00:00", -285.0, 1, "2030/01/01 14:59:59", 61);
