INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ラビリスタ", 10201, -6.0, 101, 1020101, "2025/01/31 12:00:00", -285.0, 5.18, "今度の敵は、アタシにとっても\n放ってはおけない存在だ\n少年、アタシも全力で行くからさ\n一緒に守ろう、この世界を", 7, "2030/01/01 14:59:59", 3, 133701, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("クリスティーナ", 10201, -2.0, 201, 1020102, "2025/01/31 12:00:00", -285.0, 8.77, "退屈の吹き飛ぶような\n戦いの気配がするなぁ坊や\nさっさとワタシをそこへ連れてゆけ\n世界も守ってやるさ、ついでにな", 2, "2030/01/01 14:59:59", 1, 133901, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ネネカ", 10201, 0.0, 301, 1020103, "2025/01/31 12:00:00", -285.0, 15.5, "おや、私の力が必要……と？\nグレゴールが手伝うそうですよ\n私は戦わないのか、という顔ですね\n……考えておきます", 1, "2030/01/01 14:59:59", 6, 133801, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ランファ", 10202, 0.0, 101, 1020201, "2025/02/28 12:00:00", -285.0, 9.061, "わたしだけの歌…\n一緒に、探してくれる…？\nあなたがいれば…\nもっとがんばれるから…", 5, "2030/01/01 14:59:59", 2, 134301, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("チカ", 10202, 0.0, 201, 1020202, "2025/02/28 12:00:00", -285.0, 7.699, "同じ唱喚士として、仲間として\n私にできることをします\n…これも、あなたが\n背中を押してくれたおかげですよ", 2, "2030/01/01 14:59:59", 1, 134401, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("アオイ", 10203, 0.0, 101, 1020301, "2025/03/31 12:00:00", -285.0, 8.595, "世界の平和が私とクローチェさんに\nかかってるんですよね？\nぼっちには過大すぎる期待ですけど\nふ、二人でなら背負ってみせます！", 2, "2030/01/01 14:59:59", 1, 134601, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("クローチェ", 10203, 0.0, 201, 1020302, "2025/03/31 12:00:00", -285.0, 7.758, "新しく手に入れたこの翼で…\nナイトさんがどこにいたって\nわたし、駆けつけちゃいますよっ！\nクローチェ・エアリアル発進ですっ", 2, "2030/01/01 14:59:59", 1, 134501, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ルイズマリー", 10204, 0.0, 101, 1020401, "2025/04/30 12:00:00", -285.0, 7.0, "今日はダーリンとの初デート♪\n二人でショッピングして、その後は\n…って私、指名手配されてる！？\nちょ、た…助けてダーリン～！！", 6, "2030/01/01 14:59:59", 2, 132302, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("イロハ", 10204, 0.0, 201, 1020402, "2025/04/30 12:00:00", -285.0, 7.904, "ルイちゃんのおばあちゃん優しくて\nいつもにこにこで、イロハ大好き♪\nでも一番好きなのは、角がすっごく\n大きくてかっこいいとこなんだ～♪", 2, "2030/01/01 14:59:59", 1, 132402, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ソノ", 10204, 0.0, 301, 1020403, "2025/04/30 12:00:00", -285.0, 8.487, "ノゾミちゃんのおかげで売上が安定\nしてきたのに、臨時休業、痛すぎる\n…ま、ルイちゃんのために\n一肌脱ぐか。行くよイロハ～", 2, "2030/01/01 14:59:59", 4, 132502, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("カノン", 10205, 0.0, 101, 1020502, "2025/05/31 12:00:00", -285.0, 8.763, "ヲ～ッホッホ♪\nあたくし様と同じお空気を吸える\nお幸運を噛みしめなさいですわ～♪\nヲ～ッホッホッホ～♪", 2, "2030/01/01 14:59:59", 1, 134901, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ツムギ", 10205, 0.0, 201, 1020503, "2025/05/31 12:00:00", -285.0, 6.417, "いきますよ騎士さん！\nカノンさんと力を合わせて…力を…\n…本当にあの人なんなんです？\nこれまでになく不安なんですけど！", 3, "2030/01/01 14:59:59", 1, 134801, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("エリス", 10206, 0.0, 101, 1020601, "2025/06/30 12:00:00", -285.0, 10.335, "『願いが叶う海岸』か…怪しい\nだというのに、阿賀斗紫布菜たちは\n呑気に戯れている…見ていられない\n騎士クンは、わたしが守ってみせる", 7, "2030/01/01 14:59:59", 3, 135001, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("シェフィ", 10206, 0.0, 201, 1020602, "2025/06/30 12:00:00", -285.0, 6.281, "せっかくの夏休みなんだし\nたまにはビーチでゆっくりしましょ\n…え、もしかしてこの夏もあんまり\n休めなかったりする感じなの？", 4, "2030/01/01 14:59:59", 2, 135101, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("プレシア", 10206, 0.0, 301, 1020603, "2025/06/30 12:00:00", -285.0, 12.244, "いっぱい。たべて\nおおきく。なると\nせーぎ。まもれる。はず\nお兄。いっしょに。スイカ。たべよ", 2, "2030/01/01 14:59:59", 1, 135201, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("クリア", 10207, 0.0, 101, 1020701, "2025/07/31 12:00:00", -285.0, 6.339, "ひと夏の冒険のはじまりです！\nアネモネさんも楽しんでくれたら\n…できれば、もう少し\n仲良くなれたらうれしいな…", 8, "2030/01/01 14:59:59", 2, 135301, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("アネモネ", 10207, 0.0, 201, 1020702, "2025/07/31 12:00:00", -285.0, 8.184, "─幻の花…\n本当にあるのか謎─ですけど\n探すくらいは、\n付き合ってもいいです", 9, "2030/01/01 14:59:59", 7, 135401, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("アイラ", 10208, 0.0, 101, 1020801, "2025/08/31 12:00:00", -285.0, 3.906, "あははっ！　笑う門には福来る！\n笑ってればきっと、複製世界にも\n未来はやってくるって信じてる！\nそうだよね、シナツちゃん！", 2, "2030/01/01 14:59:59", 9, 129101, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("シナツ", 10208, 0.0, 201, 1020802, "2025/08/31 12:00:00", -285.0, 14.465, "繰り返されるリセットの先の未来\n何度願っても手に入らなかった未来\n…でも、私は諦めない。絶対にだ\nキミも同じだろ…アイラ？", 2, "2030/01/01 14:59:59", 1, 129201, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ヒヨリ", 10208, 0.0, 301, 1020803, "2025/08/31 12:00:00", -285.0, 8.662, "アイラの世界のみんなも、\n一生懸命生きてるんだ…！\n…なのに、世界の危機だなんて！\nあたし、絶対、絶対力になるよ！", 3, "2030/01/01 14:59:59", 4, 100101, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("レイ", 10208, 0.0, 401, 1020804, "2025/08/31 12:00:00", -285.0, 10.915, "シナツ…もう一人の私\n彼女が大切にしている世界を\n絶対に終わらせはしない…！\n彼女と笑い合える未来のために！", 3, "2030/01/01 14:59:59", 1, 100301, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ユキノ", 10209, 0.0, 101, 1020901, "2025/09/30 12:00:00", -285.0, 6.042, "あ、ど、ども…\nあ、あんま見ねぇでくれっと\nその、恥ずかしくて…\n体が熱くなって、溶けちまう…", 7, "2030/01/01 14:59:59", 1, 135802, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("サツキ", 10209, 0.0, 201, 1020902, "2025/09/30 12:00:00", -285.0, 9.402, "なんで俺が幽霊に…意味不明だ\nこれじゃ死んでも死にきれねェ\n悪ィがこれも何かの縁だ、\nよろしく頼むぜ騎士公", 1, "2030/01/01 14:59:59", 4, 135903, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ルルィ", 10209, 0.0, 301, 1020903, "2025/09/30 12:00:00", -285.0, 8.16, "う、うじゅる…\nサツキ姉しゃまを助けるためにも\nまずは、腹ごしらえでしゅね…！\nタコ焼き、召し上がってくだしゃい", 1, "2030/01/01 14:59:59", 8, 136902, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("カスミ", 10210, 0.0, 101, 1021001, "2025/10/31 12:00:00", -285.0, 5.0, "ひさしぶりのジオ・ゲヘナ\n歓迎パーティーで起きた殺人事件！\nランドソルの名探偵の名にかけて\n解き明かしてみせようじゃないか！", 2, "2030/01/01 14:59:59", 3, 137401, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("アラクネ", 10210, 0.0, 201, 1021002, "2025/10/31 12:00:00", -285.0, 8.12, "…わたしはどこの誰なのでしょう\nどこから来てどこへ行くのでしょう\nこの事件の真相が分かれば、\n何かが掴めるような気がいたします", 1, "2030/01/01 14:59:59", 4, 136001, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ナーナ", 10210, 0.0, 301, 1021003, "2025/10/31 12:00:00", -285.0, 6.386, "ナーナ、すいりとかろんぱとか、\nむずかしくてわかんないけど…\nお友だちとドーナツのためならっ\nせーいっぱいがんばるよっ！", 2, "2030/01/01 14:59:59", 4, 136202, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ユイ", 10211, 0.0, 101, 1021101, "2025/11/30 12:00:00", -285.0, 7.438, "騎士クンに想いを伝えたい…だから\nミソラちゃん、わたしがんばるね！\n…あれ？　わたし、なんでこんなに\n必死になってるんだろう…？", 4, "2030/01/01 14:59:59", 3, 136402, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ミソラ", 10211, 0.0, 201, 1021102, "2025/11/30 12:00:00", -285.0, 8.35, "騎士さんとのデートはわたしに\nお任せです★　なのでユイさんは\n安心して計画に専念して…たくさん\nドキドキしちゃってくださいね", 5, "2030/01/01 14:59:59", 1, 136302, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("シズル", 10212, 0.0, 101, 1021201, "2025/12/31 12:00:00", -285.0, 5.0, "新しい年がやってくるね！\nお正月の料理も飾りつけも、\nお姉ちゃんにお任せだよ♪\nみんなで楽しく新年を迎えよう！", 2, "2030/01/01 14:59:59", 1, 136501, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("リノ", 10212, 0.0, 201, 1021202, "2025/12/31 12:00:00", -285.0, 5.25, "おうちの掃除もトラブルも、 \n世界を揺るがす大ピンチも！\n今年のことは今年のうちに \nサッサと片付けちゃいますっ！", 2, "2030/01/01 14:59:59", 1, 137902, 61);
INSERT INTO `v1_d147e04d639daf4e0c12b967542218102d13183cf844d2bb20d47f6e448b6a30` VALUES ("ラビリスタ", 10212, 0.0, 301, 1021203, "2025/12/31 12:00:00", -285.0, 7.5, "年の瀬はギルドマスターも\nつい駆け回っちゃう忙しい時期だ！\n一年いろいろあったけど\nお互いイイ年を迎えよう、少年！", 2, "2030/01/01 14:59:59", 1, 106801, 61);
