INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 401, "アヤネ", "2030/12/17 14:59:59", 24, 102301, 10046, 2, 0.0, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 0.0, 24001, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (1, 0.0, 402, "アヤネ", "2030/12/17 14:59:59", 24, 102301, 10046, 2, 0.0, "私たちの勝ちだね！\nぷうきちと一緒なら\nどんなボールでも\nホームランを打ってみせるよ！", 2.21, 24002, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 403, "アヤネ", "2030/12/17 14:59:59", 24, 102301, 10046, 2, 0.0, "やったやったぁ！\nお兄ちゃん、見ててくれた？\n私、すっごくがんばったよ！", 0.0, 24003, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 701, "アサヒ", "2030/12/17 14:59:59", 24, 192201, 10046, 1, 0.0, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 1.99, 24004, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (1, 0.0, 702, "アサヒ", "2030/12/17 14:59:59", 24, 192201, 10046, 2, 0.0, "よし、いい感じだ！\nキャプテンとしての仕事は\nきっちり果たせそうだよ", 2.21, 24005, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 703, "アサヒ", "2030/12/17 14:59:59", 24, 192201, 10046, 1, 0.0, "試合に向けて\nいい感覚を掴めた気がするよ！\nこのまま一気に全国大会だ！", 3.33, 24006, "2020/07/31 12:00:00");
