INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 803, "部員３", "2030/12/17 14:59:59", 24, 192301, 10046, 2, 0.0, "いい感じだ\nこれなら試合でも活躍できそうだ\n大暴れしてやる！", 0.0, 24009, "2020/07/31 12:00:00");
