INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (6, 0.0, 111, "ミサト", "2030/12/17 14:59:59", 24, 113501, 10046, 2, 0.0, "あら～…もしかして\n勝っちゃったのかしら？\nみんなの練習を見ていたから\nかもしれないわね", 5.33, 24013, "2020/07/31 12:00:00");
INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (1, 0.0, 112, "ミサト", "2030/12/17 14:59:59", 24, 113501, 10046, 2, 0.0, "みんなの力になりたくて\n先生も頑張ってみました～！\nうまく投げられたみたいで\nよかったわ～！", 6.85, 24014, "2020/07/31 12:00:00");
