INSERT INTO `v1_d1ac2f9ea2a02687bf98a0d1abe3a35bcd3ea42e521af1a0f3bc1bc9637bca50` VALUES (2, 0.0, 613, "ダイチ", "2030/12/17 14:59:59", 24, 192401, 10046, 2, 0.0, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 0.0, 24018, "2020/07/31 12:00:00");
