INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 1, "カリン", 1, 1, 0, 60000, 0, "", 1, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 2, 1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 3, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60000, 0, "", 4, 1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 5, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 6, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60000, 0, "", 7, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 8, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 9, 3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 10, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 11, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60000, 0, "", 12, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 13, 4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 14, 4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 15, 4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60000, 0, "", 16, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60000, 0, "", 17, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 2, 0, 60000, 0, "", 18, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 3, 7, 60000, 0, "", 19, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101201, 0, "ハツネ", 0, 1, 1, 80001, 0, "", 20, 0, "キミと出会ってもう\n1年もたつんだね！\nう～ん、いつも\n寝てばっかりだから、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101201, 0, "ハツネ", 0, 2, 1, 80001, 0, "", 21, 0, "少しでも\n寝る時間を減らして\nキミと一緒にいる\n時間増やしたいなぁ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103801, 1, "シオリ", 0, 1, 1, 80001, 0, "", 22, 0, "あなたと知り合ってから\nもう1年…\n時がたつのは\n早いですね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103801, 1, "シオリ", 0, 2, 1, 80001, 0, "", 23, 0, "来年も、\n一緒にいられたら…\nうれしいな");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103601, 0, "キョウカ", 0, 1, 2, 80001, 0, "", 24, 0, "あなたと一緒にいるようになって、\nもう1年ですか…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103601, 0, "キョウカ", 0, 2, 2, 80001, 0, "", 25, 0, "最初の頃よりも、頼もしく\nなりましたね。私はどうですか？\n成長していますか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102001, 1, "ミミ", 0, 1, 2, 80001, 0, "", 26, 0, "おにいちゃんとあってから、\n1年たったよ～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102001, 1, "ミミ", 0, 2, 2, 80001, 0, "", 27, 0, "ミミね、おにいちゃんと\nいっぱい遊べてたのしかったな～♪\nこれからもずっと\nいっしょにいてね～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100401, 2, "ミソギ", 0, 1, 2, 80001, 0, "", 28, 0, "にいちゃんがミソギのイタズラに\n引っかかり続けてもう1年かぁ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100401, 2, "ミソギ", 0, 2, 2, 80001, 0, "", 29, 0, "…これからもいっぱいミソギと\n遊んでね、にいちゃん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101001, 0, "マホ", 0, 1, 3, 80001, 0, "", 30, 0, "もう王子はんと会うてから\n1年ぐらいたつそうどすえ\n早いもんやなあ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101001, 0, "マホ", 0, 2, 3, 80001, 0, "", 31, 0, "このままやと、\nうちもあっという間に\n干からびてしまいそうどすえ\nなーんてな。うふふ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104401, 1, "イリヤ", 0, 1, 3, 80001, 0, "", 32, 0, "お主と会って早1年じゃな\nこれからも存分に尽くすがよい");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104401, 1, "イリヤ", 0, 2, 3, 80001, 0, "", 33, 0, "わらわもたまには、お主の献身に\n報いてやらぬでもない♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101701, 2, "カオリ", 0, 1, 3, 80001, 0, "", 34, 0, "もう、キミと出会って結構な時間が\nたつさ～。でも、私たちの旅は\nまだまだ始まったばかりさ～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101701, 2, "カオリ", 0, 2, 3, 80001, 0, "", 35, 0, "これからも、どうぞよろしく\nお願いするさ～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102801, 0, "サレン", 0, 1, 4, 80001, 0, "", 36, 0, "あんたと出会ってからもうそんなに\nたつのね～");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102801, 0, "サレン", 0, 2, 4, 80001, 0, "", 37, 0, "あっという間だったって感じるほど\n楽しかったわ！\nこれからも一緒に頑張りましょ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102501, 1, "スズメ", 0, 1, 4, 80001, 0, "", 38, 0, "出会ってから1年間、ずっと\nご迷惑をかけっぱなしで、\nすみません…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102501, 1, "スズメ", 0, 2, 4, 80001, 0, "", 39, 0, "これからはもっと頑張りますから\n見ていてくださいね\nできれば…\n今までよりも、私の近くで♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104601, 0, "タマキ", 0, 1, 5, 80001, 0, "", 40, 0, "振り返れば、今までいろんなことが\nあったにゃあ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104601, 0, "タマキ", 0, 2, 5, 80001, 0, "", 41, 0, "これからも、二人でたくさんの\n思い出をがっぽり作っていく\nにゃー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104801, 1, "ミフユ", 0, 1, 5, 80001, 0, "", 42, 0, "キミとの師弟関係も、\nもう1年なのね…\nでもキミに教えることは、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104801, 1, "ミフユ", 0, 2, 5, 80001, 0, "", 43, 0, "まだまだいっぱいあるわ\nこれからも、\nしっかりついて来てね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103201, 0, "アキノ", 0, 1, 6, 80001, 0, "", 44, 0, "あなたさまと出会ってもう1年…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103201, 0, "アキノ", 0, 2, 6, 80001, 0, "", 45, 0, "光陰矢のごとし、とはまさに\nこのことですわ。…思い出は決して\nお金では買えませんわね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103401, 1, "ユカリ", 0, 1, 6, 80001, 0, "", 46, 0, "ダメな私を1年間も見捨てないで\nくれて、本当にありがとう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103401, 1, "ユカリ", 0, 2, 6, 80001, 0, "", 47, 0, "情けないところいっぱいみせるかも\nしれないけど、これからも一緒に\nいてくれると、うれしいな");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (107101, 0, "クリスティーナ", 0, 1, 7, 80001, 0, "", 48, 0, "オマエと出会ってもう1年か\nまさかこのワタシの結婚相手に\nなろうとはな…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (107101, 0, "クリスティーナ", 0, 2, 7, 80001, 0, "", 49, 0, "ともかくこれからも\n愉しませてくれよ☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104701, 1, "ジュン", 0, 1, 7, 80001, 0, "", 50, 0, "君と出会ってからもう1年か。\n以前と比べて私の表情もいくらか\n柔らかくなった気がするんだが、\nどうだろう？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104701, 1, "ジュン", 0, 2, 7, 80001, 0, "", 51, 0, "見えないから分からないって？\nそれもそうだな…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103701, 0, "トモ", 0, 1, 8, 80001, 0, "", 52, 0, "もう1年たったみたいだね\nあなたをからかってばかりだった\nけど、楽しかった♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103701, 0, "トモ", 0, 2, 8, 80001, 0, "", 53, 0, "これからもよろしくね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100501, 1, "マツリ", 0, 1, 8, 80001, 0, "", 54, 0, "出会って、もう1年たっちゃった\nんスね。なんだか意外ッス");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100501, 1, "マツリ", 0, 2, 8, 80001, 0, "", 55, 0, "毎日楽しくて、あっという間に過ぎ\nちゃったみたいなんスよね\nこれからも一緒に悪と戦うッス！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100701, 0, "ミヤコ", 0, 1, 9, 80001, 0, "", 56, 0, "オマエと会って1年なの！\n全然成仏もできなかったし、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100701, 0, "ミヤコ", 0, 2, 9, 80001, 0, "", 57, 0, "生き返りもしなかったけど、\nすごく楽しかったの～♪\nオマエとプリンがあれば、\nミヤコはハッピーなの～♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103101, 1, "シノブ", 0, 1, 9, 80001, 0, "", 58, 0, "あなたと出会って1年なんですね\nでも、私の占いでは、私とあなたは");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (103101, 1, "シノブ", 0, 2, 9, 80001, 0, "", 59, 0, "来年も再来年も一緒にいるようです\nふふ、よろしくお願いしますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100901, 0, "アンナ", 0, 1, 10, 80001, 0, "", 60, 0, "貴様が閃光のシグルドとして覚醒し\nもう1年か…。これからも我らの\n長き戦いに終わりはないが、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100901, 0, "アンナ", 0, 2, 10, 80001, 0, "", 61, 0, "それでも貴様となら…\n添い遂げる覚悟はできているぞ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101301, 1, "ナナカ", 0, 1, 10, 80001, 0, "", 62, 0, "君と出会ってもう1年だって！\n早いねー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101301, 1, "ナナカ", 0, 2, 10, 80001, 0, "", 63, 0, "これからも最強のバディタッグで\n大活躍だー！\n頼りにしてるよ、相棒！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105101, 0, "ミツキ", 0, 1, 11, 80001, 0, "", 64, 0, "1年たつのって、あっという間ね\nまだまだ君の知らない部分が\nたくさんあるし…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105101, 0, "ミツキ", 0, 2, 11, 80001, 0, "", 65, 0, "これからも、いろんなところを\n調べさせてね？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105601, 1, "ルカ", 0, 1, 11, 80001, 0, "", 66, 0, "お前さんと出会ってもう1年か\n助太刀するつもりが");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105601, 1, "ルカ", 0, 2, 11, 80001, 0, "", 67, 0, "逆に助けられたりもしたね\nこれからもよろしく頼むよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102701, 2, "エリコ", 0, 1, 11, 80001, 0, "", 68, 0, "早いものですわね、あなた様と\nお会いして、もう1年…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102701, 2, "エリコ", 0, 2, 11, 80001, 0, "", 69, 0, "あなた様といると、時があっと\nいう間に過ぎてしまいます\n時間を止める研究をしなくては\nいけませんね…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102301, 0, "アヤネ", 0, 1, 12, 80001, 0, "", 70, 0, "お兄ちゃんと会ってから、もう、\n1年なんだね。私、少しは\nお姉ちゃんっぽくなったかな？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102301, 0, "アヤネ", 0, 2, 12, 80001, 0, "", 71, 0, "あ、ぷうきちもお兄ちゃんっぽく\nなったよね？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104201, 1, "チカ", 0, 1, 12, 80001, 0, "", 72, 0, "あなたと出会って、\nもう1年がたったんですね…\nこれからもそばにいて、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104201, 1, "チカ", 0, 2, 12, 80001, 0, "", 73, 0, "私のことを見守ってください\nできることなら、ずっと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102101, 2, "クルミ", 0, 1, 12, 80001, 0, "", 74, 0, "お兄ちゃんと出会って1年かぁ\n私、相変わらずダメダメで…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (102101, 2, "クルミ", 0, 2, 12, 80001, 0, "", 75, 0, "で、でもこれからもずっと…\n一緒にいたいなぁ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104901, 0, "シズル", 0, 1, 13, 80001, 0, "", 76, 0, "再会してちょうど1年だね\nお祝いにごちそう作ってきたよ～♪\n特製ケーキと、お寿司と、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (104901, 0, "シズル", 0, 2, 13, 80001, 0, "", 77, 0, "ハンバーグと、カレーと、\nシチューと、あ、隠しきれない隠し\n味は、お姉ちゃんの愛情だよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101101, 1, "リノ", 0, 1, 13, 80001, 0, "", 78, 0, "お兄ちゃんと再会して、もう1年\nなんですね。\n会えなかった時を思うと、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (101101, 1, "リノ", 0, 2, 13, 80001, 0, "", 79, 0, "信じられないくらい幸せな1年\nでした！もう絶対、私から離れ\nないでくださいね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100201, 0, "ユイ", 0, 1, 14, 80001, 0, "", 80, 0, "騎士クンと出会ってから今まで、\nいろんなことがあったよね\n楽しいこと、ドキドキしたこと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100201, 0, "ユイ", 0, 2, 14, 80001, 0, "", 81, 0, "そんな日々をこれからも、\nず、ずっと一緒に…\n過ごしていきたいな…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100101, 1, "ヒヨリ", 0, 1, 14, 80001, 0, "", 82, 0, "騎士くんと出会って、まだ1年しか\nたってないんだ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100101, 1, "ヒヨリ", 0, 2, 14, 80001, 0, "", 83, 0, "なんかもうずっと一緒にいる気が\nするなぁ…えへへ。これからの\n1年もまたよろしくねっ！\n騎士くん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100301, 2, "レイ", 0, 1, 14, 80001, 0, "", 84, 0, "初めてキミに出会って1年…\n随分見違えたね私もキミも、\n更に強くなっていく…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (100301, 2, "レイ", 0, 2, 14, 80001, 0, "", 85, 0, "キミに背中を預けられる日が、\n早くきますように");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 1, 15, 80001, 0, "", 86, 0, "1年もわたしのことを、忘れない\nでいてくれて、ありがとうござい\nます！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 2, 15, 80001, 0, "", 87, 0, "これからもずっと、覚えていて\nくださいね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 1, 15, 80001, 0, "", 88, 0, "こうして1年も、主さまのおそばに\n置いていただけて、とても光栄\nです。どうかこれからも、");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 2, 15, 80001, 0, "", 89, 0, "末永く主さまをお世話させて\nください。わたくしは、ずっと\n主さまのガイド役でいたいのです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 1, 15, 80001, 0, "", 90, 0, "1年か、まっさかこんなに長い\n付き合いになるなんてね～\nでも…わりと楽しい1年だったかも");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 2, 15, 80001, 0, "", 91, 0, "だから、もうしばらくあんたに\n付き合ってやるわよ喜びなさい！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (112101, 1, "スズメ", 1, 1, 0, 60001, 0, "", 92, 0, "新しい年のはじめに、\nおみくじはいかがでしょうか？\nよい運勢であるほど、\n沢山プレゼントが貰えますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 93, 1, "今日の運勢は姫吉！　あらゆる運気が最大を示しています！　　　今なら「伝説の吸血鬼」をも従え、世界征服も夢じゃありません！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 94, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 95, 1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 96, 1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60001, 0, "", 97, 1, "今日の運勢は姫吉！　その高い運気はまさにショーグンの如く！　その溢れる威光は人々を惹きつけ、皆の人気者間違いなしです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 98, 2, "今日の運勢は大吉！　運気がどんどん高まっています！　　　　　何をやっても、最大効率で素晴らしい結果になりますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 99, 2, "今日の運勢は大吉！　なにをやっても上手くいって大活躍！　　　そんな素敵なあなたを見て、後ろから誰かが……クスクス♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 100, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 101, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60001, 0, "", 102, 2, "今日の運勢は大吉！　運気が高く、何かに挑戦すると良い結果に！隠れた才能が引き出されそう！　目指せヒデサ……ではなく秀才！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 103, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたいやき！　和菓子パワーで売上アップです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 104, 3, "今日の運勢は中吉！　旅行運がぐんぐんと上昇中♪　　　　　　　もしかしたら、あの「マホマホ王国」にだって行けるかも♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 105, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 106, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 107, 3, "今日の運勢は中吉！　お出かけした先で良縁に恵まれそうです！　ダウナー系あざとい女子が、あま～い飴をくれるかも？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 108, 3, "今日の運勢は中吉！　大抵のことはゴリ押しでなんとかできます！ラッキープレイスはゲーセン。パンチングマシンでストレス発散！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60001, 0, "", 109, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかの如く、色々な事がうまくいきますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 110, 4, "今日の運勢は小吉！　早朝に幸運の兆しあり！　　　　　　　　　もしかしたら、優しいお姉ちゃんが起こしにきてくれますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 111, 4, "今日の運勢は小吉！　運気上昇中ですが、上がるまでが要注意！　ラッキーアニマルは熊！　本当の家族のように守ってくれるかも♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 112, 4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 113, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 114, 4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60001, 0, "", 115, 4, "今日の運勢は小吉！　思わぬ相手と仲良くなれるかも？　　　　　うっかり毒舌をもらしてしまわないように要注意です！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60001, 0, "", 116, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (112101, 3, "スズメ", 0, 2, 0, 60001, 0, "", 117, 0, "おみくじの結果はいかがでしたか？\n新たに始まる一日が、\nあなたにとってよい日になりますように\n明日もお待ちしていますね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (112101, 3, "スズメ", 0, 3, 7, 60001, 0, "", 118, 0, "おみくじ、楽しんで頂けましたか？\nあなたの新しい年が\n素晴らしいものになるよう、\n心からお祈りしますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 1, 1, 80003, 0, "", 119, 0, "ヒューヒュー♪\nドンドンパフパフー♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 1, 1, 80003, 0, "", 120, 0, "ひゅーひゅー\nどんどんぱふぱふー\nでございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 2, 1, 80003, 0, "", 121, 0, "さぁ、キャルさまもご一緒に\nどんどんぱふぱふいたしましょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 1, 1, 80003, 0, "", 122, 0, "え？　ちょっと\nいきなりなにっ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 2, 1, 80003, 0, "", 123, 0, "ドンドンパフパフってなんなの？\nあたしにも分かるように\nちゃんと説明しなさいっての！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 2, 1, 80003, 0, "", 124, 0, "実はですねキャルちゃん！\nもうすぐわたしたちにとっての\n大切な節目──");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 3, 1, 80003, 0, "", 125, 0, "『プリンセスコネクト！\nRe:Dive』\n2.5周年なんですよ！\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 3, 1, 80003, 0, "", 126, 0, "ですからその日に向けて\n今から気分を盛り上げているのです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 3, 1, 80003, 0, "", 127, 0, "ああ、そういえば\nそんな時期だっけ\nって言っても、もう少し先じゃない\n気が早いんじゃないの？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 4, 1, 80003, 0, "", 128, 0, "いいえ、キャルさま\nこういったお祝いは準備から\n楽しむものでございます\nそうですよね、ペコリーヌさま");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 4, 1, 80003, 0, "", 129, 0, "その通りです♪　というわけで！\nカウントダウンの始まりですよ！\nみんなで一緒に2.5周年に向けて\n盛り上がっていきましょー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 4, 1, 80003, 0, "", 130, 0, "ド、ドンドンパフパフ～\nって何言わせんのよ！\nまったくもう、浮かれちゃってさ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 5, 1, 80003, 0, "", 131, 0, "ふふ。キャルさまもワクワクして\nおいでのようですね\nそれでは主さま。大切な記念日まで\nどうぞご期待くださいませ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 5, 2, 80003, 0, "", 132, 0, "記念日を盛り上げるったって\n一体どうしたらいいのよ？\nあたし、そういうの\n苦手なんだけど…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 1, "カリン", 0, 1, 2, 80003, 0, "", 133, 0, "あら、キャルさん\nどうかなさいましたか？\n何かお困りのようですけど");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 6, 2, 80003, 0, "", 134, 0, "げっ…カリンじゃない\nなんでもないわよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 1, "カリン", 0, 2, 2, 80003, 0, "", 135, 0, "そうなのですか？\nキャルさんのことですからてっきり\n2.5周年をどう盛り上げたら\nいいか悩んでいるのかと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 7, 2, 80003, 0, "", 136, 0, "分かってんじゃないのよ！？\nいっ、いや、違うけどね！\nあたしは別にそんなことで\n悩んでないわよっ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 1, "カリン", 0, 3, 2, 80003, 0, "", 137, 0, "ふふっ…\nこういう時は、真心を込めて\n気持ちをそのまま口にしたら\nいいのではないでしょうか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 8, 2, 80003, 0, "", 138, 0, "気持ちを、ねぇ\nそれが一番難しいんじゃない…\nそもそも、あたしは記念日とか\n別にどうだっていいし");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 9, 2, 80003, 0, "", 139, 0, "…まあ、ペコリーヌもコロ助も\nやけに楽しそうだったから…\nこういうお祭り騒ぎみたいなのも\nたまにはいいかな、とは思うけど");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 1, "カリン", 0, 4, 2, 80003, 0, "", 140, 0, "なるほどなるほど～\nキャルさんは【美食殿】で祝う\n2.5周年を心から楽しみに\nしている、ということですね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 10, 2, 80003, 0, "", 141, 0, "はぁ！？\nどこ聞いたらそうなるのよ！\nあたしは別に楽しみになんか…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 1, "カリン", 0, 5, 2, 80003, 0, "", 142, 0, "キャルさんの心からの想い\nみなさんにも伝わったでしょうか？\n私も2.5周年が楽しみです！\n以上、カリンがお伝えしました！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 11, 2, 80003, 0, "", 143, 0, "心からの想いって何なのよ！？\n勝手に人の言葉を\n捻じ曲げんじゃないわよ！\nこのくそ眼鏡ーーーっ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 6, 3, 80003, 0, "", 144, 0, "ペコリーヌさま、もうすぐ\n記念日でございますね\nこういった節目というのは\n感慨深いものでございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 5, 3, 80003, 0, "", 145, 0, "色んな思い出が蘇ってきますね！\n楽しい出来事が\nいっぱいありました♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 7, 3, 80003, 0, "", 146, 0, "はい、そうでございますね\n主さまも【美食殿】の活動の他にも\nいろんなご経験をされたと\n聞いておりますよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 8, 3, 80003, 0, "", 147, 0, "たしか…ランドソルで話題沸騰と\nなっていた、魔法少女なるものに\nお会いになられたとか");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 6, 3, 80003, 0, "", 148, 0, "魔法少女って、あの美味しくって\n栄養たっぷりの大ヒット商品\n『魔法少女ソーセージ』の箱に\n描いてある女の子のことですか！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 7, 3, 80003, 0, "", 149, 0, "あのソーセージ\n美味しいんですよね～♪\nわたしもたっくさん食べてますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 9, 3, 80003, 0, "", 150, 0, "あとは、牧場に素敵な温泉が\n出来たとか…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 8, 3, 80003, 0, "", 151, 0, "牧場ですか！？　温泉につかって\nチーズもオツですね～♪　考えた\nだけでよだれが出ちゃいそうです！\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 10, 3, 80003, 0, "", 152, 0, "ペコリーヌさま\n言ってるそばからよだれが…\n今お拭きしますね。ふき、ふき");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 9, 3, 80003, 0, "", 153, 0, "えへへ、ありがとうございます\nコッコロちゃん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 11, 3, 80003, 0, "", 154, 0, "それにしても、主さまは\n【美食殿】の一員として様々な\n美食と出会われていたのですね\nわたくし、感服いたしました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 1, "ペコリーヌ", 0, 10, 3, 80003, 0, "", 155, 0, "これからもたくさん冒険をして\nめいっぱい美食を\n味わい尽くしましょうね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 11, 4, 80003, 0, "", 156, 0, "2.5周年カウントダウン♪\nキャルちゃんキャルちゃん！\nわくわくしますね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 12, 4, 80003, 0, "", 157, 0, "あぁもう、うるさいわね～\nまだまだ先でしょ？\nちょっとは落ち着きなさいよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 12, 4, 80003, 0, "", 158, 0, "キャルちゃんだって最近\nソワソワしてるじゃないですか～\nさっきもご機嫌そうに鼻歌とか\n歌ってましたよね？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 13, 4, 80003, 0, "", 159, 0, "なっ…！　き、聞いてたの！？\nじゃない、歌ってないわよ！？\nあんたいい加減なこと言うと\nぶっ殺すわよ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 13, 4, 80003, 0, "", 160, 0, "えっへへ～♪\nわたしは最近のキャルちゃんが\nもっともっと大好きですよ♪\nすごく積極的になりましたよね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 14, 4, 80003, 0, "", 161, 0, "…積極的？\nなんの話よ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 14, 4, 80003, 0, "", 162, 0, "またまた～♪\nアイドルを目指そうとしたこと\n忘れちゃったんですか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 15, 4, 80003, 0, "", 163, 0, "ムーサの町で\nきゃるきゃる～ん☆　って！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 15, 4, 80003, 0, "", 164, 0, "あっ…！？　ちちち違うわよっ！\nあれは操られてただけで\nあたしはアイドルなんか\n目指してないっての！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 16, 4, 80003, 0, "", 165, 0, "本当に本当ですか？\nわたし、キャルちゃんとっても\n乗り気だなって\n思ってたんですけど～");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 16, 4, 80003, 0, "", 166, 0, "まぁ、悪くはなかったけど…って\nだ、だから目指してないって\n言ってるでしょ！！\nなんてこと思い出させんのよ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 17, 4, 80003, 0, "", 167, 0, "ああ…ダメだわ…\nもう殺すしかない…\n2.5周年が来る前に\n記憶ごと消し去ってやるー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 18, 5, 80003, 0, "", 168, 0, "2.5周年まであと10日…\nふ～ん？\n案外早いもんね？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 12, 5, 80003, 0, "", 169, 0, "そうでございますね…\nその日のためにやるべきことは\nまだまだございます\n気を引き締めねばなりません…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 19, 5, 80003, 0, "", 170, 0, "やる気十分ねコロ助…\nそんな気負ってるとしんどいわよ？\nお気楽にやりましょ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 13, 5, 80003, 0, "", 171, 0, "キャルさま…\nありがとうございます\nキャルさまは本当に\n優しいおかたです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 20, 5, 80003, 0, "", 172, 0, "な、なにわけの分かんないこと\n言ってんのよ…\nあたしは優しくなんかないっての");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 14, 5, 80003, 0, "", 173, 0, "そんなことはございません\n…あの、実はわたくし\nキャルさまに\nひとつお願いがございまして");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 21, 5, 80003, 0, "", 174, 0, "お願い？　珍しいわね\nコロ助がそんなこと言うなんて\n…まあ、あたしに出来ることなら\n別にいいけど");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 15, 5, 80003, 0, "", 175, 0, "ありがとうございます\n実は記念日へ向けて\n『かくし芸』というものを\n用意してみようかと思いまして");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 22, 5, 80003, 0, "", 176, 0, "かくし芸？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 16, 5, 80003, 0, "", 177, 0, "はい。きゃるきゃる～ん☆　を\n参考に、ころころ～ん☆　という\nご挨拶を考えてみたのですが\nポーズがうまくいかなくて…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 23, 5, 80003, 0, "", 178, 0, "～～！！　ちょっと褒められて\nいい気になって損したわ！！\nふざけんなふざけんな\nふ～ざ～け～ん～な～っ！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 17, 6, 80003, 0, "", 179, 0, "あと９日！　もうすぐですよ♪\nえへへ、楽しみで楽しみで\nごはんが進みますね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 24, 6, 80003, 0, "", 180, 0, "ごはんが進みますね☆\nじゃなーい！\nいくらなんでも食べ過ぎよ！\n記念日、迎えられなくなるわよ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 17, 6, 80003, 0, "", 181, 0, "大丈夫ですよキャルさま\n食事はわたくしたちの\n歩んできた道そのもの\nなのですから");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 18, 6, 80003, 0, "", 182, 0, "さっすがコッコロちゃん\nいいことを言いますね！\nそう、わたしたち\n【美食殿】の活動目的は─");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 25, 6, 80003, 0, "", 183, 0, "─みんなで楽しく食事をすること\nでしょ？\nあれだけ食べ歩いてきたんだから\n分かってるわよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 18, 6, 80003, 0, "", 184, 0, "はい…そうでございますね♪\nそれにしてもわたくしたち\n本当にいろいろなものを\n食べ歩いてきましたね…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 26, 6, 80003, 0, "", 185, 0, "そうね…ほんっとうにね…\n毒まみれのニセドラゴンとか\n恐竜…は逃げられちゃったっけ\nあとワニゴ…ワニの魔物とかね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 27, 6, 80003, 0, "", 186, 0, "そして、とうとう本物の\nドラゴンまで相手に\nしちゃったのよね、あたしたちって");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 28, 6, 80003, 0, "", 187, 0, "信じられない強さだったわね\nあのドラゴン…やっぱり\n作戦はしっかり立てていかないと\nダメよね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 19, 6, 80003, 0, "", 188, 0, "えへへ…☆\nキャルちゃんが色んなことを\n覚えていてくれて\nうれしいです♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 19, 6, 80003, 0, "", 189, 0, "わたくしも鮮明に覚えておりますよ\n主さまもきっと…♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 29, 6, 80003, 0, "", 190, 0, "あったり前でしょ！\n忘れないわよ、あんな滅茶苦茶な\nものばかり食べさせられて！\n…忘れられるわけ、ないじゃない");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 30, 7, 80003, 0, "", 191, 0, "ねえちょっと、ちょっと待って！？\nカリンから、2.5周年記念の\n生放送に出演してほしいって\n言われたんだけど！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 31, 7, 80003, 0, "", 192, 0, "なんでそういう大事なことを\n急に言うわけ！？\nそんなの聞いてないわよ～！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 32, 7, 80003, 0, "", 193, 0, "いっぱい人に見られちゃうのよね？\nなんであたしがそんなこと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 33, 7, 80003, 0, "", 194, 0, "あー\nあー\n…声、これで大丈夫かしら？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 34, 7, 80003, 0, "", 195, 0, "聞き取りにくいかもしれないわね…\nもうちょっと明るい方がいいかも\nえっと…こんな感じ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 35, 7, 80003, 0, "", 196, 0, "オイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 36, 7, 80003, 0, "", 197, 0, "……\nいや、ないわ\nいつもどおりが一番ね、うん");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 37, 7, 80003, 0, "", 198, 0, "もう、記念の生放送なんて\n超責任重大なやつじゃない…\nもしなにか失敗とかしたら…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 38, 7, 80003, 0, "", 199, 0, "ど、どど、どうしよう！？\n緊張してきちゃった…！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 20, 8, 80003, 0, "", 200, 0, "主さま、わたくし\nペコリーヌさまとキャルさまと共に\n2.5周年を祝う生放送に\n出演する大役を仰せつかりました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 21, 8, 80003, 0, "", 201, 0, "多くの方々に\n『プリンセスコネクト！\nRe:Dive』の魅力をお伝えする\n重要な役割でございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 22, 8, 80003, 0, "", 202, 0, "わたくし、全身全霊をもって\n生放送に取り組みたいと\n思っております");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 23, 8, 80003, 0, "", 203, 0, "ですが、もしかしたら\nセリフを噛んでしまったり\n段取りを間違えてしまったり\nするかもしれません…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 24, 8, 80003, 0, "", 204, 0, "そのようなことがないよう\n練習はしっかりしておかなくては\nまずはこちらから…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 25, 8, 80003, 0, "", 205, 0, "オイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 26, 8, 80003, 0, "", 206, 0, "先ほどキャルさまが練習されて\nいるのをお見かけしたので\nわたくしもと思いまして…\nいかがでしょうか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 27, 8, 80003, 0, "", 207, 0, "良い感じ、ですか？\n…ふふ、ありがとうございます\n主さまのあたたかいお言葉があれば\nわたくしは百人力でございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 28, 8, 80003, 0, "", 208, 0, "この大役を無事に成し遂げ\n主さまに素敵な2.5周年を\n迎えていただけるよう…\nわたくし、尽力いたしますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 20, 9, 80003, 0, "", 209, 0, "オイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 21, 9, 80003, 0, "", 210, 0, "…う～ん、今のはちょっと\n違う感じがしますね\n堅苦しかったかもしれません");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 22, 9, 80003, 0, "", 211, 0, "あと６日で2.5周年ですか…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 23, 9, 80003, 0, "", 212, 0, "この年月がオイッスーの\n歴史なんだと思うと\nじ～んとしちゃいます！\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 24, 9, 80003, 0, "", 213, 0, "生放送で最高のオイッスー☆　を\nみなさんにお届けするために\n頑張らないと！\nそれじゃあもう一度…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 25, 9, 80003, 0, "", 214, 0, "オイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 26, 9, 80003, 0, "", 215, 0, "…うう～ん、どうしてでしょう\nまだ何かが違う気がします…\nもしかしたら\nおなかが空きすぎてるとか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 27, 9, 80003, 0, "", 216, 0, "今さっき食べたばっかりですけど\nおなかが減っては\n良いオイッスー☆　は\n出せませんよね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 28, 9, 80003, 0, "", 217, 0, "そうと決まれば、コッコロちゃんと\nキャルちゃんを誘って\nごはんにしましょう！\n善は急げです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 29, 9, 80003, 0, "", 218, 0, "あ、二人が丁度いいところに！\nオイッス～☆　…ああ！！\n今の、今のですよ～♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 30, 9, 80003, 0, "", 219, 0, "なるほど、どんな時も\n自然体が一番ってことですね！\nこの調子で、生放送も\n頑張っちゃいます♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 31, 10, 80003, 0, "", 220, 0, "2.5周年まで残り５日ですが\nその前にもうすぐ生放送ですよ！\nコッコロちゃん\n準備の方はどうですか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 29, 10, 80003, 0, "", 221, 0, "はい。わたくし、しっかりと\n備えてまいりました\n万事抜かりはございません");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 32, 10, 80003, 0, "", 222, 0, "おお～すごい自信を感じます…！\nいい生放送になりそうですね☆\n……あれ？　なんだかこの辺り\n揺れていませんか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 39, 10, 80003, 0, "", 223, 0, "…………");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 30, 10, 80003, 0, "", 224, 0, "キャルさま？\nお風邪をひかれたんでしょうか\nひどい震えですが…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 40, 10, 80003, 0, "", 225, 0, "かかかか風邪じゃないわよ！\nここここれはただ\nななな生放送が楽しみ過ぎて\nワワワワクワクが止まらないだけ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 33, 10, 80003, 0, "", 226, 0, "大丈夫ですかキャルちゃん！？\nそうだ、これを飲んでください！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 41, 10, 80003, 0, "", 227, 0, "きき気がきくじゃない、ありがとっ\nごくっ、んく…ぶはぁっ！？\nちょっと何飲ませたの！？\n辛っ！　苦ぁ！？　甘ぁっ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 34, 10, 80003, 0, "", 228, 0, "これは魔物のエキスに薬草に\nその他色んなものを混ぜた\n特製の栄養ドリンクです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 35, 10, 80003, 0, "", 229, 0, "これを飲めば体ぽかぽかで\n元気いっぱいですよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 42, 10, 80003, 0, "", 230, 0, "このアホリーヌ！\n風邪じゃないって言ったでしょ！？\nどさくさに紛れて\nそんな恐ろしいもん飲ませんな～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 31, 10, 80003, 0, "", 231, 0, "キャルさま\n震えが収まっておりますね\nよかったです…\nわたくし、心配いたしました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 43, 10, 80003, 0, "", 232, 0, "え？　ああ、ホントね…って\nだから風邪じゃないって\n言ってるのに…\nまあいいわこの際なんでも");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 36, 10, 80003, 0, "", 233, 0, "えっへへ～♪　いつも通りの\nキャルちゃんに戻りましたし\n素敵な生放送が出来そうですね！\n今から楽しみです☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 6, 11, 80003, 0, "", 234, 0, "ランドソルのみなさま、こんにちは\n生放送の会場設営を担当しています\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 7, 11, 80003, 0, "", 235, 0, "【美食殿】のみなさんは\nまもなく始まる生放送に向けて\n最後の調整のため\n席を外しています");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 8, 11, 80003, 0, "", 236, 0, "頑張っている３人の\n邪魔をするわけには\nいきませんので、ご容赦ください");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 9, 11, 80003, 0, "", 237, 0, "ですので、本日は私から\n【美食殿】のみなさんの様子を\nお伝えさせていただこうと思います");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 10, 11, 80003, 0, "", 238, 0, "まずはペコリーヌさんですが…\n何やら大量のおにぎりを\n食べているようです\nえっと…栄養は大事ですよね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 11, 11, 80003, 0, "", 239, 0, "次にコッコロさんですが\nこちらは発声練習をしています");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 12, 11, 80003, 0, "", 240, 0, "一生懸命、小さなお口を開けて\n声を出す姿が応援したくなりますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 13, 11, 80003, 0, "", 241, 0, "そしてキャルさんは…\nこれは…堂々としたものですね\n何もせず、ただ立ち尽くして…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 14, 11, 80003, 0, "", 242, 0, "あれ？\nもしかして緊張で気を失って…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 15, 11, 80003, 0, "", 243, 0, "…ええと\n【美食殿】のみなさんは\nやる気十分なようですね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 16, 11, 80003, 0, "", 244, 0, "３人とも生放送を\nいいものにしようと\n全力で頑張ってくれていますので\nどうかご期待ください");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 17, 11, 80003, 0, "", 245, 0, "それでは\nまた明日お会いしましょう♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 44, 12, 80003, 0, "", 246, 0, "2.5周年まであと３日！\nそして生放送は明日！\nなんかいよいよって感じね…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 2, "ペコリーヌ", 0, 37, 12, 80003, 0, "", 247, 0, "３人でい～っぱい練習しましたし\nきっと大丈夫ですよ☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 32, 12, 80003, 0, "", 248, 0, "ペコリーヌさまに\n大丈夫と言ってもらえると\nとても心強いですね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 45, 12, 80003, 0, "", 249, 0, "信じすぎちゃだめよコロ助\nこいつはなーんにも\n考えてないだけなんだから");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 2, "ペコリーヌ", 0, 38, 12, 80003, 0, "", 250, 0, "ひどいですよぉキャルちゃ～ん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 33, 12, 80003, 0, "", 251, 0, "ふふ。お二人とも\n普段通りの様子で\n安心いたしました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 2, "ペコリーヌ", 0, 39, 12, 80003, 0, "", 252, 0, "はいっ☆\n生放送でもなんでも\n全力全開、わたしらしく\nぶつかっていくだけです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 46, 12, 80003, 0, "", 253, 0, "そうね。おめでたいことなんだし\nここまで来たんなら\n楽しめるだけ\n楽しむしかないわよね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 34, 12, 80003, 0, "", 254, 0, "わたくしも、普段よりも心が\n熱くなっております\nこの胸の気持ちをお伝えできるよう\n精一杯努力いたします…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 2, "ペコリーヌ", 0, 40, 12, 80003, 0, "", 255, 0, "それでは、いきましょう！\n明日の生放送、絶対に\n成功させましょうね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 41, 13, 80003, 0, "", 256, 0, "えっと、最初のひと言は…あれ？\nキャルちゃん、わたし\n何て言うんでしたっけ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 47, 13, 80003, 0, "", 257, 0, "は！？\nなにいきなりど忘れしてるの！？\nっていうかあんた、台本は！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 42, 13, 80003, 0, "", 258, 0, "忘れてきちゃいました☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 48, 13, 80003, 0, "", 259, 0, "忘れてきちゃいました☆\nで済ませられると思ってんの！？\nほら、あたし二冊持ってきたから\nこれ使いなさい！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 35, 13, 80003, 0, "", 260, 0, "マイクとは、こんな高い位置に\nあるものなのですね…！\n背伸びをすれば、何とか…\nう～ん、うぅ～ん…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 49, 13, 80003, 0, "", 261, 0, "あぁもう\n少し下げればいいでしょ！？\nこれくらいでいい？\nまったく、世話が焼けるわね…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 43, 13, 80003, 0, "", 262, 0, "あれ？\nキャルちゃん、後ろ髪が\nはねちゃってますよ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 50, 13, 80003, 0, "", 263, 0, "え？　うそっ！？\nちゃんと確かめてきたのに！\nどこどこ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 36, 13, 80003, 0, "", 264, 0, "キャルさま、動かないで\nくださいまし。今くしで…\nはい、直りましたよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 51, 13, 80003, 0, "", 265, 0, "あ、ありがと…はぁ。生放送\n本番までもうすぐなのに\nこんなことしてる場合じゃ\nないわよね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 37, 13, 80003, 0, "", 266, 0, "はい、時間は残りわずかです\nですが、慌ただしく\n動き回るこの時間も\nなんだか楽しくて…♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 52, 13, 80003, 0, "", 267, 0, "コロ助、あんた…って\nそうじゃなくて！\n胸にジンワリきてる\n暇なんかないでしょ…！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 44, 13, 80003, 0, "", 268, 0, "あはは♪\nキャルちゃんの動きが\nとっても面白いです♪\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 38, 13, 80003, 0, "", 269, 0, "ふふ\nきっと面白い生放送になると\nわたくし、確信しております");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 45, 13, 80003, 0, "", 270, 0, "絶対絶対、間違いありません！\n見てくれるみなさんが\n笑顔になれる時間を\nお届けしましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 53, 13, 80003, 0, "", 271, 0, "そんなの当然でしょ！\n誰が見たって面白いって\n言ってもらえるように\nあれだけ準備したんだから！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 46, 13, 80003, 0, "", 272, 0, "それでは\n記念日直前生放送！\n楽しみにしててくださいね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 18, 14, 80003, 0, "", 273, 0, "こんにちは\n【ギルド管理協会】のカリンです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 19, 14, 80003, 0, "", 274, 0, "昨日の生放送は\nいかがだったでしょうか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 20, 14, 80003, 0, "", 275, 0, "たくさんの笑顔と喜びと\nそして驚きを受け取って\nいただけたのではないかと思います");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 21, 14, 80003, 0, "", 276, 0, "そしていよいよ\n明日は2.5周年の当日\n待ちに待った記念日を\n迎えることとなります");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 22, 14, 80003, 0, "", 277, 0, "【美食殿】のみなさんも\n張り切って様々な準備を\nされていますよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 23, 14, 80003, 0, "", 278, 0, "【ギルド管理協会】も\n万全のサポート体制で\n協力してまいりたいと思います");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 24, 14, 80003, 0, "", 279, 0, "…ふふ、本当に楽しみですね\n【ギルド管理協会】の職員として\nだけではなく、私個人としても…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 25, 14, 80003, 0, "", 280, 0, "この記念日を迎えられることが\n心から楽しみですし\n幸せを感じています");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 26, 14, 80003, 0, "", 281, 0, "きっと【美食殿】のみなさんは\nそれ以上に心を弾ませて\nいることでしょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 0, "カリン", 0, 27, 14, 80003, 0, "", 282, 0, "それでは\n明日からの2.5周年を\nどうぞ、お楽しみに…♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 47, 15, 80003, 0, "", 283, 0, "準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 54, 15, 80003, 0, "", 284, 0, "祝！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 39, 15, 80003, 0, "", 285, 0, "2.5周年！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 48, 15, 80003, 0, "", 286, 0, "おめでとうございま～す☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 55, 15, 80003, 0, "", 287, 0, "少し前まではまだまだ先じゃない\nとか言ってたけど\n本当にあっという間だったわね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 40, 15, 80003, 0, "", 288, 0, "光陰矢の如し…\nとはこのことでございますね\nでも、今日という日を無事に\n迎えることができて良かったです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 49, 15, 80003, 0, "", 289, 0, "コッコロちゃん！　キャルちゃん！\n落ち着くには\nまだ早いですよ！\n盛り上がるのはこれからです☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 56, 15, 80003, 0, "", 290, 0, "ほんとあんたはいつも\n全力全開ねー\nでもそうね、記念日なんだし\nパーっといきましょ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 41, 15, 80003, 0, "", 291, 0, "ええ、わたくしも全力全開で\nまいります…！\n主さま、さあ\nどうぞ手を取ってくださいまし…♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 57, 15, 80003, 0, "", 292, 0, "あんたもコロ助も、浮かれて\n転ぶんじゃないわよ？\n…仕方ないから、あたしも\n手を繋いであげるわよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 50, 15, 80003, 0, "", 293, 0, "あー！　二人ともずるいです！\nわたしもっ、ギュー♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 58, 15, 80003, 0, "", 294, 0, "ちょっ、ペコリーヌ！\nくっつき過ぎよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 51, 15, 80003, 0, "", 295, 0, "記念日だから\n平気平気♪\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 59, 15, 80003, 0, "", 296, 0, "意味分かんないわよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 42, 15, 80003, 0, "", 297, 0, "ふふ。このめでたき日に\n喜びがあふれておりますね\n主さまは…いかがでしょうか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 43, 15, 80003, 0, "", 298, 0, "喜んでいただけているならば\nわたくしはとてもとても\n幸せでございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 52, 15, 80003, 0, "", 299, 0, "さぁ、2.5周年を\n楽しみ尽くしますよ☆\nまだまだ食べたいものもありますし\nやりたいこともたくさんです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 2, "キャル", 0, 60, 15, 80003, 0, "", 300, 0, "分かった、分かったから\n引っ張るんじゃないわよ！\n…そんな掴まなくても\nちゃんとついていくから");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 44, 15, 80003, 0, "", 301, 0, "ペコリーヌさま…キャルさま…\n主さま…共にまいりましょう\nどこまでも…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 53, 15, 80003, 0, "", 302, 0, "はい！　それでは2.5周年\n食べつくしツアーへ！\nそして、その先の未来へ\n向かって出発しましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ・コッコロ・キャル", 0, 54, 15, 80003, 1, "105901,45,106001,61", 303, 0, "2.5周年\nありがとうございます！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 1, "カリン", 1, 1, 0, 60002, 0, "", 304, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 305, 1, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 306, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 307, 1, "今日の運勢は姫吉！　最強の運勢でどんなドジも跳ね除けます！　大切な人と一緒に、最高の一日になること間違いなし！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 308, 1, "今日の運勢は姫吉！　どんな挑戦も上手くいく、最高の運勢です！理想の自分に変身できる絶好の機会！　新しい一歩を踏み出そう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60002, 0, "", 309, 1, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 310, 2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 311, 2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 312, 2, "今日の運勢は大吉！　ラッキーワードは「ちぇる～ん♪」　　　　出会う人みんなに言ってあげると、ハッピーになれますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 313, 2, "今日の運勢は大吉！　運勢はバッチリで無敵状態！　　　　　　　龍眼で全てを見通して、思い通りの一日を過ごしましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60002, 0, "", 314, 2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 315, 3, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 316, 3, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 317, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 318, 3, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 319, 3, "今日の運勢は中吉！　商売運が上がってきています！　　　　　　ラッキーアイテムはたい焼き！　和菓子パワーで売上アップです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 320, 3, "今日の運勢は中吉！　チャンスを待つと良い結果に！　　　　　　ラッキーアイテムのプリンを食べて、運を呼び込みましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60002, 0, "", 321, 3, "今日の運勢は中吉！　運気が着々と上昇中！　　　　　　　　　　精霊の加護を受けたかのごとく、色々な事がうまくいきますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 322, 4, "今日の運勢は小吉！　まだ見ぬ危険が隠れているかも！　　　　　でも安心しろ！　提督が貴公を守ってくれるはずだぞ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 323, 4, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 324, 4, "今日の運勢は小吉！　気の向くままに過ごすのが幸せへの近道！　美味い魚を食べて猫を眺めて、自然体で過ごすのはどうだい？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 325, 4, "今日の運勢は小吉！　運気は高いけども、ハプニングには注意！　出かけた先で、「ふしんしゃ」扱いされてしまうかも……");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 326, 4, "今日の運勢は小吉！　同腹一心の友だちが見つかるかも！　　　　キーワードは「ユニちゃんズ」、記憶に刻みましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60002, 0, "", 327, 4, "今日の運勢は小吉！　周りに流されず、マイペースな一日を！　　ただし寝すぎには注意ぐうたらし過ぎず適度な運動を、だってさ～");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60002, 0, "", 328, 4, "今日の運勢は小吉！　根を詰めずにリラックスすると運気向上！　ラッキーパーソンはアイドル。歌があなたを幸せにしてくれます♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 2, 0, 60002, 0, "", 329, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 3, 7, 60002, 0, "", 330, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 1, 1, 80005, 0, "", 331, 0, "ささ、シェフィさまはこちらへ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 1, 1, 80005, 0, "", 332, 0, "コッコロさん、どこへ行くの…？\n…え？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 1, 1, 80005, 0, "", 333, 0, "『おめでとうございます！\nわ～パチパチー！』\n…という感じでどうですかね！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 2, 1, 80005, 0, "", 334, 0, "ではわたくしも\n後ろからクラッカーを鳴らして\n応戦いたします\nああ、当日が待ち遠しいですね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 2, 1, 80005, 0, "", 335, 0, "ええと…\n二人とも何をしているの？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 1, 1, 80005, 0, "", 336, 0, "シェフィは初めてだもんね\nあの日が近いからって\n予行練習してんのよ\n気が早いわよね～");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 3, 1, 80005, 0, "", 337, 0, "あの日…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 2, 1, 80005, 0, "", 338, 0, "その通りです…\nいよいよ記念すべきあの日が\n近づいてきましたよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 3, 1, 80005, 0, "", 339, 0, "『プリンセスコネクト！\nRe:Dive』3.5周年！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 1, 1, 80005, 0, "", 340, 0, "やばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 2, 1, 80005, 0, "", 341, 0, "って、なにしれっと\n参加してんのよカリン！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 2, 1, 80005, 0, "", 342, 0, "ふふ、いいじゃないですか\n【ギルド管理協会】として\n私もみなさんと一緒に\nお祝いをさせてください！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 3, 1, 80005, 0, "", 343, 0, "それに…記念日が近づけば\nとっておきの情報を\nお届けしますから♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 4, 1, 80005, 0, "", 344, 0, "というわけで\n今日からシェフィちゃんも一緒に\nカウントダウンしちゃいましょう！\n3.5周年まで…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 4, 1, 80005, 0, "", 345, 0, "えっ！？　ええとええと…！\nあ、あと14日…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 5, 2, 80005, 0, "", 346, 0, "3.5周年まであと13日\n豪華に、そして盛大に\nお祝いしたいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 3, 2, 80005, 0, "", 347, 0, "豪華といえば前にとある貴族さまが\n舞踏会を開かれたそうで\n主さまがそのお手伝いを\nしておりました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 4, 2, 80005, 0, "", 348, 0, "そこで美しい所作を学ばれたので\nわたくしも教えていただきました\nしゃなり、しゃなり…\nいかがでしょう？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 6, 2, 80005, 0, "", 349, 0, "すっごく上手ですよ☆\nまるでお姫様みたいです\nせっかくなのでわたしも\nしゃなりしゃなり");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 5, 2, 80005, 0, "", 350, 0, "さすがでございます\nペコリーヌさま");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 3, 2, 80005, 0, "", 351, 0, "いや、あんたは\nできなきゃダメでしょ\n本物のお姫様なんだし");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 5, 2, 80005, 0, "", 352, 0, "しゃなり、しゃなり…\nふふ、案外できるものね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 7, 2, 80005, 0, "", 353, 0, "わぁ！\nシェフィちゃん\n姿勢がとってもキレイです♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 6, 2, 80005, 0, "", 354, 0, "さ、次はキャルさまの\n番でございますよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 4, 2, 80005, 0, "", 355, 0, "あ、あたしはいいわよ！\nそういうの全然分かんないし…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 6, 2, 80005, 0, "", 356, 0, "大丈夫\nやってみると結構楽しいわよ\nほらほらっ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 5, 2, 80005, 0, "", 357, 0, "そ、そこまで言うなら…\nしゃなにゅわきゃあっ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 6, 2, 80005, 0, "", 358, 0, "いたたた…もぉお～！\nなんであたしがこんな目に～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 7, 2, 80005, 0, "", 359, 0, "まぁまぁ\n記念日までに練習すれば\nできるようになるかもしれません\nよろしければお教えいたしますよ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 7, 2, 80005, 0, "", 360, 0, "よし！\nあんたたちができて\nあたしだけできないのはしゃくだし\nやってやろうじゃないの！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 8, 3, 80005, 0, "", 361, 0, "3.5周年まであと12日\n記念日当日はもちろんですが\nその日を待つ時間というのも\n心躍ります");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 8, 3, 80005, 0, "", 362, 0, "分かりますっ！\nどんな特別な料理を食べられるのか\n考えるだけでワクワクしますよね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 9, 3, 80005, 0, "", 363, 0, "特別な料理といえば、お祭りですね\nそういえば主さまは学生として\n聖学祭に参加されたこと\nがあるとか…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 7, 3, 80005, 0, "", 364, 0, "聖学祭…って、なにかしら？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 10, 3, 80005, 0, "", 365, 0, "聖テレサ女学院の祭典ですよ\n二つのチームが競い合い\n青春を満喫するのが趣旨のようです");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 8, 3, 80005, 0, "", 366, 0, "それってカルミナの\nサプライズライブがあった\nお祭りよね？\nちょっと見たかったかも…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 9, 3, 80005, 0, "", 367, 0, "カルミナの後の\nライブも好評だったらしいじゃない\n確か、なかよし部\nだったっけ…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 9, 3, 80005, 0, "", 368, 0, "なかよし部…\n仲が良さそうな名前で\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 10, 3, 80005, 0, "", 369, 0, "いや、そのまんますぎでしょ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 8, 3, 80005, 0, "", 370, 0, "聖学祭かぁ…なんだか楽しそうね\nそれになかよし部の人たちも\n気になるわ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 10, 3, 80005, 0, "", 371, 0, "それじゃあわたしたちも、\n負けじと仲良くしましょう♪\nほらほらシェフィちゃん、\nぎゅーっ☆　ですよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 9, 3, 80005, 0, "", 372, 0, "えっ？　ええ…\nぎゅーっ…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 11, 4, 80005, 0, "", 373, 0, "3.5周年まであと11日！？\nお祝いの準備進んでないんだけど…\nああ～昨日に戻りたいわ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 10, 4, 80005, 0, "", 374, 0, "そういえばそんな魔道具があるって\n聞いたことがあるわね\n確か、名前はタイムラーベ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 12, 4, 80005, 0, "", 375, 0, "なにそれヤバいわね\nでも本当にあるんだったら\n準備よりももっとやり直したいこと\nたくさんあるんだけど…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 11, 4, 80005, 0, "", 376, 0, "もしや正月の件でしょうか…？\nあの時はお金がなくて\n苦労しましたから");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 13, 4, 80005, 0, "", 377, 0, "あれはそうね…\nお金を湯水のように使う\nぺコリーヌを止められたら\nあんな苦労をすることもなかったし");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 11, 4, 80005, 0, "", 378, 0, "そんなこともありましたね！\nやり直したいといえば\n『きゃるきゃる～ん♪』\nの方かと思いましたよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 14, 4, 80005, 0, "", 379, 0, "バラすんじゃないわよ\nこのアホリーヌ！\nシェフィはそれ\n知らないんだから！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 15, 4, 80005, 0, "", 380, 0, "こうなったらもう、なんとか\nタイムラーべを探し出して\n今の会話をなかったことに…\nって、どうしたのよシェフィ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 11, 4, 80005, 0, "", 381, 0, "うん…もし、私だったら…\n私が知らないみんなとの時間を\n見てみたいな、って…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 12, 4, 80005, 0, "", 382, 0, "シェフィちゃん！　タイムなんとか\nなんかなくても大丈夫ですよ！\n一緒の時間はこれから\nどんどん作ればいいんですから！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 12, 4, 80005, 0, "", 383, 0, "はい、今この瞬間も\n未来にはかけがえのない思い出に\nなっていることでしょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 16, 4, 80005, 0, "", 384, 0, "だからそんな顔しないの\nほら、お祝いの準備、進めるわよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 12, 4, 80005, 0, "", 385, 0, "みんな…そうね\n最高のお祝いにして\n最高の思い出にしましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 13, 5, 80005, 0, "", 386, 0, "3.5周年まで、あと10日！\nもうすぐですよー！\n楽しみすぎて、やばいですね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 14, 5, 80005, 0, "", 387, 0, "そういえば…たくさんのおもちゃが\n子どもたちを襲うっていう\n騒動もありましたね…\nなんでも勝手に動き出したとか！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 15, 5, 80005, 0, "", 388, 0, "学校や孤児院とかに\nちっちゃなおもちゃが乗り込んで\n暴れたみたいですよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 17, 5, 80005, 0, "", 389, 0, "暴れたって言っても\n所詮はおもちゃじゃない\nどうせちっさいんでしょ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 18, 5, 80005, 0, "", 390, 0, "だったら魔法で\n全部ぶっ飛ばせばいいじゃない！\n大したことないわよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 13, 5, 80005, 0, "", 391, 0, "キャルってば…\n相手は子どもが\n大事にしてる物なのよ…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 13, 5, 80005, 0, "", 392, 0, "乱暴な発言は\nめっ、でございますよ？\n子どもの教育に悪影響を\n与えてしまいますから…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 16, 5, 80005, 0, "", 393, 0, "キャルちゃんだって\n意外と猫グッズとか\nたっくさん集めてますよね？\nもしその子たちが暴れたら…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 19, 5, 80005, 0, "", 394, 0, "う、うっさいわね！\n意外とか言うな！\n…ま、まあ確かに正面切って\n魔法でぶっ飛ばせないかもね…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 17, 5, 80005, 0, "", 395, 0, "あはは☆\nキャルちゃんのそういうところ\n好きですよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 18, 5, 80005, 0, "", 396, 0, "そんなキャルちゃんに猫グッズを\nプレゼントしちゃいますね！\n一部で流行っていた、たい焼き屋の\nおまけフィギュアですよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 19, 5, 80005, 0, "", 397, 0, "乱暴に扱ったりしないでくださいね\nひとりでに暴れ出しちゃうかも\nしれませんから！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 4, 6, 80005, 0, "", 398, 0, "3.5周年まであと９日\nここでみなさんに\nビックニュースですよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 5, 6, 80005, 0, "", 399, 0, "3.5周年を祝う生放送に\n去年と同様【美食殿】の\nみなさんにご出演いただくことが\n決定しましたー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 20, 6, 80005, 0, "", 400, 0, "…えっ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 20, 6, 80005, 0, "", 401, 0, "本当ですか！？\nそれじゃ今から張り切って\n生放送中に食べる魔物料理を\n作らなくちゃですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 14, 6, 80005, 0, "", 402, 0, "いや…がんばるところは\nそこじゃないような…\nというか去年も出演してたのね\nすごいじゃない！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 14, 6, 80005, 0, "", 403, 0, "はい。とても緊張しますが\nやりがいのある大役です\n一緒にがんばりましょうね\nシェフィさま");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 15, 6, 80005, 0, "", 404, 0, "わ、私も出るの！？　でも…\n私も【美食殿】の一人だものね\nそれなら今からしっかり準備をして\nコンディションを整えないと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 21, 6, 80005, 0, "", 405, 0, "ふふん♪\n生放送直前のカチコチになった\nシェフィの姿が楽しみだわ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 21, 6, 80005, 0, "", 406, 0, "去年のキャルちゃんは\n緊張しすぎてずっと震えて\nかわいかったですもんね！\n今年もあの姿が見たいです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 22, 6, 80005, 0, "", 407, 0, "ぶっ殺すぞ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 6, 6, 80005, 0, "", 408, 0, "というわけで\n『プリンセスコネクト！\nRe:Dive』の魅力を\nお伝えする生放送をお楽しみに♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 15, 7, 80005, 0, "", 409, 0, "3.5周年まであと８日\nでございますよ\n楽しみですね、主さま");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 23, 7, 80005, 0, "", 410, 0, "そういえばあんた\n夏休みの宿題は\nきちんとやってるの？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 16, 7, 80005, 0, "", 411, 0, "宿題…学校に通うのも\n楽しいことばかりじゃ\nないのね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 22, 7, 80005, 0, "", 412, 0, "あれ？　そういえば\n少し前にルーセント学院で\n何かあったような…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 24, 7, 80005, 0, "", 413, 0, "ああ、始業式の日に\n大捕り物があったって\n話題になってたわね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 17, 7, 80005, 0, "", 414, 0, "夏休みを終わらせないって\n騒いでたっていう話？\nあれ本当なの？\n都市伝説だと思ってたわ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 16, 7, 80005, 0, "", 415, 0, "その一件ならよく存じ上げて\nおります。ですから主さま\n次の機会にはわたくしと一緒に\nがんばりましょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 18, 7, 80005, 0, "", 416, 0, "楽しそう…私も一緒にやりたいな\nママ…じゃ、じゃなくて！\nコッコロさん、私もまざっていい？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 17, 7, 80005, 0, "", 417, 0, "もちろんでございます\n仲良くお勉強いたしましょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 25, 7, 80005, 0, "", 418, 0, "しれっと教育ママが\n生まれてる…？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 23, 7, 80005, 0, "", 419, 0, "お勉強は大切ですよ！\nキャルちゃんも一緒に\nがんばりましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 26, 7, 80005, 0, "", 420, 0, "え～～～～！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 27, 8, 80005, 0, "", 421, 0, "3.5周年まであと７日〜♪\nふふ～ん♪\nふんふふ～ん♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 19, 8, 80005, 0, "", 422, 0, "…\nねぇコッコロさん\nキャル、どうしちゃったの？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 18, 8, 80005, 0, "", 423, 0, "とてもご機嫌そうに\n鼻歌を歌ってらっしゃいますね\n耳も尻尾もリズミカルに\n揺れて…ノリノリでございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 24, 8, 80005, 0, "", 424, 0, "あれはですね！\nカルミナのみなさんの\nサマーライブのチケットが\n手に入ったからなんですよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 20, 8, 80005, 0, "", 425, 0, "ええっ！？\nあのライブのチケットって確か\n入手困難だったんじゃ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 28, 8, 80005, 0, "", 426, 0, "ふふふふ～ん♪\nしゃいにんぐふゅ～ちゃ～♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 19, 8, 80005, 0, "", 427, 0, "なるほど…\nキャルさまがあのように\nなってしまうのも頷けますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 21, 8, 80005, 0, "", 428, 0, "…カルミナの\n『Shining Future』\nの中に、シャイニングフューチャー\nって歌詞はなかったはずだけど…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 29, 8, 80005, 0, "", 429, 0, "ふんふ…はっ！？\nあっ、あんたたち！\nなに人の鼻歌を\n勝手に聞いてるのよ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 30, 8, 80005, 0, "", 430, 0, "こっ、これは別に\nカルミナの曲じゃなくて…\nあたしのオリジナルソングよ！\n聞いたらお金取るわよっ！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 25, 8, 80005, 0, "", 431, 0, "えへへ、キャルちゃん\nカルミナのサマーライブ\n思いっきり楽しみましょうね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 31, 8, 80005, 0, "", 432, 0, "も～っ！\n生暖かい目で見るな～～！\nぶっ殺すわよ～～～！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 22, 9, 80005, 0, "", 433, 0, "3.5周年まであと６日\n３人も言ってたけど\nあっという間だったわね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 23, 9, 80005, 0, "", 434, 0, "…えっ？\n生放送の意気込み！？\n突然なに！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 24, 9, 80005, 0, "", 435, 0, "ええと…\nとにかく失敗しないように\n台本たくさん読んで…\nそれから…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 25, 9, 80005, 0, "", 436, 0, "えっ！？\nトラブルがあったら\nどうするのかって！？\nそ、それは…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 26, 9, 80005, 0, "", 437, 0, "とにかく落ち着いて\nスタッフさんの指示を聞いて…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 27, 9, 80005, 0, "", 438, 0, "それから、それから…！\nぅ、ぅぅっ…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 28, 9, 80005, 0, "", 439, 0, "―びえぇぇ～んっ！！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 20, 9, 80005, 0, "", 440, 0, "どうされましたか、シェフィさま");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 29, 9, 80005, 0, "", 441, 0, "ううっ…ひっく…\nママッ…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 21, 9, 80005, 0, "", 442, 0, "よしよし、緊張されてるのですね\nママが傍におりますよ\n主さま、少々お待ちくださいませ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 22, 9, 80005, 0, "", 443, 0, "…ふぅ\nシェフィさま\n生放送への意気込み\nお話しできそうですか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 30, 9, 80005, 0, "", 444, 0, "…ううっ\n恥ずかしすぎて\nもう生放送出られない…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 23, 10, 80005, 0, "", 445, 0, "主さま\n3.5周年まであと５日\nでございます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 24, 10, 80005, 0, "", 446, 0, "ですがその前に\n生放送がありますね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 25, 10, 80005, 0, "", 447, 0, "…生放送に向けた\n意気込みでございますか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 26, 10, 80005, 0, "", 448, 0, "もちろんわたくしは\n主さまのために\n全身全霊を込めて\n楽しい時間をご提供いたします");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 27, 10, 80005, 0, "", 449, 0, "…と、意気込んでみましたが\nうまくできるか少し不安です\n声が小さくなったり\n間違えてしまったらどうしようと…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 28, 10, 80005, 0, "", 450, 0, "ですがわたくしは\n一人ではありません\n【美食殿】のみなさまが\nおります");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 29, 10, 80005, 0, "", 451, 0, "どんな困難も\nみなさまと一緒なら\n乗り越えられると\nわたくし、確信しております");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 30, 10, 80005, 0, "", 452, 0, "それに今回はシェフィさまも\nご参加されるわけですし\nママとしてお手本に\nならねばなりませんね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 0, "コッコロ", 0, 31, 10, 80005, 0, "", 453, 0, "主さま。3.5周年も\n食卓を囲んでいる時のような\n楽しい思い出を\n一緒に作りましょうね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 26, 11, 80005, 0, "", 454, 0, "3.5周年まであと４日！\nやばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 32, 11, 80005, 0, "", 455, 0, "はいはい、やばいわね\nというかもうすぐ生放送だけど\nあんた準備はできてるの？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 27, 11, 80005, 0, "", 456, 0, "ばっちりオイッスー☆　ですよ！\n『王家の装備』も全開です！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 28, 11, 80005, 0, "", 457, 0, "あとは前回を超える\n最高のオイッスー☆　を\nお届けできるように\n練習あるのみですかね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 29, 11, 80005, 0, "", 458, 0, "キャルちゃんは\nどうなんですか？\nオイッスー☆　を\n毎日ちゃんと練習してますか？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 33, 11, 80005, 0, "", 459, 0, "はぁ！？　なんであたしまで\nその変なあいさつを練習しないと\nいけないのよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 30, 11, 80005, 0, "", 460, 0, "もしかしてやってないんですか？\nダメですよキャルちゃん！\nシェフィちゃんだってちゃんと\n毎日練習してるのに！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 31, 11, 80005, 0, "", 461, 0, "こうなったらわたしがキャルちゃん\nにオイッスー☆　の『オ』から\nしっかり教えてあげないと！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 32, 11, 80005, 0, "", 462, 0, "オイッスー☆　の『オ』は\n口を大きく開いて〜\nドラゴンの骨つき肉に\nかぶりつく直前をイメージして〜");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 34, 11, 80005, 0, "", 463, 0, "だからあたしはやるなんて一言も…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 0, "ペコリーヌ", 0, 33, 11, 80005, 0, "", 464, 0, "オイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 35, 11, 80005, 0, "", 465, 0, "聞きなさいっての！！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 31, 12, 80005, 0, "", 466, 0, "3.5周年まであと３日…\nそして生放送は、明日…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 32, 12, 80005, 0, "", 467, 0, "もっと台本を\n読み込んでおかないと…\nあと発声練習も…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 33, 12, 80005, 0, "", 468, 0, "おいっす！\n…こうじゃなかったわね\nええと確か…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 34, 12, 80005, 0, "", 469, 0, "うう…あんなに練習したのに\nこれじゃみんなに迷惑かけちゃう…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 35, 12, 80005, 0, "", 470, 0, "【美食殿】の一員として\n認められるように\n頑張らないといけないのに\nよし、もう一度…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 34, 12, 80005, 0, "", 471, 0, "やってますねシェフィちゃん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 36, 12, 80005, 0, "", 472, 0, "なによ、考えることは\nみんな同じってこと？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 32, 12, 80005, 0, "", 473, 0, "ふふ。シェフィさま\nよろしければ\n仲間に入れてください\n一緒にやればきっとうまくできます");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 36, 12, 80005, 0, "", 474, 0, "みんな…もちろんよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 35, 12, 80005, 0, "", 475, 0, "それじゃ明日の生放送に向けて\n一緒に練習がんばりましょう☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 7, 13, 80005, 0, "", 476, 0, "3.5周年まであと２日と\n迫った本日は…\nいよいよ生放送です！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 8, 13, 80005, 0, "", 477, 0, "【美食殿】のみなさんが\n準備に入られていますので\nそれぞれ繋いでみましょう！\nまずはペコリーヌさんです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 36, 13, 80005, 0, "", 478, 0, "今日もオイッスー☆\nみんなで一緒に\nオイッスー☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 9, 13, 80005, 0, "", 479, 0, "お手本になるような\n発声練習ですね！\nそれでは続いてコッコロさん");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 33, 13, 80005, 0, "", 480, 0, "はじめちょろちょろ\nなかぱっぱ～♪\n生放送前にみなさまで\n食卓を囲んで英気を養いましょう");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 10, 13, 80005, 0, "", 481, 0, "さすが【美食殿】のママですね！\nそしてキャルさんは…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 37, 13, 80005, 0, "", 482, 0, "大丈夫…大丈夫…\nあたしは大丈夫…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 11, 13, 80005, 0, "", 483, 0, "す、すごく震えてますけど\n大丈夫でしょうか…？\nそして今回初参加となる\n期待の新人シェフィさん！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 37, 13, 80005, 0, "", 484, 0, "あうー？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 4, "みんな", 0, 37, 13, 80005, 1, "105901,34,106001,38,190801,12", 485, 0, "あ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 39, 13, 80005, 0, "", 486, 0, "ちょ、ちょっとシェフィ！？\nなに赤ちゃん返りしてん―\nあ痛ぁっ！\n噛んだわこいつーっ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 35, 13, 80005, 0, "", 487, 0, "シェフィさま\n緊張しすぎて\nしまったのでしょうね…\nおーよしよし…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 38, 13, 80005, 0, "", 488, 0, "シェフィちゃん！\nそういう時は手のひらに\n好きな文字を書いて\n食べちゃうといいですよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 13, 13, 80005, 0, "", 489, 0, "…ええと、現場からは以上です！\n生放送はもうすぐ！\n特別なお知らせもありますので\nどうぞお楽しみに！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 38, 14, 80005, 0, "", 490, 0, "3.5周年の記念日も\nいよいよ明日ね\n…って、あれ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 39, 14, 80005, 0, "", 491, 0, "記念日はこの森へ行きませんか！？\nすっごく美味しいお肉が\nとれるんです☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 40, 14, 80005, 0, "", 492, 0, "うげ、またあの魔物？\nさすがに今度は\n丸焼きとかはやめてよね…\n見た目がヤバすぎるし");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 36, 14, 80005, 0, "", 493, 0, "ふふ、キャルさまもだいぶ\n魔物肉に慣れてきたようですね\n調理ならばわたくしに\nお任せください♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 39, 14, 80005, 0, "", 494, 0, "みんな、何をしているの？\nなんだか、\n不穏な単語が聞こえたんだけど…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 40, 14, 80005, 0, "", 495, 0, "シェフィちゃん！\n明日の食べ尽くしツアーの\n相談をしてたんですよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 1, "コッコロ", 0, 37, 14, 80005, 0, "", 496, 0, "はい。明日は【美食殿】の\n一員となったシェフィさまと一緒に\n初めて記念日を過ごすのですから\n特別なコース料理を用意しませんと");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 0, "キャル", 0, 41, 14, 80005, 0, "", 497, 0, "ふふん♪\n猫の毛も逆立つような\nあれこれを食べさせられるから\n覚悟しておいたほうがいいわよ？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 40, 14, 80005, 0, "", 498, 0, "へ、へぇ…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 41, 14, 80005, 0, "", 499, 0, "期待しててくださいね！\nシェフィちゃんとあなたに\n『おいちー！』って喜んでもらえる\n最高のごちそうを準備しますから☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 41, 14, 80005, 0, "", 500, 0, "ええと…その…うーんと…\nわ、分かったわ\n私も【美食殿】の一員だもの");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 2, "シェフィ", 0, 42, 14, 80005, 0, "", 501, 0, "それにあなたも一緒みたいだし\n最後までがんばってみるわ…！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 42, 15, 80005, 0, "", 502, 0, "それではいつものあれを\nやりましょう！\n準備はいいですかっ？\n…いっきますよー！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 42, 15, 80005, 0, "", 503, 0, "祝！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 38, 15, 80005, 0, "", 504, 0, "3.5周年！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 43, 15, 80005, 0, "", 505, 0, "おめでとうございます！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 14, 15, 80005, 0, "", 506, 0, "この日を迎えられたのも\nあなたが『プリンセスコネクト！\nRe:Dive』を遊んでくれている\nおかげです！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190801, 2, "カリン", 0, 15, 15, 80005, 0, "", 507, 0, "ということで\nみなさんからあなたへ一言ずつ\n感謝の言葉をどうぞ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 43, 15, 80005, 0, "", 508, 0, "感謝の言葉ですか？　わたしは\n毎日あなたに感謝してますよ\n【美食殿】の仲間になってくれて…\n一緒にごはんを食べてくれて…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 44, 15, 80005, 0, "", 509, 0, "なにより今でもわたしをぺコリーヌ\nって呼んでくれてありがとうござい\nます。これからもわたしはあなたの\nぺコリーヌです！　やばいですね☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 43, 15, 80005, 0, "", 510, 0, "えっ！？　今度はあたし！？\n感謝って…まぁ、そうね…\nあんたにはあたしが\n辛くて苦しい時に助けてもらって…");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106001, 1, "キャル", 0, 44, 15, 80005, 0, "", 511, 0, "でもおかげで色々吹っ切れたわ！\nもしあんたが困ってたら今度は\nあたしが助けてあげるから\nその時はちゃんと言いなさいよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 44, 15, 80005, 0, "", 512, 0, "ええと…次は私ね\n実はよく覚えてないのだけど\n空から落ちてきた私を受け止めて\nくれたのはあなたなのでしょう？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (106401, 0, "シェフィ", 0, 45, 15, 80005, 0, "", 513, 0, "最初に私を見つけてくれたのが\nあなたで良かったって思ってるの\nおかげで私は居場所を見つけたから\nあなたがくれたもの、大切にするわ");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 39, 15, 80005, 0, "", 514, 0, "では最後にわたくしから…\nわたくしは主さまの元を離れた日…\nこのように記念日をお祝いする\nことは二度とないと思っていました");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 40, 15, 80005, 0, "", 515, 0, "ですが、主さまはわたくしを\n迎えにきてくださり、今もこうして\nおそばにおいてくださっています\nわたくしにとって毎日が記念日です");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105901, 4, "コッコロ", 0, 41, 15, 80005, 0, "", 516, 0, "わたくしたちの物語は\nまだ続いていきます\nこれからも一緒にページを\n作っていきましょうね");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 3, "ペコリーヌ", 0, 45, 15, 80005, 0, "", 517, 0, "それじゃ\nいつものあれでしめますよ！\nみんなで一緒に、さんはい☆");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (105801, 4, "みんな", 0, 46, 15, 80005, 1, "105901,42,106001,45,106401,46,190801,16", 518, 0, "これからも\nよろしくおねがいします！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 1, "カリン", 1, 1, 0, 60003, 0, "", 519, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 520, 1, "今日の運勢は姫吉！　どんな不可能も可能にできちゃう！　　　　仲間と食卓を囲めばさらに運気上昇よ！　ただし魔物料理以外！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 521, 1, "今日の運勢は姫吉！　やること全てが最高潮！　やばいですね☆　ラッキーアイテムはおにぎり！　お腹一杯で幸せが続くでしょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 522, 1, "今日の運勢は姫吉！　羽が生えたように飛躍できる絶好調の運気！新しいことに挑戦するなら今しかないわ！　やってみましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 523, 1, "今日の運勢は姫吉！　精霊も新年の幸福をお祝いしています！　　主さまの笑顔が見られて、わたくしも幸せいっぱいでございます♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60003, 0, "", 524, 1, "今日の運勢は姫吉！　やること全てが上手くいく最高の一日！　　あなたの活躍に先生もにっこりです。いい子いい子♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 525, 2, "今日の運勢は大吉！　お裁縫好きな女の子のお墨付きです！　　　お世話したがりの妹にめいっぱい愛されちゃうかも！？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 526, 2, "今日の運勢は大吉！　ラッキーカラーは赤！　　　　　　　　　　湧き上がるアイデアの数々に、高笑いが止まりませんわ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 527, 2, "今日の運勢は大吉！　ラッキーカラーは鮮やかな黄色！　　　　　さらにシマシマのアイテムを持つと、運気倍増らしいッスよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 528, 2, "今日の運勢は大吉！　秘められた力が開花するでしょう！　　　　じゃあ、私の龍眼と力比べしてみようか？　ふふ、冗談だよ～");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60003, 0, "", 529, 2, "今日の運勢は大吉！　悩んでいたことが解決しそうです！　　　　たまには勉強も仕事も忘れて、心あたたまる家族と過ごしましょ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 530, 3, "今日の運勢は中吉！　家の中でゆっくり過ごすのがよさそう！　　ラッキーアイテムはゲームよ！　ベストスコアを更新できるかも？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 531, 3, "今日の運勢は中吉！　上手くなりたいことに積極的に挑戦してみてサックス好きの妹が、手取り足取りサポートしてくれるかも～？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 532, 3, "今日の運勢は中吉！　仕事運が急上昇中です！　　　　　　　　　きっと「煉獄に舞う一陣の疾風」の如き活躍ができますよ！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60003, 0, "", 533, 3, "今日の運勢は中吉！　日頃の努力が報われます！　　　　　　　　親しい友人がいるなら報告してみて。一緒に喜んでくれるはずだ。");
