INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60005, 0, "", 592, 4, "今日の運勢は小吉！　小さな勇気で踏み出す一歩が大事！\n遠くから見てるだけじゃダメ。今日は、少しだけ近づいてみます！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60005, 0, "", 593, 4, "今日の運勢は小吉！　外に出てゆっくり過ごすと運気が上昇！　　ラッキースポットは牧場。面白い出会いがありそうです♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60005, 0, "", 594, 4, "今日の運勢は小吉！　いろんな事件が起こる一日になりそう！　　状況をよく観察してみようじゃないか！　まさに探偵のごとくね！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60005, 0, "", 595, 4, "今日の運勢は小吉！　難しい頼み事をされるかもしれません！　　うまくやれればきっと成長につながります、挑戦してみましょう！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60005, 0, "", 596, 4, "今日の運勢は小吉！　空を眺めると気持ちが晴れやかに！　　　　眺めるだけじゃなくて、ひとっ飛びしちゃいましょうかっ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60005, 0, "", 597, 4, "今日の運勢は小吉！　掃除をすると運勢が開けそう！　　　　　　「わ、めんど」「ヴルムっ！　新年早々サボっちゃダメなんだからね！」");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 2, 0, 60005, 0, "", 598, 0, "おみくじの結果はどうでしたか？\n新たに始まる一日が、あなたに\nとって良い日になりますように\nまた明日も遊びに来て下さいね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 3, "カリン", 0, 3, 7, 60005, 0, "", 599, 0, "おみくじは楽しんで頂けましたか？\nあなたの新たなる年が\n素晴らしいものになるよう、\n心からお祈りいたします");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (190804, 1, "カリン", 1, 1, 0, 60006, 0, "", 600, 0, "新年を迎え、新たに始まる一日を\nおみくじで占ってみませんか？\n良い運勢であるほど、\n沢山のプレゼントが貰えますよ♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 601, 1, "今日の運勢は姫吉！　勇気を持って、新しいことに挑戦しよう！\n幸先がいいねえ少年！　今年もいい一年になるよう祈ってるよ。");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 602, 1, "今日の運勢は姫吉！　どんなピンチも簡単に乗り越えられそう！\nよい結果が出たようで何よりです。さて、待人の方は……ふふ。");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 603, 1, "今日の運勢は姫吉！　あの人と仲直り……できちゃうかも！？\nはあ……この私があなたの運勢に興味を抱くわけないでしょう？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60006, 0, "", 604, 2, "今日の運勢は大吉！　身近な友達との絆が深まる予感……？\n騎士様は、どなたと友誼を結ぶのでしょう？　気になります……");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 605, 2, "今日の運勢は大吉！　ラッキーアイテムは「蛇の抜け殻」！\nこのアタシにお目にかかれるなんて……アンタ、幸せものね♪");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 606, 3, "今日の運勢は中吉！　気になるあの人に会えちゃうかも！？\n『初夢を正夢にする枕』であなたに会え……な、なんでもない！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 607, 3, "今日の運勢は中吉！　子供に戻って、一日中遊びつくそう！\nお年玉でけん玉ゲット！　おじちゃん、もしかめ一万回やって！");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 1, 0, 0, 60006, 0, "", 608, 3, "今日の運勢は中吉！　今日はおうちで、ゆーっくりくつろごう。\nお雑煮よし。みかんよし。さ、こたつで寝正月だ。君も入る？");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 609, 4, "今日の運勢は小吉！　突然のドッキリに注意！\nおや、後ろに髪の長い女の霊が──フフ、冗談です。私ですよ。");
INSERT INTO `v1_d4f377f387234a3db63cc8e6a5bb2373c8252c5081c1166ce74ad085bd1d0015` VALUES (0, 2, "", 2, 0, 0, 60006, 0, "", 610, 4, "今日の運勢は小吉。慌てなくても、幸運はきっとやってくる。\n運は自分で掴むもの──媛が言ってました。でも、どうやって？");
