INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, 10.85, 1, 0.0, "2024/03/31 12:00:00", 10142, 100, 129901, 7);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, 11.15, 1, 0.0, "2024/03/31 12:00:00", 10142, 101, 129901, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, 6.4, 7, 0.0, "2024/03/31 12:00:00", 10142, 102, 129901, 5);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, 0.0, 3, 0.0, "2024/03/31 12:00:00", 10142, 103, 129901, 3);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, 2.43, 2, 0.0, "2024/03/31 12:00:00", 10142, 104, 129901, 8);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, 9.08, 4, 0.0, "2024/03/31 12:00:00", 10142, 105, 129901, 3);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, 0.0, 6, 0.0, "2024/03/31 12:00:00", 10142, 106, 129901, 6);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, 10.6, 3, 0.0, "2024/03/31 12:00:00", 10142, 107, 129901, 8);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, 7.54, 6, 0.0, "2024/03/31 12:00:00", 10142, 108, 129901, 4);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, 11.5, 2, 0.0, "2024/03/31 12:00:00", 10142, 109, 129901, 7);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, 8.83, 2, 0.0, "2024/03/31 12:00:00", 10142, 110, 129901, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, 6.68, 1, 0.0, "2024/03/31 12:00:00", 10142, 111, 129901, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, 8.8, 6, 0.0, "2024/03/31 12:00:00", 10142, 112, 129901, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, 8.9, 2, 0.0, "2024/03/31 12:00:00", 10142, 113, 129901, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("エリコ", 24, "2030/12/17 14:59:59", 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, 3.7, 2, 0.0, "2024/03/31 12:00:00", 10142, 114, 129901, 5);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, 7.6, 1, 0.0, "2024/03/31 12:00:00", 10142, 200, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, 2.25, 2, 0.0, "2024/03/31 12:00:00", 10142, 201, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, 3.28, 2, 0.0, "2024/03/31 12:00:00", 10142, 202, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, 2.98, 4, 0.0, "2024/03/31 12:00:00", 10142, 203, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, 3.0, 4, 0.0, "2024/03/31 12:00:00", 10142, 204, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, 10.3, 1, 0.0, "2024/03/31 12:00:00", 10142, 205, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, 2.93, 6, 0.0, "2024/03/31 12:00:00", 10142, 206, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, 9.3, 3, 0.0, "2024/03/31 12:00:00", 10142, 207, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, 6.9, 4, 0.0, "2024/03/31 12:00:00", 10142, 208, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, 0.0, 2, 0.0, "2024/03/31 12:00:00", 10142, 209, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, 5.95, 1, 0.0, "2024/03/31 12:00:00", 10142, 210, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, 6.73, 2, 0.0, "2024/03/31 12:00:00", 10142, 211, 129801, 1);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, 0.0, 2, 0.0, "2024/03/31 12:00:00", 10142, 212, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, 6.65, 1, 0.0, "2024/03/31 12:00:00", 10142, 213, 129801, 2);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("マコト", 24, "2030/12/17 14:59:59", 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, 3.05, 6, 0.0, "2024/03/31 12:00:00", 10142, 214, 129801, 1);
