INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 100, 0.0, 7, 24, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", "エリコ", 10.85, 10142, 129901, 100);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 101, 0.0, 2, 24, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", "エリコ", 11.15, 10142, 129901, 101);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 7, "2030/12/17 14:59:59", 102, 0.0, 5, 24, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", "エリコ", 6.4, 10142, 129901, 102);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 3, "2030/12/17 14:59:59", 103, 0.0, 3, 24, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", "エリコ", 0.0, 10142, 129901, 103);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 104, 0.0, 8, 24, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", "エリコ", 2.43, 10142, 129901, 104);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 4, "2030/12/17 14:59:59", 105, 0.0, 3, 24, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", "エリコ", 9.08, 10142, 129901, 105);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 6, "2030/12/17 14:59:59", 106, 0.0, 6, 24, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", "エリコ", 0.0, 10142, 129901, 106);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 3, "2030/12/17 14:59:59", 107, 0.0, 8, 24, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", "エリコ", 10.6, 10142, 129901, 107);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 6, "2030/12/17 14:59:59", 108, 0.0, 4, 24, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", "エリコ", 7.54, 10142, 129901, 108);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 109, 0.0, 7, 24, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", "エリコ", 11.5, 10142, 129901, 109);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 110, 0.0, 1, 24, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", "エリコ", 8.83, 10142, 129901, 110);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 111, 0.0, 2, 24, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", "エリコ", 6.68, 10142, 129901, 111);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 6, "2030/12/17 14:59:59", 112, 0.0, 2, 24, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", "エリコ", 8.8, 10142, 129901, 112);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 113, 0.0, 1, 24, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", "エリコ", 8.9, 10142, 129901, 113);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 114, 0.0, 5, 24, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", "エリコ", 3.7, 10142, 129901, 114);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 200, 0.0, 2, 24, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", "マコト", 7.6, 10142, 129801, 200);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 201, 0.0, 1, 24, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", "マコト", 2.25, 10142, 129801, 201);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 202, 0.0, 1, 24, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", "マコト", 3.28, 10142, 129801, 202);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 4, "2030/12/17 14:59:59", 203, 0.0, 1, 24, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", "マコト", 2.98, 10142, 129801, 203);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 4, "2030/12/17 14:59:59", 204, 0.0, 1, 24, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", "マコト", 3.0, 10142, 129801, 204);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 205, 0.0, 2, 24, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", "マコト", 10.3, 10142, 129801, 205);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 6, "2030/12/17 14:59:59", 206, 0.0, 1, 24, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", "マコト", 2.93, 10142, 129801, 206);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 3, "2030/12/17 14:59:59", 207, 0.0, 1, 24, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", "マコト", 9.3, 10142, 129801, 207);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 4, "2030/12/17 14:59:59", 208, 0.0, 1, 24, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", "マコト", 6.9, 10142, 129801, 208);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 209, 0.0, 2, 24, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", "マコト", 0.0, 10142, 129801, 209);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 210, 0.0, 2, 24, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", "マコト", 5.95, 10142, 129801, 210);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 211, 0.0, 1, 24, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", "マコト", 6.73, 10142, 129801, 211);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 2, "2030/12/17 14:59:59", 212, 0.0, 2, 24, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", "マコト", 0.0, 10142, 129801, 212);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 1, "2030/12/17 14:59:59", 213, 0.0, 2, 24, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", "マコト", 6.65, 10142, 129801, 213);
INSERT INTO `v1_d75214c0ec7e8e8d7314493ee45a28518d2ec427e1c98bc3788e2c0830eb1dda` VALUES ("2024/03/31 12:00:00", 0.0, 6, "2030/12/17 14:59:59", 214, 0.0, 1, 24, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", "マコト", 3.05, 10142, 129801, 214);
