INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (1, 122601, 1, 105, 160, 110, 1.5, 1, 101, 0.029, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (2, 122401, 3, 111, 120, 105, 2.5, 2, 102, 0.014, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (3, 122501, 2, 105, 350, 110, 2.5, 3, 103, 0.017, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (4, 107501, 3, 105, 130, 110, 1.5, 4, 104, 0.016, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (5, 107601, 1, 105, 160, 110, 2.5, 5, 105, 0.014, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (6, 107801, 2, 103, 255, 120, 3.5, 6, 106, 0.029, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (7, 110601, 1, 103, 160, 120, 3.5, 7, 107, 0.013, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (8, 110401, 2, 103, 350, 110, 2.5, 8, 108, 0.014, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (9, 110501, 3, 100, 130, 105, 1.5, 9, 109, 0.012, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (10, 115701, 1, 103, 200, 105, 2.5, 10, 110, 0.014, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (11, 113601, 2, 103, 350, 105, 2.5, 11, 111, 0.071, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (12, 107901, 3, 103, 130, 120, 1.5, 12, 112, 0.014, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (13, 108001, 2, 105, 200, 110, 1.5, 13, 113, 0.022, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (14, 110301, 1, 108, 160, 110, 2.5, 14, 114, 0.031, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (15, 107701, 1, 105, 130, 110, 0.5, 15, 115, 0.036, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (16, 113101, 2, 95, 350, 110, 3.5, 16, 116, 0.048, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (17, 113301, 3, 105, 130, 105, 1.5, 17, 117, 0.043, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (18, 113201, 2, 95, 130, 105, 1.5, 18, 118, 0.018, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (19, 117001, 2, 108, 160, 110, 2.5, 19, 119, 0.021, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (20, 113501, 1, 105, 255, 110, 2.5, 20, 120, 0.036, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (21, 113401, 3, 105, 350, 105, 2.5, 21, 121, 0.02, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (22, 110101, 1, 105, 130, 110, 2.5, 22, 122, 0.018, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (23, 110001, 3, 111, 160, 105, 3.5, 23, 123, 0.03, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (24, 117201, 1, 103, 130, 110, 1.5, 24, 124, 0.027, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (25, 117301, 2, 108, 160, 105, 3.5, 25, 125, 0.02, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 1, 0, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (26, 117401, 3, 105, 350, 110, 2.5, 26, 126, 0.04, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (27, 117101, 1, 105, 350, 110, 3.5, 27, 127, 0.009, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (28, 122901, 1, 105, 255, 120, 3.5, 28, 128, 0.022, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 0, 0, 1);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (29, 122801, 3, 105, 130, 110, 2.5, 29, 129, 0.028, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 0, 1, 0);
INSERT INTO `v1_d8b4e3b1872beb11d7c620cf5b470a99049adf9b39bb1a16155bbbad0794a427` VALUES (30, 122701, 2, 108, 130, 105, 3.5, 30, 130, 0.023, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 1, 0, 0);
