INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 1.5, 1, 160, 1, 105, 0, 110, 122601, 0.029, 101, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 2.5, 0, 120, 2, 111, 1, 105, 122401, 0.014, 102, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 2);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 1, 2.5, 0, 350, 3, 105, 0, 110, 122501, 0.017, 103, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 3);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 1.5, 0, 130, 4, 105, 1, 110, 107501, 0.016, 104, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 4);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 2.5, 1, 160, 5, 105, 0, 110, 107601, 0.014, 105, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 5);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 1, 3.5, 0, 255, 6, 103, 0, 120, 107801, 0.029, 106, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 6);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 3.5, 1, 160, 7, 103, 0, 120, 110601, 0.013, 107, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 7);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 1, 2.5, 0, 350, 8, 103, 0, 110, 110401, 0.014, 108, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 8);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 1.5, 0, 130, 9, 100, 1, 105, 110501, 0.012, 109, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 9);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 2.5, 1, 200, 10, 103, 0, 105, 115701, 0.014, 110, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 10);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 1, 2.5, 0, 350, 11, 103, 0, 105, 113601, 0.071, 111, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 11);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 1.5, 0, 130, 12, 103, 1, 120, 107901, 0.014, 112, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 12);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 0, 1.5, 1, 200, 13, 105, 0, 110, 108001, 0.022, 113, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 13);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 2.5, 1, 160, 14, 108, 0, 110, 110301, 0.031, 114, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 14);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 0.5, 0, 130, 15, 105, 1, 110, 107701, 0.036, 115, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 15);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 1, 3.5, 0, 350, 16, 95, 0, 110, 113101, 0.048, 116, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 16);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 1.5, 0, 130, 17, 105, 1, 105, 113301, 0.043, 117, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 17);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 0, 1.5, 0, 130, 18, 95, 1, 105, 113201, 0.018, 118, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 18);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 0, 2.5, 1, 160, 19, 108, 0, 110, 117001, 0.021, 119, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 19);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 1, 2.5, 0, 255, 20, 105, 0, 110, 113501, 0.036, 120, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 20);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 1, 2.5, 0, 350, 21, 105, 0, 105, 113401, 0.02, 121, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 21);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 2.5, 0, 130, 22, 105, 1, 110, 110101, 0.018, 122, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 22);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 3.5, 1, 160, 23, 111, 0, 105, 110001, 0.03, 123, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 23);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 0, 1.5, 0, 130, 24, 103, 1, 110, 117201, 0.027, 124, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 24);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 0, 3.5, 1, 160, 25, 108, 0, 105, 117301, 0.02, 125, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 25);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 1, 2.5, 0, 350, 26, 105, 0, 110, 117401, 0.04, 126, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 26);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 1, 3.5, 0, 350, 27, 105, 0, 110, 117101, 0.009, 127, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 27);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 1, 3.5, 0, 255, 28, 105, 0, 120, 122901, 0.022, 128, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 28);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 0, 2.5, 0, 130, 29, 105, 1, 110, 122801, 0.028, 129, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 29);
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 0, 3.5, 1, 130, 30, 108, 0, 105, 122701, 0.023, 130, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 30);
