INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (1, 105, 122601, 1, 0.029, 1, 0, 110, 0, 160, 1, 1.5, 101, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (2, 111, 122401, 3, 0.014, 2, 1, 105, 0, 120, 0, 2.5, 102, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (3, 105, 122501, 2, 0.017, 3, 0, 110, 1, 350, 0, 2.5, 103, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (4, 105, 107501, 3, 0.016, 4, 1, 110, 0, 130, 0, 1.5, 104, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (5, 105, 107601, 1, 0.014, 5, 0, 110, 0, 160, 1, 2.5, 105, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (6, 103, 107801, 2, 0.029, 6, 0, 120, 1, 255, 0, 3.5, 106, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (7, 103, 110601, 1, 0.013, 7, 0, 120, 0, 160, 1, 3.5, 107, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (8, 103, 110401, 2, 0.014, 8, 0, 110, 1, 350, 0, 2.5, 108, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (9, 100, 110501, 3, 0.012, 9, 1, 105, 0, 130, 0, 1.5, 109, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (10, 103, 115701, 1, 0.014, 10, 0, 105, 0, 200, 1, 2.5, 110, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (11, 103, 113601, 2, 0.071, 11, 0, 105, 1, 350, 0, 2.5, 111, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (12, 103, 107901, 3, 0.014, 12, 1, 120, 0, 130, 0, 1.5, 112, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (13, 105, 108001, 2, 0.022, 13, 0, 110, 0, 200, 1, 1.5, 113, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (14, 108, 110301, 1, 0.031, 14, 0, 110, 0, 160, 1, 2.5, 114, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (15, 105, 107701, 1, 0.036, 15, 1, 110, 0, 130, 0, 0.5, 115, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (16, 95, 113101, 2, 0.048, 16, 0, 110, 1, 350, 0, 3.5, 116, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (17, 105, 113301, 3, 0.043, 17, 1, 105, 0, 130, 0, 1.5, 117, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (18, 95, 113201, 2, 0.018, 18, 1, 105, 0, 130, 0, 1.5, 118, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (19, 108, 117001, 2, 0.021, 19, 0, 110, 0, 160, 1, 2.5, 119, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (20, 105, 113501, 1, 0.036, 20, 0, 110, 1, 255, 0, 2.5, 120, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (21, 105, 113401, 3, 0.02, 21, 0, 105, 1, 350, 0, 2.5, 121, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (22, 105, 110101, 1, 0.018, 22, 1, 110, 0, 130, 0, 2.5, 122, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (23, 111, 110001, 3, 0.03, 23, 0, 105, 0, 160, 1, 3.5, 123, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (24, 103, 117201, 1, 0.027, 24, 1, 110, 0, 130, 0, 1.5, 124, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (25, 108, 117301, 2, 0.02, 25, 0, 105, 0, 160, 1, 3.5, 125, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (26, 105, 117401, 3, 0.04, 26, 0, 110, 1, 350, 0, 2.5, 126, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (27, 105, 117101, 1, 0.009, 27, 0, 110, 1, 350, 0, 3.5, 127, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (28, 105, 122901, 1, 0.022, 28, 0, 120, 1, 255, 0, 3.5, 128, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (29, 105, 122801, 3, 0.028, 29, 1, 110, 0, 130, 0, 2.5, 129, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_d99342c97a582638dba3332ebbf9453dae70b7b2f3455597d4179cb0dc23fea3` VALUES (30, 108, 122701, 2, 0.023, 30, 0, 105, 0, 130, 1, 3.5, 130, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
