INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("エルピス島原産　火の実、オオトウガラシ", 10135107, 1, 91002, "我慢くらべはほどほどに　火の実の湯", 8, "2024/01/16 12:00:00", "エルピス島のドラゴンもどきから分けて\nもらった火の実をふんだんにつかった\n情熱的な温泉。ひとたび浸かれば汗が\n滝のように噴き出してくる。その熱さは\n己の限界を極めようとする漢・漢女たちの\n勝負心をがっちりと掴んで離さない。", 1010801, "冷え性改善、代謝促進、保温効果\n※長時間の入浴にはご注意ください", 106701, 10108, 40, 106601);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("エルフの森産　かぐわし花のエッセンス", 0, 2, 91002, "心華やぐリラクゼーション　アロマの湯", 8, "2024/01/17", "香り高い花とその花のアロマエッセンスを\n使用した目にも鮮やかな温泉。時間を\n忘れるほどのリラクゼーション効果は\n忙しい都会人のニーズにピッタリ。高い\n美容効果もあいまって、若い女性たちの\n間で大注目のスポットになりつつある。", 1010802, "美肌効果、リラックス効果、保香効果\n※甘酸っぱい気持ちになることがあります", 101501, 10108, 40, 101601);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("仲居特製の炭酸泉、メロンエッセンス", 0, 3, 91002, "童心くすぐる微炭酸　メロンソーダの湯", 8, "2024/01/17", "優しい微炭酸と甘いメロンの香りに満ちた\n遊び心をくすぐる温泉。子どもたちが喜ぶ\nような温泉を、というミツキの想いが形に\nなったもの。いつもは大人びている優等生\nさんも、この湯舟に浸かればつい年相応に\nはしゃいでしまうのだとか。", 1010803, "血行促進、ピーリング効果\n童心に帰る効果", 105101, 10108, 40, 103601);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("若女将の魔法（電撃）", 0, 4, 91002, "ときめき電撃かけながし☆　ビリビリの湯", 8, "2024/01/18", "当旅館自慢の源泉に若女将の電撃を加えた\n刺激的な温泉。体中のがんこなコリに効果\nてき面と大好評。今日もデスクワーカー\nから老人たちまで、コリをほぐしに足を\n運んでいる。ちょっぴり痛みを感じるのも\nクセになる理由の一つなのだとか。", 1010804, "腰痛・肩こりの解消、血行促進\nビリビリエクスタシートリップ（？）", 106701, 10108, 40, 104501);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("エルフの森産　ヒールリーフの根っこ", 0, 5, 91002, "明日への活力をあなたに　疲労回復の湯", 8, "2024/01/18", "あらゆる疲労を回復すると名高い温泉。\nエルフの森自慢の薬草類を調合し、その\n効果を高めた薬湯は、疲れ切った人々の\nいこいの場。世の働き者たちは、この湯\nに浸かっては働き、また浸かっては働く\nのを繰り返して精を出すのだとか。", 1010805, "超・肉体疲労回復、超・神経疲労回復\n超・精神疲労回復", 101501, 10108, 40, 104801);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("仲居特製のカンポー、希少なトウガラシ", 0, 6, 91002, "荒業デトックス！　ヒリヒリの湯", 8, "2024/01/19", "あまりの辛さで一般市場では流通できない\nとまで言われるトウガラシをぜいたくに\n使用した高デトックス効果のある温泉。\n湯気も刺激的なため、希望者には目を守る\nゴーグルも貸し出している（お気軽に\nフロントまでお申しつけください）", 1010806, "脂肪燃焼効果、高デトックス効果\n※長時間の入浴には十分にご注意ください", 105101, 10108, 40, 118001);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("若女将特製のポーション、元気なゼラチナ", 0, 7, 91002, "高粘度の新感覚温泉　ゼラチナの湯", 8, "2024/01/19", "魔力を回復するポーションとゼラチナを\nかけ合わせた異色の温泉。ゼラチナたちは\n若女将がスカウトしてきたアルバイトとの\nこと。その不思議な湯ざわりと確かな魔力\n回復効果は、新しもの好きの魔法使いや\n魔族たちの間で話題になっているとか。", 1010807, "魔力回復、美肌効果、保湿効果\n※お湯に甘噛みされることがあります", 106701, 10108, 40, 106001);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("エルフの森産　スースー草のシードオイル", 0, 8, 91002, "心と体に爽やかな癒しを♪　スースーの湯", 8, "2024/01/20", "清涼効果のある薬草類を調合した薬湯。\n湯気を吸うことでのどや鼻が保湿され、\n声の通りが格段によくなると好評。\n湯上がり後のスッキリ感も相まって\nリピーターが多い。歌手やアイドルたちも\nお忍びでこっそり訪れているとか。", 1010808, "清涼効果、のど・鼻の保湿効果\n※鼻歌が旅館内に届く可能性があります", 101501, 10108, 40, 102101);
INSERT INTO `v1_dda5948e88cc23ee77c17f7f6135f697e5ce0c54c97edaad6b158588c5aeaee6` VALUES ("仲居特製の超美肌薬、大輪のバラ", 0, 9, 91002, "ああバラ色の人生　美肌の湯", 8, "2024/01/20", "とにもかくにも肌を美しくする効果を追求\nした珠玉の温泉。お湯を軽く浴びただけで\n違いが分かるとウワサになり、このお湯を\n利用した化粧水の発売も模索されている\nとか。ちなみにバラはあくまで香りづけの\nものであり、美肌効果はないとのこと。", 1010809, "美肌効果、保湿効果、保温効果\nバラの香りによるリラックス効果", 105101, 10108, 40, 100801);
