INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1002001, 1002, 190801, 12, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1003001, 1003, 190801, 11, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1004001, 1004, 190801, 3, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1005001, 1005, 190801, 7, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1006001, 1006, 190801, 2, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", 0, "2020-05-31 12:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1007001, 1007, 190801, 4, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", 0, "2020-08-15 15:00:00", "2030-04-01 14:00:00");
