INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1010001, 1010, 190801, 4, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", 0, "2022-02-15 15:00:00", "2030-04-01 14:00:00");
INSERT INTO `v1_de2c5f96e02e8bac63300b6795d13a500e0468fbfe4ad6d68dfcc456f8a03bcc` VALUES (1011001, 1011, 190801, 6, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", 0, "2022-07-18 15:00:00", "2030-04-01 14:00:00");
