INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (100, "エリコ", 1, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 0.0, "2030/12/17 14:59:59", 10142, 129901, 10.85, 0.0, 24, 100, 7, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (101, "エリコ", 1, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 0.0, "2030/12/17 14:59:59", 10142, 129901, 11.15, 0.0, 24, 101, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (102, "エリコ", 7, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 0.0, "2030/12/17 14:59:59", 10142, 129901, 6.4, 0.0, 24, 102, 5, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (103, "エリコ", 3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 0.0, "2030/12/17 14:59:59", 10142, 129901, 0.0, 0.0, 24, 103, 3, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (104, "エリコ", 2, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 0.0, "2030/12/17 14:59:59", 10142, 129901, 2.43, 0.0, 24, 104, 8, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (105, "エリコ", 4, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 0.0, "2030/12/17 14:59:59", 10142, 129901, 9.08, 0.0, 24, 105, 3, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (106, "エリコ", 6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 0.0, "2030/12/17 14:59:59", 10142, 129901, 0.0, 0.0, 24, 106, 6, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (107, "エリコ", 3, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 0.0, "2030/12/17 14:59:59", 10142, 129901, 10.6, 0.0, 24, 107, 8, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (108, "エリコ", 6, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 0.0, "2030/12/17 14:59:59", 10142, 129901, 7.54, 0.0, 24, 108, 4, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (109, "エリコ", 2, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 0.0, "2030/12/17 14:59:59", 10142, 129901, 11.5, 0.0, 24, 109, 7, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (110, "エリコ", 2, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 0.0, "2030/12/17 14:59:59", 10142, 129901, 8.83, 0.0, 24, 110, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (111, "エリコ", 1, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 0.0, "2030/12/17 14:59:59", 10142, 129901, 6.68, 0.0, 24, 111, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (112, "エリコ", 6, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 0.0, "2030/12/17 14:59:59", 10142, 129901, 8.8, 0.0, 24, 112, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (113, "エリコ", 2, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 0.0, "2030/12/17 14:59:59", 10142, 129901, 8.9, 0.0, 24, 113, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (114, "エリコ", 2, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 0.0, "2030/12/17 14:59:59", 10142, 129901, 3.7, 0.0, 24, 114, 5, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (200, "マコト", 1, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 7.6, 0.0, 24, 200, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (201, "マコト", 2, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 2.25, 0.0, 24, 201, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (202, "マコト", 2, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 3.28, 0.0, 24, 202, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (203, "マコト", 4, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 2.98, 0.0, 24, 203, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (204, "マコト", 4, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 3.0, 0.0, 24, 204, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (205, "マコト", 1, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 10.3, 0.0, 24, 205, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (206, "マコト", 6, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 2.93, 0.0, 24, 206, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (207, "マコト", 3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 9.3, 0.0, 24, 207, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (208, "マコト", 4, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 6.9, 0.0, 24, 208, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (209, "マコト", 2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 0.0, 0.0, 24, 209, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (210, "マコト", 1, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 5.95, 0.0, 24, 210, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (211, "マコト", 2, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 0.0, "2030/12/17 14:59:59", 10142, 129801, 6.73, 0.0, 24, 211, 1, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (212, "マコト", 2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 0.0, 0.0, 24, 212, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (213, "マコト", 1, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 0.0, "2030/12/17 14:59:59", 10142, 129801, 6.65, 0.0, 24, 213, 2, "2024/03/31 12:00:00");
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (214, "マコト", 6, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 0.0, "2030/12/17 14:59:59", 10142, 129801, 3.05, 0.0, 24, 214, 1, "2024/03/31 12:00:00");
