INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 129901, 100, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 100, 10142, "2024/03/31 12:00:00", 10.85, 7, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 129901, 101, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 101, 10142, "2024/03/31 12:00:00", 11.15, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (7, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 129901, 102, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 102, 10142, "2024/03/31 12:00:00", 6.4, 5, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (3, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 129901, 103, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 103, 10142, "2024/03/31 12:00:00", 0.0, 3, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 129901, 104, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 104, 10142, "2024/03/31 12:00:00", 2.43, 8, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (4, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 129901, 105, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 105, 10142, "2024/03/31 12:00:00", 9.08, 3, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (6, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 129901, 106, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 106, 10142, "2024/03/31 12:00:00", 0.0, 6, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (3, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 129901, 107, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 107, 10142, "2024/03/31 12:00:00", 10.6, 8, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (6, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 129901, 108, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 108, 10142, "2024/03/31 12:00:00", 7.54, 4, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 129901, 109, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 109, 10142, "2024/03/31 12:00:00", 11.5, 7, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 129901, 110, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 110, 10142, "2024/03/31 12:00:00", 8.83, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 129901, 111, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 111, 10142, "2024/03/31 12:00:00", 6.68, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (6, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 129901, 112, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 112, 10142, "2024/03/31 12:00:00", 8.8, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 129901, 113, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 113, 10142, "2024/03/31 12:00:00", 8.9, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 129901, 114, "エリコ", "2030/12/17 14:59:59", 0.0, 0.0, 114, 10142, "2024/03/31 12:00:00", 3.7, 5, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 129801, 200, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 200, 10142, "2024/03/31 12:00:00", 7.6, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 129801, 201, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 201, 10142, "2024/03/31 12:00:00", 2.25, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 129801, 202, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 202, 10142, "2024/03/31 12:00:00", 3.28, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (4, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 129801, 203, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 203, 10142, "2024/03/31 12:00:00", 2.98, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (4, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 129801, 204, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 204, 10142, "2024/03/31 12:00:00", 3.0, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 129801, 205, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 205, 10142, "2024/03/31 12:00:00", 10.3, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (6, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 129801, 206, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 206, 10142, "2024/03/31 12:00:00", 2.93, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (3, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 129801, 207, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 207, 10142, "2024/03/31 12:00:00", 9.3, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (4, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 129801, 208, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 208, 10142, "2024/03/31 12:00:00", 6.9, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 129801, 209, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 209, 10142, "2024/03/31 12:00:00", 0.0, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 129801, 210, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 210, 10142, "2024/03/31 12:00:00", 5.95, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 129801, 211, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 211, 10142, "2024/03/31 12:00:00", 6.73, 1, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (2, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 129801, 212, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 212, 10142, "2024/03/31 12:00:00", 0.0, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (1, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 129801, 213, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 213, 10142, "2024/03/31 12:00:00", 6.65, 2, 24);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (6, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 129801, 214, "マコト", "2030/12/17 14:59:59", 0.0, 0.0, 214, 10142, "2024/03/31 12:00:00", 3.05, 1, 24);
