INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 10.85, 7, "エリコ", 0.0, 100, "訓練成功、おめでとうございます\n褒めるべき時にはしっかり褒めるのも\n教官の務めですものね。それとも、\n罵られる方が好みですか？　クスクス", 100, "2030/12/17 14:59:59", 129901, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 11.15, 2, "エリコ", 0.0, 101, "厳しい訓練も、終わってみれば\n短く感じるものですわね。\n正直、私は暴れ足りないくらい…\nいえ、何でもありませんわ。クスクス", 101, "2030/12/17 14:59:59", 129901, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 6.4, 5, "エリコ", 0.0, 102, "行く手を阻む忌々しい邪魔者を\n蹴散らして、見事ゴールイン…！\nまるで私とあのおかたの人生を\n象徴するような道のりでした…", 102, "2030/12/17 14:59:59", 129901, 24, 7);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 0.0, 3, "エリコ", 0.0, 103, "まったくもって不甲斐ない結果です\nしかし、挽回の機会はまだありますわ\n次はゴミ虫の地位から脱却できるよう\n死力を尽くしなさい。いいですね？", 103, "2030/12/17 14:59:59", 129901, 24, 3);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 2.43, 8, "エリコ", 0.0, 104, "クスクス…ああご心配なさらず\nなんとも情けない成績を残す輩には\nどんな罰が相応しいか、考えている\nだけですので…クスクスクス", 104, "2030/12/17 14:59:59", 129901, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 9.08, 3, "エリコ", 0.0, 105, "私が教官として認められなければ、\nあのおかたを失望させてしまう…\nそれだけはあってはなりません\nあなた方も、わかっていますわね？", 105, "2030/12/17 14:59:59", 129901, 24, 4);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 0.0, 6, "エリコ", 0.0, 106, "まずまずの結果ではありますが、\nこの程度で満足してもらっては\n困りますわ。エリコ班の一員として、\nより高みを目指していただきますので", 106, "2030/12/17 14:59:59", 129901, 24, 6);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 10.6, 8, "エリコ", 0.0, 107, "私の指導を受けながら、この結果は…\nもっと真剣に考えねばなりませんね\nあなた方が怠けずにいるには、\nどんなしつけが必要かを…クスクス", 107, "2030/12/17 14:59:59", 129901, 24, 3);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 7.54, 4, "エリコ", 0.0, 108, "ひとまず訓練をこなしたご褒美か、\n実力を出しきれたとは言えない罰か…\nみなさんにどちらを与えるべきか\n少し悩む結果ですわね", 108, "2030/12/17 14:59:59", 129901, 24, 6);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 11.5, 7, "エリコ", 0.0, 109, "なかなかいい戦いぶりでしたわ\nこれもあなた方の頑張りと、何より\n優秀な教官である私の教育の賜物\nそうですわね？　クスクスクス…", 109, "2030/12/17 14:59:59", 129901, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 8.83, 1, "エリコ", 0.0, 110, "これだけ進めたのならば合格点ですわ\nでも、もっと高みを目指したいと\n思っているのなら…さらなる訓練にも\n付き合って差し上げましょう", 110, "2030/12/17 14:59:59", 129901, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 6.68, 2, "エリコ", 0.0, 111, "未熟で頼りなかったあなた方が、\n見違えるように成長しました\nなるほど…これこそが、教官冥利に\n尽きるという気持ちなのですね", 111, "2030/12/17 14:59:59", 129901, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 8.8, 2, "エリコ", 0.0, 112, "まさかあなた方が、私の予想すら\n超える力を発揮するとは…驚きました\nもはやゴミ虫などではありません\n一人前の騎士として胸を張りなさい", 112, "2030/12/17 14:59:59", 129901, 24, 6);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 8.9, 1, "エリコ", 0.0, 113, "クスクス、見事な働きです\nみなさんならできると信じていました\n他でもない私の指導を受けた、\nエリコ班の優秀な新兵なのですから", 113, "2030/12/17 14:59:59", 129901, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 3.7, 5, "エリコ", 0.0, 114, "なんてすばらしい成績…！\n今日という日は『あのおかたを想い\nエリコが限界を超えた記念日』として\n歴史に刻まれることでしょう…！", 114, "2030/12/17 14:59:59", 129901, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 7.6, 2, "マコト", 0.0, 200, "よし、いい訓練になったな\nおまえたちもよく動けてたし、\nちゃんと成長できてると思うぜ\nその調子で頑張れよっ", 200, "2030/12/17 14:59:59", 129801, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 2.25, 1, "マコト", 0.0, 201, "ひとまずゴールおめでとう！\nでも、努力は継続してこそだからな\nおまえたちにやる気があるなら、\n何度だって付き合ってやるよ", 201, "2030/12/17 14:59:59", 129801, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 3.28, 1, "マコト", 0.0, 202, "行軍訓練はこれにて完了！\n…なーんて思ってないよな？\n学んだことが体に染みつくまで\n地道に繰り返すのが練習ってもんさ", 202, "2030/12/17 14:59:59", 129801, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 2.98, 1, "マコト", 0.0, 203, "みんなに無理をさせちまったか…\nでもこれで諦めることはないぜ\n次はきっとうまくいく。そう信じて\n鍛え続ければ、道は開けるはずだ！", 203, "2030/12/17 14:59:59", 129801, 24, 4);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 3.0, 1, "マコト", 0.0, 204, "こいつは悔しい結果だな…\nま、反省点はいろいろあったし\nひとつずつ乗り越えていこう\nあたしたちで力を合わせて、なっ！", 204, "2030/12/17 14:59:59", 129801, 24, 4);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 10.3, 2, "マコト", 0.0, 205, "おまえたちはまだ未熟な訓練兵、\n失敗を必要以上に悔やむことはねえよ\nでも実戦じゃこうはいかないからな\n次は気合を入れ直していけよっ！", 205, "2030/12/17 14:59:59", 129801, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 2.93, 1, "マコト", 0.0, 206, "これがおまえたちの全力？\nいやいや、そんなはずはないよなっ\nおまえらならもっと強くなれる！\nあたしが言うんだ、間違いないぜ！", 206, "2030/12/17 14:59:59", 129801, 24, 6);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 9.3, 1, "マコト", 0.0, 207, "おまえたちの実力を考えりゃ、\nベストを尽くせたとは言えない成績だ\nなら、次やるべきことも決まってる\n気持ちを切り替えて、鍛え直すんだ！", 207, "2030/12/17 14:59:59", 129801, 24, 3);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 6.9, 1, "マコト", 0.0, 208, "まだまだ課題がある内容だったな～\n無駄な動き、判断の遅れ…\nそういうのをしっかり直していけば、\n記録ももっと伸びるはずだっ", 208, "2030/12/17 14:59:59", 129801, 24, 4);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 0.0, 2, "マコト", 0.0, 209, "ここまでいけりゃ、合格点！\nむしろ、最初の頼りなさを思えば\nすげえ成長してるんじゃねえか？\n胸を張れる結果だと思うぜっ！", 209, "2030/12/17 14:59:59", 129801, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 5.95, 2, "マコト", 0.0, 210, "なかなかハードな訓練だったけど、\nみんなしっかりついてこられたな\n地道に続けた基礎トレの成果が\n形になってる、その証だぜっ", 210, "2030/12/17 14:59:59", 129801, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 6.73, 1, "マコト", 0.0, 211, "今回の訓練もよく頑張ったな！\nしっかり休んで次に備えろよ！\nなんつーか、教え子が成長してくのを\n見るのは楽しいもんだな…", 211, "2030/12/17 14:59:59", 129801, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 0.0, 2, "マコト", 0.0, 212, "おまえら、すげえじゃねえか！\nこれならもう、ひよっこ新兵なんて\n言えねえな。きっと騎士団でも\n大活躍間違いなしだぜ！", 212, "2030/12/17 14:59:59", 129801, 24, 2);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 6.65, 2, "マコト", 0.0, 213, "敵との戦い方、状況判断の正確さ\n何より根性…どれも最高だったぞ！\nうかうかしてたら、教官のあたしまで\n追い抜かれちまうかもな…へへっ", 213, "2030/12/17 14:59:59", 129801, 24, 1);
INSERT INTO `v1_e017b1ccacdf49be036d5b5e13ef17db36498845ea9ee14d683db91f6e8142ea` VALUES (0.0, 10142, "2024/03/31 12:00:00", 3.05, 1, "マコト", 0.0, 214, "あたしの指導が良かった？\nいやいや、ここまで強くなれたのは、\n他でもないおまえたち自身が努力した\nおかげだよ。改めて…おめでとうっ！", 214, "2030/12/17 14:59:59", 129801, 24, 6);
