INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (1, 1, 105, 122601, 1, 0, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 1, 1.5, 0, 110, 101, 160, 0.029);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (2, 2, 111, 122401, 0, 0, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 3, 2.5, 1, 105, 102, 120, 0.014);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (3, 3, 105, 122501, 0, 1, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 2, 2.5, 0, 110, 103, 350, 0.017);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (4, 4, 105, 107501, 0, 0, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 3, 1.5, 1, 110, 104, 130, 0.016);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (5, 5, 105, 107601, 1, 0, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 1, 2.5, 0, 110, 105, 160, 0.014);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (6, 6, 103, 107801, 0, 1, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 2, 3.5, 0, 120, 106, 255, 0.029);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (7, 7, 103, 110601, 1, 0, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 1, 3.5, 0, 120, 107, 160, 0.013);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (8, 8, 103, 110401, 0, 1, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 2, 2.5, 0, 110, 108, 350, 0.014);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (9, 9, 100, 110501, 0, 0, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 3, 1.5, 1, 105, 109, 130, 0.012);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (10, 10, 103, 115701, 1, 0, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 1, 2.5, 0, 105, 110, 200, 0.014);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (11, 11, 103, 113601, 0, 1, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 2, 2.5, 0, 105, 111, 350, 0.071);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (12, 12, 103, 107901, 0, 0, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 3, 1.5, 1, 120, 112, 130, 0.014);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (13, 13, 105, 108001, 1, 0, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 2, 1.5, 0, 110, 113, 200, 0.022);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (14, 14, 108, 110301, 1, 0, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 1, 2.5, 0, 110, 114, 160, 0.031);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (15, 15, 105, 107701, 0, 0, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 1, 0.5, 1, 110, 115, 130, 0.036);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (16, 16, 95, 113101, 0, 1, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 2, 3.5, 0, 110, 116, 350, 0.048);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (17, 17, 105, 113301, 0, 0, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 3, 1.5, 1, 105, 117, 130, 0.043);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (18, 18, 95, 113201, 0, 0, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 2, 1.5, 1, 105, 118, 130, 0.018);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (19, 19, 108, 117001, 1, 0, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 2, 2.5, 0, 110, 119, 160, 0.021);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (20, 20, 105, 113501, 0, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 1, 2.5, 0, 110, 120, 255, 0.036);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (21, 21, 105, 113401, 0, 1, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 3, 2.5, 0, 105, 121, 350, 0.02);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (22, 22, 105, 110101, 0, 0, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 1, 2.5, 1, 110, 122, 130, 0.018);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (23, 23, 111, 110001, 1, 0, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 3, 3.5, 0, 105, 123, 160, 0.03);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (24, 24, 103, 117201, 0, 0, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 1, 1.5, 1, 110, 124, 130, 0.027);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (25, 25, 108, 117301, 1, 0, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 2, 3.5, 0, 105, 125, 160, 0.02);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (26, 26, 105, 117401, 0, 1, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 3, 2.5, 0, 110, 126, 350, 0.04);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (27, 27, 105, 117101, 0, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 1, 3.5, 0, 110, 127, 350, 0.009);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (28, 28, 105, 122901, 0, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 1, 3.5, 0, 120, 128, 255, 0.022);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (29, 29, 105, 122801, 0, 0, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 3, 2.5, 1, 110, 129, 130, 0.028);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (30, 30, 108, 122701, 1, 0, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 2, 3.5, 0, 105, 130, 130, 0.023);
