INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (1, 110, 0, 101, 0, 122601, 1, 0.029, 1, 1.5, 105, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (2, 105, 0, 102, 1, 122401, 0, 0.014, 3, 2.5, 111, 2, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 120);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (3, 110, 1, 103, 0, 122501, 0, 0.017, 2, 2.5, 105, 3, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (4, 110, 0, 104, 1, 107501, 0, 0.016, 3, 1.5, 105, 4, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (5, 110, 0, 105, 0, 107601, 1, 0.014, 1, 2.5, 105, 5, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (6, 120, 1, 106, 0, 107801, 0, 0.029, 2, 3.5, 103, 6, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 255);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (7, 120, 0, 107, 0, 110601, 1, 0.013, 1, 3.5, 103, 7, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (8, 110, 1, 108, 0, 110401, 0, 0.014, 2, 2.5, 103, 8, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (9, 105, 0, 109, 1, 110501, 0, 0.012, 3, 1.5, 100, 9, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (10, 105, 0, 110, 0, 115701, 1, 0.014, 1, 2.5, 103, 10, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 200);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (11, 105, 1, 111, 0, 113601, 0, 0.071, 2, 2.5, 103, 11, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (12, 120, 0, 112, 1, 107901, 0, 0.014, 3, 1.5, 103, 12, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (13, 110, 0, 113, 0, 108001, 1, 0.022, 2, 1.5, 105, 13, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 200);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (14, 110, 0, 114, 0, 110301, 1, 0.031, 1, 2.5, 108, 14, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (15, 110, 0, 115, 1, 107701, 0, 0.036, 1, 0.5, 105, 15, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (16, 110, 1, 116, 0, 113101, 0, 0.048, 2, 3.5, 95, 16, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (17, 105, 0, 117, 1, 113301, 0, 0.043, 3, 1.5, 105, 17, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (18, 105, 0, 118, 1, 113201, 0, 0.018, 2, 1.5, 95, 18, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (19, 110, 0, 119, 0, 117001, 1, 0.021, 2, 2.5, 108, 19, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (20, 110, 1, 120, 0, 113501, 0, 0.036, 1, 2.5, 105, 20, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 255);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (21, 105, 1, 121, 0, 113401, 0, 0.02, 3, 2.5, 105, 21, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (22, 110, 0, 122, 1, 110101, 0, 0.018, 1, 2.5, 105, 22, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (23, 105, 0, 123, 0, 110001, 1, 0.03, 3, 3.5, 111, 23, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (24, 110, 0, 124, 1, 117201, 0, 0.027, 1, 1.5, 103, 24, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (25, 105, 0, 125, 0, 117301, 1, 0.02, 2, 3.5, 108, 25, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 160);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (26, 110, 1, 126, 0, 117401, 0, 0.04, 3, 2.5, 105, 26, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (27, 110, 1, 127, 0, 117101, 0, 0.009, 1, 3.5, 105, 27, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 350);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (28, 120, 1, 128, 0, 122901, 0, 0.022, 1, 3.5, 105, 28, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 255);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (29, 110, 0, 129, 1, 122801, 0, 0.028, 3, 2.5, 105, 29, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 130);
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (30, 105, 0, 130, 0, 122701, 1, 0.023, 2, 3.5, 108, 30, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 130);
