INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (1, 0, 1, 105, 1.5, 1, 122601, 101, 110, 1, 160, 0, 0.029, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (2, 1, 0, 111, 2.5, 2, 122401, 102, 105, 3, 120, 0, 0.014, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (3, 0, 0, 105, 2.5, 3, 122501, 103, 110, 2, 350, 1, 0.017, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (4, 1, 0, 105, 1.5, 4, 107501, 104, 110, 3, 130, 0, 0.016, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (5, 0, 1, 105, 2.5, 5, 107601, 105, 110, 1, 160, 0, 0.014, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (6, 0, 0, 103, 3.5, 6, 107801, 106, 120, 2, 255, 1, 0.029, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (7, 0, 1, 103, 3.5, 7, 110601, 107, 120, 1, 160, 0, 0.013, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (8, 0, 0, 103, 2.5, 8, 110401, 108, 110, 2, 350, 1, 0.014, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (9, 1, 0, 100, 1.5, 9, 110501, 109, 105, 3, 130, 0, 0.012, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (10, 0, 1, 103, 2.5, 10, 115701, 110, 105, 1, 200, 0, 0.014, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (11, 0, 0, 103, 2.5, 11, 113601, 111, 105, 2, 350, 1, 0.071, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (12, 1, 0, 103, 1.5, 12, 107901, 112, 120, 3, 130, 0, 0.014, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (13, 0, 1, 105, 1.5, 13, 108001, 113, 110, 2, 200, 0, 0.022, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (14, 0, 1, 108, 2.5, 14, 110301, 114, 110, 1, 160, 0, 0.031, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (15, 1, 0, 105, 0.5, 15, 107701, 115, 110, 1, 130, 0, 0.036, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (16, 0, 0, 95, 3.5, 16, 113101, 116, 110, 2, 350, 1, 0.048, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (17, 1, 0, 105, 1.5, 17, 113301, 117, 105, 3, 130, 0, 0.043, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (18, 1, 0, 95, 1.5, 18, 113201, 118, 105, 2, 130, 0, 0.018, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (19, 0, 1, 108, 2.5, 19, 117001, 119, 110, 2, 160, 0, 0.021, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (20, 0, 0, 105, 2.5, 20, 113501, 120, 110, 1, 255, 1, 0.036, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (21, 0, 0, 105, 2.5, 21, 113401, 121, 105, 3, 350, 1, 0.02, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (22, 1, 0, 105, 2.5, 22, 110101, 122, 110, 1, 130, 0, 0.018, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (23, 0, 1, 111, 3.5, 23, 110001, 123, 105, 3, 160, 0, 0.03, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (24, 1, 0, 103, 1.5, 24, 117201, 124, 110, 1, 130, 0, 0.027, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (25, 0, 1, 108, 3.5, 25, 117301, 125, 105, 2, 160, 0, 0.02, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (26, 0, 0, 105, 2.5, 26, 117401, 126, 110, 3, 350, 1, 0.04, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (27, 0, 0, 105, 3.5, 27, 117101, 127, 110, 1, 350, 1, 0.009, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (28, 0, 0, 105, 3.5, 28, 122901, 128, 120, 1, 255, 1, 0.022, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (29, 1, 0, 105, 2.5, 29, 122801, 129, 110, 3, 130, 0, 0.028, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？");
INSERT INTO `v1_e0cdd776da3f2aa40717185bb281d139e51b18cdf23d33a0f8d44e2b6570b16f` VALUES (30, 0, 1, 108, 3.5, 30, 122701, 130, 105, 2, 130, 0, 0.023, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。");
