INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (13, 1001, 0, "こちらは{0}ルピで\n販売中！\nぜひ購入して\n遊んでくださいね！", 190801, "2030-04-01 14:00:00", 1001001, "2020-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (12, 1002, 0, "動くハートに合わせてタップ！\nシズルさんとエリコさんの\nチョコレートケーキ作りを\nお手伝いしましょう！", 190801, "2030-04-01 14:00:00", 1002001, "2020-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (11, 1003, 0, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！", 190801, "2030-04-01 14:00:00", 1003001, "2020-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (3, 1004, 0, "左右をタップしてマジムンを倒し\nましょう！\n放て！\n琉球犬アラウンドシュート！", 190801, "2030-04-01 14:00:00", 1004001, "2020-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (7, 1005, 0, "パネルをタップしてドラゴンズ\nネストのみなさんと一緒に\nしりとり対決をしませんか？\nおしりに「ん」はご法度です。", 190801, "2030-04-01 14:00:00", 1005001, "2020-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (2, 1006, 0, "素早い動きのネズミに合わせ、\nタップで撃退！\nやる気のリンさんと一緒に\n大切なチーズを守りましょう！", 190801, "2030-04-01 14:00:00", 1006001, "2020-05-31 12:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (4, 1007, 0, "ミサトさんや部員のみなさんと\nバッティングの特訓ですよ！\nホームランを目指して、ボールを\nタイミングよく打ち返しましょう", 190801, "2030-04-01 14:00:00", 1007001, "2020-08-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1008, 0, "カルミナメダルを使って、\n応援グッズをゲットしましょう！\nメダルはルピを使って\n交換できますよ。\n", 190801, "2030-04-01 14:00:00", 1008001, "2021-08-31 12:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (13, 1009, 0, "食べたプリンはプリンノート\nで確認してみましょう！\nプリンの材料はルピを使って\n購入できますよ。", 190801, "2030-04-01 14:00:00", 1009001, "2021-12-18 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (4, 1010, 0, "チームでクイズに挑みましょう！\nソロで自分の実力を試すか、マル\nチで仲間と一緒に遊ぶか、好きな\nモードを選んでくださいね♪", 190801, "2030-04-01 14:00:00", 1010001, "2022-02-15 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1011, 0, "３つのラウンドを駆け抜け、\n総合１位を目指しましょう！\n参加者の皆さんの特徴や、\nＧＰの特徴にも注目です♪", 190801, "2030-04-01 14:00:00", 1011001, "2022-07-18 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1012, 0, "キャルさん、スズナさんと一緒に\nクイズでお勉強しましょう！\n放課後の教室で二人きりって…\nなんだかドキドキしますね♪", 190801, "2030-04-01 14:00:00", 1012001, "2023-09-18 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1013, 0, "魔物の討伐任務にいざ行軍！\nエリコさん、マコトさんの\n頼もしい背中は、\n新兵さんたちの憧れですね♪", 190801, "2030-04-01 14:00:00", 1013001, "2024-04-18 15:00:00");
INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (6, 1014, 0, "バンディ・シスターズのみなさん\nと戦車バトルに挑戦です！\nパーツやメンバーの組み合わせで\nマシンの特徴が変化しますよ♪", 190801, "2030-04-01 14:00:00", 1014001, "2024-11-18 15:00:00");
