INSERT INTO `v1_e126a47905676890a95b544a2760b0678689327ece67a522e0bb2e05e32cbdb3` VALUES (13, 1000, 1000001, 0, "2020-02-15 15:00:00", "2030-04-01 14:00:00", 190801, "様々なミニゲームをご用意してい\nますよ！\nどのミニゲームで遊びますか？");
